        },{
          "_id": {
            "$oid": "5ecd87c02f61d12bf6abd123"
          },
          "AyaContent": "وما أسألكم عليه من أجر ۖ إن أجري إلا علىٰ رب العالمين",
          "AyaNumber": 3077,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c02f61d12bf6abd124"
          },
          "AyaContent": "أتتركون في ما هاهنا آمنين",
