          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c42f61d12bf6abd136"
          },
          "AyaContent": "وما أسألكم عليه من أجر ۖ إن أجري إلا علىٰ رب العالمين",
