            "$oid": "5ecd87c72f61d12bf6abd141"
          },
          "AyaContent": "وإن ربك لهو العزيز الرحيم",
          "AyaNumber": 3107,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c72f61d12bf6abd142"
          },
          "AyaContent": "كذب أصحاب الأيكة المرسلين",
          "AyaNumber": 3108,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c72f61d12bf6abd143"
          },
          "AyaContent": "إذ قال لهم شعيب ألا تتقون",
          "AyaNumber": 3109,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c82f61d12bf6abd144"
          },
          "AyaContent": "إني لكم رسول أمين",
          "AyaNumber": 3110,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c82f61d12bf6abd145"
          },
          "AyaContent": "فاتقوا الله وأطيعون",
          "AyaNumber": 3111,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c82f61d12bf6abd146"
          },
          "AyaContent": "وما أسألكم عليه من أجر ۖ إن أجري إلا علىٰ رب العالمين",
          "AyaNumber": 3112,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c82f61d12bf6abd147"
          },
          "AyaContent": "۞ أوفوا الكيل ولا تكونوا من المخسرين",
          "AyaNumber": 3113,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c92f61d12bf6abd148"
          },
          "AyaContent": "وزنوا بالقسطاس المستقيم",
          "AyaNumber": 3114,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c92f61d12bf6abd149"
          },
          "AyaContent": "ولا تبخسوا الناس أشياءهم ولا تعثوا في الأرض مفسدين",
          "AyaNumber": 3115,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87c92f61d12bf6abd14a"
          },
          "AyaContent": "واتقوا الذي خلقكم والجبلة الأولين",
          "AyaNumber": 3116,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87ca2f61d12bf6abd14b"
          },
          "AyaContent": "قالوا إنما أنت من المسحرين",
          "AyaNumber": 3117,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87ca2f61d12bf6abd14c"
          },
          "AyaContent": "وما أنت إلا بشر مثلنا وإن نظنك لمن الكاذبين",
          "AyaNumber": 3118,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87ca2f61d12bf6abd14d"
          },
          "AyaContent": "فأسقط علينا كسفا من السماء إن كنت من الصادقين",
          "AyaNumber": 3119,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87ca2f61d12bf6abd14e"
          },
          "AyaContent": "قال ربي أعلم بما تعملون",
          "AyaNumber": 3120,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cb2f61d12bf6abd14f"
          },
          "AyaContent": "فكذبوه فأخذهم عذاب يوم الظلة ۚ إنه كان عذاب يوم عظيم",
          "AyaNumber": 3121,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cb2f61d12bf6abd150"
          },
          "AyaContent": "إن في ذٰلك لآية ۖ وما كان أكثرهم مؤمنين",
          "AyaNumber": 3122,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cb2f61d12bf6abd151"
          },
          "AyaContent": "وإن ربك لهو العزيز الرحيم",
          "AyaNumber": 3123,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cb2f61d12bf6abd152"
          },
          "AyaContent": "وإنه لتنزيل رب العالمين",
          "AyaNumber": 3124,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cb2f61d12bf6abd153"
          },
          "AyaContent": "نزل به الروح الأمين",
          "AyaNumber": 3125,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cc2f61d12bf6abd154"
          },
          "AyaContent": "علىٰ قلبك لتكون من المنذرين",
          "AyaNumber": 3126,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cc2f61d12bf6abd155"
          },
          "AyaContent": "بلسان عربي مبين",
          "AyaNumber": 3127,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cc2f61d12bf6abd156"
          },
          "AyaContent": "وإنه لفي زبر الأولين",
          "AyaNumber": 3128,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cd2f61d12bf6abd157"
          },
          "AyaContent": "أولم يكن لهم آية أن يعلمه علماء بني إسرائيل",
          "AyaNumber": 3129,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cd2f61d12bf6abd158"
          },
          "AyaContent": "ولو نزلناه علىٰ بعض الأعجمين",
          "AyaNumber": 3130,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cd2f61d12bf6abd159"
          },
          "AyaContent": "فقرأه عليهم ما كانوا به مؤمنين",
          "AyaNumber": 3131,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87ce2f61d12bf6abd15a"
          },
          "AyaContent": "كذٰلك سلكناه في قلوب المجرمين",
          "AyaNumber": 3132,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87ce2f61d12bf6abd15b"
          },
          "AyaContent": "لا يؤمنون به حتىٰ يروا العذاب الأليم",
          "AyaNumber": 3133,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87ce2f61d12bf6abd15c"
          },
          "AyaContent": "فيأتيهم بغتة وهم لا يشعرون",
          "AyaNumber": 3134,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cf2f61d12bf6abd15d"
          },
          "AyaContent": "فيقولوا هل نحن منظرون",
          "AyaNumber": 3135,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cf2f61d12bf6abd15e"
          },
          "AyaContent": "أفبعذابنا يستعجلون",
          "AyaNumber": 3136,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cf2f61d12bf6abd15f"
          },
          "AyaContent": "أفرأيت إن متعناهم سنين",
          "AyaNumber": 3137,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cf2f61d12bf6abd160"
          },
          "AyaContent": "ثم جاءهم ما كانوا يوعدون",
          "AyaNumber": 3138,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87cf2f61d12bf6abd161"
          },
          "AyaContent": "ما أغنىٰ عنهم ما كانوا يمتعون",
          "AyaNumber": 3139,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d02f61d12bf6abd162"
          },
          "AyaContent": "وما أهلكنا من قرية إلا لها منذرون",
          "AyaNumber": 3140,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d02f61d12bf6abd163"
          },
          "AyaContent": "ذكرىٰ وما كنا ظالمين",
          "AyaNumber": 3141,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d02f61d12bf6abd164"
          },
          "AyaContent": "وما تنزلت به الشياطين",
          "AyaNumber": 3142,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d12f61d12bf6abd165"
          },
          "AyaContent": "وما ينبغي لهم وما يستطيعون",
          "AyaNumber": 3143,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d12f61d12bf6abd166"
          },
          "AyaContent": "إنهم عن السمع لمعزولون",
          "AyaNumber": 3144,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d12f61d12bf6abd167"
          },
          "AyaContent": "فلا تدع مع الله إلٰها آخر فتكون من المعذبين",
          "AyaNumber": 3145,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d12f61d12bf6abd168"
          },
          "AyaContent": "وأنذر عشيرتك الأقربين",
          "AyaNumber": 3146,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d12f61d12bf6abd169"
          },
          "AyaContent": "واخفض جناحك لمن اتبعك من المؤمنين",
          "AyaNumber": 3147,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d12f61d12bf6abd16a"
          },
          "AyaContent": "فإن عصوك فقل إني بريء مما تعملون",
          "AyaNumber": 3148,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d22f61d12bf6abd16b"
          },
          "AyaContent": "وتوكل على العزيز الرحيم",
          "AyaNumber": 3149,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d22f61d12bf6abd16c"
          },
          "AyaContent": "الذي يراك حين تقوم",
          "AyaNumber": 3150,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d22f61d12bf6abd16d"
          },
          "AyaContent": "وتقلبك في الساجدين",
          "AyaNumber": 3151,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d22f61d12bf6abd16e"
          },
          "AyaContent": "إنه هو السميع العليم",
          "AyaNumber": 3152,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d22f61d12bf6abd16f"
          },
          "AyaContent": "هل أنبئكم علىٰ من تنزل الشياطين",
          "AyaNumber": 3153,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d32f61d12bf6abd170"
          },
          "AyaContent": "تنزل علىٰ كل أفاك أثيم",
          "AyaNumber": 3154,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d32f61d12bf6abd171"
          },
          "AyaContent": "يلقون السمع وأكثرهم كاذبون",
          "AyaNumber": 3155,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d32f61d12bf6abd172"
          },
          "AyaContent": "والشعراء يتبعهم الغاوون",
          "AyaNumber": 3156,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d32f61d12bf6abd173"
          },
          "AyaContent": "ألم تر أنهم في كل واد يهيمون",
          "AyaNumber": 3157,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d42f61d12bf6abd174"
          },
          "AyaContent": "وأنهم يقولون ما لا يفعلون",
          "AyaNumber": 3158,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d42f61d12bf6abd175"
          },
          "AyaContent": "إلا الذين آمنوا وعملوا الصالحات وذكروا الله كثيرا وانتصروا من بعد ما ظلموا ۗ وسيعلم الذين ظلموا أي منقلب ينقلبون",
          "AyaNumber": 3159,
          "SurahName": "سورة الشعراء",
          "SurahNumber": 26
        },{
          "_id": {
            "$oid": "5ecd87d42f61d12bf6abd176"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم طس ۚ تلك آيات القرآن وكتاب مبين",
          "AyaNumber": 3160,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d42f61d12bf6abd177"
          },
          "AyaContent": "هدى وبشرىٰ للمؤمنين",
          "AyaNumber": 3161,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d52f61d12bf6abd178"
          },
          "AyaContent": "الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون",
          "AyaNumber": 3162,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d52f61d12bf6abd179"
          },
          "AyaContent": "إن الذين لا يؤمنون بالآخرة زينا لهم أعمالهم فهم يعمهون",
          "AyaNumber": 3163,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d52f61d12bf6abd17a"
          },
          "AyaContent": "أولٰئك الذين لهم سوء العذاب وهم في الآخرة هم الأخسرون",
          "AyaNumber": 3164,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d62f61d12bf6abd17b"
          },
          "AyaContent": "وإنك لتلقى القرآن من لدن حكيم عليم",
          "AyaNumber": 3165,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d62f61d12bf6abd17c"
          },
          "AyaContent": "إذ قال موسىٰ لأهله إني آنست نارا سآتيكم منها بخبر أو آتيكم بشهاب قبس لعلكم تصطلون",
          "AyaNumber": 3166,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d62f61d12bf6abd17d"
          },
          "AyaContent": "فلما جاءها نودي أن بورك من في النار ومن حولها وسبحان الله رب العالمين",
          "AyaNumber": 3167,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d72f61d12bf6abd17e"
          },
          "AyaContent": "يا موسىٰ إنه أنا الله العزيز الحكيم",
          "AyaNumber": 3168,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d72f61d12bf6abd17f"
          },
          "AyaContent": "وألق عصاك ۚ فلما رآها تهتز كأنها جان ولىٰ مدبرا ولم يعقب ۚ يا موسىٰ لا تخف إني لا يخاف لدي المرسلون",
          "AyaNumber": 3169,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d72f61d12bf6abd180"
          },
          "AyaContent": "إلا من ظلم ثم بدل حسنا بعد سوء فإني غفور رحيم",
          "AyaNumber": 3170,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d72f61d12bf6abd181"
          },
          "AyaContent": "وأدخل يدك في جيبك تخرج بيضاء من غير سوء ۖ في تسع آيات إلىٰ فرعون وقومه ۚ إنهم كانوا قوما فاسقين",
          "AyaNumber": 3171,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d72f61d12bf6abd182"
          },
          "AyaContent": "فلما جاءتهم آياتنا مبصرة قالوا هٰذا سحر مبين",
          "AyaNumber": 3172,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d82f61d12bf6abd183"
          },
          "AyaContent": "وجحدوا بها واستيقنتها أنفسهم ظلما وعلوا ۚ فانظر كيف كان عاقبة المفسدين",
          "AyaNumber": 3173,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d82f61d12bf6abd184"
          },
          "AyaContent": "ولقد آتينا داوود وسليمان علما ۖ وقالا الحمد لله الذي فضلنا علىٰ كثير من عباده المؤمنين",
          "AyaNumber": 3174,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d82f61d12bf6abd185"
          },
          "AyaContent": "وورث سليمان داوود ۖ وقال يا أيها الناس علمنا منطق الطير وأوتينا من كل شيء ۖ إن هٰذا لهو الفضل المبين",
          "AyaNumber": 3175,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d82f61d12bf6abd186"
          },
          "AyaContent": "وحشر لسليمان جنوده من الجن والإنس والطير فهم يوزعون",
          "AyaNumber": 3176,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d82f61d12bf6abd187"
          },
          "AyaContent": "حتىٰ إذا أتوا علىٰ واد النمل قالت نملة يا أيها النمل ادخلوا مساكنكم لا يحطمنكم سليمان وجنوده وهم لا يشعرون",
          "AyaNumber": 3177,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d92f61d12bf6abd188"
          },
          "AyaContent": "فتبسم ضاحكا من قولها وقال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلىٰ والدي وأن أعمل صالحا ترضاه وأدخلني برحمتك في عبادك الصالحين",
          "AyaNumber": 3178,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d92f61d12bf6abd189"
          },
          "AyaContent": "وتفقد الطير فقال ما لي لا أرى الهدهد أم كان من الغائبين",
          "AyaNumber": 3179,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d92f61d12bf6abd18a"
          },
          "AyaContent": "لأعذبنه عذابا شديدا أو لأذبحنه أو ليأتيني بسلطان مبين",
          "AyaNumber": 3180,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d92f61d12bf6abd18b"
          },
          "AyaContent": "فمكث غير بعيد فقال أحطت بما لم تحط به وجئتك من سبإ بنبإ يقين",
          "AyaNumber": 3181,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87d92f61d12bf6abd18c"
          },
          "AyaContent": "إني وجدت امرأة تملكهم وأوتيت من كل شيء ولها عرش عظيم",
          "AyaNumber": 3182,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87da2f61d12bf6abd18d"
          },
          "AyaContent": "وجدتها وقومها يسجدون للشمس من دون الله وزين لهم الشيطان أعمالهم فصدهم عن السبيل فهم لا يهتدون",
          "AyaNumber": 3183,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87da2f61d12bf6abd18e"
          },
          "AyaContent": "ألا يسجدوا لله الذي يخرج الخبء في السماوات والأرض ويعلم ما تخفون وما تعلنون",
          "AyaNumber": 3184,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87da2f61d12bf6abd18f"
          },
          "AyaContent": "الله لا إلٰه إلا هو رب العرش العظيم ۩",
          "AyaNumber": 3185,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87da2f61d12bf6abd190"
          },
          "AyaContent": "۞ قال سننظر أصدقت أم كنت من الكاذبين",
          "AyaNumber": 3186,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87db2f61d12bf6abd191"
          },
          "AyaContent": "اذهب بكتابي هٰذا فألقه إليهم ثم تول عنهم فانظر ماذا يرجعون",
          "AyaNumber": 3187,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87db2f61d12bf6abd192"
          },
          "AyaContent": "قالت يا أيها الملأ إني ألقي إلي كتاب كريم",
          "AyaNumber": 3188,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87db2f61d12bf6abd193"
          },
          "AyaContent": "إنه من سليمان وإنه بسم الله الرحمٰن الرحيم",
          "AyaNumber": 3189,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87db2f61d12bf6abd194"
          },
          "AyaContent": "ألا تعلوا علي وأتوني مسلمين",
          "AyaNumber": 3190,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87db2f61d12bf6abd195"
          },
          "AyaContent": "قالت يا أيها الملأ أفتوني في أمري ما كنت قاطعة أمرا حتىٰ تشهدون",
          "AyaNumber": 3191,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87dc2f61d12bf6abd196"
          },
          "AyaContent": "قالوا نحن أولو قوة وأولو بأس شديد والأمر إليك فانظري ماذا تأمرين",
          "AyaNumber": 3192,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87dc2f61d12bf6abd197"
          },
          "AyaContent": "قالت إن الملوك إذا دخلوا قرية أفسدوها وجعلوا أعزة أهلها أذلة ۖ وكذٰلك يفعلون",
          "AyaNumber": 3193,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87dc2f61d12bf6abd198"
          },
          "AyaContent": "وإني مرسلة إليهم بهدية فناظرة بم يرجع المرسلون",
          "AyaNumber": 3194,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87dd2f61d12bf6abd199"
          },
          "AyaContent": "فلما جاء سليمان قال أتمدونن بمال فما آتاني الله خير مما آتاكم بل أنتم بهديتكم تفرحون",
          "AyaNumber": 3195,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87dd2f61d12bf6abd19a"
          },
          "AyaContent": "ارجع إليهم فلنأتينهم بجنود لا قبل لهم بها ولنخرجنهم منها أذلة وهم صاغرون",
          "AyaNumber": 3196,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87dd2f61d12bf6abd19b"
          },
          "AyaContent": "قال يا أيها الملأ أيكم يأتيني بعرشها قبل أن يأتوني مسلمين",
          "AyaNumber": 3197,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87de2f61d12bf6abd19c"
          },
          "AyaContent": "قال عفريت من الجن أنا آتيك به قبل أن تقوم من مقامك ۖ وإني عليه لقوي أمين",
          "AyaNumber": 3198,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87de2f61d12bf6abd19d"
          },
          "AyaContent": "قال الذي عنده علم من الكتاب أنا آتيك به قبل أن يرتد إليك طرفك ۚ فلما رآه مستقرا عنده قال هٰذا من فضل ربي ليبلوني أأشكر أم أكفر ۖ ومن شكر فإنما يشكر لنفسه ۖ ومن كفر فإن ربي غني كريم",
          "AyaNumber": 3199,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87de2f61d12bf6abd19e"
          },
          "AyaContent": "قال نكروا لها عرشها ننظر أتهتدي أم تكون من الذين لا يهتدون",
          "AyaNumber": 3200,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87de2f61d12bf6abd19f"
          },
          "AyaContent": "فلما جاءت قيل أهٰكذا عرشك ۖ قالت كأنه هو ۚ وأوتينا العلم من قبلها وكنا مسلمين",
          "AyaNumber": 3201,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87df2f61d12bf6abd1a0"
          },
          "AyaContent": "وصدها ما كانت تعبد من دون الله ۖ إنها كانت من قوم كافرين",
          "AyaNumber": 3202,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87df2f61d12bf6abd1a1"
          },
          "AyaContent": "قيل لها ادخلي الصرح ۖ فلما رأته حسبته لجة وكشفت عن ساقيها ۚ قال إنه صرح ممرد من قوارير ۗ قالت رب إني ظلمت نفسي وأسلمت مع سليمان لله رب العالمين",
          "AyaNumber": 3203,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87df2f61d12bf6abd1a2"
          },
          "AyaContent": "ولقد أرسلنا إلىٰ ثمود أخاهم صالحا أن اعبدوا الله فإذا هم فريقان يختصمون",
          "AyaNumber": 3204,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87df2f61d12bf6abd1a3"
          },
          "AyaContent": "قال يا قوم لم تستعجلون بالسيئة قبل الحسنة ۖ لولا تستغفرون الله لعلكم ترحمون",
          "AyaNumber": 3205,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e02f61d12bf6abd1a4"
          },
          "AyaContent": "قالوا اطيرنا بك وبمن معك ۚ قال طائركم عند الله ۖ بل أنتم قوم تفتنون",
          "AyaNumber": 3206,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e02f61d12bf6abd1a5"
          },
          "AyaContent": "وكان في المدينة تسعة رهط يفسدون في الأرض ولا يصلحون",
          "AyaNumber": 3207,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e02f61d12bf6abd1a6"
          },
          "AyaContent": "قالوا تقاسموا بالله لنبيتنه وأهله ثم لنقولن لوليه ما شهدنا مهلك أهله وإنا لصادقون",
          "AyaNumber": 3208,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e02f61d12bf6abd1a7"
          },
          "AyaContent": "ومكروا مكرا ومكرنا مكرا وهم لا يشعرون",
          "AyaNumber": 3209,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e02f61d12bf6abd1a8"
          },
          "AyaContent": "فانظر كيف كان عاقبة مكرهم أنا دمرناهم وقومهم أجمعين",
          "AyaNumber": 3210,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e12f61d12bf6abd1a9"
          },
          "AyaContent": "فتلك بيوتهم خاوية بما ظلموا ۗ إن في ذٰلك لآية لقوم يعلمون",
          "AyaNumber": 3211,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e12f61d12bf6abd1aa"
          },
          "AyaContent": "وأنجينا الذين آمنوا وكانوا يتقون",
          "AyaNumber": 3212,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e12f61d12bf6abd1ab"
          },
          "AyaContent": "ولوطا إذ قال لقومه أتأتون الفاحشة وأنتم تبصرون",
          "AyaNumber": 3213,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e12f61d12bf6abd1ac"
          },
          "AyaContent": "أئنكم لتأتون الرجال شهوة من دون النساء ۚ بل أنتم قوم تجهلون",
          "AyaNumber": 3214,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e12f61d12bf6abd1ad"
          },
          "AyaContent": "۞ فما كان جواب قومه إلا أن قالوا أخرجوا آل لوط من قريتكم ۖ إنهم أناس يتطهرون",
          "AyaNumber": 3215,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e22f61d12bf6abd1ae"
          },
          "AyaContent": "فأنجيناه وأهله إلا امرأته قدرناها من الغابرين",
          "AyaNumber": 3216,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e22f61d12bf6abd1af"
          },
          "AyaContent": "وأمطرنا عليهم مطرا ۖ فساء مطر المنذرين",
          "AyaNumber": 3217,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e22f61d12bf6abd1b0"
          },
          "AyaContent": "قل الحمد لله وسلام علىٰ عباده الذين اصطفىٰ ۗ آلله خير أما يشركون",
          "AyaNumber": 3218,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e32f61d12bf6abd1b1"
          },
          "AyaContent": "أمن خلق السماوات والأرض وأنزل لكم من السماء ماء فأنبتنا به حدائق ذات بهجة ما كان لكم أن تنبتوا شجرها ۗ أإلٰه مع الله ۚ بل هم قوم يعدلون",
          "AyaNumber": 3219,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e32f61d12bf6abd1b2"
          },
          "AyaContent": "أمن جعل الأرض قرارا وجعل خلالها أنهارا وجعل لها رواسي وجعل بين البحرين حاجزا ۗ أإلٰه مع الله ۚ بل أكثرهم لا يعلمون",
          "AyaNumber": 3220,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e32f61d12bf6abd1b3"
          },
          "AyaContent": "أمن يجيب المضطر إذا دعاه ويكشف السوء ويجعلكم خلفاء الأرض ۗ أإلٰه مع الله ۚ قليلا ما تذكرون",
          "AyaNumber": 3221,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e32f61d12bf6abd1b4"
          },
          "AyaContent": "أمن يهديكم في ظلمات البر والبحر ومن يرسل الرياح بشرا بين يدي رحمته ۗ أإلٰه مع الله ۚ تعالى الله عما يشركون",
          "AyaNumber": 3222,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e42f61d12bf6abd1b5"
          },
          "AyaContent": "أمن يبدأ الخلق ثم يعيده ومن يرزقكم من السماء والأرض ۗ أإلٰه مع الله ۚ قل هاتوا برهانكم إن كنتم صادقين",
          "AyaNumber": 3223,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e42f61d12bf6abd1b6"
          },
          "AyaContent": "قل لا يعلم من في السماوات والأرض الغيب إلا الله ۚ وما يشعرون أيان يبعثون",
          "AyaNumber": 3224,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e42f61d12bf6abd1b7"
          },
          "AyaContent": "بل ادارك علمهم في الآخرة ۚ بل هم في شك منها ۖ بل هم منها عمون",
          "AyaNumber": 3225,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e52f61d12bf6abd1b8"
          },
          "AyaContent": "وقال الذين كفروا أإذا كنا ترابا وآباؤنا أئنا لمخرجون",
          "AyaNumber": 3226,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e52f61d12bf6abd1b9"
          },
          "AyaContent": "لقد وعدنا هٰذا نحن وآباؤنا من قبل إن هٰذا إلا أساطير الأولين",
          "AyaNumber": 3227,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e52f61d12bf6abd1ba"
          },
          "AyaContent": "قل سيروا في الأرض فانظروا كيف كان عاقبة المجرمين",
          "AyaNumber": 3228,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e62f61d12bf6abd1bb"
          },
          "AyaContent": "ولا تحزن عليهم ولا تكن في ضيق مما يمكرون",
          "AyaNumber": 3229,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e62f61d12bf6abd1bc"
          },
          "AyaContent": "ويقولون متىٰ هٰذا الوعد إن كنتم صادقين",
          "AyaNumber": 3230,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e72f61d12bf6abd1bd"
          },
          "AyaContent": "قل عسىٰ أن يكون ردف لكم بعض الذي تستعجلون",
          "AyaNumber": 3231,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e72f61d12bf6abd1be"
          },
          "AyaContent": "وإن ربك لذو فضل على الناس ولٰكن أكثرهم لا يشكرون",
          "AyaNumber": 3232,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e72f61d12bf6abd1bf"
          },
          "AyaContent": "وإن ربك ليعلم ما تكن صدورهم وما يعلنون",
          "AyaNumber": 3233,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e72f61d12bf6abd1c0"
          },
          "AyaContent": "وما من غائبة في السماء والأرض إلا في كتاب مبين",
          "AyaNumber": 3234,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e82f61d12bf6abd1c1"
          },
          "AyaContent": "إن هٰذا القرآن يقص علىٰ بني إسرائيل أكثر الذي هم فيه يختلفون",
          "AyaNumber": 3235,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e82f61d12bf6abd1c2"
          },
          "AyaContent": "وإنه لهدى ورحمة للمؤمنين",
          "AyaNumber": 3236,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e82f61d12bf6abd1c3"
          },
          "AyaContent": "إن ربك يقضي بينهم بحكمه ۚ وهو العزيز العليم",
          "AyaNumber": 3237,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e82f61d12bf6abd1c4"
          },
          "AyaContent": "فتوكل على الله ۖ إنك على الحق المبين",
          "AyaNumber": 3238,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e92f61d12bf6abd1c5"
          },
          "AyaContent": "إنك لا تسمع الموتىٰ ولا تسمع الصم الدعاء إذا ولوا مدبرين",
          "AyaNumber": 3239,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e92f61d12bf6abd1c6"
          },
          "AyaContent": "وما أنت بهادي العمي عن ضلالتهم ۖ إن تسمع إلا من يؤمن بآياتنا فهم مسلمون",
          "AyaNumber": 3240,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87e92f61d12bf6abd1c7"
          },
          "AyaContent": "۞ وإذا وقع القول عليهم أخرجنا لهم دابة من الأرض تكلمهم أن الناس كانوا بآياتنا لا يوقنون",
          "AyaNumber": 3241,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ea2f61d12bf6abd1c8"
          },
          "AyaContent": "ويوم نحشر من كل أمة فوجا ممن يكذب بآياتنا فهم يوزعون",
          "AyaNumber": 3242,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ea2f61d12bf6abd1c9"
          },
          "AyaContent": "حتىٰ إذا جاءوا قال أكذبتم بآياتي ولم تحيطوا بها علما أماذا كنتم تعملون",
          "AyaNumber": 3243,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ea2f61d12bf6abd1ca"
          },
          "AyaContent": "ووقع القول عليهم بما ظلموا فهم لا ينطقون",
          "AyaNumber": 3244,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87eb2f61d12bf6abd1cb"
          },
          "AyaContent": "ألم يروا أنا جعلنا الليل ليسكنوا فيه والنهار مبصرا ۚ إن في ذٰلك لآيات لقوم يؤمنون",
          "AyaNumber": 3245,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87eb2f61d12bf6abd1cc"
          },
          "AyaContent": "ويوم ينفخ في الصور ففزع من في السماوات ومن في الأرض إلا من شاء الله ۚ وكل أتوه داخرين",
          "AyaNumber": 3246,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87eb2f61d12bf6abd1cd"
          },
          "AyaContent": "وترى الجبال تحسبها جامدة وهي تمر مر السحاب ۚ صنع الله الذي أتقن كل شيء ۚ إنه خبير بما تفعلون",
          "AyaNumber": 3247,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87eb2f61d12bf6abd1ce"
          },
          "AyaContent": "من جاء بالحسنة فله خير منها وهم من فزع يومئذ آمنون",
          "AyaNumber": 3248,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ec2f61d12bf6abd1cf"
          },
          "AyaContent": "ومن جاء بالسيئة فكبت وجوههم في النار هل تجزون إلا ما كنتم تعملون",
          "AyaNumber": 3249,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ec2f61d12bf6abd1d0"
          },
          "AyaContent": "إنما أمرت أن أعبد رب هٰذه البلدة الذي حرمها وله كل شيء ۖ وأمرت أن أكون من المسلمين",
          "AyaNumber": 3250,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ec2f61d12bf6abd1d1"
          },
          "AyaContent": "وأن أتلو القرآن ۖ فمن اهتدىٰ فإنما يهتدي لنفسه ۖ ومن ضل فقل إنما أنا من المنذرين",
          "AyaNumber": 3251,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ed2f61d12bf6abd1d2"
          },
          "AyaContent": "وقل الحمد لله سيريكم آياته فتعرفونها ۚ وما ربك بغافل عما تعملون",
          "AyaNumber": 3252,
          "SurahName": "سورة النمل",
          "SurahNumber": 27
        },{
          "_id": {
            "$oid": "5ecd87ed2f61d12bf6abd1d3"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم طسم",
          "AyaNumber": 3253,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ed2f61d12bf6abd1d4"
          },
          "AyaContent": "تلك آيات الكتاب المبين",
          "AyaNumber": 3254,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ee2f61d12bf6abd1d5"
          },
          "AyaContent": "نتلو عليك من نبإ موسىٰ وفرعون بالحق لقوم يؤمنون",
          "AyaNumber": 3255,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ee2f61d12bf6abd1d6"
          },
          "AyaContent": "إن فرعون علا في الأرض وجعل أهلها شيعا يستضعف طائفة منهم يذبح أبناءهم ويستحيي نساءهم ۚ إنه كان من المفسدين",
          "AyaNumber": 3256,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ee2f61d12bf6abd1d7"
          },
          "AyaContent": "ونريد أن نمن على الذين استضعفوا في الأرض ونجعلهم أئمة ونجعلهم الوارثين",
          "AyaNumber": 3257,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ee2f61d12bf6abd1d8"
          },
          "AyaContent": "ونمكن لهم في الأرض ونري فرعون وهامان وجنودهما منهم ما كانوا يحذرون",
          "AyaNumber": 3258,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ef2f61d12bf6abd1d9"
          },
          "AyaContent": "وأوحينا إلىٰ أم موسىٰ أن أرضعيه ۖ فإذا خفت عليه فألقيه في اليم ولا تخافي ولا تحزني ۖ إنا رادوه إليك وجاعلوه من المرسلين",
          "AyaNumber": 3259,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ef2f61d12bf6abd1da"
          },
          "AyaContent": "فالتقطه آل فرعون ليكون لهم عدوا وحزنا ۗ إن فرعون وهامان وجنودهما كانوا خاطئين",
          "AyaNumber": 3260,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ef2f61d12bf6abd1db"
          },
          "AyaContent": "وقالت امرأت فرعون قرت عين لي ولك ۖ لا تقتلوه عسىٰ أن ينفعنا أو نتخذه ولدا وهم لا يشعرون",
          "AyaNumber": 3261,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f02f61d12bf6abd1dc"
          },
          "AyaContent": "وأصبح فؤاد أم موسىٰ فارغا ۖ إن كادت لتبدي به لولا أن ربطنا علىٰ قلبها لتكون من المؤمنين",
          "AyaNumber": 3262,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f02f61d12bf6abd1dd"
          },
          "AyaContent": "وقالت لأخته قصيه ۖ فبصرت به عن جنب وهم لا يشعرون",
          "AyaNumber": 3263,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f02f61d12bf6abd1de"
          },
          "AyaContent": "۞ وحرمنا عليه المراضع من قبل فقالت هل أدلكم علىٰ أهل بيت يكفلونه لكم وهم له ناصحون",
          "AyaNumber": 3264,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f12f61d12bf6abd1df"
          },
          "AyaContent": "فرددناه إلىٰ أمه كي تقر عينها ولا تحزن ولتعلم أن وعد الله حق ولٰكن أكثرهم لا يعلمون",
          "AyaNumber": 3265,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f12f61d12bf6abd1e0"
          },
          "AyaContent": "ولما بلغ أشده واستوىٰ آتيناه حكما وعلما ۚ وكذٰلك نجزي المحسنين",
          "AyaNumber": 3266,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f12f61d12bf6abd1e1"
          },
          "AyaContent": "ودخل المدينة علىٰ حين غفلة من أهلها فوجد فيها رجلين يقتتلان هٰذا من شيعته وهٰذا من عدوه ۖ فاستغاثه الذي من شيعته على الذي من عدوه فوكزه موسىٰ فقضىٰ عليه ۖ قال هٰذا من عمل الشيطان ۖ إنه عدو مضل مبين",
          "AyaNumber": 3267,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f12f61d12bf6abd1e2"
          },
          "AyaContent": "قال رب إني ظلمت نفسي فاغفر لي فغفر له ۚ إنه هو الغفور الرحيم",
          "AyaNumber": 3268,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f12f61d12bf6abd1e3"
          },
          "AyaContent": "قال رب بما أنعمت علي فلن أكون ظهيرا للمجرمين",
          "AyaNumber": 3269,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f22f61d12bf6abd1e4"
          },
          "AyaContent": "فأصبح في المدينة خائفا يترقب فإذا الذي استنصره بالأمس يستصرخه ۚ قال له موسىٰ إنك لغوي مبين",
          "AyaNumber": 3270,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f22f61d12bf6abd1e5"
          },
          "AyaContent": "فلما أن أراد أن يبطش بالذي هو عدو لهما قال يا موسىٰ أتريد أن تقتلني كما قتلت نفسا بالأمس ۖ إن تريد إلا أن تكون جبارا في الأرض وما تريد أن تكون من المصلحين",
          "AyaNumber": 3271,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f22f61d12bf6abd1e6"
          },
          "AyaContent": "وجاء رجل من أقصى المدينة يسعىٰ قال يا موسىٰ إن الملأ يأتمرون بك ليقتلوك فاخرج إني لك من الناصحين",
          "AyaNumber": 3272,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f22f61d12bf6abd1e7"
          },
          "AyaContent": "فخرج منها خائفا يترقب ۖ قال رب نجني من القوم الظالمين",
          "AyaNumber": 3273,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f32f61d12bf6abd1e8"
          },
          "AyaContent": "ولما توجه تلقاء مدين قال عسىٰ ربي أن يهديني سواء السبيل",
          "AyaNumber": 3274,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f32f61d12bf6abd1e9"
          },
          "AyaContent": "ولما ورد ماء مدين وجد عليه أمة من الناس يسقون ووجد من دونهم امرأتين تذودان ۖ قال ما خطبكما ۖ قالتا لا نسقي حتىٰ يصدر الرعاء ۖ وأبونا شيخ كبير",
          "AyaNumber": 3275,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f32f61d12bf6abd1ea"
          },
          "AyaContent": "فسقىٰ لهما ثم تولىٰ إلى الظل فقال رب إني لما أنزلت إلي من خير فقير",
          "AyaNumber": 3276,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f32f61d12bf6abd1eb"
          },
          "AyaContent": "فجاءته إحداهما تمشي على استحياء قالت إن أبي يدعوك ليجزيك أجر ما سقيت لنا ۚ فلما جاءه وقص عليه القصص قال لا تخف ۖ نجوت من القوم الظالمين",
          "AyaNumber": 3277,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f32f61d12bf6abd1ec"
          },
          "AyaContent": "قالت إحداهما يا أبت استأجره ۖ إن خير من استأجرت القوي الأمين",
          "AyaNumber": 3278,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f42f61d12bf6abd1ed"
          },
          "AyaContent": "قال إني أريد أن أنكحك إحدى ابنتي هاتين علىٰ أن تأجرني ثماني حجج ۖ فإن أتممت عشرا فمن عندك ۖ وما أريد أن أشق عليك ۚ ستجدني إن شاء الله من الصالحين",
          "AyaNumber": 3279,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f42f61d12bf6abd1ee"
          },
          "AyaContent": "قال ذٰلك بيني وبينك ۖ أيما الأجلين قضيت فلا عدوان علي ۖ والله علىٰ ما نقول وكيل",
          "AyaNumber": 3280,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f42f61d12bf6abd1ef"
          },
          "AyaContent": "۞ فلما قضىٰ موسى الأجل وسار بأهله آنس من جانب الطور نارا قال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بخبر أو جذوة من النار لعلكم تصطلون",
          "AyaNumber": 3281,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f42f61d12bf6abd1f0"
          },
          "AyaContent": "فلما أتاها نودي من شاطئ الواد الأيمن في البقعة المباركة من الشجرة أن يا موسىٰ إني أنا الله رب العالمين",
          "AyaNumber": 3282,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f52f61d12bf6abd1f1"
          },
          "AyaContent": "وأن ألق عصاك ۖ فلما رآها تهتز كأنها جان ولىٰ مدبرا ولم يعقب ۚ يا موسىٰ أقبل ولا تخف ۖ إنك من الآمنين",
          "AyaNumber": 3283,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f52f61d12bf6abd1f2"
          },
          "AyaContent": "اسلك يدك في جيبك تخرج بيضاء من غير سوء واضمم إليك جناحك من الرهب ۖ فذانك برهانان من ربك إلىٰ فرعون وملئه ۚ إنهم كانوا قوما فاسقين",
          "AyaNumber": 3284,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f52f61d12bf6abd1f3"
          },
          "AyaContent": "قال رب إني قتلت منهم نفسا فأخاف أن يقتلون",
          "AyaNumber": 3285,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f62f61d12bf6abd1f4"
          },
          "AyaContent": "وأخي هارون هو أفصح مني لسانا فأرسله معي ردءا يصدقني ۖ إني أخاف أن يكذبون",
          "AyaNumber": 3286,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f62f61d12bf6abd1f5"
          },
          "AyaContent": "قال سنشد عضدك بأخيك ونجعل لكما سلطانا فلا يصلون إليكما ۚ بآياتنا أنتما ومن اتبعكما الغالبون",
          "AyaNumber": 3287,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f62f61d12bf6abd1f6"
          },
          "AyaContent": "فلما جاءهم موسىٰ بآياتنا بينات قالوا ما هٰذا إلا سحر مفترى وما سمعنا بهٰذا في آبائنا الأولين",
          "AyaNumber": 3288,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f62f61d12bf6abd1f7"
          },
          "AyaContent": "وقال موسىٰ ربي أعلم بمن جاء بالهدىٰ من عنده ومن تكون له عاقبة الدار ۖ إنه لا يفلح الظالمون",
          "AyaNumber": 3289,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f72f61d12bf6abd1f8"
          },
          "AyaContent": "وقال فرعون يا أيها الملأ ما علمت لكم من إلٰه غيري فأوقد لي يا هامان على الطين فاجعل لي صرحا لعلي أطلع إلىٰ إلٰه موسىٰ وإني لأظنه من الكاذبين",
          "AyaNumber": 3290,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f72f61d12bf6abd1f9"
          },
          "AyaContent": "واستكبر هو وجنوده في الأرض بغير الحق وظنوا أنهم إلينا لا يرجعون",
          "AyaNumber": 3291,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f72f61d12bf6abd1fa"
          },
          "AyaContent": "فأخذناه وجنوده فنبذناهم في اليم ۖ فانظر كيف كان عاقبة الظالمين",
          "AyaNumber": 3292,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f82f61d12bf6abd1fb"
          },
          "AyaContent": "وجعلناهم أئمة يدعون إلى النار ۖ ويوم القيامة لا ينصرون",
          "AyaNumber": 3293,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f82f61d12bf6abd1fc"
          },
          "AyaContent": "وأتبعناهم في هٰذه الدنيا لعنة ۖ ويوم القيامة هم من المقبوحين",
          "AyaNumber": 3294,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f82f61d12bf6abd1fd"
          },
          "AyaContent": "ولقد آتينا موسى الكتاب من بعد ما أهلكنا القرون الأولىٰ بصائر للناس وهدى ورحمة لعلهم يتذكرون",
          "AyaNumber": 3295,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f82f61d12bf6abd1fe"
          },
          "AyaContent": "وما كنت بجانب الغربي إذ قضينا إلىٰ موسى الأمر وما كنت من الشاهدين",
          "AyaNumber": 3296,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f92f61d12bf6abd1ff"
          },
          "AyaContent": "ولٰكنا أنشأنا قرونا فتطاول عليهم العمر ۚ وما كنت ثاويا في أهل مدين تتلو عليهم آياتنا ولٰكنا كنا مرسلين",
          "AyaNumber": 3297,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f92f61d12bf6abd200"
          },
          "AyaContent": "وما كنت بجانب الطور إذ نادينا ولٰكن رحمة من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يتذكرون",
          "AyaNumber": 3298,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f92f61d12bf6abd201"
          },
          "AyaContent": "ولولا أن تصيبهم مصيبة بما قدمت أيديهم فيقولوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك ونكون من المؤمنين",
          "AyaNumber": 3299,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87f92f61d12bf6abd202"
          },
          "AyaContent": "فلما جاءهم الحق من عندنا قالوا لولا أوتي مثل ما أوتي موسىٰ ۚ أولم يكفروا بما أوتي موسىٰ من قبل ۖ قالوا سحران تظاهرا وقالوا إنا بكل كافرون",
          "AyaNumber": 3300,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fa2f61d12bf6abd203"
          },
          "AyaContent": "قل فأتوا بكتاب من عند الله هو أهدىٰ منهما أتبعه إن كنتم صادقين",
          "AyaNumber": 3301,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fa2f61d12bf6abd204"
          },
          "AyaContent": "فإن لم يستجيبوا لك فاعلم أنما يتبعون أهواءهم ۚ ومن أضل ممن اتبع هواه بغير هدى من الله ۚ إن الله لا يهدي القوم الظالمين",
          "AyaNumber": 3302,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fa2f61d12bf6abd205"
          },
          "AyaContent": "۞ ولقد وصلنا لهم القول لعلهم يتذكرون",
          "AyaNumber": 3303,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fa2f61d12bf6abd206"
          },
          "AyaContent": "الذين آتيناهم الكتاب من قبله هم به يؤمنون",
          "AyaNumber": 3304,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fa2f61d12bf6abd207"
          },
          "AyaContent": "وإذا يتلىٰ عليهم قالوا آمنا به إنه الحق من ربنا إنا كنا من قبله مسلمين",
          "AyaNumber": 3305,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fb2f61d12bf6abd208"
          },
          "AyaContent": "أولٰئك يؤتون أجرهم مرتين بما صبروا ويدرءون بالحسنة السيئة ومما رزقناهم ينفقون",
          "AyaNumber": 3306,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fb2f61d12bf6abd209"
          },
          "AyaContent": "وإذا سمعوا اللغو أعرضوا عنه وقالوا لنا أعمالنا ولكم أعمالكم سلام عليكم لا نبتغي الجاهلين",
          "AyaNumber": 3307,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fb2f61d12bf6abd20a"
          },
          "AyaContent": "إنك لا تهدي من أحببت ولٰكن الله يهدي من يشاء ۚ وهو أعلم بالمهتدين",
          "AyaNumber": 3308,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fb2f61d12bf6abd20b"
          },
          "AyaContent": "وقالوا إن نتبع الهدىٰ معك نتخطف من أرضنا ۚ أولم نمكن لهم حرما آمنا يجبىٰ إليه ثمرات كل شيء رزقا من لدنا ولٰكن أكثرهم لا يعلمون",
          "AyaNumber": 3309,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fc2f61d12bf6abd20c"
          },
          "AyaContent": "وكم أهلكنا من قرية بطرت معيشتها ۖ فتلك مساكنهم لم تسكن من بعدهم إلا قليلا ۖ وكنا نحن الوارثين",
          "AyaNumber": 3310,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fc2f61d12bf6abd20d"
          },
          "AyaContent": "وما كان ربك مهلك القرىٰ حتىٰ يبعث في أمها رسولا يتلو عليهم آياتنا ۚ وما كنا مهلكي القرىٰ إلا وأهلها ظالمون",
          "AyaNumber": 3311,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fc2f61d12bf6abd20e"
          },
          "AyaContent": "وما أوتيتم من شيء فمتاع الحياة الدنيا وزينتها ۚ وما عند الله خير وأبقىٰ ۚ أفلا تعقلون",
          "AyaNumber": 3312,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fc2f61d12bf6abd20f"
          },
          "AyaContent": "أفمن وعدناه وعدا حسنا فهو لاقيه كمن متعناه متاع الحياة الدنيا ثم هو يوم القيامة من المحضرين",
          "AyaNumber": 3313,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fc2f61d12bf6abd210"
          },
          "AyaContent": "ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون",
          "AyaNumber": 3314,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fd2f61d12bf6abd211"
          },
          "AyaContent": "قال الذين حق عليهم القول ربنا هٰؤلاء الذين أغوينا أغويناهم كما غوينا ۖ تبرأنا إليك ۖ ما كانوا إيانا يعبدون",
          "AyaNumber": 3315,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fd2f61d12bf6abd212"
          },
          "AyaContent": "وقيل ادعوا شركاءكم فدعوهم فلم يستجيبوا لهم ورأوا العذاب ۚ لو أنهم كانوا يهتدون",
          "AyaNumber": 3316,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fd2f61d12bf6abd213"
          },
          "AyaContent": "ويوم يناديهم فيقول ماذا أجبتم المرسلين",
          "AyaNumber": 3317,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fd2f61d12bf6abd214"
          },
          "AyaContent": "فعميت عليهم الأنباء يومئذ فهم لا يتساءلون",
          "AyaNumber": 3318,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fd2f61d12bf6abd215"
          },
          "AyaContent": "فأما من تاب وآمن وعمل صالحا فعسىٰ أن يكون من المفلحين",
          "AyaNumber": 3319,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fe2f61d12bf6abd216"
          },
          "AyaContent": "وربك يخلق ما يشاء ويختار ۗ ما كان لهم الخيرة ۚ سبحان الله وتعالىٰ عما يشركون",
          "AyaNumber": 3320,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fe2f61d12bf6abd217"
          },
          "AyaContent": "وربك يعلم ما تكن صدورهم وما يعلنون",
          "AyaNumber": 3321,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fe2f61d12bf6abd218"
          },
          "AyaContent": "وهو الله لا إلٰه إلا هو ۖ له الحمد في الأولىٰ والآخرة ۖ وله الحكم وإليه ترجعون",
          "AyaNumber": 3322,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87fe2f61d12bf6abd219"
          },
          "AyaContent": "قل أرأيتم إن جعل الله عليكم الليل سرمدا إلىٰ يوم القيامة من إلٰه غير الله يأتيكم بضياء ۖ أفلا تسمعون",
          "AyaNumber": 3323,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ff2f61d12bf6abd21a"
          },
          "AyaContent": "قل أرأيتم إن جعل الله عليكم النهار سرمدا إلىٰ يوم القيامة من إلٰه غير الله يأتيكم بليل تسكنون فيه ۖ أفلا تبصرون",
          "AyaNumber": 3324,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ff2f61d12bf6abd21b"
          },
          "AyaContent": "ومن رحمته جعل لكم الليل والنهار لتسكنوا فيه ولتبتغوا من فضله ولعلكم تشكرون",
          "AyaNumber": 3325,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd87ff2f61d12bf6abd21c"
          },
          "AyaContent": "ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون",
          "AyaNumber": 3326,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88002f61d12bf6abd21d"
          },
          "AyaContent": "ونزعنا من كل أمة شهيدا فقلنا هاتوا برهانكم فعلموا أن الحق لله وضل عنهم ما كانوا يفترون",
          "AyaNumber": 3327,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88002f61d12bf6abd21e"
          },
          "AyaContent": "۞ إن قارون كان من قوم موسىٰ فبغىٰ عليهم ۖ وآتيناه من الكنوز ما إن مفاتحه لتنوء بالعصبة أولي القوة إذ قال له قومه لا تفرح ۖ إن الله لا يحب الفرحين",
          "AyaNumber": 3328,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88002f61d12bf6abd21f"
          },
          "AyaContent": "وابتغ فيما آتاك الله الدار الآخرة ۖ ولا تنس نصيبك من الدنيا ۖ وأحسن كما أحسن الله إليك ۖ ولا تبغ الفساد في الأرض ۖ إن الله لا يحب المفسدين",
          "AyaNumber": 3329,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88002f61d12bf6abd220"
          },
          "AyaContent": "قال إنما أوتيته علىٰ علم عندي ۚ أولم يعلم أن الله قد أهلك من قبله من القرون من هو أشد منه قوة وأكثر جمعا ۚ ولا يسأل عن ذنوبهم المجرمون",
          "AyaNumber": 3330,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88012f61d12bf6abd221"
          },
          "AyaContent": "فخرج علىٰ قومه في زينته ۖ قال الذين يريدون الحياة الدنيا يا ليت لنا مثل ما أوتي قارون إنه لذو حظ عظيم",
          "AyaNumber": 3331,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88012f61d12bf6abd222"
          },
          "AyaContent": "وقال الذين أوتوا العلم ويلكم ثواب الله خير لمن آمن وعمل صالحا ولا يلقاها إلا الصابرون",
          "AyaNumber": 3332,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88012f61d12bf6abd223"
          },
          "AyaContent": "فخسفنا به وبداره الأرض فما كان له من فئة ينصرونه من دون الله وما كان من المنتصرين",
          "AyaNumber": 3333,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88022f61d12bf6abd224"
          },
          "AyaContent": "وأصبح الذين تمنوا مكانه بالأمس يقولون ويكأن الله يبسط الرزق لمن يشاء من عباده ويقدر ۖ لولا أن من الله علينا لخسف بنا ۖ ويكأنه لا يفلح الكافرون",
          "AyaNumber": 3334,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88022f61d12bf6abd225"
          },
          "AyaContent": "تلك الدار الآخرة نجعلها للذين لا يريدون علوا في الأرض ولا فسادا ۚ والعاقبة للمتقين",
          "AyaNumber": 3335,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88022f61d12bf6abd226"
          },
          "AyaContent": "من جاء بالحسنة فله خير منها ۖ ومن جاء بالسيئة فلا يجزى الذين عملوا السيئات إلا ما كانوا يعملون",
          "AyaNumber": 3336,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88022f61d12bf6abd227"
          },
          "AyaContent": "إن الذي فرض عليك القرآن لرادك إلىٰ معاد ۚ قل ربي أعلم من جاء بالهدىٰ ومن هو في ضلال مبين",
          "AyaNumber": 3337,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88032f61d12bf6abd228"
          },
          "AyaContent": "وما كنت ترجو أن يلقىٰ إليك الكتاب إلا رحمة من ربك ۖ فلا تكونن ظهيرا للكافرين",
          "AyaNumber": 3338,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88032f61d12bf6abd229"
          },
          "AyaContent": "ولا يصدنك عن آيات الله بعد إذ أنزلت إليك ۖ وادع إلىٰ ربك ۖ ولا تكونن من المشركين",
          "AyaNumber": 3339,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88032f61d12bf6abd22a"
          },
          "AyaContent": "ولا تدع مع الله إلٰها آخر ۘ لا إلٰه إلا هو ۚ كل شيء هالك إلا وجهه ۚ له الحكم وإليه ترجعون",
          "AyaNumber": 3340,
          "SurahName": "سورة القصص",
          "SurahNumber": 28
        },{
          "_id": {
            "$oid": "5ecd88042f61d12bf6abd22b"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الم",
          "AyaNumber": 3341,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88042f61d12bf6abd22c"
          },
          "AyaContent": "أحسب الناس أن يتركوا أن يقولوا آمنا وهم لا يفتنون",
          "AyaNumber": 3342,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88042f61d12bf6abd22d"
          },
          "AyaContent": "ولقد فتنا الذين من قبلهم ۖ فليعلمن الله الذين صدقوا وليعلمن الكاذبين",
          "AyaNumber": 3343,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88052f61d12bf6abd22e"
          },
          "AyaContent": "أم حسب الذين يعملون السيئات أن يسبقونا ۚ ساء ما يحكمون",
          "AyaNumber": 3344,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88052f61d12bf6abd22f"
          },
          "AyaContent": "من كان يرجو لقاء الله فإن أجل الله لآت ۚ وهو السميع العليم",
          "AyaNumber": 3345,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88052f61d12bf6abd230"
          },
          "AyaContent": "ومن جاهد فإنما يجاهد لنفسه ۚ إن الله لغني عن العالمين",
          "AyaNumber": 3346,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88062f61d12bf6abd231"
          },
          "AyaContent": "والذين آمنوا وعملوا الصالحات لنكفرن عنهم سيئاتهم ولنجزينهم أحسن الذي كانوا يعملون",
          "AyaNumber": 3347,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88062f61d12bf6abd232"
          },
          "AyaContent": "ووصينا الإنسان بوالديه حسنا ۖ وإن جاهداك لتشرك بي ما ليس لك به علم فلا تطعهما ۚ إلي مرجعكم فأنبئكم بما كنتم تعملون",
          "AyaNumber": 3348,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88062f61d12bf6abd233"
          },
          "AyaContent": "والذين آمنوا وعملوا الصالحات لندخلنهم في الصالحين",
          "AyaNumber": 3349,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88062f61d12bf6abd234"
          },
          "AyaContent": "ومن الناس من يقول آمنا بالله فإذا أوذي في الله جعل فتنة الناس كعذاب الله ولئن جاء نصر من ربك ليقولن إنا كنا معكم ۚ أوليس الله بأعلم بما في صدور العالمين",
          "AyaNumber": 3350,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88072f61d12bf6abd235"
          },
          "AyaContent": "وليعلمن الله الذين آمنوا وليعلمن المنافقين",
          "AyaNumber": 3351,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88072f61d12bf6abd236"
          },
          "AyaContent": "وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا ولنحمل خطاياكم وما هم بحاملين من خطاياهم من شيء ۖ إنهم لكاذبون",
          "AyaNumber": 3352,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88072f61d12bf6abd237"
          },
          "AyaContent": "وليحملن أثقالهم وأثقالا مع أثقالهم ۖ وليسألن يوم القيامة عما كانوا يفترون",
          "AyaNumber": 3353,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88072f61d12bf6abd238"
          },
          "AyaContent": "ولقد أرسلنا نوحا إلىٰ قومه فلبث فيهم ألف سنة إلا خمسين عاما فأخذهم الطوفان وهم ظالمون",
          "AyaNumber": 3354,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88082f61d12bf6abd239"
          },
          "AyaContent": "فأنجيناه وأصحاب السفينة وجعلناها آية للعالمين",
          "AyaNumber": 3355,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88082f61d12bf6abd23a"
          },
          "AyaContent": "وإبراهيم إذ قال لقومه اعبدوا الله واتقوه ۖ ذٰلكم خير لكم إن كنتم تعلمون",
          "AyaNumber": 3356,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88082f61d12bf6abd23b"
          },
          "AyaContent": "إنما تعبدون من دون الله أوثانا وتخلقون إفكا ۚ إن الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا عند الله الرزق واعبدوه واشكروا له ۖ إليه ترجعون",
          "AyaNumber": 3357,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88092f61d12bf6abd23c"
          },
          "AyaContent": "وإن تكذبوا فقد كذب أمم من قبلكم ۖ وما على الرسول إلا البلاغ المبين",
          "AyaNumber": 3358,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88092f61d12bf6abd23d"
          },
          "AyaContent": "أولم يروا كيف يبدئ الله الخلق ثم يعيده ۚ إن ذٰلك على الله يسير",
          "AyaNumber": 3359,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880a2f61d12bf6abd23e"
          },
          "AyaContent": "قل سيروا في الأرض فانظروا كيف بدأ الخلق ۚ ثم الله ينشئ النشأة الآخرة ۚ إن الله علىٰ كل شيء قدير",
          "AyaNumber": 3360,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880a2f61d12bf6abd23f"
          },
          "AyaContent": "يعذب من يشاء ويرحم من يشاء ۖ وإليه تقلبون",
          "AyaNumber": 3361,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880a2f61d12bf6abd240"
          },
          "AyaContent": "وما أنتم بمعجزين في الأرض ولا في السماء ۖ وما لكم من دون الله من ولي ولا نصير",
          "AyaNumber": 3362,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880a2f61d12bf6abd241"
          },
          "AyaContent": "والذين كفروا بآيات الله ولقائه أولٰئك يئسوا من رحمتي وأولٰئك لهم عذاب أليم",
          "AyaNumber": 3363,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880b2f61d12bf6abd242"
          },
          "AyaContent": "فما كان جواب قومه إلا أن قالوا اقتلوه أو حرقوه فأنجاه الله من النار ۚ إن في ذٰلك لآيات لقوم يؤمنون",
          "AyaNumber": 3364,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880b2f61d12bf6abd243"
          },
          "AyaContent": "وقال إنما اتخذتم من دون الله أوثانا مودة بينكم في الحياة الدنيا ۖ ثم يوم القيامة يكفر بعضكم ببعض ويلعن بعضكم بعضا ومأواكم النار وما لكم من ناصرين",
          "AyaNumber": 3365,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880b2f61d12bf6abd244"
          },
          "AyaContent": "۞ فآمن له لوط ۘ وقال إني مهاجر إلىٰ ربي ۖ إنه هو العزيز الحكيم",
          "AyaNumber": 3366,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880c2f61d12bf6abd245"
          },
          "AyaContent": "ووهبنا له إسحاق ويعقوب وجعلنا في ذريته النبوة والكتاب وآتيناه أجره في الدنيا ۖ وإنه في الآخرة لمن الصالحين",
          "AyaNumber": 3367,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880c2f61d12bf6abd246"
          },
          "AyaContent": "ولوطا إذ قال لقومه إنكم لتأتون الفاحشة ما سبقكم بها من أحد من العالمين",
          "AyaNumber": 3368,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880c2f61d12bf6abd247"
          },
          "AyaContent": "أئنكم لتأتون الرجال وتقطعون السبيل وتأتون في ناديكم المنكر ۖ فما كان جواب قومه إلا أن قالوا ائتنا بعذاب الله إن كنت من الصادقين",
          "AyaNumber": 3369,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880d2f61d12bf6abd248"
          },
          "AyaContent": "قال رب انصرني على القوم المفسدين",
          "AyaNumber": 3370,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880d2f61d12bf6abd249"
          },
          "AyaContent": "ولما جاءت رسلنا إبراهيم بالبشرىٰ قالوا إنا مهلكو أهل هٰذه القرية ۖ إن أهلها كانوا ظالمين",
          "AyaNumber": 3371,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880d2f61d12bf6abd24a"
          },
          "AyaContent": "قال إن فيها لوطا ۚ قالوا نحن أعلم بمن فيها ۖ لننجينه وأهله إلا امرأته كانت من الغابرين",
          "AyaNumber": 3372,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880e2f61d12bf6abd24b"
          },
          "AyaContent": "ولما أن جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقالوا لا تخف ولا تحزن ۖ إنا منجوك وأهلك إلا امرأتك كانت من الغابرين",
          "AyaNumber": 3373,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880e2f61d12bf6abd24c"
          },
          "AyaContent": "إنا منزلون علىٰ أهل هٰذه القرية رجزا من السماء بما كانوا يفسقون",
          "AyaNumber": 3374,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880e2f61d12bf6abd24d"
          },
          "AyaContent": "ولقد تركنا منها آية بينة لقوم يعقلون",
          "AyaNumber": 3375,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880f2f61d12bf6abd24e"
          },
          "AyaContent": "وإلىٰ مدين أخاهم شعيبا فقال يا قوم اعبدوا الله وارجوا اليوم الآخر ولا تعثوا في الأرض مفسدين",
          "AyaNumber": 3376,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880f2f61d12bf6abd24f"
          },
          "AyaContent": "فكذبوه فأخذتهم الرجفة فأصبحوا في دارهم جاثمين",
          "AyaNumber": 3377,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880f2f61d12bf6abd250"
          },
          "AyaContent": "وعادا وثمود وقد تبين لكم من مساكنهم ۖ وزين لهم الشيطان أعمالهم فصدهم عن السبيل وكانوا مستبصرين",
          "AyaNumber": 3378,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd880f2f61d12bf6abd251"
          },
          "AyaContent": "وقارون وفرعون وهامان ۖ ولقد جاءهم موسىٰ بالبينات فاستكبروا في الأرض وما كانوا سابقين",
          "AyaNumber": 3379,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88102f61d12bf6abd252"
          },
          "AyaContent": "فكلا أخذنا بذنبه ۖ فمنهم من أرسلنا عليه حاصبا ومنهم من أخذته الصيحة ومنهم من خسفنا به الأرض ومنهم من أغرقنا ۚ وما كان الله ليظلمهم ولٰكن كانوا أنفسهم يظلمون",
          "AyaNumber": 3380,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88112f61d12bf6abd253"
          },
          "AyaContent": "مثل الذين اتخذوا من دون الله أولياء كمثل العنكبوت اتخذت بيتا ۖ وإن أوهن البيوت لبيت العنكبوت ۖ لو كانوا يعلمون",
          "AyaNumber": 3381,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88112f61d12bf6abd254"
          },
          "AyaContent": "إن الله يعلم ما يدعون من دونه من شيء ۚ وهو العزيز الحكيم",
          "AyaNumber": 3382,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88122f61d12bf6abd255"
          },
          "AyaContent": "وتلك الأمثال نضربها للناس ۖ وما يعقلها إلا العالمون",
          "AyaNumber": 3383,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88132f61d12bf6abd256"
          },
          "AyaContent": "خلق الله السماوات والأرض بالحق ۚ إن في ذٰلك لآية للمؤمنين",
          "AyaNumber": 3384,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88132f61d12bf6abd257"
          },
          "AyaContent": "اتل ما أوحي إليك من الكتاب وأقم الصلاة ۖ إن الصلاة تنهىٰ عن الفحشاء والمنكر ۗ ولذكر الله أكبر ۗ والله يعلم ما تصنعون",
          "AyaNumber": 3385,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88142f61d12bf6abd258"
          },
          "AyaContent": "۞ ولا تجادلوا أهل الكتاب إلا بالتي هي أحسن إلا الذين ظلموا منهم ۖ وقولوا آمنا بالذي أنزل إلينا وأنزل إليكم وإلٰهنا وإلٰهكم واحد ونحن له مسلمون",
          "AyaNumber": 3386,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88152f61d12bf6abd259"
          },
          "AyaContent": "وكذٰلك أنزلنا إليك الكتاب ۚ فالذين آتيناهم الكتاب يؤمنون به ۖ ومن هٰؤلاء من يؤمن به ۚ وما يجحد بآياتنا إلا الكافرون",
          "AyaNumber": 3387,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88152f61d12bf6abd25a"
          },
          "AyaContent": "وما كنت تتلو من قبله من كتاب ولا تخطه بيمينك ۖ إذا لارتاب المبطلون",
          "AyaNumber": 3388,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88152f61d12bf6abd25b"
          },
          "AyaContent": "بل هو آيات بينات في صدور الذين أوتوا العلم ۚ وما يجحد بآياتنا إلا الظالمون",
          "AyaNumber": 3389,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88162f61d12bf6abd25c"
          },
          "AyaContent": "وقالوا لولا أنزل عليه آيات من ربه ۖ قل إنما الآيات عند الله وإنما أنا نذير مبين",
          "AyaNumber": 3390,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88162f61d12bf6abd25d"
          },
          "AyaContent": "أولم يكفهم أنا أنزلنا عليك الكتاب يتلىٰ عليهم ۚ إن في ذٰلك لرحمة وذكرىٰ لقوم يؤمنون",
          "AyaNumber": 3391,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88162f61d12bf6abd25e"
          },
          "AyaContent": "قل كفىٰ بالله بيني وبينكم شهيدا ۖ يعلم ما في السماوات والأرض ۗ والذين آمنوا بالباطل وكفروا بالله أولٰئك هم الخاسرون",
          "AyaNumber": 3392,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88162f61d12bf6abd25f"
          },
          "AyaContent": "ويستعجلونك بالعذاب ۚ ولولا أجل مسمى لجاءهم العذاب وليأتينهم بغتة وهم لا يشعرون",
          "AyaNumber": 3393,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88172f61d12bf6abd260"
          },
          "AyaContent": "يستعجلونك بالعذاب وإن جهنم لمحيطة بالكافرين",
          "AyaNumber": 3394,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88172f61d12bf6abd261"
          },
          "AyaContent": "يوم يغشاهم العذاب من فوقهم ومن تحت أرجلهم ويقول ذوقوا ما كنتم تعملون",
          "AyaNumber": 3395,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88172f61d12bf6abd262"
          },
          "AyaContent": "يا عبادي الذين آمنوا إن أرضي واسعة فإياي فاعبدون",
          "AyaNumber": 3396,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88182f61d12bf6abd263"
          },
          "AyaContent": "كل نفس ذائقة الموت ۖ ثم إلينا ترجعون",
          "AyaNumber": 3397,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88182f61d12bf6abd264"
          },
          "AyaContent": "والذين آمنوا وعملوا الصالحات لنبوئنهم من الجنة غرفا تجري من تحتها الأنهار خالدين فيها ۚ نعم أجر العاملين",
          "AyaNumber": 3398,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88182f61d12bf6abd265"
          },
          "AyaContent": "الذين صبروا وعلىٰ ربهم يتوكلون",
          "AyaNumber": 3399,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88192f61d12bf6abd266"
          },
          "AyaContent": "وكأين من دابة لا تحمل رزقها الله يرزقها وإياكم ۚ وهو السميع العليم",
          "AyaNumber": 3400,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88192f61d12bf6abd267"
          },
          "AyaContent": "ولئن سألتهم من خلق السماوات والأرض وسخر الشمس والقمر ليقولن الله ۖ فأنىٰ يؤفكون",
          "AyaNumber": 3401,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd88192f61d12bf6abd268"
          },
          "AyaContent": "الله يبسط الرزق لمن يشاء من عباده ويقدر له ۚ إن الله بكل شيء عليم",
          "AyaNumber": 3402,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881a2f61d12bf6abd269"
          },
          "AyaContent": "ولئن سألتهم من نزل من السماء ماء فأحيا به الأرض من بعد موتها ليقولن الله ۚ قل الحمد لله ۚ بل أكثرهم لا يعقلون",
          "AyaNumber": 3403,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881a2f61d12bf6abd26a"
          },
          "AyaContent": "وما هٰذه الحياة الدنيا إلا لهو ولعب ۚ وإن الدار الآخرة لهي الحيوان ۚ لو كانوا يعلمون",
          "AyaNumber": 3404,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881a2f61d12bf6abd26b"
          },
          "AyaContent": "فإذا ركبوا في الفلك دعوا الله مخلصين له الدين فلما نجاهم إلى البر إذا هم يشركون",
          "AyaNumber": 3405,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881a2f61d12bf6abd26c"
          },
          "AyaContent": "ليكفروا بما آتيناهم وليتمتعوا ۖ فسوف يعلمون",
          "AyaNumber": 3406,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881b2f61d12bf6abd26d"
          },
          "AyaContent": "أولم يروا أنا جعلنا حرما آمنا ويتخطف الناس من حولهم ۚ أفبالباطل يؤمنون وبنعمة الله يكفرون",
          "AyaNumber": 3407,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881b2f61d12bf6abd26e"
          },
          "AyaContent": "ومن أظلم ممن افترىٰ على الله كذبا أو كذب بالحق لما جاءه ۚ أليس في جهنم مثوى للكافرين",
          "AyaNumber": 3408,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881b2f61d12bf6abd26f"
          },
          "AyaContent": "والذين جاهدوا فينا لنهدينهم سبلنا ۚ وإن الله لمع المحسنين",
          "AyaNumber": 3409,
          "SurahName": "سورة العنكبوت",
          "SurahNumber": 29
        },{
          "_id": {
            "$oid": "5ecd881b2f61d12bf6abd270"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الم",
          "AyaNumber": 3410,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881c2f61d12bf6abd271"
          },
          "AyaContent": "غلبت الروم",
          "AyaNumber": 3411,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881c2f61d12bf6abd272"
          },
          "AyaContent": "في أدنى الأرض وهم من بعد غلبهم سيغلبون",
          "AyaNumber": 3412,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881c2f61d12bf6abd273"
          },
          "AyaContent": "في بضع سنين ۗ لله الأمر من قبل ومن بعد ۚ ويومئذ يفرح المؤمنون",
          "AyaNumber": 3413,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881c2f61d12bf6abd274"
          },
          "AyaContent": "بنصر الله ۚ ينصر من يشاء ۖ وهو العزيز الرحيم",
          "AyaNumber": 3414,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881d2f61d12bf6abd275"
          },
          "AyaContent": "وعد الله ۖ لا يخلف الله وعده ولٰكن أكثر الناس لا يعلمون",
          "AyaNumber": 3415,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881d2f61d12bf6abd276"
          },
          "AyaContent": "يعلمون ظاهرا من الحياة الدنيا وهم عن الآخرة هم غافلون",
          "AyaNumber": 3416,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881d2f61d12bf6abd277"
          },
          "AyaContent": "أولم يتفكروا في أنفسهم ۗ ما خلق الله السماوات والأرض وما بينهما إلا بالحق وأجل مسمى ۗ وإن كثيرا من الناس بلقاء ربهم لكافرون",
          "AyaNumber": 3417,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881d2f61d12bf6abd278"
          },
          "AyaContent": "أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ۚ كانوا أشد منهم قوة وأثاروا الأرض وعمروها أكثر مما عمروها وجاءتهم رسلهم بالبينات ۖ فما كان الله ليظلمهم ولٰكن كانوا أنفسهم يظلمون",
          "AyaNumber": 3418,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881e2f61d12bf6abd279"
          },
          "AyaContent": "ثم كان عاقبة الذين أساءوا السوأىٰ أن كذبوا بآيات الله وكانوا بها يستهزئون",
          "AyaNumber": 3419,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881e2f61d12bf6abd27a"
          },
          "AyaContent": "الله يبدأ الخلق ثم يعيده ثم إليه ترجعون",
          "AyaNumber": 3420,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881e2f61d12bf6abd27b"
          },
          "AyaContent": "ويوم تقوم الساعة يبلس المجرمون",
          "AyaNumber": 3421,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881e2f61d12bf6abd27c"
          },
          "AyaContent": "ولم يكن لهم من شركائهم شفعاء وكانوا بشركائهم كافرين",
          "AyaNumber": 3422,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881e2f61d12bf6abd27d"
          },
          "AyaContent": "ويوم تقوم الساعة يومئذ يتفرقون",
          "AyaNumber": 3423,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881f2f61d12bf6abd27e"
          },
          "AyaContent": "فأما الذين آمنوا وعملوا الصالحات فهم في روضة يحبرون",
          "AyaNumber": 3424,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881f2f61d12bf6abd27f"
          },
          "AyaContent": "وأما الذين كفروا وكذبوا بآياتنا ولقاء الآخرة فأولٰئك في العذاب محضرون",
          "AyaNumber": 3425,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881f2f61d12bf6abd280"
          },
          "AyaContent": "فسبحان الله حين تمسون وحين تصبحون",
          "AyaNumber": 3426,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881f2f61d12bf6abd281"
          },
          "AyaContent": "وله الحمد في السماوات والأرض وعشيا وحين تظهرون",
          "AyaNumber": 3427,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881f2f61d12bf6abd282"
          },
          "AyaContent": "يخرج الحي من الميت ويخرج الميت من الحي ويحيي الأرض بعد موتها ۚ وكذٰلك تخرجون",
          "AyaNumber": 3428,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd881f2f61d12bf6abd283"
          },
          "AyaContent": "ومن آياته أن خلقكم من تراب ثم إذا أنتم بشر تنتشرون",
          "AyaNumber": 3429,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88202f61d12bf6abd284"
          },
          "AyaContent": "ومن آياته أن خلق لكم من أنفسكم أزواجا لتسكنوا إليها وجعل بينكم مودة ورحمة ۚ إن في ذٰلك لآيات لقوم يتفكرون",
          "AyaNumber": 3430,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88202f61d12bf6abd285"
          },
          "AyaContent": "ومن آياته خلق السماوات والأرض واختلاف ألسنتكم وألوانكم ۚ إن في ذٰلك لآيات للعالمين",
          "AyaNumber": 3431,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88202f61d12bf6abd286"
          },
          "AyaContent": "ومن آياته منامكم بالليل والنهار وابتغاؤكم من فضله ۚ إن في ذٰلك لآيات لقوم يسمعون",
          "AyaNumber": 3432,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88202f61d12bf6abd287"
          },
          "AyaContent": "ومن آياته يريكم البرق خوفا وطمعا وينزل من السماء ماء فيحيي به الأرض بعد موتها ۚ إن في ذٰلك لآيات لقوم يعقلون",
          "AyaNumber": 3433,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88202f61d12bf6abd288"
          },
          "AyaContent": "ومن آياته أن تقوم السماء والأرض بأمره ۚ ثم إذا دعاكم دعوة من الأرض إذا أنتم تخرجون",
          "AyaNumber": 3434,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88212f61d12bf6abd289"
          },
          "AyaContent": "وله من في السماوات والأرض ۖ كل له قانتون",
          "AyaNumber": 3435,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88212f61d12bf6abd28a"
          },
          "AyaContent": "وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه ۚ وله المثل الأعلىٰ في السماوات والأرض ۚ وهو العزيز الحكيم",
          "AyaNumber": 3436,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88212f61d12bf6abd28b"
          },
          "AyaContent": "ضرب لكم مثلا من أنفسكم ۖ هل لكم من ما ملكت أيمانكم من شركاء في ما رزقناكم فأنتم فيه سواء تخافونهم كخيفتكم أنفسكم ۚ كذٰلك نفصل الآيات لقوم يعقلون",
          "AyaNumber": 3437,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88222f61d12bf6abd28c"
          },
          "AyaContent": "بل اتبع الذين ظلموا أهواءهم بغير علم ۖ فمن يهدي من أضل الله ۖ وما لهم من ناصرين",
          "AyaNumber": 3438,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88222f61d12bf6abd28d"
          },
          "AyaContent": "فأقم وجهك للدين حنيفا ۚ فطرت الله التي فطر الناس عليها ۚ لا تبديل لخلق الله ۚ ذٰلك الدين القيم ولٰكن أكثر الناس لا يعلمون",
          "AyaNumber": 3439,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88222f61d12bf6abd28e"
          },
          "AyaContent": "۞ منيبين إليه واتقوه وأقيموا الصلاة ولا تكونوا من المشركين",
          "AyaNumber": 3440,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88232f61d12bf6abd28f"
          },
          "AyaContent": "من الذين فرقوا دينهم وكانوا شيعا ۖ كل حزب بما لديهم فرحون",
          "AyaNumber": 3441,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88232f61d12bf6abd290"
          },
          "AyaContent": "وإذا مس الناس ضر دعوا ربهم منيبين إليه ثم إذا أذاقهم منه رحمة إذا فريق منهم بربهم يشركون",
          "AyaNumber": 3442,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88232f61d12bf6abd291"
          },
          "AyaContent": "ليكفروا بما آتيناهم ۚ فتمتعوا فسوف تعلمون",
          "AyaNumber": 3443,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88242f61d12bf6abd292"
          },
          "AyaContent": "أم أنزلنا عليهم سلطانا فهو يتكلم بما كانوا به يشركون",
          "AyaNumber": 3444,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88242f61d12bf6abd293"
          },
          "AyaContent": "وإذا أذقنا الناس رحمة فرحوا بها ۖ وإن تصبهم سيئة بما قدمت أيديهم إذا هم يقنطون",
          "AyaNumber": 3445,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88242f61d12bf6abd294"
          },
          "AyaContent": "أولم يروا أن الله يبسط الرزق لمن يشاء ويقدر ۚ إن في ذٰلك لآيات لقوم يؤمنون",
          "AyaNumber": 3446,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88252f61d12bf6abd295"
          },
          "AyaContent": "فآت ذا القربىٰ حقه والمسكين وابن السبيل ۚ ذٰلك خير للذين يريدون وجه الله ۖ وأولٰئك هم المفلحون",
          "AyaNumber": 3447,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88252f61d12bf6abd296"
          },
          "AyaContent": "وما آتيتم من ربا ليربو في أموال الناس فلا يربو عند الله ۖ وما آتيتم من زكاة تريدون وجه الله فأولٰئك هم المضعفون",
          "AyaNumber": 3448,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88252f61d12bf6abd297"
          },
          "AyaContent": "الله الذي خلقكم ثم رزقكم ثم يميتكم ثم يحييكم ۖ هل من شركائكم من يفعل من ذٰلكم من شيء ۚ سبحانه وتعالىٰ عما يشركون",
          "AyaNumber": 3449,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88252f61d12bf6abd298"
          },
          "AyaContent": "ظهر الفساد في البر والبحر بما كسبت أيدي الناس ليذيقهم بعض الذي عملوا لعلهم يرجعون",
          "AyaNumber": 3450,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88262f61d12bf6abd299"
          },
          "AyaContent": "قل سيروا في الأرض فانظروا كيف كان عاقبة الذين من قبل ۚ كان أكثرهم مشركين",
          "AyaNumber": 3451,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88262f61d12bf6abd29a"
          },
          "AyaContent": "فأقم وجهك للدين القيم من قبل أن يأتي يوم لا مرد له من الله ۖ يومئذ يصدعون",
          "AyaNumber": 3452,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88262f61d12bf6abd29b"
          },
          "AyaContent": "من كفر فعليه كفره ۖ ومن عمل صالحا فلأنفسهم يمهدون",
          "AyaNumber": 3453,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88272f61d12bf6abd29c"
          },
          "AyaContent": "ليجزي الذين آمنوا وعملوا الصالحات من فضله ۚ إنه لا يحب الكافرين",
          "AyaNumber": 3454,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88272f61d12bf6abd29d"
          },
          "AyaContent": "ومن آياته أن يرسل الرياح مبشرات وليذيقكم من رحمته ولتجري الفلك بأمره ولتبتغوا من فضله ولعلكم تشكرون",
          "AyaNumber": 3455,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88272f61d12bf6abd29e"
          },
          "AyaContent": "ولقد أرسلنا من قبلك رسلا إلىٰ قومهم فجاءوهم بالبينات فانتقمنا من الذين أجرموا ۖ وكان حقا علينا نصر المؤمنين",
          "AyaNumber": 3456,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88282f61d12bf6abd29f"
          },
          "AyaContent": "الله الذي يرسل الرياح فتثير سحابا فيبسطه في السماء كيف يشاء ويجعله كسفا فترى الودق يخرج من خلاله ۖ فإذا أصاب به من يشاء من عباده إذا هم يستبشرون",
          "AyaNumber": 3457,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88282f61d12bf6abd2a0"
          },
          "AyaContent": "وإن كانوا من قبل أن ينزل عليهم من قبله لمبلسين",
          "AyaNumber": 3458,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88282f61d12bf6abd2a1"
          },
          "AyaContent": "فانظر إلىٰ آثار رحمت الله كيف يحيي الأرض بعد موتها ۚ إن ذٰلك لمحيي الموتىٰ ۖ وهو علىٰ كل شيء قدير",
          "AyaNumber": 3459,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88282f61d12bf6abd2a2"
          },
          "AyaContent": "ولئن أرسلنا ريحا فرأوه مصفرا لظلوا من بعده يكفرون",
          "AyaNumber": 3460,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88292f61d12bf6abd2a3"
          },
          "AyaContent": "فإنك لا تسمع الموتىٰ ولا تسمع الصم الدعاء إذا ولوا مدبرين",
          "AyaNumber": 3461,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88292f61d12bf6abd2a4"
          },
          "AyaContent": "وما أنت بهاد العمي عن ضلالتهم ۖ إن تسمع إلا من يؤمن بآياتنا فهم مسلمون",
          "AyaNumber": 3462,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88292f61d12bf6abd2a5"
          },
          "AyaContent": "۞ الله الذي خلقكم من ضعف ثم جعل من بعد ضعف قوة ثم جعل من بعد قوة ضعفا وشيبة ۚ يخلق ما يشاء ۖ وهو العليم القدير",
          "AyaNumber": 3463,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd88292f61d12bf6abd2a6"
          },
          "AyaContent": "ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة ۚ كذٰلك كانوا يؤفكون",
          "AyaNumber": 3464,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd882a2f61d12bf6abd2a7"
          },
          "AyaContent": "وقال الذين أوتوا العلم والإيمان لقد لبثتم في كتاب الله إلىٰ يوم البعث ۖ فهٰذا يوم البعث ولٰكنكم كنتم لا تعلمون",
          "AyaNumber": 3465,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd882a2f61d12bf6abd2a8"
          },
          "AyaContent": "فيومئذ لا ينفع الذين ظلموا معذرتهم ولا هم يستعتبون",
          "AyaNumber": 3466,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd882a2f61d12bf6abd2a9"
          },
          "AyaContent": "ولقد ضربنا للناس في هٰذا القرآن من كل مثل ۚ ولئن جئتهم بآية ليقولن الذين كفروا إن أنتم إلا مبطلون",
          "AyaNumber": 3467,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd882b2f61d12bf6abd2aa"
          },
          "AyaContent": "كذٰلك يطبع الله علىٰ قلوب الذين لا يعلمون",
          "AyaNumber": 3468,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd882b2f61d12bf6abd2ab"
          },
          "AyaContent": "فاصبر إن وعد الله حق ۖ ولا يستخفنك الذين لا يوقنون",
          "AyaNumber": 3469,
          "SurahName": "سورة الروم",
          "SurahNumber": 30
        },{
          "_id": {
            "$oid": "5ecd882b2f61d12bf6abd2ac"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الم",
          "AyaNumber": 3470,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882c2f61d12bf6abd2ad"
          },
          "AyaContent": "تلك آيات الكتاب الحكيم",
          "AyaNumber": 3471,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882c2f61d12bf6abd2ae"
          },
          "AyaContent": "هدى ورحمة للمحسنين",
          "AyaNumber": 3472,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882c2f61d12bf6abd2af"
          },
          "AyaContent": "الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون",
          "AyaNumber": 3473,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882c2f61d12bf6abd2b0"
          },
          "AyaContent": "أولٰئك علىٰ هدى من ربهم ۖ وأولٰئك هم المفلحون",
          "AyaNumber": 3474,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882d2f61d12bf6abd2b1"
          },
          "AyaContent": "ومن الناس من يشتري لهو الحديث ليضل عن سبيل الله بغير علم ويتخذها هزوا ۚ أولٰئك لهم عذاب مهين",
          "AyaNumber": 3475,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882d2f61d12bf6abd2b2"
          },
          "AyaContent": "وإذا تتلىٰ عليه آياتنا ولىٰ مستكبرا كأن لم يسمعها كأن في أذنيه وقرا ۖ فبشره بعذاب أليم",
          "AyaNumber": 3476,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882d2f61d12bf6abd2b3"
          },
          "AyaContent": "إن الذين آمنوا وعملوا الصالحات لهم جنات النعيم",
          "AyaNumber": 3477,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882e2f61d12bf6abd2b4"
          },
          "AyaContent": "خالدين فيها ۖ وعد الله حقا ۚ وهو العزيز الحكيم",
          "AyaNumber": 3478,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882e2f61d12bf6abd2b5"
          },
          "AyaContent": "خلق السماوات بغير عمد ترونها ۖ وألقىٰ في الأرض رواسي أن تميد بكم وبث فيها من كل دابة ۚ وأنزلنا من السماء ماء فأنبتنا فيها من كل زوج كريم",
          "AyaNumber": 3479,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882e2f61d12bf6abd2b6"
          },
          "AyaContent": "هٰذا خلق الله فأروني ماذا خلق الذين من دونه ۚ بل الظالمون في ضلال مبين",
          "AyaNumber": 3480,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882e2f61d12bf6abd2b7"
          },
          "AyaContent": "ولقد آتينا لقمان الحكمة أن اشكر لله ۚ ومن يشكر فإنما يشكر لنفسه ۖ ومن كفر فإن الله غني حميد",
          "AyaNumber": 3481,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882f2f61d12bf6abd2b8"
          },
          "AyaContent": "وإذ قال لقمان لابنه وهو يعظه يا بني لا تشرك بالله ۖ إن الشرك لظلم عظيم",
          "AyaNumber": 3482,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882f2f61d12bf6abd2b9"
          },
          "AyaContent": "ووصينا الإنسان بوالديه حملته أمه وهنا علىٰ وهن وفصاله في عامين أن اشكر لي ولوالديك إلي المصير",
          "AyaNumber": 3483,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882f2f61d12bf6abd2ba"
          },
          "AyaContent": "وإن جاهداك علىٰ أن تشرك بي ما ليس لك به علم فلا تطعهما ۖ وصاحبهما في الدنيا معروفا ۖ واتبع سبيل من أناب إلي ۚ ثم إلي مرجعكم فأنبئكم بما كنتم تعملون",
          "AyaNumber": 3484,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd882f2f61d12bf6abd2bb"
          },
          "AyaContent": "يا بني إنها إن تك مثقال حبة من خردل فتكن في صخرة أو في السماوات أو في الأرض يأت بها الله ۚ إن الله لطيف خبير",
          "AyaNumber": 3485,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88302f61d12bf6abd2bc"
          },
          "AyaContent": "يا بني أقم الصلاة وأمر بالمعروف وانه عن المنكر واصبر علىٰ ما أصابك ۖ إن ذٰلك من عزم الأمور",
          "AyaNumber": 3486,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88302f61d12bf6abd2bd"
          },
          "AyaContent": "ولا تصعر خدك للناس ولا تمش في الأرض مرحا ۖ إن الله لا يحب كل مختال فخور",
          "AyaNumber": 3487,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88302f61d12bf6abd2be"
          },
          "AyaContent": "واقصد في مشيك واغضض من صوتك ۚ إن أنكر الأصوات لصوت الحمير",
          "AyaNumber": 3488,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88312f61d12bf6abd2bf"
          },
          "AyaContent": "ألم تروا أن الله سخر لكم ما في السماوات وما في الأرض وأسبغ عليكم نعمه ظاهرة وباطنة ۗ ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير",
          "AyaNumber": 3489,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88312f61d12bf6abd2c0"
          },
          "AyaContent": "وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما وجدنا عليه آباءنا ۚ أولو كان الشيطان يدعوهم إلىٰ عذاب السعير",
          "AyaNumber": 3490,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88312f61d12bf6abd2c1"
          },
          "AyaContent": "۞ ومن يسلم وجهه إلى الله وهو محسن فقد استمسك بالعروة الوثقىٰ ۗ وإلى الله عاقبة الأمور",
          "AyaNumber": 3491,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88312f61d12bf6abd2c2"
          },
          "AyaContent": "ومن كفر فلا يحزنك كفره ۚ إلينا مرجعهم فننبئهم بما عملوا ۚ إن الله عليم بذات الصدور",
          "AyaNumber": 3492,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88312f61d12bf6abd2c3"
          },
          "AyaContent": "نمتعهم قليلا ثم نضطرهم إلىٰ عذاب غليظ",
          "AyaNumber": 3493,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88322f61d12bf6abd2c4"
          },
          "AyaContent": "ولئن سألتهم من خلق السماوات والأرض ليقولن الله ۚ قل الحمد لله ۚ بل أكثرهم لا يعلمون",
          "AyaNumber": 3494,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88322f61d12bf6abd2c5"
          },
          "AyaContent": "لله ما في السماوات والأرض ۚ إن الله هو الغني الحميد",
          "AyaNumber": 3495,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88322f61d12bf6abd2c6"
          },
          "AyaContent": "ولو أنما في الأرض من شجرة أقلام والبحر يمده من بعده سبعة أبحر ما نفدت كلمات الله ۗ إن الله عزيز حكيم",
          "AyaNumber": 3496,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88322f61d12bf6abd2c7"
          },
          "AyaContent": "ما خلقكم ولا بعثكم إلا كنفس واحدة ۗ إن الله سميع بصير",
          "AyaNumber": 3497,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88332f61d12bf6abd2c8"
          },
          "AyaContent": "ألم تر أن الله يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري إلىٰ أجل مسمى وأن الله بما تعملون خبير",
          "AyaNumber": 3498,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88332f61d12bf6abd2c9"
          },
          "AyaContent": "ذٰلك بأن الله هو الحق وأن ما يدعون من دونه الباطل وأن الله هو العلي الكبير",
          "AyaNumber": 3499,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88332f61d12bf6abd2ca"
          },
          "AyaContent": "ألم تر أن الفلك تجري في البحر بنعمت الله ليريكم من آياته ۚ إن في ذٰلك لآيات لكل صبار شكور",
          "AyaNumber": 3500,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88332f61d12bf6abd2cb"
          },
          "AyaContent": "وإذا غشيهم موج كالظلل دعوا الله مخلصين له الدين فلما نجاهم إلى البر فمنهم مقتصد ۚ وما يجحد بآياتنا إلا كل ختار كفور",
          "AyaNumber": 3501,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88342f61d12bf6abd2cc"
          },
          "AyaContent": "يا أيها الناس اتقوا ربكم واخشوا يوما لا يجزي والد عن ولده ولا مولود هو جاز عن والده شيئا ۚ إن وعد الله حق ۖ فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور",
          "AyaNumber": 3502,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88342f61d12bf6abd2cd"
          },
          "AyaContent": "إن الله عنده علم الساعة وينزل الغيث ويعلم ما في الأرحام ۖ وما تدري نفس ماذا تكسب غدا ۖ وما تدري نفس بأي أرض تموت ۚ إن الله عليم خبير",
          "AyaNumber": 3503,
          "SurahName": "سورة لقمان",
          "SurahNumber": 31
        },{
          "_id": {
            "$oid": "5ecd88342f61d12bf6abd2ce"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الم",
          "AyaNumber": 3504,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88352f61d12bf6abd2cf"
          },
          "AyaContent": "تنزيل الكتاب لا ريب فيه من رب العالمين",
          "AyaNumber": 3505,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88352f61d12bf6abd2d0"
          },
          "AyaContent": "أم يقولون افتراه ۚ بل هو الحق من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يهتدون",
          "AyaNumber": 3506,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88352f61d12bf6abd2d1"
          },
          "AyaContent": "الله الذي خلق السماوات والأرض وما بينهما في ستة أيام ثم استوىٰ على العرش ۖ ما لكم من دونه من ولي ولا شفيع ۚ أفلا تتذكرون",
          "AyaNumber": 3507,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88352f61d12bf6abd2d2"
          },
          "AyaContent": "يدبر الأمر من السماء إلى الأرض ثم يعرج إليه في يوم كان مقداره ألف سنة مما تعدون",
          "AyaNumber": 3508,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88362f61d12bf6abd2d3"
          },
          "AyaContent": "ذٰلك عالم الغيب والشهادة العزيز الرحيم",
          "AyaNumber": 3509,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88362f61d12bf6abd2d4"
          },
          "AyaContent": "الذي أحسن كل شيء خلقه ۖ وبدأ خلق الإنسان من طين",
          "AyaNumber": 3510,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88362f61d12bf6abd2d5"
          },
          "AyaContent": "ثم جعل نسله من سلالة من ماء مهين",
          "AyaNumber": 3511,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88362f61d12bf6abd2d6"
          },
          "AyaContent": "ثم سواه ونفخ فيه من روحه ۖ وجعل لكم السمع والأبصار والأفئدة ۚ قليلا ما تشكرون",
          "AyaNumber": 3512,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88372f61d12bf6abd2d7"
          },
          "AyaContent": "وقالوا أإذا ضللنا في الأرض أإنا لفي خلق جديد ۚ بل هم بلقاء ربهم كافرون",
          "AyaNumber": 3513,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88372f61d12bf6abd2d8"
          },
          "AyaContent": "۞ قل يتوفاكم ملك الموت الذي وكل بكم ثم إلىٰ ربكم ترجعون",
          "AyaNumber": 3514,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88372f61d12bf6abd2d9"
          },
          "AyaContent": "ولو ترىٰ إذ المجرمون ناكسو رءوسهم عند ربهم ربنا أبصرنا وسمعنا فارجعنا نعمل صالحا إنا موقنون",
          "AyaNumber": 3515,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88382f61d12bf6abd2da"
          },
          "AyaContent": "ولو شئنا لآتينا كل نفس هداها ولٰكن حق القول مني لأملأن جهنم من الجنة والناس أجمعين",
          "AyaNumber": 3516,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88382f61d12bf6abd2db"
          },
          "AyaContent": "فذوقوا بما نسيتم لقاء يومكم هٰذا إنا نسيناكم ۖ وذوقوا عذاب الخلد بما كنتم تعملون",
          "AyaNumber": 3517,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88382f61d12bf6abd2dc"
          },
          "AyaContent": "إنما يؤمن بآياتنا الذين إذا ذكروا بها خروا سجدا وسبحوا بحمد ربهم وهم لا يستكبرون ۩",
          "AyaNumber": 3518,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88382f61d12bf6abd2dd"
          },
          "AyaContent": "تتجافىٰ جنوبهم عن المضاجع يدعون ربهم خوفا وطمعا ومما رزقناهم ينفقون",
          "AyaNumber": 3519,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88392f61d12bf6abd2de"
          },
          "AyaContent": "فلا تعلم نفس ما أخفي لهم من قرة أعين جزاء بما كانوا يعملون",
          "AyaNumber": 3520,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88392f61d12bf6abd2df"
          },
          "AyaContent": "أفمن كان مؤمنا كمن كان فاسقا ۚ لا يستوون",
          "AyaNumber": 3521,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd88392f61d12bf6abd2e0"
          },
          "AyaContent": "أما الذين آمنوا وعملوا الصالحات فلهم جنات المأوىٰ نزلا بما كانوا يعملون",
          "AyaNumber": 3522,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883a2f61d12bf6abd2e1"
          },
          "AyaContent": "وأما الذين فسقوا فمأواهم النار ۖ كلما أرادوا أن يخرجوا منها أعيدوا فيها وقيل لهم ذوقوا عذاب النار الذي كنتم به تكذبون",
          "AyaNumber": 3523,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883a2f61d12bf6abd2e2"
          },
          "AyaContent": "ولنذيقنهم من العذاب الأدنىٰ دون العذاب الأكبر لعلهم يرجعون",
          "AyaNumber": 3524,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883a2f61d12bf6abd2e3"
          },
          "AyaContent": "ومن أظلم ممن ذكر بآيات ربه ثم أعرض عنها ۚ إنا من المجرمين منتقمون",
          "AyaNumber": 3525,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883b2f61d12bf6abd2e4"
          },
          "AyaContent": "ولقد آتينا موسى الكتاب فلا تكن في مرية من لقائه ۖ وجعلناه هدى لبني إسرائيل",
          "AyaNumber": 3526,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883b2f61d12bf6abd2e5"
          },
          "AyaContent": "وجعلنا منهم أئمة يهدون بأمرنا لما صبروا ۖ وكانوا بآياتنا يوقنون",
          "AyaNumber": 3527,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883b2f61d12bf6abd2e6"
          },
          "AyaContent": "إن ربك هو يفصل بينهم يوم القيامة فيما كانوا فيه يختلفون",
          "AyaNumber": 3528,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883c2f61d12bf6abd2e7"
          },
          "AyaContent": "أولم يهد لهم كم أهلكنا من قبلهم من القرون يمشون في مساكنهم ۚ إن في ذٰلك لآيات ۖ أفلا يسمعون",
          "AyaNumber": 3529,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883c2f61d12bf6abd2e8"
          },
          "AyaContent": "أولم يروا أنا نسوق الماء إلى الأرض الجرز فنخرج به زرعا تأكل منه أنعامهم وأنفسهم ۖ أفلا يبصرون",
          "AyaNumber": 3530,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883c2f61d12bf6abd2e9"
          },
          "AyaContent": "ويقولون متىٰ هٰذا الفتح إن كنتم صادقين",
          "AyaNumber": 3531,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883c2f61d12bf6abd2ea"
          },
          "AyaContent": "قل يوم الفتح لا ينفع الذين كفروا إيمانهم ولا هم ينظرون",
          "AyaNumber": 3532,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883d2f61d12bf6abd2eb"
          },
          "AyaContent": "فأعرض عنهم وانتظر إنهم منتظرون",
          "AyaNumber": 3533,
          "SurahName": "سورة السجدة",
          "SurahNumber": 32
        },{
          "_id": {
            "$oid": "5ecd883d2f61d12bf6abd2ec"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يا أيها النبي اتق الله ولا تطع الكافرين والمنافقين ۗ إن الله كان عليما حكيما",
          "AyaNumber": 3534,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883d2f61d12bf6abd2ed"
          },
          "AyaContent": "واتبع ما يوحىٰ إليك من ربك ۚ إن الله كان بما تعملون خبيرا",
          "AyaNumber": 3535,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883d2f61d12bf6abd2ee"
          },
          "AyaContent": "وتوكل على الله ۚ وكفىٰ بالله وكيلا",
          "AyaNumber": 3536,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883e2f61d12bf6abd2ef"
          },
          "AyaContent": "ما جعل الله لرجل من قلبين في جوفه ۚ وما جعل أزواجكم اللائي تظاهرون منهن أمهاتكم ۚ وما جعل أدعياءكم أبناءكم ۚ ذٰلكم قولكم بأفواهكم ۖ والله يقول الحق وهو يهدي السبيل",
          "AyaNumber": 3537,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883e2f61d12bf6abd2f0"
          },
          "AyaContent": "ادعوهم لآبائهم هو أقسط عند الله ۚ فإن لم تعلموا آباءهم فإخوانكم في الدين ومواليكم ۚ وليس عليكم جناح فيما أخطأتم به ولٰكن ما تعمدت قلوبكم ۚ وكان الله غفورا رحيما",
          "AyaNumber": 3538,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883e2f61d12bf6abd2f1"
          },
          "AyaContent": "النبي أولىٰ بالمؤمنين من أنفسهم ۖ وأزواجه أمهاتهم ۗ وأولو الأرحام بعضهم أولىٰ ببعض في كتاب الله من المؤمنين والمهاجرين إلا أن تفعلوا إلىٰ أوليائكم معروفا ۚ كان ذٰلك في الكتاب مسطورا",
          "AyaNumber": 3539,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883e2f61d12bf6abd2f2"
          },
          "AyaContent": "وإذ أخذنا من النبيين ميثاقهم ومنك ومن نوح وإبراهيم وموسىٰ وعيسى ابن مريم ۖ وأخذنا منهم ميثاقا غليظا",
          "AyaNumber": 3540,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883f2f61d12bf6abd2f3"
          },
          "AyaContent": "ليسأل الصادقين عن صدقهم ۚ وأعد للكافرين عذابا أليما",
          "AyaNumber": 3541,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883f2f61d12bf6abd2f4"
          },
          "AyaContent": "يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود فأرسلنا عليهم ريحا وجنودا لم تروها ۚ وكان الله بما تعملون بصيرا",
          "AyaNumber": 3542,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883f2f61d12bf6abd2f5"
          },
          "AyaContent": "إذ جاءوكم من فوقكم ومن أسفل منكم وإذ زاغت الأبصار وبلغت القلوب الحناجر وتظنون بالله الظنونا",
          "AyaNumber": 3543,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883f2f61d12bf6abd2f6"
          },
          "AyaContent": "هنالك ابتلي المؤمنون وزلزلوا زلزالا شديدا",
          "AyaNumber": 3544,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883f2f61d12bf6abd2f7"
          },
          "AyaContent": "وإذ يقول المنافقون والذين في قلوبهم مرض ما وعدنا الله ورسوله إلا غرورا",
          "AyaNumber": 3545,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd883f2f61d12bf6abd2f8"
          },
          "AyaContent": "وإذ قالت طائفة منهم يا أهل يثرب لا مقام لكم فارجعوا ۚ ويستأذن فريق منهم النبي يقولون إن بيوتنا عورة وما هي بعورة ۖ إن يريدون إلا فرارا",
          "AyaNumber": 3546,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88402f61d12bf6abd2f9"
          },
          "AyaContent": "ولو دخلت عليهم من أقطارها ثم سئلوا الفتنة لآتوها وما تلبثوا بها إلا يسيرا",
          "AyaNumber": 3547,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88402f61d12bf6abd2fa"
          },
          "AyaContent": "ولقد كانوا عاهدوا الله من قبل لا يولون الأدبار ۚ وكان عهد الله مسئولا",
          "AyaNumber": 3548,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88402f61d12bf6abd2fb"
          },
          "AyaContent": "قل لن ينفعكم الفرار إن فررتم من الموت أو القتل وإذا لا تمتعون إلا قليلا",
          "AyaNumber": 3549,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88402f61d12bf6abd2fc"
          },
          "AyaContent": "قل من ذا الذي يعصمكم من الله إن أراد بكم سوءا أو أراد بكم رحمة ۚ ولا يجدون لهم من دون الله وليا ولا نصيرا",
          "AyaNumber": 3550,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88412f61d12bf6abd2fd"
          },
          "AyaContent": "۞ قد يعلم الله المعوقين منكم والقائلين لإخوانهم هلم إلينا ۖ ولا يأتون البأس إلا قليلا",
          "AyaNumber": 3551,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88412f61d12bf6abd2fe"
          },
          "AyaContent": "أشحة عليكم ۖ فإذا جاء الخوف رأيتهم ينظرون إليك تدور أعينهم كالذي يغشىٰ عليه من الموت ۖ فإذا ذهب الخوف سلقوكم بألسنة حداد أشحة على الخير ۚ أولٰئك لم يؤمنوا فأحبط الله أعمالهم ۚ وكان ذٰلك على الله يسيرا",
          "AyaNumber": 3552,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88412f61d12bf6abd2ff"
          },
          "AyaContent": "يحسبون الأحزاب لم يذهبوا ۖ وإن يأت الأحزاب يودوا لو أنهم بادون في الأعراب يسألون عن أنبائكم ۖ ولو كانوا فيكم ما قاتلوا إلا قليلا",
          "AyaNumber": 3553,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88412f61d12bf6abd300"
          },
          "AyaContent": "لقد كان لكم في رسول الله أسوة حسنة لمن كان يرجو الله واليوم الآخر وذكر الله كثيرا",
          "AyaNumber": 3554,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88422f61d12bf6abd301"
          },
          "AyaContent": "ولما رأى المؤمنون الأحزاب قالوا هٰذا ما وعدنا الله ورسوله وصدق الله ورسوله ۚ وما زادهم إلا إيمانا وتسليما",
          "AyaNumber": 3555,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88422f61d12bf6abd302"
          },
          "AyaContent": "من المؤمنين رجال صدقوا ما عاهدوا الله عليه ۖ فمنهم من قضىٰ نحبه ومنهم من ينتظر ۖ وما بدلوا تبديلا",
          "AyaNumber": 3556,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88422f61d12bf6abd303"
          },
          "AyaContent": "ليجزي الله الصادقين بصدقهم ويعذب المنافقين إن شاء أو يتوب عليهم ۚ إن الله كان غفورا رحيما",
          "AyaNumber": 3557,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88422f61d12bf6abd304"
          },
          "AyaContent": "ورد الله الذين كفروا بغيظهم لم ينالوا خيرا ۚ وكفى الله المؤمنين القتال ۚ وكان الله قويا عزيزا",
          "AyaNumber": 3558,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88432f61d12bf6abd305"
          },
          "AyaContent": "وأنزل الذين ظاهروهم من أهل الكتاب من صياصيهم وقذف في قلوبهم الرعب فريقا تقتلون وتأسرون فريقا",
          "AyaNumber": 3559,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88432f61d12bf6abd306"
          },
          "AyaContent": "وأورثكم أرضهم وديارهم وأموالهم وأرضا لم تطئوها ۚ وكان الله علىٰ كل شيء قديرا",
          "AyaNumber": 3560,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88432f61d12bf6abd307"
          },
          "AyaContent": "يا أيها النبي قل لأزواجك إن كنتن تردن الحياة الدنيا وزينتها فتعالين أمتعكن وأسرحكن سراحا جميلا",
          "AyaNumber": 3561,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88432f61d12bf6abd308"
          },
          "AyaContent": "وإن كنتن تردن الله ورسوله والدار الآخرة فإن الله أعد للمحسنات منكن أجرا عظيما",
          "AyaNumber": 3562,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88432f61d12bf6abd309"
          },
          "AyaContent": "يا نساء النبي من يأت منكن بفاحشة مبينة يضاعف لها العذاب ضعفين ۚ وكان ذٰلك على الله يسيرا",
          "AyaNumber": 3563,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88442f61d12bf6abd30a"
          },
          "AyaContent": "۞ ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها أجرها مرتين وأعتدنا لها رزقا كريما",
          "AyaNumber": 3564,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88442f61d12bf6abd30b"
          },
          "AyaContent": "يا نساء النبي لستن كأحد من النساء ۚ إن اتقيتن فلا تخضعن بالقول فيطمع الذي في قلبه مرض وقلن قولا معروفا",
          "AyaNumber": 3565,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88442f61d12bf6abd30c"
          },
          "AyaContent": "وقرن في بيوتكن ولا تبرجن تبرج الجاهلية الأولىٰ ۖ وأقمن الصلاة وآتين الزكاة وأطعن الله ورسوله ۚ إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا",
          "AyaNumber": 3566,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88442f61d12bf6abd30d"
          },
          "AyaContent": "واذكرن ما يتلىٰ في بيوتكن من آيات الله والحكمة ۚ إن الله كان لطيفا خبيرا",
          "AyaNumber": 3567,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88452f61d12bf6abd30e"
          },
          "AyaContent": "إن المسلمين والمسلمات والمؤمنين والمؤمنات والقانتين والقانتات والصادقين والصادقات والصابرين والصابرات والخاشعين والخاشعات والمتصدقين والمتصدقات والصائمين والصائمات والحافظين فروجهم والحافظات والذاكرين الله كثيرا والذاكرات أعد الله لهم مغفرة وأجرا عظيما",
          "AyaNumber": 3568,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88452f61d12bf6abd30f"
          },
          "AyaContent": "وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة من أمرهم ۗ ومن يعص الله ورسوله فقد ضل ضلالا مبينا",
          "AyaNumber": 3569,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88452f61d12bf6abd310"
          },
          "AyaContent": "وإذ تقول للذي أنعم الله عليه وأنعمت عليه أمسك عليك زوجك واتق الله وتخفي في نفسك ما الله مبديه وتخشى الناس والله أحق أن تخشاه ۖ فلما قضىٰ زيد منها وطرا زوجناكها لكي لا يكون على المؤمنين حرج في أزواج أدعيائهم إذا قضوا منهن وطرا ۚ وكان أمر الله مفعولا",
          "AyaNumber": 3570,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88452f61d12bf6abd311"
          },
          "AyaContent": "ما كان على النبي من حرج فيما فرض الله له ۖ سنة الله في الذين خلوا من قبل ۚ وكان أمر الله قدرا مقدورا",
          "AyaNumber": 3571,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88462f61d12bf6abd312"
          },
          "AyaContent": "الذين يبلغون رسالات الله ويخشونه ولا يخشون أحدا إلا الله ۗ وكفىٰ بالله حسيبا",
          "AyaNumber": 3572,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88472f61d12bf6abd313"
          },
          "AyaContent": "ما كان محمد أبا أحد من رجالكم ولٰكن رسول الله وخاتم النبيين ۗ وكان الله بكل شيء عليما",
          "AyaNumber": 3573,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88472f61d12bf6abd314"
          },
          "AyaContent": "يا أيها الذين آمنوا اذكروا الله ذكرا كثيرا",
          "AyaNumber": 3574,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88482f61d12bf6abd315"
          },
          "AyaContent": "وسبحوه بكرة وأصيلا",
          "AyaNumber": 3575,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88492f61d12bf6abd316"
          },
          "AyaContent": "هو الذي يصلي عليكم وملائكته ليخرجكم من الظلمات إلى النور ۚ وكان بالمؤمنين رحيما",
          "AyaNumber": 3576,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88492f61d12bf6abd317"
          },
          "AyaContent": "تحيتهم يوم يلقونه سلام ۚ وأعد لهم أجرا كريما",
          "AyaNumber": 3577,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88492f61d12bf6abd318"
          },
          "AyaContent": "يا أيها النبي إنا أرسلناك شاهدا ومبشرا ونذيرا",
          "AyaNumber": 3578,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884a2f61d12bf6abd319"
          },
          "AyaContent": "وداعيا إلى الله بإذنه وسراجا منيرا",
          "AyaNumber": 3579,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884a2f61d12bf6abd31a"
          },
          "AyaContent": "وبشر المؤمنين بأن لهم من الله فضلا كبيرا",
          "AyaNumber": 3580,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884b2f61d12bf6abd31b"
          },
          "AyaContent": "ولا تطع الكافرين والمنافقين ودع أذاهم وتوكل على الله ۚ وكفىٰ بالله وكيلا",
          "AyaNumber": 3581,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884b2f61d12bf6abd31c"
          },
          "AyaContent": "يا أيها الذين آمنوا إذا نكحتم المؤمنات ثم طلقتموهن من قبل أن تمسوهن فما لكم عليهن من عدة تعتدونها ۖ فمتعوهن وسرحوهن سراحا جميلا",
          "AyaNumber": 3582,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884c2f61d12bf6abd31d"
          },
          "AyaContent": "يا أيها النبي إنا أحللنا لك أزواجك اللاتي آتيت أجورهن وما ملكت يمينك مما أفاء الله عليك وبنات عمك وبنات عماتك وبنات خالك وبنات خالاتك اللاتي هاجرن معك وامرأة مؤمنة إن وهبت نفسها للنبي إن أراد النبي أن يستنكحها خالصة لك من دون المؤمنين ۗ قد علمنا ما فرضنا عليهم في أزواجهم وما ملكت أيمانهم لكيلا يكون عليك حرج ۗ وكان الله غفورا رحيما",
          "AyaNumber": 3583,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884c2f61d12bf6abd31e"
          },
          "AyaContent": "۞ ترجي من تشاء منهن وتؤوي إليك من تشاء ۖ ومن ابتغيت ممن عزلت فلا جناح عليك ۚ ذٰلك أدنىٰ أن تقر أعينهن ولا يحزن ويرضين بما آتيتهن كلهن ۚ والله يعلم ما في قلوبكم ۚ وكان الله عليما حليما",
          "AyaNumber": 3584,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884c2f61d12bf6abd31f"
          },
          "AyaContent": "لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك ۗ وكان الله علىٰ كل شيء رقيبا",
          "AyaNumber": 3585,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884d2f61d12bf6abd320"
          },
          "AyaContent": "يا أيها الذين آمنوا لا تدخلوا بيوت النبي إلا أن يؤذن لكم إلىٰ طعام غير ناظرين إناه ولٰكن إذا دعيتم فادخلوا فإذا طعمتم فانتشروا ولا مستأنسين لحديث ۚ إن ذٰلكم كان يؤذي النبي فيستحيي منكم ۖ والله لا يستحيي من الحق ۚ وإذا سألتموهن متاعا فاسألوهن من وراء حجاب ۚ ذٰلكم أطهر لقلوبكم وقلوبهن ۚ وما كان لكم أن تؤذوا رسول الله ولا أن تنكحوا أزواجه من بعده أبدا ۚ إن ذٰلكم كان عند الله عظيما",
          "AyaNumber": 3586,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884d2f61d12bf6abd321"
          },
          "AyaContent": "إن تبدوا شيئا أو تخفوه فإن الله كان بكل شيء عليما",
          "AyaNumber": 3587,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884d2f61d12bf6abd322"
          },
          "AyaContent": "لا جناح عليهن في آبائهن ولا أبنائهن ولا إخوانهن ولا أبناء إخوانهن ولا أبناء أخواتهن ولا نسائهن ولا ما ملكت أيمانهن ۗ واتقين الله ۚ إن الله كان علىٰ كل شيء شهيدا",
          "AyaNumber": 3588,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884d2f61d12bf6abd323"
          },
          "AyaContent": "إن الله وملائكته يصلون على النبي ۚ يا أيها الذين آمنوا صلوا عليه وسلموا تسليما",
          "AyaNumber": 3589,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884e2f61d12bf6abd324"
          },
          "AyaContent": "إن الذين يؤذون الله ورسوله لعنهم الله في الدنيا والآخرة وأعد لهم عذابا مهينا",
          "AyaNumber": 3590,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884e2f61d12bf6abd325"
          },
          "AyaContent": "والذين يؤذون المؤمنين والمؤمنات بغير ما اكتسبوا فقد احتملوا بهتانا وإثما مبينا",
          "AyaNumber": 3591,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884e2f61d12bf6abd326"
          },
          "AyaContent": "يا أيها النبي قل لأزواجك وبناتك ونساء المؤمنين يدنين عليهن من جلابيبهن ۚ ذٰلك أدنىٰ أن يعرفن فلا يؤذين ۗ وكان الله غفورا رحيما",
          "AyaNumber": 3592,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884f2f61d12bf6abd327"
          },
          "AyaContent": "۞ لئن لم ينته المنافقون والذين في قلوبهم مرض والمرجفون في المدينة لنغرينك بهم ثم لا يجاورونك فيها إلا قليلا",
          "AyaNumber": 3593,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884f2f61d12bf6abd328"
          },
          "AyaContent": "ملعونين ۖ أينما ثقفوا أخذوا وقتلوا تقتيلا",
          "AyaNumber": 3594,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884f2f61d12bf6abd329"
          },
          "AyaContent": "سنة الله في الذين خلوا من قبل ۖ ولن تجد لسنة الله تبديلا",
          "AyaNumber": 3595,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd884f2f61d12bf6abd32a"
          },
          "AyaContent": "يسألك الناس عن الساعة ۖ قل إنما علمها عند الله ۚ وما يدريك لعل الساعة تكون قريبا",
          "AyaNumber": 3596,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88502f61d12bf6abd32b"
          },
          "AyaContent": "إن الله لعن الكافرين وأعد لهم سعيرا",
          "AyaNumber": 3597,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88502f61d12bf6abd32c"
          },
          "AyaContent": "خالدين فيها أبدا ۖ لا يجدون وليا ولا نصيرا",
          "AyaNumber": 3598,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88502f61d12bf6abd32d"
          },
          "AyaContent": "يوم تقلب وجوههم في النار يقولون يا ليتنا أطعنا الله وأطعنا الرسولا",
          "AyaNumber": 3599,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88512f61d12bf6abd32e"
          },
          "AyaContent": "وقالوا ربنا إنا أطعنا سادتنا وكبراءنا فأضلونا السبيلا",
          "AyaNumber": 3600,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88512f61d12bf6abd32f"
          },
          "AyaContent": "ربنا آتهم ضعفين من العذاب والعنهم لعنا كبيرا",
          "AyaNumber": 3601,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88512f61d12bf6abd330"
          },
          "AyaContent": "يا أيها الذين آمنوا لا تكونوا كالذين آذوا موسىٰ فبرأه الله مما قالوا ۚ وكان عند الله وجيها",
          "AyaNumber": 3602,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88522f61d12bf6abd331"
          },
          "AyaContent": "يا أيها الذين آمنوا اتقوا الله وقولوا قولا سديدا",
          "AyaNumber": 3603,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88522f61d12bf6abd332"
          },
          "AyaContent": "يصلح لكم أعمالكم ويغفر لكم ذنوبكم ۗ ومن يطع الله ورسوله فقد فاز فوزا عظيما",
          "AyaNumber": 3604,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88522f61d12bf6abd333"
          },
          "AyaContent": "إنا عرضنا الأمانة على السماوات والأرض والجبال فأبين أن يحملنها وأشفقن منها وحملها الإنسان ۖ إنه كان ظلوما جهولا",
          "AyaNumber": 3605,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88532f61d12bf6abd334"
          },
          "AyaContent": "ليعذب الله المنافقين والمنافقات والمشركين والمشركات ويتوب الله على المؤمنين والمؤمنات ۗ وكان الله غفورا رحيما",
          "AyaNumber": 3606,
          "SurahName": "سورة الأحزاب",
          "SurahNumber": 33
        },{
          "_id": {
            "$oid": "5ecd88532f61d12bf6abd335"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الحمد لله الذي له ما في السماوات وما في الأرض وله الحمد في الآخرة ۚ وهو الحكيم الخبير",
          "AyaNumber": 3607,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88532f61d12bf6abd336"
          },
          "AyaContent": "يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها ۚ وهو الرحيم الغفور",
          "AyaNumber": 3608,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88542f61d12bf6abd337"
          },
          "AyaContent": "وقال الذين كفروا لا تأتينا الساعة ۖ قل بلىٰ وربي لتأتينكم عالم الغيب ۖ لا يعزب عنه مثقال ذرة في السماوات ولا في الأرض ولا أصغر من ذٰلك ولا أكبر إلا في كتاب مبين",
          "AyaNumber": 3609,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88542f61d12bf6abd338"
          },
          "AyaContent": "ليجزي الذين آمنوا وعملوا الصالحات ۚ أولٰئك لهم مغفرة ورزق كريم",
          "AyaNumber": 3610,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88542f61d12bf6abd339"
          },
          "AyaContent": "والذين سعوا في آياتنا معاجزين أولٰئك لهم عذاب من رجز أليم",
          "AyaNumber": 3611,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88542f61d12bf6abd33a"
          },
          "AyaContent": "ويرى الذين أوتوا العلم الذي أنزل إليك من ربك هو الحق ويهدي إلىٰ صراط العزيز الحميد",
          "AyaNumber": 3612,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88552f61d12bf6abd33b"
          },
          "AyaContent": "وقال الذين كفروا هل ندلكم علىٰ رجل ينبئكم إذا مزقتم كل ممزق إنكم لفي خلق جديد",
          "AyaNumber": 3613,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88552f61d12bf6abd33c"
          },
          "AyaContent": "أفترىٰ على الله كذبا أم به جنة ۗ بل الذين لا يؤمنون بالآخرة في العذاب والضلال البعيد",
          "AyaNumber": 3614,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88552f61d12bf6abd33d"
          },
          "AyaContent": "أفلم يروا إلىٰ ما بين أيديهم وما خلفهم من السماء والأرض ۚ إن نشأ نخسف بهم الأرض أو نسقط عليهم كسفا من السماء ۚ إن في ذٰلك لآية لكل عبد منيب",
          "AyaNumber": 3615,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88562f61d12bf6abd33e"
          },
          "AyaContent": "۞ ولقد آتينا داوود منا فضلا ۖ يا جبال أوبي معه والطير ۖ وألنا له الحديد",
          "AyaNumber": 3616,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88562f61d12bf6abd33f"
          },
          "AyaContent": "أن اعمل سابغات وقدر في السرد ۖ واعملوا صالحا ۖ إني بما تعملون بصير",
          "AyaNumber": 3617,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88562f61d12bf6abd340"
          },
          "AyaContent": "ولسليمان الريح غدوها شهر ورواحها شهر ۖ وأسلنا له عين القطر ۖ ومن الجن من يعمل بين يديه بإذن ربه ۖ ومن يزغ منهم عن أمرنا نذقه من عذاب السعير",
          "AyaNumber": 3618,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88572f61d12bf6abd341"
          },
          "AyaContent": "يعملون له ما يشاء من محاريب وتماثيل وجفان كالجواب وقدور راسيات ۚ اعملوا آل داوود شكرا ۚ وقليل من عبادي الشكور",
          "AyaNumber": 3619,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88572f61d12bf6abd342"
          },
          "AyaContent": "فلما قضينا عليه الموت ما دلهم علىٰ موته إلا دابة الأرض تأكل منسأته ۖ فلما خر تبينت الجن أن لو كانوا يعلمون الغيب ما لبثوا في العذاب المهين",
          "AyaNumber": 3620,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88572f61d12bf6abd343"
          },
          "AyaContent": "لقد كان لسبإ في مسكنهم آية ۖ جنتان عن يمين وشمال ۖ كلوا من رزق ربكم واشكروا له ۚ بلدة طيبة ورب غفور",
          "AyaNumber": 3621,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88572f61d12bf6abd344"
          },
          "AyaContent": "فأعرضوا فأرسلنا عليهم سيل العرم وبدلناهم بجنتيهم جنتين ذواتي أكل خمط وأثل وشيء من سدر قليل",
          "AyaNumber": 3622,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88572f61d12bf6abd345"
          },
          "AyaContent": "ذٰلك جزيناهم بما كفروا ۖ وهل نجازي إلا الكفور",
          "AyaNumber": 3623,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88582f61d12bf6abd346"
          },
          "AyaContent": "وجعلنا بينهم وبين القرى التي باركنا فيها قرى ظاهرة وقدرنا فيها السير ۖ سيروا فيها ليالي وأياما آمنين",
          "AyaNumber": 3624,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88582f61d12bf6abd347"
          },
          "AyaContent": "فقالوا ربنا باعد بين أسفارنا وظلموا أنفسهم فجعلناهم أحاديث ومزقناهم كل ممزق ۚ إن في ذٰلك لآيات لكل صبار شكور",
          "AyaNumber": 3625,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88582f61d12bf6abd348"
          },
          "AyaContent": "ولقد صدق عليهم إبليس ظنه فاتبعوه إلا فريقا من المؤمنين",
          "AyaNumber": 3626,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88582f61d12bf6abd349"
          },
          "AyaContent": "وما كان له عليهم من سلطان إلا لنعلم من يؤمن بالآخرة ممن هو منها في شك ۗ وربك علىٰ كل شيء حفيظ",
          "AyaNumber": 3627,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88582f61d12bf6abd34a"
          },
          "AyaContent": "قل ادعوا الذين زعمتم من دون الله ۖ لا يملكون مثقال ذرة في السماوات ولا في الأرض وما لهم فيهما من شرك وما له منهم من ظهير",
          "AyaNumber": 3628,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88592f61d12bf6abd34b"
          },
          "AyaContent": "ولا تنفع الشفاعة عنده إلا لمن أذن له ۚ حتىٰ إذا فزع عن قلوبهم قالوا ماذا قال ربكم ۖ قالوا الحق ۖ وهو العلي الكبير",
          "AyaNumber": 3629,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88592f61d12bf6abd34c"
          },
          "AyaContent": "۞ قل من يرزقكم من السماوات والأرض ۖ قل الله ۖ وإنا أو إياكم لعلىٰ هدى أو في ضلال مبين",
          "AyaNumber": 3630,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88592f61d12bf6abd34d"
          },
          "AyaContent": "قل لا تسألون عما أجرمنا ولا نسأل عما تعملون",
          "AyaNumber": 3631,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88592f61d12bf6abd34e"
          },
          "AyaContent": "قل يجمع بيننا ربنا ثم يفتح بيننا بالحق وهو الفتاح العليم",
          "AyaNumber": 3632,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885a2f61d12bf6abd34f"
          },
          "AyaContent": "قل أروني الذين ألحقتم به شركاء ۖ كلا ۚ بل هو الله العزيز الحكيم",
          "AyaNumber": 3633,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885a2f61d12bf6abd350"
          },
          "AyaContent": "وما أرسلناك إلا كافة للناس بشيرا ونذيرا ولٰكن أكثر الناس لا يعلمون",
          "AyaNumber": 3634,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885a2f61d12bf6abd351"
          },
          "AyaContent": "ويقولون متىٰ هٰذا الوعد إن كنتم صادقين",
          "AyaNumber": 3635,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885b2f61d12bf6abd352"
          },
          "AyaContent": "قل لكم ميعاد يوم لا تستأخرون عنه ساعة ولا تستقدمون",
          "AyaNumber": 3636,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885b2f61d12bf6abd353"
          },
          "AyaContent": "وقال الذين كفروا لن نؤمن بهٰذا القرآن ولا بالذي بين يديه ۗ ولو ترىٰ إذ الظالمون موقوفون عند ربهم يرجع بعضهم إلىٰ بعض القول يقول الذين استضعفوا للذين استكبروا لولا أنتم لكنا مؤمنين",
          "AyaNumber": 3637,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885b2f61d12bf6abd354"
          },
          "AyaContent": "قال الذين استكبروا للذين استضعفوا أنحن صددناكم عن الهدىٰ بعد إذ جاءكم ۖ بل كنتم مجرمين",
          "AyaNumber": 3638,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885c2f61d12bf6abd355"
          },
          "AyaContent": "وقال الذين استضعفوا للذين استكبروا بل مكر الليل والنهار إذ تأمروننا أن نكفر بالله ونجعل له أندادا ۚ وأسروا الندامة لما رأوا العذاب وجعلنا الأغلال في أعناق الذين كفروا ۚ هل يجزون إلا ما كانوا يعملون",
          "AyaNumber": 3639,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885c2f61d12bf6abd356"
          },
          "AyaContent": "وما أرسلنا في قرية من نذير إلا قال مترفوها إنا بما أرسلتم به كافرون",
          "AyaNumber": 3640,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885c2f61d12bf6abd357"
          },
          "AyaContent": "وقالوا نحن أكثر أموالا وأولادا وما نحن بمعذبين",
          "AyaNumber": 3641,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885c2f61d12bf6abd358"
          },
          "AyaContent": "قل إن ربي يبسط الرزق لمن يشاء ويقدر ولٰكن أكثر الناس لا يعلمون",
          "AyaNumber": 3642,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885e2f61d12bf6abd359"
          },
          "AyaContent": "وما أموالكم ولا أولادكم بالتي تقربكم عندنا زلفىٰ إلا من آمن وعمل صالحا فأولٰئك لهم جزاء الضعف بما عملوا وهم في الغرفات آمنون",
          "AyaNumber": 3643,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885e2f61d12bf6abd35a"
          },
          "AyaContent": "والذين يسعون في آياتنا معاجزين أولٰئك في العذاب محضرون",
          "AyaNumber": 3644,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885e2f61d12bf6abd35b"
          },
          "AyaContent": "قل إن ربي يبسط الرزق لمن يشاء من عباده ويقدر له ۚ وما أنفقتم من شيء فهو يخلفه ۖ وهو خير الرازقين",
          "AyaNumber": 3645,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885f2f61d12bf6abd35c"
          },
          "AyaContent": "ويوم يحشرهم جميعا ثم يقول للملائكة أهٰؤلاء إياكم كانوا يعبدون",
          "AyaNumber": 3646,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885f2f61d12bf6abd35d"
          },
          "AyaContent": "قالوا سبحانك أنت ولينا من دونهم ۖ بل كانوا يعبدون الجن ۖ أكثرهم بهم مؤمنون",
          "AyaNumber": 3647,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd885f2f61d12bf6abd35e"
          },
          "AyaContent": "فاليوم لا يملك بعضكم لبعض نفعا ولا ضرا ونقول للذين ظلموا ذوقوا عذاب النار التي كنتم بها تكذبون",
          "AyaNumber": 3648,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88602f61d12bf6abd35f"
          },
          "AyaContent": "وإذا تتلىٰ عليهم آياتنا بينات قالوا ما هٰذا إلا رجل يريد أن يصدكم عما كان يعبد آباؤكم وقالوا ما هٰذا إلا إفك مفترى ۚ وقال الذين كفروا للحق لما جاءهم إن هٰذا إلا سحر مبين",
          "AyaNumber": 3649,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88602f61d12bf6abd360"
          },
          "AyaContent": "وما آتيناهم من كتب يدرسونها ۖ وما أرسلنا إليهم قبلك من نذير",
          "AyaNumber": 3650,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88602f61d12bf6abd361"
          },
          "AyaContent": "وكذب الذين من قبلهم وما بلغوا معشار ما آتيناهم فكذبوا رسلي ۖ فكيف كان نكير",
          "AyaNumber": 3651,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88612f61d12bf6abd362"
          },
          "AyaContent": "۞ قل إنما أعظكم بواحدة ۖ أن تقوموا لله مثنىٰ وفرادىٰ ثم تتفكروا ۚ ما بصاحبكم من جنة ۚ إن هو إلا نذير لكم بين يدي عذاب شديد",
          "AyaNumber": 3652,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88612f61d12bf6abd363"
          },
          "AyaContent": "قل ما سألتكم من أجر فهو لكم ۖ إن أجري إلا على الله ۖ وهو علىٰ كل شيء شهيد",
          "AyaNumber": 3653,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88612f61d12bf6abd364"
          },
          "AyaContent": "قل إن ربي يقذف بالحق علام الغيوب",
          "AyaNumber": 3654,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88622f61d12bf6abd365"
          },
          "AyaContent": "قل جاء الحق وما يبدئ الباطل وما يعيد",
          "AyaNumber": 3655,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88622f61d12bf6abd366"
          },
          "AyaContent": "قل إن ضللت فإنما أضل علىٰ نفسي ۖ وإن اهتديت فبما يوحي إلي ربي ۚ إنه سميع قريب",
          "AyaNumber": 3656,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88622f61d12bf6abd367"
          },
          "AyaContent": "ولو ترىٰ إذ فزعوا فلا فوت وأخذوا من مكان قريب",
          "AyaNumber": 3657,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88622f61d12bf6abd368"
          },
          "AyaContent": "وقالوا آمنا به وأنىٰ لهم التناوش من مكان بعيد",
          "AyaNumber": 3658,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88632f61d12bf6abd369"
          },
          "AyaContent": "وقد كفروا به من قبل ۖ ويقذفون بالغيب من مكان بعيد",
          "AyaNumber": 3659,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88632f61d12bf6abd36a"
          },
          "AyaContent": "وحيل بينهم وبين ما يشتهون كما فعل بأشياعهم من قبل ۚ إنهم كانوا في شك مريب",
          "AyaNumber": 3660,
          "SurahName": "سورة سَبَأ",
          "SurahNumber": 34
        },{
          "_id": {
            "$oid": "5ecd88632f61d12bf6abd36b"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الحمد لله فاطر السماوات والأرض جاعل الملائكة رسلا أولي أجنحة مثنىٰ وثلاث ورباع ۚ يزيد في الخلق ما يشاء ۚ إن الله علىٰ كل شيء قدير",
          "AyaNumber": 3661,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88642f61d12bf6abd36c"
          },
          "AyaContent": "ما يفتح الله للناس من رحمة فلا ممسك لها ۖ وما يمسك فلا مرسل له من بعده ۚ وهو العزيز الحكيم",
          "AyaNumber": 3662,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88642f61d12bf6abd36d"
          },
          "AyaContent": "يا أيها الناس اذكروا نعمت الله عليكم ۚ هل من خالق غير الله يرزقكم من السماء والأرض ۚ لا إلٰه إلا هو ۖ فأنىٰ تؤفكون",
          "AyaNumber": 3663,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88642f61d12bf6abd36e"
          },
          "AyaContent": "وإن يكذبوك فقد كذبت رسل من قبلك ۚ وإلى الله ترجع الأمور",
          "AyaNumber": 3664,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88642f61d12bf6abd36f"
          },
          "AyaContent": "يا أيها الناس إن وعد الله حق ۖ فلا تغرنكم الحياة الدنيا ۖ ولا يغرنكم بالله الغرور",
          "AyaNumber": 3665,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88652f61d12bf6abd370"
          },
          "AyaContent": "إن الشيطان لكم عدو فاتخذوه عدوا ۚ إنما يدعو حزبه ليكونوا من أصحاب السعير",
          "AyaNumber": 3666,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88652f61d12bf6abd371"
          },
          "AyaContent": "الذين كفروا لهم عذاب شديد ۖ والذين آمنوا وعملوا الصالحات لهم مغفرة وأجر كبير",
          "AyaNumber": 3667,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88652f61d12bf6abd372"
          },
          "AyaContent": "أفمن زين له سوء عمله فرآه حسنا ۖ فإن الله يضل من يشاء ويهدي من يشاء ۖ فلا تذهب نفسك عليهم حسرات ۚ إن الله عليم بما يصنعون",
          "AyaNumber": 3668,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88662f61d12bf6abd373"
          },
          "AyaContent": "والله الذي أرسل الرياح فتثير سحابا فسقناه إلىٰ بلد ميت فأحيينا به الأرض بعد موتها ۚ كذٰلك النشور",
          "AyaNumber": 3669,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88662f61d12bf6abd374"
          },
          "AyaContent": "من كان يريد العزة فلله العزة جميعا ۚ إليه يصعد الكلم الطيب والعمل الصالح يرفعه ۚ والذين يمكرون السيئات لهم عذاب شديد ۖ ومكر أولٰئك هو يبور",
          "AyaNumber": 3670,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88672f61d12bf6abd375"
          },
          "AyaContent": "والله خلقكم من تراب ثم من نطفة ثم جعلكم أزواجا ۚ وما تحمل من أنثىٰ ولا تضع إلا بعلمه ۚ وما يعمر من معمر ولا ينقص من عمره إلا في كتاب ۚ إن ذٰلك على الله يسير",
          "AyaNumber": 3671,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88672f61d12bf6abd376"
          },
          "AyaContent": "وما يستوي البحران هٰذا عذب فرات سائغ شرابه وهٰذا ملح أجاج ۖ ومن كل تأكلون لحما طريا وتستخرجون حلية تلبسونها ۖ وترى الفلك فيه مواخر لتبتغوا من فضله ولعلكم تشكرون",
          "AyaNumber": 3672,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88672f61d12bf6abd377"
          },
          "AyaContent": "يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري لأجل مسمى ۚ ذٰلكم الله ربكم له الملك ۚ والذين تدعون من دونه ما يملكون من قطمير",
          "AyaNumber": 3673,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88682f61d12bf6abd378"
          },
          "AyaContent": "إن تدعوهم لا يسمعوا دعاءكم ولو سمعوا ما استجابوا لكم ۖ ويوم القيامة يكفرون بشرككم ۚ ولا ينبئك مثل خبير",
          "AyaNumber": 3674,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88682f61d12bf6abd379"
          },
          "AyaContent": "۞ يا أيها الناس أنتم الفقراء إلى الله ۖ والله هو الغني الحميد",
          "AyaNumber": 3675,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88682f61d12bf6abd37a"
          },
          "AyaContent": "إن يشأ يذهبكم ويأت بخلق جديد",
          "AyaNumber": 3676,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88682f61d12bf6abd37b"
          },
          "AyaContent": "وما ذٰلك على الله بعزيز",
          "AyaNumber": 3677,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88692f61d12bf6abd37c"
          },
          "AyaContent": "ولا تزر وازرة وزر أخرىٰ ۚ وإن تدع مثقلة إلىٰ حملها لا يحمل منه شيء ولو كان ذا قربىٰ ۗ إنما تنذر الذين يخشون ربهم بالغيب وأقاموا الصلاة ۚ ومن تزكىٰ فإنما يتزكىٰ لنفسه ۚ وإلى الله المصير",
          "AyaNumber": 3678,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88692f61d12bf6abd37d"
          },
          "AyaContent": "وما يستوي الأعمىٰ والبصير",
          "AyaNumber": 3679,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88692f61d12bf6abd37e"
          },
          "AyaContent": "ولا الظلمات ولا النور",
          "AyaNumber": 3680,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88692f61d12bf6abd37f"
          },
          "AyaContent": "ولا الظل ولا الحرور",
          "AyaNumber": 3681,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd88692f61d12bf6abd380"
          },
          "AyaContent": "وما يستوي الأحياء ولا الأموات ۚ إن الله يسمع من يشاء ۖ وما أنت بمسمع من في القبور",
          "AyaNumber": 3682,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886a2f61d12bf6abd381"
          },
          "AyaContent": "إن أنت إلا نذير",
          "AyaNumber": 3683,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886b2f61d12bf6abd382"
          },
          "AyaContent": "إنا أرسلناك بالحق بشيرا ونذيرا ۚ وإن من أمة إلا خلا فيها نذير",
          "AyaNumber": 3684,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886b2f61d12bf6abd383"
          },
          "AyaContent": "وإن يكذبوك فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينات وبالزبر وبالكتاب المنير",
          "AyaNumber": 3685,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886b2f61d12bf6abd384"
          },
          "AyaContent": "ثم أخذت الذين كفروا ۖ فكيف كان نكير",
          "AyaNumber": 3686,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886b2f61d12bf6abd385"
          },
          "AyaContent": "ألم تر أن الله أنزل من السماء ماء فأخرجنا به ثمرات مختلفا ألوانها ۚ ومن الجبال جدد بيض وحمر مختلف ألوانها وغرابيب سود",
          "AyaNumber": 3687,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886b2f61d12bf6abd386"
          },
          "AyaContent": "ومن الناس والدواب والأنعام مختلف ألوانه كذٰلك ۗ إنما يخشى الله من عباده العلماء ۗ إن الله عزيز غفور",
          "AyaNumber": 3688,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886c2f61d12bf6abd387"
          },
          "AyaContent": "إن الذين يتلون كتاب الله وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور",
          "AyaNumber": 3689,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886c2f61d12bf6abd388"
          },
          "AyaContent": "ليوفيهم أجورهم ويزيدهم من فضله ۚ إنه غفور شكور",
          "AyaNumber": 3690,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886c2f61d12bf6abd389"
          },
          "AyaContent": "والذي أوحينا إليك من الكتاب هو الحق مصدقا لما بين يديه ۗ إن الله بعباده لخبير بصير",
          "AyaNumber": 3691,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886c2f61d12bf6abd38a"
          },
          "AyaContent": "ثم أورثنا الكتاب الذين اصطفينا من عبادنا ۖ فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات بإذن الله ۚ ذٰلك هو الفضل الكبير",
          "AyaNumber": 3692,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886d2f61d12bf6abd38b"
          },
          "AyaContent": "جنات عدن يدخلونها يحلون فيها من أساور من ذهب ولؤلؤا ۖ ولباسهم فيها حرير",
          "AyaNumber": 3693,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886d2f61d12bf6abd38c"
          },
          "AyaContent": "وقالوا الحمد لله الذي أذهب عنا الحزن ۖ إن ربنا لغفور شكور",
          "AyaNumber": 3694,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886d2f61d12bf6abd38d"
          },
          "AyaContent": "الذي أحلنا دار المقامة من فضله لا يمسنا فيها نصب ولا يمسنا فيها لغوب",
          "AyaNumber": 3695,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886d2f61d12bf6abd38e"
          },
          "AyaContent": "والذين كفروا لهم نار جهنم لا يقضىٰ عليهم فيموتوا ولا يخفف عنهم من عذابها ۚ كذٰلك نجزي كل كفور",
          "AyaNumber": 3696,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886d2f61d12bf6abd38f"
          },
          "AyaContent": "وهم يصطرخون فيها ربنا أخرجنا نعمل صالحا غير الذي كنا نعمل ۚ أولم نعمركم ما يتذكر فيه من تذكر وجاءكم النذير ۖ فذوقوا فما للظالمين من نصير",
          "AyaNumber": 3697,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886e2f61d12bf6abd390"
          },
          "AyaContent": "إن الله عالم غيب السماوات والأرض ۚ إنه عليم بذات الصدور",
          "AyaNumber": 3698,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886e2f61d12bf6abd391"
          },
          "AyaContent": "هو الذي جعلكم خلائف في الأرض ۚ فمن كفر فعليه كفره ۖ ولا يزيد الكافرين كفرهم عند ربهم إلا مقتا ۖ ولا يزيد الكافرين كفرهم إلا خسارا",
          "AyaNumber": 3699,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886e2f61d12bf6abd392"
          },
          "AyaContent": "قل أرأيتم شركاءكم الذين تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات أم آتيناهم كتابا فهم علىٰ بينت منه ۚ بل إن يعد الظالمون بعضهم بعضا إلا غرورا",
          "AyaNumber": 3700,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886e2f61d12bf6abd393"
          },
          "AyaContent": "۞ إن الله يمسك السماوات والأرض أن تزولا ۚ ولئن زالتا إن أمسكهما من أحد من بعده ۚ إنه كان حليما غفورا",
          "AyaNumber": 3701,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886e2f61d12bf6abd394"
          },
          "AyaContent": "وأقسموا بالله جهد أيمانهم لئن جاءهم نذير ليكونن أهدىٰ من إحدى الأمم ۖ فلما جاءهم نذير ما زادهم إلا نفورا",
          "AyaNumber": 3702,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886f2f61d12bf6abd395"
          },
          "AyaContent": "استكبارا في الأرض ومكر السيئ ۚ ولا يحيق المكر السيئ إلا بأهله ۚ فهل ينظرون إلا سنت الأولين ۚ فلن تجد لسنت الله تبديلا ۖ ولن تجد لسنت الله تحويلا",
          "AyaNumber": 3703,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886f2f61d12bf6abd396"
          },
          "AyaContent": "أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم وكانوا أشد منهم قوة ۚ وما كان الله ليعجزه من شيء في السماوات ولا في الأرض ۚ إنه كان عليما قديرا",
          "AyaNumber": 3704,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886f2f61d12bf6abd397"
          },
          "AyaContent": "ولو يؤاخذ الله الناس بما كسبوا ما ترك علىٰ ظهرها من دابة ولٰكن يؤخرهم إلىٰ أجل مسمى ۖ فإذا جاء أجلهم فإن الله كان بعباده بصيرا",
          "AyaNumber": 3705,
          "SurahName": "سورة فاطر",
          "SurahNumber": 35
        },{
          "_id": {
            "$oid": "5ecd886f2f61d12bf6abd398"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يس",
          "AyaNumber": 3706,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88702f61d12bf6abd399"
          },
          "AyaContent": "والقرآن الحكيم",
          "AyaNumber": 3707,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88702f61d12bf6abd39a"
          },
          "AyaContent": "إنك لمن المرسلين",
          "AyaNumber": 3708,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88702f61d12bf6abd39b"
          },
          "AyaContent": "علىٰ صراط مستقيم",
          "AyaNumber": 3709,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88702f61d12bf6abd39c"
          },
          "AyaContent": "تنزيل العزيز الرحيم",
          "AyaNumber": 3710,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88702f61d12bf6abd39d"
          },
          "AyaContent": "لتنذر قوما ما أنذر آباؤهم فهم غافلون",
          "AyaNumber": 3711,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88702f61d12bf6abd39e"
          },
          "AyaContent": "لقد حق القول علىٰ أكثرهم فهم لا يؤمنون",
          "AyaNumber": 3712,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88712f61d12bf6abd39f"
          },
          "AyaContent": "إنا جعلنا في أعناقهم أغلالا فهي إلى الأذقان فهم مقمحون",
          "AyaNumber": 3713,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88712f61d12bf6abd3a0"
          },
          "AyaContent": "وجعلنا من بين أيديهم سدا ومن خلفهم سدا فأغشيناهم فهم لا يبصرون",
          "AyaNumber": 3714,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88712f61d12bf6abd3a1"
          },
          "AyaContent": "وسواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون",
          "AyaNumber": 3715,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88712f61d12bf6abd3a2"
          },
          "AyaContent": "إنما تنذر من اتبع الذكر وخشي الرحمٰن بالغيب ۖ فبشره بمغفرة وأجر كريم",
          "AyaNumber": 3716,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88712f61d12bf6abd3a3"
          },
          "AyaContent": "إنا نحن نحيي الموتىٰ ونكتب ما قدموا وآثارهم ۚ وكل شيء أحصيناه في إمام مبين",
          "AyaNumber": 3717,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88712f61d12bf6abd3a4"
          },
          "AyaContent": "واضرب لهم مثلا أصحاب القرية إذ جاءها المرسلون",
          "AyaNumber": 3718,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88722f61d12bf6abd3a5"
          },
          "AyaContent": "إذ أرسلنا إليهم اثنين فكذبوهما فعززنا بثالث فقالوا إنا إليكم مرسلون",
          "AyaNumber": 3719,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88722f61d12bf6abd3a6"
          },
          "AyaContent": "قالوا ما أنتم إلا بشر مثلنا وما أنزل الرحمٰن من شيء إن أنتم إلا تكذبون",
          "AyaNumber": 3720,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88722f61d12bf6abd3a7"
          },
          "AyaContent": "قالوا ربنا يعلم إنا إليكم لمرسلون",
          "AyaNumber": 3721,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88732f61d12bf6abd3a8"
          },
          "AyaContent": "وما علينا إلا البلاغ المبين",
          "AyaNumber": 3722,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88732f61d12bf6abd3a9"
          },
          "AyaContent": "قالوا إنا تطيرنا بكم ۖ لئن لم تنتهوا لنرجمنكم وليمسنكم منا عذاب أليم",
          "AyaNumber": 3723,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88732f61d12bf6abd3aa"
          },
          "AyaContent": "قالوا طائركم معكم ۚ أئن ذكرتم ۚ بل أنتم قوم مسرفون",
          "AyaNumber": 3724,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88732f61d12bf6abd3ab"
          },
          "AyaContent": "وجاء من أقصى المدينة رجل يسعىٰ قال يا قوم اتبعوا المرسلين",
          "AyaNumber": 3725,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88732f61d12bf6abd3ac"
          },
          "AyaContent": "اتبعوا من لا يسألكم أجرا وهم مهتدون",
          "AyaNumber": 3726,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88732f61d12bf6abd3ad"
          },
          "AyaContent": "وما لي لا أعبد الذي فطرني وإليه ترجعون",
          "AyaNumber": 3727,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88742f61d12bf6abd3ae"
          },
          "AyaContent": "أأتخذ من دونه آلهة إن يردن الرحمٰن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون",
          "AyaNumber": 3728,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88742f61d12bf6abd3af"
          },
          "AyaContent": "إني إذا لفي ضلال مبين",
          "AyaNumber": 3729,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88742f61d12bf6abd3b0"
          },
          "AyaContent": "إني آمنت بربكم فاسمعون",
          "AyaNumber": 3730,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88742f61d12bf6abd3b1"
          },
          "AyaContent": "قيل ادخل الجنة ۖ قال يا ليت قومي يعلمون",
          "AyaNumber": 3731,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88742f61d12bf6abd3b2"
          },
          "AyaContent": "بما غفر لي ربي وجعلني من المكرمين",
          "AyaNumber": 3732,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88742f61d12bf6abd3b3"
          },
          "AyaContent": "۞ وما أنزلنا علىٰ قومه من بعده من جند من السماء وما كنا منزلين",
          "AyaNumber": 3733,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88742f61d12bf6abd3b4"
          },
          "AyaContent": "إن كانت إلا صيحة واحدة فإذا هم خامدون",
          "AyaNumber": 3734,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88752f61d12bf6abd3b5"
          },
          "AyaContent": "يا حسرة على العباد ۚ ما يأتيهم من رسول إلا كانوا به يستهزئون",
          "AyaNumber": 3735,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88752f61d12bf6abd3b6"
          },
          "AyaContent": "ألم يروا كم أهلكنا قبلهم من القرون أنهم إليهم لا يرجعون",
          "AyaNumber": 3736,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88752f61d12bf6abd3b7"
          },
          "AyaContent": "وإن كل لما جميع لدينا محضرون",
          "AyaNumber": 3737,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88752f61d12bf6abd3b8"
          },
          "AyaContent": "وآية لهم الأرض الميتة أحييناها وأخرجنا منها حبا فمنه يأكلون",
          "AyaNumber": 3738,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88762f61d12bf6abd3b9"
          },
          "AyaContent": "وجعلنا فيها جنات من نخيل وأعناب وفجرنا فيها من العيون",
          "AyaNumber": 3739,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88762f61d12bf6abd3ba"
          },
          "AyaContent": "ليأكلوا من ثمره وما عملته أيديهم ۖ أفلا يشكرون",
          "AyaNumber": 3740,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88762f61d12bf6abd3bb"
          },
          "AyaContent": "سبحان الذي خلق الأزواج كلها مما تنبت الأرض ومن أنفسهم ومما لا يعلمون",
          "AyaNumber": 3741,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88762f61d12bf6abd3bc"
          },
          "AyaContent": "وآية لهم الليل نسلخ منه النهار فإذا هم مظلمون",
          "AyaNumber": 3742,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88762f61d12bf6abd3bd"
          },
          "AyaContent": "والشمس تجري لمستقر لها ۚ ذٰلك تقدير العزيز العليم",
          "AyaNumber": 3743,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88772f61d12bf6abd3be"
          },
          "AyaContent": "والقمر قدرناه منازل حتىٰ عاد كالعرجون القديم",
          "AyaNumber": 3744,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88772f61d12bf6abd3bf"
          },
          "AyaContent": "لا الشمس ينبغي لها أن تدرك القمر ولا الليل سابق النهار ۚ وكل في فلك يسبحون",
          "AyaNumber": 3745,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88772f61d12bf6abd3c0"
          },
          "AyaContent": "وآية لهم أنا حملنا ذريتهم في الفلك المشحون",
          "AyaNumber": 3746,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88772f61d12bf6abd3c1"
          },
          "AyaContent": "وخلقنا لهم من مثله ما يركبون",
          "AyaNumber": 3747,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88772f61d12bf6abd3c2"
          },
          "AyaContent": "وإن نشأ نغرقهم فلا صريخ لهم ولا هم ينقذون",
          "AyaNumber": 3748,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88772f61d12bf6abd3c3"
          },
          "AyaContent": "إلا رحمة منا ومتاعا إلىٰ حين",
          "AyaNumber": 3749,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88782f61d12bf6abd3c4"
          },
          "AyaContent": "وإذا قيل لهم اتقوا ما بين أيديكم وما خلفكم لعلكم ترحمون",
          "AyaNumber": 3750,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88782f61d12bf6abd3c5"
          },
          "AyaContent": "وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين",
          "AyaNumber": 3751,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88782f61d12bf6abd3c6"
          },
          "AyaContent": "وإذا قيل لهم أنفقوا مما رزقكم الله قال الذين كفروا للذين آمنوا أنطعم من لو يشاء الله أطعمه إن أنتم إلا في ضلال مبين",
          "AyaNumber": 3752,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88782f61d12bf6abd3c7"
          },
          "AyaContent": "ويقولون متىٰ هٰذا الوعد إن كنتم صادقين",
          "AyaNumber": 3753,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88782f61d12bf6abd3c8"
          },
          "AyaContent": "ما ينظرون إلا صيحة واحدة تأخذهم وهم يخصمون",
          "AyaNumber": 3754,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88792f61d12bf6abd3c9"
          },
          "AyaContent": "فلا يستطيعون توصية ولا إلىٰ أهلهم يرجعون",
          "AyaNumber": 3755,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88792f61d12bf6abd3ca"
          },
          "AyaContent": "ونفخ في الصور فإذا هم من الأجداث إلىٰ ربهم ينسلون",
          "AyaNumber": 3756,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88792f61d12bf6abd3cb"
          },
          "AyaContent": "قالوا يا ويلنا من بعثنا من مرقدنا ۜ ۗ هٰذا ما وعد الرحمٰن وصدق المرسلون",
          "AyaNumber": 3757,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88792f61d12bf6abd3cc"
          },
          "AyaContent": "إن كانت إلا صيحة واحدة فإذا هم جميع لدينا محضرون",
          "AyaNumber": 3758,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd88792f61d12bf6abd3cd"
          },
          "AyaContent": "فاليوم لا تظلم نفس شيئا ولا تجزون إلا ما كنتم تعملون",
          "AyaNumber": 3759,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887a2f61d12bf6abd3ce"
          },
          "AyaContent": "إن أصحاب الجنة اليوم في شغل فاكهون",
          "AyaNumber": 3760,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887a2f61d12bf6abd3cf"
          },
          "AyaContent": "هم وأزواجهم في ظلال على الأرائك متكئون",
          "AyaNumber": 3761,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887a2f61d12bf6abd3d0"
          },
          "AyaContent": "لهم فيها فاكهة ولهم ما يدعون",
          "AyaNumber": 3762,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887a2f61d12bf6abd3d1"
          },
          "AyaContent": "سلام قولا من رب رحيم",
          "AyaNumber": 3763,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887a2f61d12bf6abd3d2"
          },
          "AyaContent": "وامتازوا اليوم أيها المجرمون",
          "AyaNumber": 3764,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887a2f61d12bf6abd3d3"
          },
          "AyaContent": "۞ ألم أعهد إليكم يا بني آدم أن لا تعبدوا الشيطان ۖ إنه لكم عدو مبين",
          "AyaNumber": 3765,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887a2f61d12bf6abd3d4"
          },
          "AyaContent": "وأن اعبدوني ۚ هٰذا صراط مستقيم",
          "AyaNumber": 3766,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887b2f61d12bf6abd3d5"
          },
          "AyaContent": "ولقد أضل منكم جبلا كثيرا ۖ أفلم تكونوا تعقلون",
          "AyaNumber": 3767,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887b2f61d12bf6abd3d6"
          },
          "AyaContent": "هٰذه جهنم التي كنتم توعدون",
          "AyaNumber": 3768,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887b2f61d12bf6abd3d7"
          },
          "AyaContent": "اصلوها اليوم بما كنتم تكفرون",
          "AyaNumber": 3769,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887b2f61d12bf6abd3d8"
          },
          "AyaContent": "اليوم نختم علىٰ أفواههم وتكلمنا أيديهم وتشهد أرجلهم بما كانوا يكسبون",
          "AyaNumber": 3770,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887b2f61d12bf6abd3d9"
          },
          "AyaContent": "ولو نشاء لطمسنا علىٰ أعينهم فاستبقوا الصراط فأنىٰ يبصرون",
          "AyaNumber": 3771,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887b2f61d12bf6abd3da"
          },
          "AyaContent": "ولو نشاء لمسخناهم علىٰ مكانتهم فما استطاعوا مضيا ولا يرجعون",
          "AyaNumber": 3772,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887c2f61d12bf6abd3db"
          },
          "AyaContent": "ومن نعمره ننكسه في الخلق ۖ أفلا يعقلون",
          "AyaNumber": 3773,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887c2f61d12bf6abd3dc"
          },
          "AyaContent": "وما علمناه الشعر وما ينبغي له ۚ إن هو إلا ذكر وقرآن مبين",
          "AyaNumber": 3774,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887c2f61d12bf6abd3dd"
          },
          "AyaContent": "لينذر من كان حيا ويحق القول على الكافرين",
          "AyaNumber": 3775,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887c2f61d12bf6abd3de"
          },
          "AyaContent": "أولم يروا أنا خلقنا لهم مما عملت أيدينا أنعاما فهم لها مالكون",
          "AyaNumber": 3776,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887c2f61d12bf6abd3df"
          },
          "AyaContent": "وذللناها لهم فمنها ركوبهم ومنها يأكلون",
          "AyaNumber": 3777,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887c2f61d12bf6abd3e0"
          },
          "AyaContent": "ولهم فيها منافع ومشارب ۖ أفلا يشكرون",
          "AyaNumber": 3778,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887d2f61d12bf6abd3e1"
          },
          "AyaContent": "واتخذوا من دون الله آلهة لعلهم ينصرون",
          "AyaNumber": 3779,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887d2f61d12bf6abd3e2"
          },
          "AyaContent": "لا يستطيعون نصرهم وهم لهم جند محضرون",
          "AyaNumber": 3780,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887d2f61d12bf6abd3e3"
          },
          "AyaContent": "فلا يحزنك قولهم ۘ إنا نعلم ما يسرون وما يعلنون",
          "AyaNumber": 3781,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887d2f61d12bf6abd3e4"
          },
          "AyaContent": "أولم ير الإنسان أنا خلقناه من نطفة فإذا هو خصيم مبين",
          "AyaNumber": 3782,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887d2f61d12bf6abd3e5"
          },
          "AyaContent": "وضرب لنا مثلا ونسي خلقه ۖ قال من يحيي العظام وهي رميم",
          "AyaNumber": 3783,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887e2f61d12bf6abd3e6"
          },
          "AyaContent": "قل يحييها الذي أنشأها أول مرة ۖ وهو بكل خلق عليم",
          "AyaNumber": 3784,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887e2f61d12bf6abd3e7"
          },
          "AyaContent": "الذي جعل لكم من الشجر الأخضر نارا فإذا أنتم منه توقدون",
          "AyaNumber": 3785,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887e2f61d12bf6abd3e8"
          },
          "AyaContent": "أوليس الذي خلق السماوات والأرض بقادر علىٰ أن يخلق مثلهم ۚ بلىٰ وهو الخلاق العليم",
          "AyaNumber": 3786,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887e2f61d12bf6abd3e9"
          },
          "AyaContent": "إنما أمره إذا أراد شيئا أن يقول له كن فيكون",
          "AyaNumber": 3787,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887e2f61d12bf6abd3ea"
          },
          "AyaContent": "فسبحان الذي بيده ملكوت كل شيء وإليه ترجعون",
          "AyaNumber": 3788,
          "SurahName": "سورة يس",
          "SurahNumber": 36
        },{
          "_id": {
            "$oid": "5ecd887f2f61d12bf6abd3eb"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والصافات صفا",
          "AyaNumber": 3789,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd887f2f61d12bf6abd3ec"
          },
          "AyaContent": "فالزاجرات زجرا",
          "AyaNumber": 3790,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd887f2f61d12bf6abd3ed"
          },
          "AyaContent": "فالتاليات ذكرا",
          "AyaNumber": 3791,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd887f2f61d12bf6abd3ee"
          },
          "AyaContent": "إن إلٰهكم لواحد",
          "AyaNumber": 3792,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd887f2f61d12bf6abd3ef"
          },
          "AyaContent": "رب السماوات والأرض وما بينهما ورب المشارق",
          "AyaNumber": 3793,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd887f2f61d12bf6abd3f0"
          },
          "AyaContent": "إنا زينا السماء الدنيا بزينة الكواكب",
          "AyaNumber": 3794,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88802f61d12bf6abd3f1"
          },
          "AyaContent": "وحفظا من كل شيطان مارد",
          "AyaNumber": 3795,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88802f61d12bf6abd3f2"
          },
          "AyaContent": "لا يسمعون إلى الملإ الأعلىٰ ويقذفون من كل جانب",
          "AyaNumber": 3796,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88802f61d12bf6abd3f3"
          },
          "AyaContent": "دحورا ۖ ولهم عذاب واصب",
          "AyaNumber": 3797,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88802f61d12bf6abd3f4"
          },
          "AyaContent": "إلا من خطف الخطفة فأتبعه شهاب ثاقب",
          "AyaNumber": 3798,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88802f61d12bf6abd3f5"
          },
          "AyaContent": "فاستفتهم أهم أشد خلقا أم من خلقنا ۚ إنا خلقناهم من طين لازب",
          "AyaNumber": 3799,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88802f61d12bf6abd3f6"
          },
          "AyaContent": "بل عجبت ويسخرون",
          "AyaNumber": 3800,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88812f61d12bf6abd3f7"
          },
          "AyaContent": "وإذا ذكروا لا يذكرون",
          "AyaNumber": 3801,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88812f61d12bf6abd3f8"
          },
          "AyaContent": "وإذا رأوا آية يستسخرون",
          "AyaNumber": 3802,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88812f61d12bf6abd3f9"
          },
          "AyaContent": "وقالوا إن هٰذا إلا سحر مبين",
          "AyaNumber": 3803,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88812f61d12bf6abd3fa"
          },
          "AyaContent": "أإذا متنا وكنا ترابا وعظاما أإنا لمبعوثون",
          "AyaNumber": 3804,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88812f61d12bf6abd3fb"
          },
          "AyaContent": "أوآباؤنا الأولون",
          "AyaNumber": 3805,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88822f61d12bf6abd3fc"
          },
          "AyaContent": "قل نعم وأنتم داخرون",
          "AyaNumber": 3806,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88822f61d12bf6abd3fd"
          },
          "AyaContent": "فإنما هي زجرة واحدة فإذا هم ينظرون",
          "AyaNumber": 3807,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88822f61d12bf6abd3fe"
          },
          "AyaContent": "وقالوا يا ويلنا هٰذا يوم الدين",
          "AyaNumber": 3808,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88822f61d12bf6abd3ff"
          },
          "AyaContent": "هٰذا يوم الفصل الذي كنتم به تكذبون",
          "AyaNumber": 3809,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88822f61d12bf6abd400"
          },
          "AyaContent": "۞ احشروا الذين ظلموا وأزواجهم وما كانوا يعبدون",
          "AyaNumber": 3810,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88832f61d12bf6abd401"
          },
          "AyaContent": "من دون الله فاهدوهم إلىٰ صراط الجحيم",
          "AyaNumber": 3811,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88832f61d12bf6abd402"
          },
          "AyaContent": "وقفوهم ۖ إنهم مسئولون",
          "AyaNumber": 3812,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88832f61d12bf6abd403"
          },
          "AyaContent": "ما لكم لا تناصرون",
          "AyaNumber": 3813,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88832f61d12bf6abd404"
          },
          "AyaContent": "بل هم اليوم مستسلمون",
          "AyaNumber": 3814,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88832f61d12bf6abd405"
          },
          "AyaContent": "وأقبل بعضهم علىٰ بعض يتساءلون",
          "AyaNumber": 3815,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88832f61d12bf6abd406"
          },
          "AyaContent": "قالوا إنكم كنتم تأتوننا عن اليمين",
          "AyaNumber": 3816,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88842f61d12bf6abd407"
          },
          "AyaContent": "قالوا بل لم تكونوا مؤمنين",
          "AyaNumber": 3817,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88842f61d12bf6abd408"
          },
          "AyaContent": "وما كان لنا عليكم من سلطان ۖ بل كنتم قوما طاغين",
          "AyaNumber": 3818,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88842f61d12bf6abd409"
          },
          "AyaContent": "فحق علينا قول ربنا ۖ إنا لذائقون",
          "AyaNumber": 3819,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88842f61d12bf6abd40a"
          },
          "AyaContent": "فأغويناكم إنا كنا غاوين",
          "AyaNumber": 3820,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88842f61d12bf6abd40b"
          },
          "AyaContent": "فإنهم يومئذ في العذاب مشتركون",
          "AyaNumber": 3821,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88852f61d12bf6abd40c"
          },
          "AyaContent": "إنا كذٰلك نفعل بالمجرمين",
          "AyaNumber": 3822,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88852f61d12bf6abd40d"
          },
          "AyaContent": "إنهم كانوا إذا قيل لهم لا إلٰه إلا الله يستكبرون",
          "AyaNumber": 3823,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88852f61d12bf6abd40e"
          },
          "AyaContent": "ويقولون أئنا لتاركو آلهتنا لشاعر مجنون",
          "AyaNumber": 3824,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88852f61d12bf6abd40f"
          },
          "AyaContent": "بل جاء بالحق وصدق المرسلين",
          "AyaNumber": 3825,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88862f61d12bf6abd410"
          },
          "AyaContent": "إنكم لذائقو العذاب الأليم",
          "AyaNumber": 3826,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88862f61d12bf6abd411"
          },
          "AyaContent": "وما تجزون إلا ما كنتم تعملون",
          "AyaNumber": 3827,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88862f61d12bf6abd412"
          },
          "AyaContent": "إلا عباد الله المخلصين",
          "AyaNumber": 3828,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88862f61d12bf6abd413"
          },
          "AyaContent": "أولٰئك لهم رزق معلوم",
          "AyaNumber": 3829,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88862f61d12bf6abd414"
          },
          "AyaContent": "فواكه ۖ وهم مكرمون",
          "AyaNumber": 3830,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88862f61d12bf6abd415"
          },
          "AyaContent": "في جنات النعيم",
          "AyaNumber": 3831,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88862f61d12bf6abd416"
          },
          "AyaContent": "علىٰ سرر متقابلين",
          "AyaNumber": 3832,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88872f61d12bf6abd417"
          },
          "AyaContent": "يطاف عليهم بكأس من معين",
          "AyaNumber": 3833,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88872f61d12bf6abd418"
          },
          "AyaContent": "بيضاء لذة للشاربين",
          "AyaNumber": 3834,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88872f61d12bf6abd419"
          },
          "AyaContent": "لا فيها غول ولا هم عنها ينزفون",
          "AyaNumber": 3835,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88872f61d12bf6abd41a"
          },
          "AyaContent": "وعندهم قاصرات الطرف عين",
          "AyaNumber": 3836,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88872f61d12bf6abd41b"
          },
          "AyaContent": "كأنهن بيض مكنون",
          "AyaNumber": 3837,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88872f61d12bf6abd41c"
          },
          "AyaContent": "فأقبل بعضهم علىٰ بعض يتساءلون",
          "AyaNumber": 3838,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88882f61d12bf6abd41d"
          },
          "AyaContent": "قال قائل منهم إني كان لي قرين",
          "AyaNumber": 3839,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88882f61d12bf6abd41e"
          },
          "AyaContent": "يقول أإنك لمن المصدقين",
          "AyaNumber": 3840,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88882f61d12bf6abd41f"
          },
          "AyaContent": "أإذا متنا وكنا ترابا وعظاما أإنا لمدينون",
          "AyaNumber": 3841,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88882f61d12bf6abd420"
          },
          "AyaContent": "قال هل أنتم مطلعون",
          "AyaNumber": 3842,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88882f61d12bf6abd421"
          },
          "AyaContent": "فاطلع فرآه في سواء الجحيم",
          "AyaNumber": 3843,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88882f61d12bf6abd422"
          },
          "AyaContent": "قال تالله إن كدت لتردين",
          "AyaNumber": 3844,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88892f61d12bf6abd423"
          },
          "AyaContent": "ولولا نعمة ربي لكنت من المحضرين",
          "AyaNumber": 3845,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88892f61d12bf6abd424"
          },
          "AyaContent": "أفما نحن بميتين",
          "AyaNumber": 3846,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88892f61d12bf6abd425"
          },
          "AyaContent": "إلا موتتنا الأولىٰ وما نحن بمعذبين",
          "AyaNumber": 3847,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88892f61d12bf6abd426"
          },
          "AyaContent": "إن هٰذا لهو الفوز العظيم",
          "AyaNumber": 3848,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88892f61d12bf6abd427"
          },
          "AyaContent": "لمثل هٰذا فليعمل العاملون",
          "AyaNumber": 3849,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888a2f61d12bf6abd428"
          },
          "AyaContent": "أذٰلك خير نزلا أم شجرة الزقوم",
          "AyaNumber": 3850,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888a2f61d12bf6abd429"
          },
          "AyaContent": "إنا جعلناها فتنة للظالمين",
          "AyaNumber": 3851,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888a2f61d12bf6abd42a"
          },
          "AyaContent": "إنها شجرة تخرج في أصل الجحيم",
          "AyaNumber": 3852,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888a2f61d12bf6abd42b"
          },
          "AyaContent": "طلعها كأنه رءوس الشياطين",
          "AyaNumber": 3853,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888a2f61d12bf6abd42c"
          },
          "AyaContent": "فإنهم لآكلون منها فمالئون منها البطون",
          "AyaNumber": 3854,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888a2f61d12bf6abd42d"
          },
          "AyaContent": "ثم إن لهم عليها لشوبا من حميم",
          "AyaNumber": 3855,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888a2f61d12bf6abd42e"
          },
          "AyaContent": "ثم إن مرجعهم لإلى الجحيم",
          "AyaNumber": 3856,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888b2f61d12bf6abd42f"
          },
          "AyaContent": "إنهم ألفوا آباءهم ضالين",
          "AyaNumber": 3857,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888b2f61d12bf6abd430"
          },
          "AyaContent": "فهم علىٰ آثارهم يهرعون",
          "AyaNumber": 3858,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888b2f61d12bf6abd431"
          },
          "AyaContent": "ولقد ضل قبلهم أكثر الأولين",
          "AyaNumber": 3859,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888b2f61d12bf6abd432"
          },
          "AyaContent": "ولقد أرسلنا فيهم منذرين",
          "AyaNumber": 3860,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888b2f61d12bf6abd433"
          },
          "AyaContent": "فانظر كيف كان عاقبة المنذرين",
          "AyaNumber": 3861,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888b2f61d12bf6abd434"
          },
          "AyaContent": "إلا عباد الله المخلصين",
          "AyaNumber": 3862,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888c2f61d12bf6abd435"
          },
          "AyaContent": "ولقد نادانا نوح فلنعم المجيبون",
          "AyaNumber": 3863,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888c2f61d12bf6abd436"
          },
          "AyaContent": "ونجيناه وأهله من الكرب العظيم",
          "AyaNumber": 3864,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888c2f61d12bf6abd437"
          },
          "AyaContent": "وجعلنا ذريته هم الباقين",
          "AyaNumber": 3865,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888c2f61d12bf6abd438"
          },
          "AyaContent": "وتركنا عليه في الآخرين",
          "AyaNumber": 3866,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888c2f61d12bf6abd439"
          },
          "AyaContent": "سلام علىٰ نوح في العالمين",
          "AyaNumber": 3867,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888d2f61d12bf6abd43a"
          },
          "AyaContent": "إنا كذٰلك نجزي المحسنين",
          "AyaNumber": 3868,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888d2f61d12bf6abd43b"
          },
          "AyaContent": "إنه من عبادنا المؤمنين",
          "AyaNumber": 3869,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888d2f61d12bf6abd43c"
          },
          "AyaContent": "ثم أغرقنا الآخرين",
          "AyaNumber": 3870,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888d2f61d12bf6abd43d"
          },
          "AyaContent": "۞ وإن من شيعته لإبراهيم",
          "AyaNumber": 3871,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888d2f61d12bf6abd43e"
          },
          "AyaContent": "إذ جاء ربه بقلب سليم",
          "AyaNumber": 3872,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888d2f61d12bf6abd43f"
          },
          "AyaContent": "إذ قال لأبيه وقومه ماذا تعبدون",
          "AyaNumber": 3873,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888e2f61d12bf6abd440"
          },
          "AyaContent": "أئفكا آلهة دون الله تريدون",
          "AyaNumber": 3874,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888e2f61d12bf6abd441"
          },
          "AyaContent": "فما ظنكم برب العالمين",
          "AyaNumber": 3875,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888e2f61d12bf6abd442"
          },
          "AyaContent": "فنظر نظرة في النجوم",
          "AyaNumber": 3876,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888e2f61d12bf6abd443"
          },
          "AyaContent": "فقال إني سقيم",
          "AyaNumber": 3877,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888e2f61d12bf6abd444"
          },
          "AyaContent": "فتولوا عنه مدبرين",
          "AyaNumber": 3878,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888e2f61d12bf6abd445"
          },
          "AyaContent": "فراغ إلىٰ آلهتهم فقال ألا تأكلون",
          "AyaNumber": 3879,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888f2f61d12bf6abd446"
          },
          "AyaContent": "ما لكم لا تنطقون",
          "AyaNumber": 3880,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888f2f61d12bf6abd447"
          },
          "AyaContent": "فراغ عليهم ضربا باليمين",
          "AyaNumber": 3881,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888f2f61d12bf6abd448"
          },
          "AyaContent": "فأقبلوا إليه يزفون",
          "AyaNumber": 3882,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888f2f61d12bf6abd449"
          },
          "AyaContent": "قال أتعبدون ما تنحتون",
          "AyaNumber": 3883,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888f2f61d12bf6abd44a"
          },
          "AyaContent": "والله خلقكم وما تعملون",
          "AyaNumber": 3884,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd888f2f61d12bf6abd44b"
          },
          "AyaContent": "قالوا ابنوا له بنيانا فألقوه في الجحيم",
          "AyaNumber": 3885,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88902f61d12bf6abd44c"
          },
          "AyaContent": "فأرادوا به كيدا فجعلناهم الأسفلين",
          "AyaNumber": 3886,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88902f61d12bf6abd44d"
          },
          "AyaContent": "وقال إني ذاهب إلىٰ ربي سيهدين",
          "AyaNumber": 3887,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88902f61d12bf6abd44e"
          },
          "AyaContent": "رب هب لي من الصالحين",
          "AyaNumber": 3888,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88902f61d12bf6abd44f"
          },
          "AyaContent": "فبشرناه بغلام حليم",
          "AyaNumber": 3889,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88902f61d12bf6abd450"
          },
          "AyaContent": "فلما بلغ معه السعي قال يا بني إني أرىٰ في المنام أني أذبحك فانظر ماذا ترىٰ ۚ قال يا أبت افعل ما تؤمر ۖ ستجدني إن شاء الله من الصابرين",
          "AyaNumber": 3890,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88912f61d12bf6abd451"
          },
          "AyaContent": "فلما أسلما وتله للجبين",
          "AyaNumber": 3891,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88912f61d12bf6abd452"
          },
          "AyaContent": "وناديناه أن يا إبراهيم",
          "AyaNumber": 3892,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88912f61d12bf6abd453"
          },
          "AyaContent": "قد صدقت الرؤيا ۚ إنا كذٰلك نجزي المحسنين",
          "AyaNumber": 3893,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88912f61d12bf6abd454"
          },
          "AyaContent": "إن هٰذا لهو البلاء المبين",
          "AyaNumber": 3894,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88912f61d12bf6abd455"
          },
          "AyaContent": "وفديناه بذبح عظيم",
          "AyaNumber": 3895,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88912f61d12bf6abd456"
          },
          "AyaContent": "وتركنا عليه في الآخرين",
          "AyaNumber": 3896,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88922f61d12bf6abd457"
          },
          "AyaContent": "سلام علىٰ إبراهيم",
          "AyaNumber": 3897,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88922f61d12bf6abd458"
          },
          "AyaContent": "كذٰلك نجزي المحسنين",
          "AyaNumber": 3898,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88922f61d12bf6abd459"
          },
          "AyaContent": "إنه من عبادنا المؤمنين",
          "AyaNumber": 3899,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88922f61d12bf6abd45a"
          },
          "AyaContent": "وبشرناه بإسحاق نبيا من الصالحين",
          "AyaNumber": 3900,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88922f61d12bf6abd45b"
          },
          "AyaContent": "وباركنا عليه وعلىٰ إسحاق ۚ ومن ذريتهما محسن وظالم لنفسه مبين",
          "AyaNumber": 3901,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88932f61d12bf6abd45c"
          },
          "AyaContent": "ولقد مننا علىٰ موسىٰ وهارون",
          "AyaNumber": 3902,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88932f61d12bf6abd45d"
          },
          "AyaContent": "ونجيناهما وقومهما من الكرب العظيم",
          "AyaNumber": 3903,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88932f61d12bf6abd45e"
          },
          "AyaContent": "ونصرناهم فكانوا هم الغالبين",
          "AyaNumber": 3904,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88932f61d12bf6abd45f"
          },
          "AyaContent": "وآتيناهما الكتاب المستبين",
          "AyaNumber": 3905,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88942f61d12bf6abd460"
          },
          "AyaContent": "وهديناهما الصراط المستقيم",
          "AyaNumber": 3906,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88942f61d12bf6abd461"
          },
          "AyaContent": "وتركنا عليهما في الآخرين",
          "AyaNumber": 3907,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88942f61d12bf6abd462"
          },
          "AyaContent": "سلام علىٰ موسىٰ وهارون",
          "AyaNumber": 3908,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88942f61d12bf6abd463"
          },
          "AyaContent": "إنا كذٰلك نجزي المحسنين",
          "AyaNumber": 3909,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88942f61d12bf6abd464"
          },
          "AyaContent": "إنهما من عبادنا المؤمنين",
          "AyaNumber": 3910,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88952f61d12bf6abd465"
          },
          "AyaContent": "وإن إلياس لمن المرسلين",
          "AyaNumber": 3911,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88952f61d12bf6abd466"
          },
          "AyaContent": "إذ قال لقومه ألا تتقون",
          "AyaNumber": 3912,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88952f61d12bf6abd467"
          },
          "AyaContent": "أتدعون بعلا وتذرون أحسن الخالقين",
          "AyaNumber": 3913,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88952f61d12bf6abd468"
          },
          "AyaContent": "الله ربكم ورب آبائكم الأولين",
          "AyaNumber": 3914,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88962f61d12bf6abd469"
          },
          "AyaContent": "فكذبوه فإنهم لمحضرون",
          "AyaNumber": 3915,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88962f61d12bf6abd46a"
          },
          "AyaContent": "إلا عباد الله المخلصين",
          "AyaNumber": 3916,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88962f61d12bf6abd46b"
          },
          "AyaContent": "وتركنا عليه في الآخرين",
          "AyaNumber": 3917,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88962f61d12bf6abd46c"
          },
          "AyaContent": "سلام علىٰ إل ياسين",
          "AyaNumber": 3918,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88962f61d12bf6abd46d"
          },
          "AyaContent": "إنا كذٰلك نجزي المحسنين",
          "AyaNumber": 3919,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88972f61d12bf6abd46e"
          },
          "AyaContent": "إنه من عبادنا المؤمنين",
          "AyaNumber": 3920,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88972f61d12bf6abd46f"
          },
          "AyaContent": "وإن لوطا لمن المرسلين",
          "AyaNumber": 3921,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88972f61d12bf6abd470"
          },
          "AyaContent": "إذ نجيناه وأهله أجمعين",
          "AyaNumber": 3922,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88972f61d12bf6abd471"
          },
          "AyaContent": "إلا عجوزا في الغابرين",
          "AyaNumber": 3923,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88972f61d12bf6abd472"
          },
          "AyaContent": "ثم دمرنا الآخرين",
          "AyaNumber": 3924,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88982f61d12bf6abd473"
          },
          "AyaContent": "وإنكم لتمرون عليهم مصبحين",
          "AyaNumber": 3925,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88982f61d12bf6abd474"
          },
          "AyaContent": "وبالليل ۗ أفلا تعقلون",
          "AyaNumber": 3926,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88982f61d12bf6abd475"
          },
          "AyaContent": "وإن يونس لمن المرسلين",
          "AyaNumber": 3927,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88982f61d12bf6abd476"
          },
          "AyaContent": "إذ أبق إلى الفلك المشحون",
          "AyaNumber": 3928,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88992f61d12bf6abd477"
          },
          "AyaContent": "فساهم فكان من المدحضين",
          "AyaNumber": 3929,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88992f61d12bf6abd478"
          },
          "AyaContent": "فالتقمه الحوت وهو مليم",
          "AyaNumber": 3930,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88992f61d12bf6abd479"
          },
          "AyaContent": "فلولا أنه كان من المسبحين",
          "AyaNumber": 3931,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88992f61d12bf6abd47a"
          },
          "AyaContent": "للبث في بطنه إلىٰ يوم يبعثون",
          "AyaNumber": 3932,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889a2f61d12bf6abd47b"
          },
          "AyaContent": "۞ فنبذناه بالعراء وهو سقيم",
          "AyaNumber": 3933,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889a2f61d12bf6abd47c"
          },
          "AyaContent": "وأنبتنا عليه شجرة من يقطين",
          "AyaNumber": 3934,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889a2f61d12bf6abd47d"
          },
          "AyaContent": "وأرسلناه إلىٰ مائة ألف أو يزيدون",
          "AyaNumber": 3935,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889a2f61d12bf6abd47e"
          },
          "AyaContent": "فآمنوا فمتعناهم إلىٰ حين",
          "AyaNumber": 3936,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889a2f61d12bf6abd47f"
          },
          "AyaContent": "فاستفتهم ألربك البنات ولهم البنون",
          "AyaNumber": 3937,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889b2f61d12bf6abd480"
          },
          "AyaContent": "أم خلقنا الملائكة إناثا وهم شاهدون",
          "AyaNumber": 3938,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889b2f61d12bf6abd481"
          },
          "AyaContent": "ألا إنهم من إفكهم ليقولون",
          "AyaNumber": 3939,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889b2f61d12bf6abd482"
          },
          "AyaContent": "ولد الله وإنهم لكاذبون",
          "AyaNumber": 3940,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889b2f61d12bf6abd483"
          },
          "AyaContent": "أصطفى البنات على البنين",
          "AyaNumber": 3941,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889c2f61d12bf6abd484"
          },
          "AyaContent": "ما لكم كيف تحكمون",
          "AyaNumber": 3942,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889c2f61d12bf6abd485"
          },
          "AyaContent": "أفلا تذكرون",
          "AyaNumber": 3943,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889c2f61d12bf6abd486"
          },
          "AyaContent": "أم لكم سلطان مبين",
          "AyaNumber": 3944,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889c2f61d12bf6abd487"
          },
          "AyaContent": "فأتوا بكتابكم إن كنتم صادقين",
          "AyaNumber": 3945,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889c2f61d12bf6abd488"
          },
          "AyaContent": "وجعلوا بينه وبين الجنة نسبا ۚ ولقد علمت الجنة إنهم لمحضرون",
          "AyaNumber": 3946,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889d2f61d12bf6abd489"
          },
          "AyaContent": "سبحان الله عما يصفون",
          "AyaNumber": 3947,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889d2f61d12bf6abd48a"
          },
          "AyaContent": "إلا عباد الله المخلصين",
          "AyaNumber": 3948,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889d2f61d12bf6abd48b"
          },
          "AyaContent": "فإنكم وما تعبدون",
          "AyaNumber": 3949,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889d2f61d12bf6abd48c"
          },
          "AyaContent": "ما أنتم عليه بفاتنين",
          "AyaNumber": 3950,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889d2f61d12bf6abd48d"
          },
          "AyaContent": "إلا من هو صال الجحيم",
          "AyaNumber": 3951,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889d2f61d12bf6abd48e"
          },
          "AyaContent": "وما منا إلا له مقام معلوم",
          "AyaNumber": 3952,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889e2f61d12bf6abd48f"
          },
          "AyaContent": "وإنا لنحن الصافون",
          "AyaNumber": 3953,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889e2f61d12bf6abd490"
          },
          "AyaContent": "وإنا لنحن المسبحون",
          "AyaNumber": 3954,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889e2f61d12bf6abd491"
          },
          "AyaContent": "وإن كانوا ليقولون",
          "AyaNumber": 3955,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889e2f61d12bf6abd492"
          },
          "AyaContent": "لو أن عندنا ذكرا من الأولين",
          "AyaNumber": 3956,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889e2f61d12bf6abd493"
          },
          "AyaContent": "لكنا عباد الله المخلصين",
          "AyaNumber": 3957,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889f2f61d12bf6abd494"
          },
          "AyaContent": "فكفروا به ۖ فسوف يعلمون",
          "AyaNumber": 3958,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889f2f61d12bf6abd495"
          },
          "AyaContent": "ولقد سبقت كلمتنا لعبادنا المرسلين",
          "AyaNumber": 3959,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889f2f61d12bf6abd496"
          },
          "AyaContent": "إنهم لهم المنصورون",
          "AyaNumber": 3960,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd889f2f61d12bf6abd497"
          },
          "AyaContent": "وإن جندنا لهم الغالبون",
          "AyaNumber": 3961,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a02f61d12bf6abd498"
          },
          "AyaContent": "فتول عنهم حتىٰ حين",
          "AyaNumber": 3962,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a02f61d12bf6abd499"
          },
          "AyaContent": "وأبصرهم فسوف يبصرون",
          "AyaNumber": 3963,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a02f61d12bf6abd49a"
          },
          "AyaContent": "أفبعذابنا يستعجلون",
          "AyaNumber": 3964,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a02f61d12bf6abd49b"
          },
          "AyaContent": "فإذا نزل بساحتهم فساء صباح المنذرين",
          "AyaNumber": 3965,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a02f61d12bf6abd49c"
          },
          "AyaContent": "وتول عنهم حتىٰ حين",
          "AyaNumber": 3966,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a12f61d12bf6abd49d"
          },
          "AyaContent": "وأبصر فسوف يبصرون",
          "AyaNumber": 3967,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a12f61d12bf6abd49e"
          },
          "AyaContent": "سبحان ربك رب العزة عما يصفون",
          "AyaNumber": 3968,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a12f61d12bf6abd49f"
          },
          "AyaContent": "وسلام على المرسلين",
          "AyaNumber": 3969,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a12f61d12bf6abd4a0"
          },
          "AyaContent": "والحمد لله رب العالمين",
          "AyaNumber": 3970,
          "SurahName": "سورة الصافات",
          "SurahNumber": 37
        },{
          "_id": {
            "$oid": "5ecd88a12f61d12bf6abd4a1"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم ص ۚ والقرآن ذي الذكر",
          "AyaNumber": 3971,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a12f61d12bf6abd4a2"
          },
          "AyaContent": "بل الذين كفروا في عزة وشقاق",
          "AyaNumber": 3972,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a22f61d12bf6abd4a3"
          },
          "AyaContent": "كم أهلكنا من قبلهم من قرن فنادوا ولات حين مناص",
          "AyaNumber": 3973,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a22f61d12bf6abd4a4"
          },
          "AyaContent": "وعجبوا أن جاءهم منذر منهم ۖ وقال الكافرون هٰذا ساحر كذاب",
          "AyaNumber": 3974,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a22f61d12bf6abd4a5"
          },
          "AyaContent": "أجعل الآلهة إلٰها واحدا ۖ إن هٰذا لشيء عجاب",
          "AyaNumber": 3975,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a22f61d12bf6abd4a6"
          },
          "AyaContent": "وانطلق الملأ منهم أن امشوا واصبروا علىٰ آلهتكم ۖ إن هٰذا لشيء يراد",
          "AyaNumber": 3976,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a22f61d12bf6abd4a7"
          },
          "AyaContent": "ما سمعنا بهٰذا في الملة الآخرة إن هٰذا إلا اختلاق",
          "AyaNumber": 3977,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a22f61d12bf6abd4a8"
          },
          "AyaContent": "أأنزل عليه الذكر من بيننا ۚ بل هم في شك من ذكري ۖ بل لما يذوقوا عذاب",
          "AyaNumber": 3978,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a32f61d12bf6abd4a9"
          },
          "AyaContent": "أم عندهم خزائن رحمة ربك العزيز الوهاب",
          "AyaNumber": 3979,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a32f61d12bf6abd4aa"
          },
          "AyaContent": "أم لهم ملك السماوات والأرض وما بينهما ۖ فليرتقوا في الأسباب",
          "AyaNumber": 3980,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a32f61d12bf6abd4ab"
          },
          "AyaContent": "جند ما هنالك مهزوم من الأحزاب",
          "AyaNumber": 3981,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a32f61d12bf6abd4ac"
          },
          "AyaContent": "كذبت قبلهم قوم نوح وعاد وفرعون ذو الأوتاد",
          "AyaNumber": 3982,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a32f61d12bf6abd4ad"
          },
          "AyaContent": "وثمود وقوم لوط وأصحاب الأيكة ۚ أولٰئك الأحزاب",
          "AyaNumber": 3983,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a42f61d12bf6abd4ae"
          },
          "AyaContent": "إن كل إلا كذب الرسل فحق عقاب",
          "AyaNumber": 3984,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a42f61d12bf6abd4af"
          },
          "AyaContent": "وما ينظر هٰؤلاء إلا صيحة واحدة ما لها من فواق",
          "AyaNumber": 3985,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a42f61d12bf6abd4b0"
          },
          "AyaContent": "وقالوا ربنا عجل لنا قطنا قبل يوم الحساب",
          "AyaNumber": 3986,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a42f61d12bf6abd4b1"
          },
          "AyaContent": "اصبر علىٰ ما يقولون واذكر عبدنا داوود ذا الأيد ۖ إنه أواب",
          "AyaNumber": 3987,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a42f61d12bf6abd4b2"
          },
          "AyaContent": "إنا سخرنا الجبال معه يسبحن بالعشي والإشراق",
          "AyaNumber": 3988,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a42f61d12bf6abd4b3"
          },
          "AyaContent": "والطير محشورة ۖ كل له أواب",
          "AyaNumber": 3989,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a52f61d12bf6abd4b4"
          },
          "AyaContent": "وشددنا ملكه وآتيناه الحكمة وفصل الخطاب",
          "AyaNumber": 3990,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a52f61d12bf6abd4b5"
          },
          "AyaContent": "۞ وهل أتاك نبأ الخصم إذ تسوروا المحراب",
          "AyaNumber": 3991,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a52f61d12bf6abd4b6"
          },
          "AyaContent": "إذ دخلوا علىٰ داوود ففزع منهم ۖ قالوا لا تخف ۖ خصمان بغىٰ بعضنا علىٰ بعض فاحكم بيننا بالحق ولا تشطط واهدنا إلىٰ سواء الصراط",
          "AyaNumber": 3992,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a62f61d12bf6abd4b7"
          },
          "AyaContent": "إن هٰذا أخي له تسع وتسعون نعجة ولي نعجة واحدة فقال أكفلنيها وعزني في الخطاب",
          "AyaNumber": 3993,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a62f61d12bf6abd4b8"
          },
          "AyaContent": "قال لقد ظلمك بسؤال نعجتك إلىٰ نعاجه ۖ وإن كثيرا من الخلطاء ليبغي بعضهم علىٰ بعض إلا الذين آمنوا وعملوا الصالحات وقليل ما هم ۗ وظن داوود أنما فتناه فاستغفر ربه وخر راكعا وأناب ۩",
          "AyaNumber": 3994,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a62f61d12bf6abd4b9"
          },
          "AyaContent": "فغفرنا له ذٰلك ۖ وإن له عندنا لزلفىٰ وحسن مآب",
          "AyaNumber": 3995,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a72f61d12bf6abd4ba"
          },
          "AyaContent": "يا داوود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق ولا تتبع الهوىٰ فيضلك عن سبيل الله ۚ إن الذين يضلون عن سبيل الله لهم عذاب شديد بما نسوا يوم الحساب",
          "AyaNumber": 3996,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a72f61d12bf6abd4bb"
          },
          "AyaContent": "وما خلقنا السماء والأرض وما بينهما باطلا ۚ ذٰلك ظن الذين كفروا ۚ فويل للذين كفروا من النار",
          "AyaNumber": 3997,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a72f61d12bf6abd4bc"
          },
          "AyaContent": "أم نجعل الذين آمنوا وعملوا الصالحات كالمفسدين في الأرض أم نجعل المتقين كالفجار",
          "AyaNumber": 3998,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a72f61d12bf6abd4bd"
          },
          "AyaContent": "كتاب أنزلناه إليك مبارك ليدبروا آياته وليتذكر أولو الألباب",
          "AyaNumber": 3999,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a72f61d12bf6abd4be"
          },
          "AyaContent": "ووهبنا لداوود سليمان ۚ نعم العبد ۖ إنه أواب",
          "AyaNumber": 4000,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a82f61d12bf6abd4bf"
          },
          "AyaContent": "إذ عرض عليه بالعشي الصافنات الجياد",
          "AyaNumber": 4001,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a82f61d12bf6abd4c0"
          },
          "AyaContent": "فقال إني أحببت حب الخير عن ذكر ربي حتىٰ توارت بالحجاب",
          "AyaNumber": 4002,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a82f61d12bf6abd4c1"
          },
          "AyaContent": "ردوها علي ۖ فطفق مسحا بالسوق والأعناق",
          "AyaNumber": 4003,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a92f61d12bf6abd4c2"
          },
          "AyaContent": "ولقد فتنا سليمان وألقينا علىٰ كرسيه جسدا ثم أناب",
          "AyaNumber": 4004,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a92f61d12bf6abd4c3"
          },
          "AyaContent": "قال رب اغفر لي وهب لي ملكا لا ينبغي لأحد من بعدي ۖ إنك أنت الوهاب",
          "AyaNumber": 4005,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88a92f61d12bf6abd4c4"
          },
          "AyaContent": "فسخرنا له الريح تجري بأمره رخاء حيث أصاب",
          "AyaNumber": 4006,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88aa2f61d12bf6abd4c5"
          },
          "AyaContent": "والشياطين كل بناء وغواص",
          "AyaNumber": 4007,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88aa2f61d12bf6abd4c6"
          },
          "AyaContent": "وآخرين مقرنين في الأصفاد",
          "AyaNumber": 4008,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88aa2f61d12bf6abd4c7"
          },
          "AyaContent": "هٰذا عطاؤنا فامنن أو أمسك بغير حساب",
          "AyaNumber": 4009,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ab2f61d12bf6abd4c8"
          },
          "AyaContent": "وإن له عندنا لزلفىٰ وحسن مآب",
          "AyaNumber": 4010,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ab2f61d12bf6abd4c9"
          },
          "AyaContent": "واذكر عبدنا أيوب إذ نادىٰ ربه أني مسني الشيطان بنصب وعذاب",
          "AyaNumber": 4011,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ab2f61d12bf6abd4ca"
          },
          "AyaContent": "اركض برجلك ۖ هٰذا مغتسل بارد وشراب",
          "AyaNumber": 4012,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ab2f61d12bf6abd4cb"
          },
          "AyaContent": "ووهبنا له أهله ومثلهم معهم رحمة منا وذكرىٰ لأولي الألباب",
          "AyaNumber": 4013,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ab2f61d12bf6abd4cc"
          },
          "AyaContent": "وخذ بيدك ضغثا فاضرب به ولا تحنث ۗ إنا وجدناه صابرا ۚ نعم العبد ۖ إنه أواب",
          "AyaNumber": 4014,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ac2f61d12bf6abd4cd"
          },
          "AyaContent": "واذكر عبادنا إبراهيم وإسحاق ويعقوب أولي الأيدي والأبصار",
          "AyaNumber": 4015,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ac2f61d12bf6abd4ce"
          },
          "AyaContent": "إنا أخلصناهم بخالصة ذكرى الدار",
          "AyaNumber": 4016,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ac2f61d12bf6abd4cf"
          },
          "AyaContent": "وإنهم عندنا لمن المصطفين الأخيار",
          "AyaNumber": 4017,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ac2f61d12bf6abd4d0"
          },
          "AyaContent": "واذكر إسماعيل واليسع وذا الكفل ۖ وكل من الأخيار",
          "AyaNumber": 4018,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ad2f61d12bf6abd4d1"
          },
          "AyaContent": "هٰذا ذكر ۚ وإن للمتقين لحسن مآب",
          "AyaNumber": 4019,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ad2f61d12bf6abd4d2"
          },
          "AyaContent": "جنات عدن مفتحة لهم الأبواب",
          "AyaNumber": 4020,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ad2f61d12bf6abd4d3"
          },
          "AyaContent": "متكئين فيها يدعون فيها بفاكهة كثيرة وشراب",
          "AyaNumber": 4021,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ad2f61d12bf6abd4d4"
          },
          "AyaContent": "۞ وعندهم قاصرات الطرف أتراب",
          "AyaNumber": 4022,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ad2f61d12bf6abd4d5"
          },
          "AyaContent": "هٰذا ما توعدون ليوم الحساب",
          "AyaNumber": 4023,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ad2f61d12bf6abd4d6"
          },
          "AyaContent": "إن هٰذا لرزقنا ما له من نفاد",
          "AyaNumber": 4024,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ae2f61d12bf6abd4d7"
          },
          "AyaContent": "هٰذا ۚ وإن للطاغين لشر مآب",
          "AyaNumber": 4025,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ae2f61d12bf6abd4d8"
          },
          "AyaContent": "جهنم يصلونها فبئس المهاد",
          "AyaNumber": 4026,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ae2f61d12bf6abd4d9"
          },
          "AyaContent": "هٰذا فليذوقوه حميم وغساق",
          "AyaNumber": 4027,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88ae2f61d12bf6abd4da"
          },
          "AyaContent": "وآخر من شكله أزواج",
          "AyaNumber": 4028,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88af2f61d12bf6abd4db"
          },
          "AyaContent": "هٰذا فوج مقتحم معكم ۖ لا مرحبا بهم ۚ إنهم صالو النار",
          "AyaNumber": 4029,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88af2f61d12bf6abd4dc"
          },
          "AyaContent": "قالوا بل أنتم لا مرحبا بكم ۖ أنتم قدمتموه لنا ۖ فبئس القرار",
          "AyaNumber": 4030,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88af2f61d12bf6abd4dd"
          },
          "AyaContent": "قالوا ربنا من قدم لنا هٰذا فزده عذابا ضعفا في النار",
          "AyaNumber": 4031,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88af2f61d12bf6abd4de"
          },
          "AyaContent": "وقالوا ما لنا لا نرىٰ رجالا كنا نعدهم من الأشرار",
          "AyaNumber": 4032,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b02f61d12bf6abd4df"
          },
          "AyaContent": "أتخذناهم سخريا أم زاغت عنهم الأبصار",
          "AyaNumber": 4033,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b02f61d12bf6abd4e0"
          },
          "AyaContent": "إن ذٰلك لحق تخاصم أهل النار",
          "AyaNumber": 4034,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b02f61d12bf6abd4e1"
          },
          "AyaContent": "قل إنما أنا منذر ۖ وما من إلٰه إلا الله الواحد القهار",
          "AyaNumber": 4035,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b12f61d12bf6abd4e2"
          },
          "AyaContent": "رب السماوات والأرض وما بينهما العزيز الغفار",
          "AyaNumber": 4036,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b12f61d12bf6abd4e3"
          },
          "AyaContent": "قل هو نبأ عظيم",
          "AyaNumber": 4037,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b12f61d12bf6abd4e4"
          },
          "AyaContent": "أنتم عنه معرضون",
          "AyaNumber": 4038,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b12f61d12bf6abd4e5"
          },
          "AyaContent": "ما كان لي من علم بالملإ الأعلىٰ إذ يختصمون",
          "AyaNumber": 4039,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b22f61d12bf6abd4e6"
          },
          "AyaContent": "إن يوحىٰ إلي إلا أنما أنا نذير مبين",
          "AyaNumber": 4040,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b22f61d12bf6abd4e7"
          },
          "AyaContent": "إذ قال ربك للملائكة إني خالق بشرا من طين",
          "AyaNumber": 4041,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b22f61d12bf6abd4e8"
          },
          "AyaContent": "فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين",
          "AyaNumber": 4042,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b22f61d12bf6abd4e9"
          },
          "AyaContent": "فسجد الملائكة كلهم أجمعون",
          "AyaNumber": 4043,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b22f61d12bf6abd4ea"
          },
          "AyaContent": "إلا إبليس استكبر وكان من الكافرين",
          "AyaNumber": 4044,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b32f61d12bf6abd4eb"
          },
          "AyaContent": "قال يا إبليس ما منعك أن تسجد لما خلقت بيدي ۖ أستكبرت أم كنت من العالين",
          "AyaNumber": 4045,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b42f61d12bf6abd4ec"
          },
          "AyaContent": "قال أنا خير منه ۖ خلقتني من نار وخلقته من طين",
          "AyaNumber": 4046,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b42f61d12bf6abd4ed"
          },
          "AyaContent": "قال فاخرج منها فإنك رجيم",
          "AyaNumber": 4047,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b42f61d12bf6abd4ee"
          },
          "AyaContent": "وإن عليك لعنتي إلىٰ يوم الدين",
          "AyaNumber": 4048,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b42f61d12bf6abd4ef"
          },
          "AyaContent": "قال رب فأنظرني إلىٰ يوم يبعثون",
          "AyaNumber": 4049,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b42f61d12bf6abd4f0"
          },
          "AyaContent": "قال فإنك من المنظرين",
          "AyaNumber": 4050,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b52f61d12bf6abd4f1"
          },
          "AyaContent": "إلىٰ يوم الوقت المعلوم",
          "AyaNumber": 4051,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b52f61d12bf6abd4f2"
          },
          "AyaContent": "قال فبعزتك لأغوينهم أجمعين",
          "AyaNumber": 4052,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b52f61d12bf6abd4f3"
          },
          "AyaContent": "إلا عبادك منهم المخلصين",
          "AyaNumber": 4053,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b52f61d12bf6abd4f4"
          },
          "AyaContent": "قال فالحق والحق أقول",
          "AyaNumber": 4054,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b52f61d12bf6abd4f5"
          },
          "AyaContent": "لأملأن جهنم منك وممن تبعك منهم أجمعين",
          "AyaNumber": 4055,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b62f61d12bf6abd4f6"
          },
          "AyaContent": "قل ما أسألكم عليه من أجر وما أنا من المتكلفين",
          "AyaNumber": 4056,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b62f61d12bf6abd4f7"
          },
          "AyaContent": "إن هو إلا ذكر للعالمين",
          "AyaNumber": 4057,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b62f61d12bf6abd4f8"
          },
          "AyaContent": "ولتعلمن نبأه بعد حين",
          "AyaNumber": 4058,
          "SurahName": "سورة ص",
          "SurahNumber": 38
        },{
          "_id": {
            "$oid": "5ecd88b62f61d12bf6abd4f9"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم تنزيل الكتاب من الله العزيز الحكيم",
          "AyaNumber": 4059,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b72f61d12bf6abd4fa"
          },
          "AyaContent": "إنا أنزلنا إليك الكتاب بالحق فاعبد الله مخلصا له الدين",
          "AyaNumber": 4060,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b72f61d12bf6abd4fb"
          },
          "AyaContent": "ألا لله الدين الخالص ۚ والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفىٰ إن الله يحكم بينهم في ما هم فيه يختلفون ۗ إن الله لا يهدي من هو كاذب كفار",
          "AyaNumber": 4061,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b72f61d12bf6abd4fc"
          },
          "AyaContent": "لو أراد الله أن يتخذ ولدا لاصطفىٰ مما يخلق ما يشاء ۚ سبحانه ۖ هو الله الواحد القهار",
          "AyaNumber": 4062,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b72f61d12bf6abd4fd"
          },
          "AyaContent": "خلق السماوات والأرض بالحق ۖ يكور الليل على النهار ويكور النهار على الليل ۖ وسخر الشمس والقمر ۖ كل يجري لأجل مسمى ۗ ألا هو العزيز الغفار",
          "AyaNumber": 4063,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b82f61d12bf6abd4fe"
          },
          "AyaContent": "خلقكم من نفس واحدة ثم جعل منها زوجها وأنزل لكم من الأنعام ثمانية أزواج ۚ يخلقكم في بطون أمهاتكم خلقا من بعد خلق في ظلمات ثلاث ۚ ذٰلكم الله ربكم له الملك ۖ لا إلٰه إلا هو ۖ فأنىٰ تصرفون",
          "AyaNumber": 4064,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b82f61d12bf6abd4ff"
          },
          "AyaContent": "إن تكفروا فإن الله غني عنكم ۖ ولا يرضىٰ لعباده الكفر ۖ وإن تشكروا يرضه لكم ۗ ولا تزر وازرة وزر أخرىٰ ۗ ثم إلىٰ ربكم مرجعكم فينبئكم بما كنتم تعملون ۚ إنه عليم بذات الصدور",
          "AyaNumber": 4065,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b82f61d12bf6abd500"
          },
          "AyaContent": "۞ وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله ۚ قل تمتع بكفرك قليلا ۖ إنك من أصحاب النار",
          "AyaNumber": 4066,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b82f61d12bf6abd501"
          },
          "AyaContent": "أمن هو قانت آناء الليل ساجدا وقائما يحذر الآخرة ويرجو رحمة ربه ۗ قل هل يستوي الذين يعلمون والذين لا يعلمون ۗ إنما يتذكر أولو الألباب",
          "AyaNumber": 4067,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b82f61d12bf6abd502"
          },
          "AyaContent": "قل يا عباد الذين آمنوا اتقوا ربكم ۚ للذين أحسنوا في هٰذه الدنيا حسنة ۗ وأرض الله واسعة ۗ إنما يوفى الصابرون أجرهم بغير حساب",
          "AyaNumber": 4068,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b82f61d12bf6abd503"
          },
          "AyaContent": "قل إني أمرت أن أعبد الله مخلصا له الدين",
          "AyaNumber": 4069,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b92f61d12bf6abd504"
          },
          "AyaContent": "وأمرت لأن أكون أول المسلمين",
          "AyaNumber": 4070,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b92f61d12bf6abd505"
          },
          "AyaContent": "قل إني أخاف إن عصيت ربي عذاب يوم عظيم",
          "AyaNumber": 4071,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b92f61d12bf6abd506"
          },
          "AyaContent": "قل الله أعبد مخلصا له ديني",
          "AyaNumber": 4072,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88b92f61d12bf6abd507"
          },
          "AyaContent": "فاعبدوا ما شئتم من دونه ۗ قل إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ۗ ألا ذٰلك هو الخسران المبين",
          "AyaNumber": 4073,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88ba2f61d12bf6abd508"
          },
          "AyaContent": "لهم من فوقهم ظلل من النار ومن تحتهم ظلل ۚ ذٰلك يخوف الله به عباده ۚ يا عباد فاتقون",
          "AyaNumber": 4074,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88ba2f61d12bf6abd509"
          },
          "AyaContent": "والذين اجتنبوا الطاغوت أن يعبدوها وأنابوا إلى الله لهم البشرىٰ ۚ فبشر عباد",
          "AyaNumber": 4075,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88ba2f61d12bf6abd50a"
          },
          "AyaContent": "الذين يستمعون القول فيتبعون أحسنه ۚ أولٰئك الذين هداهم الله ۖ وأولٰئك هم أولو الألباب",
          "AyaNumber": 4076,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88ba2f61d12bf6abd50b"
          },
          "AyaContent": "أفمن حق عليه كلمة العذاب أفأنت تنقذ من في النار",
          "AyaNumber": 4077,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bb2f61d12bf6abd50c"
          },
          "AyaContent": "لٰكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية تجري من تحتها الأنهار ۖ وعد الله ۖ لا يخلف الله الميعاد",
          "AyaNumber": 4078,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bb2f61d12bf6abd50d"
          },
          "AyaContent": "ألم تر أن الله أنزل من السماء ماء فسلكه ينابيع في الأرض ثم يخرج به زرعا مختلفا ألوانه ثم يهيج فتراه مصفرا ثم يجعله حطاما ۚ إن في ذٰلك لذكرىٰ لأولي الألباب",
          "AyaNumber": 4079,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bb2f61d12bf6abd50e"
          },
          "AyaContent": "أفمن شرح الله صدره للإسلام فهو علىٰ نور من ربه ۚ فويل للقاسية قلوبهم من ذكر الله ۚ أولٰئك في ضلال مبين",
          "AyaNumber": 4080,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bb2f61d12bf6abd50f"
          },
          "AyaContent": "الله نزل أحسن الحديث كتابا متشابها مثاني تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم إلىٰ ذكر الله ۚ ذٰلك هدى الله يهدي به من يشاء ۚ ومن يضلل الله فما له من هاد",
          "AyaNumber": 4081,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bc2f61d12bf6abd510"
          },
          "AyaContent": "أفمن يتقي بوجهه سوء العذاب يوم القيامة ۚ وقيل للظالمين ذوقوا ما كنتم تكسبون",
          "AyaNumber": 4082,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bc2f61d12bf6abd511"
          },
          "AyaContent": "كذب الذين من قبلهم فأتاهم العذاب من حيث لا يشعرون",
          "AyaNumber": 4083,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bc2f61d12bf6abd512"
          },
          "AyaContent": "فأذاقهم الله الخزي في الحياة الدنيا ۖ ولعذاب الآخرة أكبر ۚ لو كانوا يعلمون",
          "AyaNumber": 4084,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bc2f61d12bf6abd513"
          },
          "AyaContent": "ولقد ضربنا للناس في هٰذا القرآن من كل مثل لعلهم يتذكرون",
          "AyaNumber": 4085,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bc2f61d12bf6abd514"
          },
          "AyaContent": "قرآنا عربيا غير ذي عوج لعلهم يتقون",
          "AyaNumber": 4086,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bd2f61d12bf6abd515"
          },
          "AyaContent": "ضرب الله مثلا رجلا فيه شركاء متشاكسون ورجلا سلما لرجل هل يستويان مثلا ۚ الحمد لله ۚ بل أكثرهم لا يعلمون",
          "AyaNumber": 4087,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bd2f61d12bf6abd516"
          },
          "AyaContent": "إنك ميت وإنهم ميتون",
          "AyaNumber": 4088,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bd2f61d12bf6abd517"
          },
          "AyaContent": "ثم إنكم يوم القيامة عند ربكم تختصمون",
          "AyaNumber": 4089,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bd2f61d12bf6abd518"
          },
          "AyaContent": "۞ فمن أظلم ممن كذب على الله وكذب بالصدق إذ جاءه ۚ أليس في جهنم مثوى للكافرين",
          "AyaNumber": 4090,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bd2f61d12bf6abd519"
          },
          "AyaContent": "والذي جاء بالصدق وصدق به ۙ أولٰئك هم المتقون",
          "AyaNumber": 4091,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bd2f61d12bf6abd51a"
          },
          "AyaContent": "لهم ما يشاءون عند ربهم ۚ ذٰلك جزاء المحسنين",
          "AyaNumber": 4092,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88be2f61d12bf6abd51b"
          },
          "AyaContent": "ليكفر الله عنهم أسوأ الذي عملوا ويجزيهم أجرهم بأحسن الذي كانوا يعملون",
          "AyaNumber": 4093,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88be2f61d12bf6abd51c"
          },
          "AyaContent": "أليس الله بكاف عبده ۖ ويخوفونك بالذين من دونه ۚ ومن يضلل الله فما له من هاد",
          "AyaNumber": 4094,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88be2f61d12bf6abd51d"
          },
          "AyaContent": "ومن يهد الله فما له من مضل ۗ أليس الله بعزيز ذي انتقام",
          "AyaNumber": 4095,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88be2f61d12bf6abd51e"
          },
          "AyaContent": "ولئن سألتهم من خلق السماوات والأرض ليقولن الله ۚ قل أفرأيتم ما تدعون من دون الله إن أرادني الله بضر هل هن كاشفات ضره أو أرادني برحمة هل هن ممسكات رحمته ۚ قل حسبي الله ۖ عليه يتوكل المتوكلون",
          "AyaNumber": 4096,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88be2f61d12bf6abd51f"
          },
          "AyaContent": "قل يا قوم اعملوا علىٰ مكانتكم إني عامل ۖ فسوف تعلمون",
          "AyaNumber": 4097,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88be2f61d12bf6abd520"
          },
          "AyaContent": "من يأتيه عذاب يخزيه ويحل عليه عذاب مقيم",
          "AyaNumber": 4098,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bf2f61d12bf6abd521"
          },
          "AyaContent": "إنا أنزلنا عليك الكتاب للناس بالحق ۖ فمن اهتدىٰ فلنفسه ۖ ومن ضل فإنما يضل عليها ۖ وما أنت عليهم بوكيل",
          "AyaNumber": 4099,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bf2f61d12bf6abd522"
          },
          "AyaContent": "الله يتوفى الأنفس حين موتها والتي لم تمت في منامها ۖ فيمسك التي قضىٰ عليها الموت ويرسل الأخرىٰ إلىٰ أجل مسمى ۚ إن في ذٰلك لآيات لقوم يتفكرون",
          "AyaNumber": 4100,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bf2f61d12bf6abd523"
          },
          "AyaContent": "أم اتخذوا من دون الله شفعاء ۚ قل أولو كانوا لا يملكون شيئا ولا يعقلون",
          "AyaNumber": 4101,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bf2f61d12bf6abd524"
          },
          "AyaContent": "قل لله الشفاعة جميعا ۖ له ملك السماوات والأرض ۖ ثم إليه ترجعون",
          "AyaNumber": 4102,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88bf2f61d12bf6abd525"
          },
          "AyaContent": "وإذا ذكر الله وحده اشمأزت قلوب الذين لا يؤمنون بالآخرة ۖ وإذا ذكر الذين من دونه إذا هم يستبشرون",
          "AyaNumber": 4103,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c02f61d12bf6abd526"
          },
          "AyaContent": "قل اللهم فاطر السماوات والأرض عالم الغيب والشهادة أنت تحكم بين عبادك في ما كانوا فيه يختلفون",
          "AyaNumber": 4104,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c02f61d12bf6abd527"
          },
          "AyaContent": "ولو أن للذين ظلموا ما في الأرض جميعا ومثله معه لافتدوا به من سوء العذاب يوم القيامة ۚ وبدا لهم من الله ما لم يكونوا يحتسبون",
          "AyaNumber": 4105,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c02f61d12bf6abd528"
          },
          "AyaContent": "وبدا لهم سيئات ما كسبوا وحاق بهم ما كانوا به يستهزئون",
          "AyaNumber": 4106,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c02f61d12bf6abd529"
          },
          "AyaContent": "فإذا مس الإنسان ضر دعانا ثم إذا خولناه نعمة منا قال إنما أوتيته علىٰ علم ۚ بل هي فتنة ولٰكن أكثرهم لا يعلمون",
          "AyaNumber": 4107,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c02f61d12bf6abd52a"
          },
          "AyaContent": "قد قالها الذين من قبلهم فما أغنىٰ عنهم ما كانوا يكسبون",
          "AyaNumber": 4108,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c12f61d12bf6abd52b"
          },
          "AyaContent": "فأصابهم سيئات ما كسبوا ۚ والذين ظلموا من هٰؤلاء سيصيبهم سيئات ما كسبوا وما هم بمعجزين",
          "AyaNumber": 4109,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c12f61d12bf6abd52c"
          },
          "AyaContent": "أولم يعلموا أن الله يبسط الرزق لمن يشاء ويقدر ۚ إن في ذٰلك لآيات لقوم يؤمنون",
          "AyaNumber": 4110,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c12f61d12bf6abd52d"
          },
          "AyaContent": "۞ قل يا عبادي الذين أسرفوا علىٰ أنفسهم لا تقنطوا من رحمة الله ۚ إن الله يغفر الذنوب جميعا ۚ إنه هو الغفور الرحيم",
          "AyaNumber": 4111,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c12f61d12bf6abd52e"
          },
          "AyaContent": "وأنيبوا إلىٰ ربكم وأسلموا له من قبل أن يأتيكم العذاب ثم لا تنصرون",
          "AyaNumber": 4112,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c12f61d12bf6abd52f"
          },
          "AyaContent": "واتبعوا أحسن ما أنزل إليكم من ربكم من قبل أن يأتيكم العذاب بغتة وأنتم لا تشعرون",
          "AyaNumber": 4113,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c12f61d12bf6abd530"
          },
          "AyaContent": "أن تقول نفس يا حسرتا علىٰ ما فرطت في جنب الله وإن كنت لمن الساخرين",
          "AyaNumber": 4114,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c22f61d12bf6abd531"
          },
          "AyaContent": "أو تقول لو أن الله هداني لكنت من المتقين",
          "AyaNumber": 4115,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c22f61d12bf6abd532"
          },
          "AyaContent": "أو تقول حين ترى العذاب لو أن لي كرة فأكون من المحسنين",
          "AyaNumber": 4116,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c22f61d12bf6abd533"
          },
          "AyaContent": "بلىٰ قد جاءتك آياتي فكذبت بها واستكبرت وكنت من الكافرين",
          "AyaNumber": 4117,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c22f61d12bf6abd534"
          },
          "AyaContent": "ويوم القيامة ترى الذين كذبوا على الله وجوههم مسودة ۚ أليس في جهنم مثوى للمتكبرين",
          "AyaNumber": 4118,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c22f61d12bf6abd535"
          },
          "AyaContent": "وينجي الله الذين اتقوا بمفازتهم لا يمسهم السوء ولا هم يحزنون",
          "AyaNumber": 4119,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c22f61d12bf6abd536"
          },
          "AyaContent": "الله خالق كل شيء ۖ وهو علىٰ كل شيء وكيل",
          "AyaNumber": 4120,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c22f61d12bf6abd537"
          },
          "AyaContent": "له مقاليد السماوات والأرض ۗ والذين كفروا بآيات الله أولٰئك هم الخاسرون",
          "AyaNumber": 4121,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c32f61d12bf6abd538"
          },
          "AyaContent": "قل أفغير الله تأمروني أعبد أيها الجاهلون",
          "AyaNumber": 4122,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c32f61d12bf6abd539"
          },
          "AyaContent": "ولقد أوحي إليك وإلى الذين من قبلك لئن أشركت ليحبطن عملك ولتكونن من الخاسرين",
          "AyaNumber": 4123,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c32f61d12bf6abd53a"
          },
          "AyaContent": "بل الله فاعبد وكن من الشاكرين",
          "AyaNumber": 4124,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c32f61d12bf6abd53b"
          },
          "AyaContent": "وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه ۚ سبحانه وتعالىٰ عما يشركون",
          "AyaNumber": 4125,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c42f61d12bf6abd53c"
          },
          "AyaContent": "ونفخ في الصور فصعق من في السماوات ومن في الأرض إلا من شاء الله ۖ ثم نفخ فيه أخرىٰ فإذا هم قيام ينظرون",
          "AyaNumber": 4126,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c42f61d12bf6abd53d"
          },
          "AyaContent": "وأشرقت الأرض بنور ربها ووضع الكتاب وجيء بالنبيين والشهداء وقضي بينهم بالحق وهم لا يظلمون",
          "AyaNumber": 4127,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c42f61d12bf6abd53e"
          },
          "AyaContent": "ووفيت كل نفس ما عملت وهو أعلم بما يفعلون",
          "AyaNumber": 4128,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c42f61d12bf6abd53f"
          },
          "AyaContent": "وسيق الذين كفروا إلىٰ جهنم زمرا ۖ حتىٰ إذا جاءوها فتحت أبوابها وقال لهم خزنتها ألم يأتكم رسل منكم يتلون عليكم آيات ربكم وينذرونكم لقاء يومكم هٰذا ۚ قالوا بلىٰ ولٰكن حقت كلمة العذاب على الكافرين",
          "AyaNumber": 4129,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c42f61d12bf6abd540"
          },
          "AyaContent": "قيل ادخلوا أبواب جهنم خالدين فيها ۖ فبئس مثوى المتكبرين",
          "AyaNumber": 4130,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c52f61d12bf6abd541"
          },
          "AyaContent": "وسيق الذين اتقوا ربهم إلى الجنة زمرا ۖ حتىٰ إذا جاءوها وفتحت أبوابها وقال لهم خزنتها سلام عليكم طبتم فادخلوها خالدين",
          "AyaNumber": 4131,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c52f61d12bf6abd542"
          },
          "AyaContent": "وقالوا الحمد لله الذي صدقنا وعده وأورثنا الأرض نتبوأ من الجنة حيث نشاء ۖ فنعم أجر العاملين",
          "AyaNumber": 4132,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c52f61d12bf6abd543"
          },
          "AyaContent": "وترى الملائكة حافين من حول العرش يسبحون بحمد ربهم ۖ وقضي بينهم بالحق وقيل الحمد لله رب العالمين",
          "AyaNumber": 4133,
          "SurahName": "سورة الزمر",
          "SurahNumber": 39
        },{
          "_id": {
            "$oid": "5ecd88c52f61d12bf6abd544"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم حم",
          "AyaNumber": 4134,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c62f61d12bf6abd545"
          },
          "AyaContent": "تنزيل الكتاب من الله العزيز العليم",
          "AyaNumber": 4135,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c62f61d12bf6abd546"
          },
          "AyaContent": "غافر الذنب وقابل التوب شديد العقاب ذي الطول ۖ لا إلٰه إلا هو ۖ إليه المصير",
          "AyaNumber": 4136,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c62f61d12bf6abd547"
          },
          "AyaContent": "ما يجادل في آيات الله إلا الذين كفروا فلا يغررك تقلبهم في البلاد",
          "AyaNumber": 4137,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c62f61d12bf6abd548"
          },
          "AyaContent": "كذبت قبلهم قوم نوح والأحزاب من بعدهم ۖ وهمت كل أمة برسولهم ليأخذوه ۖ وجادلوا بالباطل ليدحضوا به الحق فأخذتهم ۖ فكيف كان عقاب",
          "AyaNumber": 4138,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c72f61d12bf6abd549"
          },
          "AyaContent": "وكذٰلك حقت كلمت ربك على الذين كفروا أنهم أصحاب النار",
          "AyaNumber": 4139,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c72f61d12bf6abd54a"
          },
          "AyaContent": "الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به ويستغفرون للذين آمنوا ربنا وسعت كل شيء رحمة وعلما فاغفر للذين تابوا واتبعوا سبيلك وقهم عذاب الجحيم",
          "AyaNumber": 4140,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c72f61d12bf6abd54b"
          },
          "AyaContent": "ربنا وأدخلهم جنات عدن التي وعدتهم ومن صلح من آبائهم وأزواجهم وذرياتهم ۚ إنك أنت العزيز الحكيم",
          "AyaNumber": 4141,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c82f61d12bf6abd54c"
          },
          "AyaContent": "وقهم السيئات ۚ ومن تق السيئات يومئذ فقد رحمته ۚ وذٰلك هو الفوز العظيم",
          "AyaNumber": 4142,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c82f61d12bf6abd54d"
          },
          "AyaContent": "إن الذين كفروا ينادون لمقت الله أكبر من مقتكم أنفسكم إذ تدعون إلى الإيمان فتكفرون",
          "AyaNumber": 4143,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c82f61d12bf6abd54e"
          },
          "AyaContent": "قالوا ربنا أمتنا اثنتين وأحييتنا اثنتين فاعترفنا بذنوبنا فهل إلىٰ خروج من سبيل",
          "AyaNumber": 4144,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c92f61d12bf6abd54f"
          },
          "AyaContent": "ذٰلكم بأنه إذا دعي الله وحده كفرتم ۖ وإن يشرك به تؤمنوا ۚ فالحكم لله العلي الكبير",
          "AyaNumber": 4145,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c92f61d12bf6abd550"
          },
          "AyaContent": "هو الذي يريكم آياته وينزل لكم من السماء رزقا ۚ وما يتذكر إلا من ينيب",
          "AyaNumber": 4146,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88c92f61d12bf6abd551"
          },
          "AyaContent": "فادعوا الله مخلصين له الدين ولو كره الكافرون",
          "AyaNumber": 4147,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ca2f61d12bf6abd552"
          },
          "AyaContent": "رفيع الدرجات ذو العرش يلقي الروح من أمره علىٰ من يشاء من عباده لينذر يوم التلاق",
          "AyaNumber": 4148,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ca2f61d12bf6abd553"
          },
          "AyaContent": "يوم هم بارزون ۖ لا يخفىٰ على الله منهم شيء ۚ لمن الملك اليوم ۖ لله الواحد القهار",
          "AyaNumber": 4149,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ca2f61d12bf6abd554"
          },
          "AyaContent": "اليوم تجزىٰ كل نفس بما كسبت ۚ لا ظلم اليوم ۚ إن الله سريع الحساب",
          "AyaNumber": 4150,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ca2f61d12bf6abd555"
          },
          "AyaContent": "وأنذرهم يوم الآزفة إذ القلوب لدى الحناجر كاظمين ۚ ما للظالمين من حميم ولا شفيع يطاع",
          "AyaNumber": 4151,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cb2f61d12bf6abd556"
          },
          "AyaContent": "يعلم خائنة الأعين وما تخفي الصدور",
          "AyaNumber": 4152,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cb2f61d12bf6abd557"
          },
          "AyaContent": "والله يقضي بالحق ۖ والذين يدعون من دونه لا يقضون بشيء ۗ إن الله هو السميع البصير",
          "AyaNumber": 4153,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cb2f61d12bf6abd558"
          },
          "AyaContent": "۞ أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين كانوا من قبلهم ۚ كانوا هم أشد منهم قوة وآثارا في الأرض فأخذهم الله بذنوبهم وما كان لهم من الله من واق",
          "AyaNumber": 4154,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cb2f61d12bf6abd559"
          },
          "AyaContent": "ذٰلك بأنهم كانت تأتيهم رسلهم بالبينات فكفروا فأخذهم الله ۚ إنه قوي شديد العقاب",
          "AyaNumber": 4155,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cb2f61d12bf6abd55a"
          },
          "AyaContent": "ولقد أرسلنا موسىٰ بآياتنا وسلطان مبين",
          "AyaNumber": 4156,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cc2f61d12bf6abd55b"
          },
          "AyaContent": "إلىٰ فرعون وهامان وقارون فقالوا ساحر كذاب",
          "AyaNumber": 4157,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cc2f61d12bf6abd55c"
          },
          "AyaContent": "فلما جاءهم بالحق من عندنا قالوا اقتلوا أبناء الذين آمنوا معه واستحيوا نساءهم ۚ وما كيد الكافرين إلا في ضلال",
          "AyaNumber": 4158,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cc2f61d12bf6abd55d"
          },
          "AyaContent": "وقال فرعون ذروني أقتل موسىٰ وليدع ربه ۖ إني أخاف أن يبدل دينكم أو أن يظهر في الأرض الفساد",
          "AyaNumber": 4159,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cc2f61d12bf6abd55e"
          },
          "AyaContent": "وقال موسىٰ إني عذت بربي وربكم من كل متكبر لا يؤمن بيوم الحساب",
          "AyaNumber": 4160,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cc2f61d12bf6abd55f"
          },
          "AyaContent": "وقال رجل مؤمن من آل فرعون يكتم إيمانه أتقتلون رجلا أن يقول ربي الله وقد جاءكم بالبينات من ربكم ۖ وإن يك كاذبا فعليه كذبه ۖ وإن يك صادقا يصبكم بعض الذي يعدكم ۖ إن الله لا يهدي من هو مسرف كذاب",
          "AyaNumber": 4161,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cd2f61d12bf6abd560"
          },
          "AyaContent": "يا قوم لكم الملك اليوم ظاهرين في الأرض فمن ينصرنا من بأس الله إن جاءنا ۚ قال فرعون ما أريكم إلا ما أرىٰ وما أهديكم إلا سبيل الرشاد",
          "AyaNumber": 4162,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cd2f61d12bf6abd561"
          },
          "AyaContent": "وقال الذي آمن يا قوم إني أخاف عليكم مثل يوم الأحزاب",
          "AyaNumber": 4163,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cd2f61d12bf6abd562"
          },
          "AyaContent": "مثل دأب قوم نوح وعاد وثمود والذين من بعدهم ۚ وما الله يريد ظلما للعباد",
          "AyaNumber": 4164,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cd2f61d12bf6abd563"
          },
          "AyaContent": "ويا قوم إني أخاف عليكم يوم التناد",
          "AyaNumber": 4165,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ce2f61d12bf6abd564"
          },
          "AyaContent": "يوم تولون مدبرين ما لكم من الله من عاصم ۗ ومن يضلل الله فما له من هاد",
          "AyaNumber": 4166,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ce2f61d12bf6abd565"
          },
          "AyaContent": "ولقد جاءكم يوسف من قبل بالبينات فما زلتم في شك مما جاءكم به ۖ حتىٰ إذا هلك قلتم لن يبعث الله من بعده رسولا ۚ كذٰلك يضل الله من هو مسرف مرتاب",
          "AyaNumber": 4167,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ce2f61d12bf6abd566"
          },
          "AyaContent": "الذين يجادلون في آيات الله بغير سلطان أتاهم ۖ كبر مقتا عند الله وعند الذين آمنوا ۚ كذٰلك يطبع الله علىٰ كل قلب متكبر جبار",
          "AyaNumber": 4168,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88ce2f61d12bf6abd567"
          },
          "AyaContent": "وقال فرعون يا هامان ابن لي صرحا لعلي أبلغ الأسباب",
          "AyaNumber": 4169,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cf2f61d12bf6abd568"
          },
          "AyaContent": "أسباب السماوات فأطلع إلىٰ إلٰه موسىٰ وإني لأظنه كاذبا ۚ وكذٰلك زين لفرعون سوء عمله وصد عن السبيل ۚ وما كيد فرعون إلا في تباب",
          "AyaNumber": 4170,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cf2f61d12bf6abd569"
          },
          "AyaContent": "وقال الذي آمن يا قوم اتبعون أهدكم سبيل الرشاد",
          "AyaNumber": 4171,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cf2f61d12bf6abd56a"
          },
          "AyaContent": "يا قوم إنما هٰذه الحياة الدنيا متاع وإن الآخرة هي دار القرار",
          "AyaNumber": 4172,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cf2f61d12bf6abd56b"
          },
          "AyaContent": "من عمل سيئة فلا يجزىٰ إلا مثلها ۖ ومن عمل صالحا من ذكر أو أنثىٰ وهو مؤمن فأولٰئك يدخلون الجنة يرزقون فيها بغير حساب",
          "AyaNumber": 4173,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88cf2f61d12bf6abd56c"
          },
          "AyaContent": "۞ ويا قوم ما لي أدعوكم إلى النجاة وتدعونني إلى النار",
          "AyaNumber": 4174,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d02f61d12bf6abd56d"
          },
          "AyaContent": "تدعونني لأكفر بالله وأشرك به ما ليس لي به علم وأنا أدعوكم إلى العزيز الغفار",
          "AyaNumber": 4175,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d02f61d12bf6abd56e"
          },
          "AyaContent": "لا جرم أنما تدعونني إليه ليس له دعوة في الدنيا ولا في الآخرة وأن مردنا إلى الله وأن المسرفين هم أصحاب النار",
          "AyaNumber": 4176,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d02f61d12bf6abd56f"
          },
          "AyaContent": "فستذكرون ما أقول لكم ۚ وأفوض أمري إلى الله ۚ إن الله بصير بالعباد",
          "AyaNumber": 4177,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d12f61d12bf6abd570"
          },
          "AyaContent": "فوقاه الله سيئات ما مكروا ۖ وحاق بآل فرعون سوء العذاب",
          "AyaNumber": 4178,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d12f61d12bf6abd571"
          },
          "AyaContent": "النار يعرضون عليها غدوا وعشيا ۖ ويوم تقوم الساعة أدخلوا آل فرعون أشد العذاب",
          "AyaNumber": 4179,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d12f61d12bf6abd572"
          },
          "AyaContent": "وإذ يتحاجون في النار فيقول الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا نصيبا من النار",
          "AyaNumber": 4180,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d12f61d12bf6abd573"
          },
          "AyaContent": "قال الذين استكبروا إنا كل فيها إن الله قد حكم بين العباد",
          "AyaNumber": 4181,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d22f61d12bf6abd574"
          },
          "AyaContent": "وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما من العذاب",
          "AyaNumber": 4182,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d22f61d12bf6abd575"
          },
          "AyaContent": "قالوا أولم تك تأتيكم رسلكم بالبينات ۖ قالوا بلىٰ ۚ قالوا فادعوا ۗ وما دعاء الكافرين إلا في ضلال",
          "AyaNumber": 4183,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d22f61d12bf6abd576"
          },
          "AyaContent": "إنا لننصر رسلنا والذين آمنوا في الحياة الدنيا ويوم يقوم الأشهاد",
          "AyaNumber": 4184,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d22f61d12bf6abd577"
          },
          "AyaContent": "يوم لا ينفع الظالمين معذرتهم ۖ ولهم اللعنة ولهم سوء الدار",
          "AyaNumber": 4185,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d32f61d12bf6abd578"
          },
          "AyaContent": "ولقد آتينا موسى الهدىٰ وأورثنا بني إسرائيل الكتاب",
          "AyaNumber": 4186,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d32f61d12bf6abd579"
          },
          "AyaContent": "هدى وذكرىٰ لأولي الألباب",
          "AyaNumber": 4187,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d32f61d12bf6abd57a"
          },
          "AyaContent": "فاصبر إن وعد الله حق واستغفر لذنبك وسبح بحمد ربك بالعشي والإبكار",
          "AyaNumber": 4188,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d42f61d12bf6abd57b"
          },
          "AyaContent": "إن الذين يجادلون في آيات الله بغير سلطان أتاهم ۙ إن في صدورهم إلا كبر ما هم ببالغيه ۚ فاستعذ بالله ۖ إنه هو السميع البصير",
          "AyaNumber": 4189,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d42f61d12bf6abd57c"
          },
          "AyaContent": "لخلق السماوات والأرض أكبر من خلق الناس ولٰكن أكثر الناس لا يعلمون",
          "AyaNumber": 4190,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d42f61d12bf6abd57d"
          },
          "AyaContent": "وما يستوي الأعمىٰ والبصير والذين آمنوا وعملوا الصالحات ولا المسيء ۚ قليلا ما تتذكرون",
          "AyaNumber": 4191,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d42f61d12bf6abd57e"
          },
          "AyaContent": "إن الساعة لآتية لا ريب فيها ولٰكن أكثر الناس لا يؤمنون",
          "AyaNumber": 4192,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d42f61d12bf6abd57f"
          },
          "AyaContent": "وقال ربكم ادعوني أستجب لكم ۚ إن الذين يستكبرون عن عبادتي سيدخلون جهنم داخرين",
          "AyaNumber": 4193,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d52f61d12bf6abd580"
          },
          "AyaContent": "الله الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا ۚ إن الله لذو فضل على الناس ولٰكن أكثر الناس لا يشكرون",
          "AyaNumber": 4194,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d52f61d12bf6abd581"
          },
          "AyaContent": "ذٰلكم الله ربكم خالق كل شيء لا إلٰه إلا هو ۖ فأنىٰ تؤفكون",
          "AyaNumber": 4195,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d52f61d12bf6abd582"
          },
          "AyaContent": "كذٰلك يؤفك الذين كانوا بآيات الله يجحدون",
          "AyaNumber": 4196,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d52f61d12bf6abd583"
          },
          "AyaContent": "الله الذي جعل لكم الأرض قرارا والسماء بناء وصوركم فأحسن صوركم ورزقكم من الطيبات ۚ ذٰلكم الله ربكم ۖ فتبارك الله رب العالمين",
          "AyaNumber": 4197,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d52f61d12bf6abd584"
          },
          "AyaContent": "هو الحي لا إلٰه إلا هو فادعوه مخلصين له الدين ۗ الحمد لله رب العالمين",
          "AyaNumber": 4198,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d62f61d12bf6abd585"
          },
          "AyaContent": "۞ قل إني نهيت أن أعبد الذين تدعون من دون الله لما جاءني البينات من ربي وأمرت أن أسلم لرب العالمين",
          "AyaNumber": 4199,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d62f61d12bf6abd586"
          },
          "AyaContent": "هو الذي خلقكم من تراب ثم من نطفة ثم من علقة ثم يخرجكم طفلا ثم لتبلغوا أشدكم ثم لتكونوا شيوخا ۚ ومنكم من يتوفىٰ من قبل ۖ ولتبلغوا أجلا مسمى ولعلكم تعقلون",
          "AyaNumber": 4200,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d62f61d12bf6abd587"
          },
          "AyaContent": "هو الذي يحيي ويميت ۖ فإذا قضىٰ أمرا فإنما يقول له كن فيكون",
          "AyaNumber": 4201,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d72f61d12bf6abd588"
          },
          "AyaContent": "ألم تر إلى الذين يجادلون في آيات الله أنىٰ يصرفون",
          "AyaNumber": 4202,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d72f61d12bf6abd589"
          },
          "AyaContent": "الذين كذبوا بالكتاب وبما أرسلنا به رسلنا ۖ فسوف يعلمون",
          "AyaNumber": 4203,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d72f61d12bf6abd58a"
          },
          "AyaContent": "إذ الأغلال في أعناقهم والسلاسل يسحبون",
          "AyaNumber": 4204,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d72f61d12bf6abd58b"
          },
          "AyaContent": "في الحميم ثم في النار يسجرون",
          "AyaNumber": 4205,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d82f61d12bf6abd58c"
          },
          "AyaContent": "ثم قيل لهم أين ما كنتم تشركون",
          "AyaNumber": 4206,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d82f61d12bf6abd58d"
          },
          "AyaContent": "من دون الله ۖ قالوا ضلوا عنا بل لم نكن ندعو من قبل شيئا ۚ كذٰلك يضل الله الكافرين",
          "AyaNumber": 4207,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d82f61d12bf6abd58e"
          },
          "AyaContent": "ذٰلكم بما كنتم تفرحون في الأرض بغير الحق وبما كنتم تمرحون",
          "AyaNumber": 4208,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d82f61d12bf6abd58f"
          },
          "AyaContent": "ادخلوا أبواب جهنم خالدين فيها ۖ فبئس مثوى المتكبرين",
          "AyaNumber": 4209,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d92f61d12bf6abd590"
          },
          "AyaContent": "فاصبر إن وعد الله حق ۚ فإما نرينك بعض الذي نعدهم أو نتوفينك فإلينا يرجعون",
          "AyaNumber": 4210,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d92f61d12bf6abd591"
          },
          "AyaContent": "ولقد أرسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك ۗ وما كان لرسول أن يأتي بآية إلا بإذن الله ۚ فإذا جاء أمر الله قضي بالحق وخسر هنالك المبطلون",
          "AyaNumber": 4211,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d92f61d12bf6abd592"
          },
          "AyaContent": "الله الذي جعل لكم الأنعام لتركبوا منها ومنها تأكلون",
          "AyaNumber": 4212,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d92f61d12bf6abd593"
          },
          "AyaContent": "ولكم فيها منافع ولتبلغوا عليها حاجة في صدوركم وعليها وعلى الفلك تحملون",
          "AyaNumber": 4213,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88d92f61d12bf6abd594"
          },
          "AyaContent": "ويريكم آياته فأي آيات الله تنكرون",
          "AyaNumber": 4214,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88da2f61d12bf6abd595"
          },
          "AyaContent": "أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ۚ كانوا أكثر منهم وأشد قوة وآثارا في الأرض فما أغنىٰ عنهم ما كانوا يكسبون",
          "AyaNumber": 4215,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88da2f61d12bf6abd596"
          },
          "AyaContent": "فلما جاءتهم رسلهم بالبينات فرحوا بما عندهم من العلم وحاق بهم ما كانوا به يستهزئون",
          "AyaNumber": 4216,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88da2f61d12bf6abd597"
          },
          "AyaContent": "فلما رأوا بأسنا قالوا آمنا بالله وحده وكفرنا بما كنا به مشركين",
          "AyaNumber": 4217,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88da2f61d12bf6abd598"
          },
          "AyaContent": "فلم يك ينفعهم إيمانهم لما رأوا بأسنا ۖ سنت الله التي قد خلت في عباده ۖ وخسر هنالك الكافرون",
          "AyaNumber": 4218,
          "SurahName": "سورة غافر",
          "SurahNumber": 40
        },{
          "_id": {
            "$oid": "5ecd88da2f61d12bf6abd599"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم حم",
          "AyaNumber": 4219,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88db2f61d12bf6abd59a"
          },
          "AyaContent": "تنزيل من الرحمٰن الرحيم",
          "AyaNumber": 4220,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88db2f61d12bf6abd59b"
          },
          "AyaContent": "كتاب فصلت آياته قرآنا عربيا لقوم يعلمون",
          "AyaNumber": 4221,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88db2f61d12bf6abd59c"
          },
          "AyaContent": "بشيرا ونذيرا فأعرض أكثرهم فهم لا يسمعون",
          "AyaNumber": 4222,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88db2f61d12bf6abd59d"
          },
          "AyaContent": "وقالوا قلوبنا في أكنة مما تدعونا إليه وفي آذاننا وقر ومن بيننا وبينك حجاب فاعمل إننا عاملون",
          "AyaNumber": 4223,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88db2f61d12bf6abd59e"
          },
          "AyaContent": "قل إنما أنا بشر مثلكم يوحىٰ إلي أنما إلٰهكم إلٰه واحد فاستقيموا إليه واستغفروه ۗ وويل للمشركين",
          "AyaNumber": 4224,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dc2f61d12bf6abd59f"
          },
          "AyaContent": "الذين لا يؤتون الزكاة وهم بالآخرة هم كافرون",
          "AyaNumber": 4225,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dc2f61d12bf6abd5a0"
          },
          "AyaContent": "إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون",
          "AyaNumber": 4226,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dc2f61d12bf6abd5a1"
          },
          "AyaContent": "۞ قل أئنكم لتكفرون بالذي خلق الأرض في يومين وتجعلون له أندادا ۚ ذٰلك رب العالمين",
          "AyaNumber": 4227,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dc2f61d12bf6abd5a2"
          },
          "AyaContent": "وجعل فيها رواسي من فوقها وبارك فيها وقدر فيها أقواتها في أربعة أيام سواء للسائلين",
          "AyaNumber": 4228,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dc2f61d12bf6abd5a3"
          },
          "AyaContent": "ثم استوىٰ إلى السماء وهي دخان فقال لها وللأرض ائتيا طوعا أو كرها قالتا أتينا طائعين",
          "AyaNumber": 4229,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dc2f61d12bf6abd5a4"
          },
          "AyaContent": "فقضاهن سبع سماوات في يومين وأوحىٰ في كل سماء أمرها ۚ وزينا السماء الدنيا بمصابيح وحفظا ۚ ذٰلك تقدير العزيز العليم",
          "AyaNumber": 4230,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dd2f61d12bf6abd5a5"
          },
          "AyaContent": "فإن أعرضوا فقل أنذرتكم صاعقة مثل صاعقة عاد وثمود",
          "AyaNumber": 4231,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dd2f61d12bf6abd5a6"
          },
          "AyaContent": "إذ جاءتهم الرسل من بين أيديهم ومن خلفهم ألا تعبدوا إلا الله ۖ قالوا لو شاء ربنا لأنزل ملائكة فإنا بما أرسلتم به كافرون",
          "AyaNumber": 4232,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dd2f61d12bf6abd5a7"
          },
          "AyaContent": "فأما عاد فاستكبروا في الأرض بغير الحق وقالوا من أشد منا قوة ۖ أولم يروا أن الله الذي خلقهم هو أشد منهم قوة ۖ وكانوا بآياتنا يجحدون",
          "AyaNumber": 4233,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dd2f61d12bf6abd5a8"
          },
          "AyaContent": "فأرسلنا عليهم ريحا صرصرا في أيام نحسات لنذيقهم عذاب الخزي في الحياة الدنيا ۖ ولعذاب الآخرة أخزىٰ ۖ وهم لا ينصرون",
          "AyaNumber": 4234,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dd2f61d12bf6abd5a9"
          },
          "AyaContent": "وأما ثمود فهديناهم فاستحبوا العمىٰ على الهدىٰ فأخذتهم صاعقة العذاب الهون بما كانوا يكسبون",
          "AyaNumber": 4235,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88dd2f61d12bf6abd5aa"
          },
          "AyaContent": "ونجينا الذين آمنوا وكانوا يتقون",
          "AyaNumber": 4236,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88de2f61d12bf6abd5ab"
          },
          "AyaContent": "ويوم يحشر أعداء الله إلى النار فهم يوزعون",
          "AyaNumber": 4237,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88de2f61d12bf6abd5ac"
          },
          "AyaContent": "حتىٰ إذا ما جاءوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون",
          "AyaNumber": 4238,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88de2f61d12bf6abd5ad"
          },
          "AyaContent": "وقالوا لجلودهم لم شهدتم علينا ۖ قالوا أنطقنا الله الذي أنطق كل شيء وهو خلقكم أول مرة وإليه ترجعون",
          "AyaNumber": 4239,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88de2f61d12bf6abd5ae"
          },
          "AyaContent": "وما كنتم تستترون أن يشهد عليكم سمعكم ولا أبصاركم ولا جلودكم ولٰكن ظننتم أن الله لا يعلم كثيرا مما تعملون",
          "AyaNumber": 4240,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88de2f61d12bf6abd5af"
          },
          "AyaContent": "وذٰلكم ظنكم الذي ظننتم بربكم أرداكم فأصبحتم من الخاسرين",
          "AyaNumber": 4241,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88de2f61d12bf6abd5b0"
          },
          "AyaContent": "فإن يصبروا فالنار مثوى لهم ۖ وإن يستعتبوا فما هم من المعتبين",
          "AyaNumber": 4242,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88de2f61d12bf6abd5b1"
          },
          "AyaContent": "۞ وقيضنا لهم قرناء فزينوا لهم ما بين أيديهم وما خلفهم وحق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس ۖ إنهم كانوا خاسرين",
          "AyaNumber": 4243,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88df2f61d12bf6abd5b2"
          },
          "AyaContent": "وقال الذين كفروا لا تسمعوا لهٰذا القرآن والغوا فيه لعلكم تغلبون",
          "AyaNumber": 4244,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88df2f61d12bf6abd5b3"
          },
          "AyaContent": "فلنذيقن الذين كفروا عذابا شديدا ولنجزينهم أسوأ الذي كانوا يعملون",
          "AyaNumber": 4245,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88df2f61d12bf6abd5b4"
          },
          "AyaContent": "ذٰلك جزاء أعداء الله النار ۖ لهم فيها دار الخلد ۖ جزاء بما كانوا بآياتنا يجحدون",
          "AyaNumber": 4246,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88df2f61d12bf6abd5b5"
          },
          "AyaContent": "وقال الذين كفروا ربنا أرنا اللذين أضلانا من الجن والإنس نجعلهما تحت أقدامنا ليكونا من الأسفلين",
          "AyaNumber": 4247,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88df2f61d12bf6abd5b6"
          },
          "AyaContent": "إن الذين قالوا ربنا الله ثم استقاموا تتنزل عليهم الملائكة ألا تخافوا ولا تحزنوا وأبشروا بالجنة التي كنتم توعدون",
          "AyaNumber": 4248,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88df2f61d12bf6abd5b7"
          },
          "AyaContent": "نحن أولياؤكم في الحياة الدنيا وفي الآخرة ۖ ولكم فيها ما تشتهي أنفسكم ولكم فيها ما تدعون",
          "AyaNumber": 4249,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e02f61d12bf6abd5b8"
          },
          "AyaContent": "نزلا من غفور رحيم",
          "AyaNumber": 4250,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e02f61d12bf6abd5b9"
          },
          "AyaContent": "ومن أحسن قولا ممن دعا إلى الله وعمل صالحا وقال إنني من المسلمين",
          "AyaNumber": 4251,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e02f61d12bf6abd5ba"
          },
          "AyaContent": "ولا تستوي الحسنة ولا السيئة ۚ ادفع بالتي هي أحسن فإذا الذي بينك وبينه عداوة كأنه ولي حميم",
          "AyaNumber": 4252,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e02f61d12bf6abd5bb"
          },
          "AyaContent": "وما يلقاها إلا الذين صبروا وما يلقاها إلا ذو حظ عظيم",
          "AyaNumber": 4253,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e02f61d12bf6abd5bc"
          },
          "AyaContent": "وإما ينزغنك من الشيطان نزغ فاستعذ بالله ۖ إنه هو السميع العليم",
          "AyaNumber": 4254,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e02f61d12bf6abd5bd"
          },
          "AyaContent": "ومن آياته الليل والنهار والشمس والقمر ۚ لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن إن كنتم إياه تعبدون",
          "AyaNumber": 4255,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e02f61d12bf6abd5be"
          },
          "AyaContent": "فإن استكبروا فالذين عند ربك يسبحون له بالليل والنهار وهم لا يسأمون ۩",
          "AyaNumber": 4256,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e12f61d12bf6abd5bf"
          },
          "AyaContent": "ومن آياته أنك ترى الأرض خاشعة فإذا أنزلنا عليها الماء اهتزت وربت ۚ إن الذي أحياها لمحيي الموتىٰ ۚ إنه علىٰ كل شيء قدير",
          "AyaNumber": 4257,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e12f61d12bf6abd5c0"
          },
          "AyaContent": "إن الذين يلحدون في آياتنا لا يخفون علينا ۗ أفمن يلقىٰ في النار خير أم من يأتي آمنا يوم القيامة ۚ اعملوا ما شئتم ۖ إنه بما تعملون بصير",
          "AyaNumber": 4258,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e12f61d12bf6abd5c1"
          },
          "AyaContent": "إن الذين كفروا بالذكر لما جاءهم ۖ وإنه لكتاب عزيز",
          "AyaNumber": 4259,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e12f61d12bf6abd5c2"
          },
          "AyaContent": "لا يأتيه الباطل من بين يديه ولا من خلفه ۖ تنزيل من حكيم حميد",
          "AyaNumber": 4260,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e12f61d12bf6abd5c3"
          },
          "AyaContent": "ما يقال لك إلا ما قد قيل للرسل من قبلك ۚ إن ربك لذو مغفرة وذو عقاب أليم",
          "AyaNumber": 4261,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e12f61d12bf6abd5c4"
          },
          "AyaContent": "ولو جعلناه قرآنا أعجميا لقالوا لولا فصلت آياته ۖ أأعجمي وعربي ۗ قل هو للذين آمنوا هدى وشفاء ۖ والذين لا يؤمنون في آذانهم وقر وهو عليهم عمى ۚ أولٰئك ينادون من مكان بعيد",
          "AyaNumber": 4262,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e22f61d12bf6abd5c5"
          },
          "AyaContent": "ولقد آتينا موسى الكتاب فاختلف فيه ۗ ولولا كلمة سبقت من ربك لقضي بينهم ۚ وإنهم لفي شك منه مريب",
          "AyaNumber": 4263,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e22f61d12bf6abd5c6"
          },
          "AyaContent": "من عمل صالحا فلنفسه ۖ ومن أساء فعليها ۗ وما ربك بظلام للعبيد",
          "AyaNumber": 4264,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e22f61d12bf6abd5c7"
          },
          "AyaContent": "۞ إليه يرد علم الساعة ۚ وما تخرج من ثمرات من أكمامها وما تحمل من أنثىٰ ولا تضع إلا بعلمه ۚ ويوم يناديهم أين شركائي قالوا آذناك ما منا من شهيد",
          "AyaNumber": 4265,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e22f61d12bf6abd5c8"
          },
          "AyaContent": "وضل عنهم ما كانوا يدعون من قبل ۖ وظنوا ما لهم من محيص",
          "AyaNumber": 4266,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e22f61d12bf6abd5c9"
          },
          "AyaContent": "لا يسأم الإنسان من دعاء الخير وإن مسه الشر فيئوس قنوط",
          "AyaNumber": 4267,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e22f61d12bf6abd5ca"
          },
          "AyaContent": "ولئن أذقناه رحمة منا من بعد ضراء مسته ليقولن هٰذا لي وما أظن الساعة قائمة ولئن رجعت إلىٰ ربي إن لي عنده للحسنىٰ ۚ فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ",
          "AyaNumber": 4268,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5cb"
          },
          "AyaContent": "وإذا أنعمنا على الإنسان أعرض ونأىٰ بجانبه وإذا مسه الشر فذو دعاء عريض",
          "AyaNumber": 4269,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5cc"
          },
          "AyaContent": "قل أرأيتم إن كان من عند الله ثم كفرتم به من أضل ممن هو في شقاق بعيد",
          "AyaNumber": 4270,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5cd"
          },
          "AyaContent": "سنريهم آياتنا في الآفاق وفي أنفسهم حتىٰ يتبين لهم أنه الحق ۗ أولم يكف بربك أنه علىٰ كل شيء شهيد",
          "AyaNumber": 4271,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5ce"
          },
          "AyaContent": "ألا إنهم في مرية من لقاء ربهم ۗ ألا إنه بكل شيء محيط",
          "AyaNumber": 4272,
          "SurahName": "سورة فصلت",
          "SurahNumber": 41
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5cf"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم حم",
          "AyaNumber": 4273,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5d0"
          },
          "AyaContent": "عسق",
          "AyaNumber": 4274,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5d1"
          },
          "AyaContent": "كذٰلك يوحي إليك وإلى الذين من قبلك الله العزيز الحكيم",
          "AyaNumber": 4275,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e32f61d12bf6abd5d2"
          },
          "AyaContent": "له ما في السماوات وما في الأرض ۖ وهو العلي العظيم",
          "AyaNumber": 4276,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e42f61d12bf6abd5d3"
          },
          "AyaContent": "تكاد السماوات يتفطرن من فوقهن ۚ والملائكة يسبحون بحمد ربهم ويستغفرون لمن في الأرض ۗ ألا إن الله هو الغفور الرحيم",
          "AyaNumber": 4277,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e42f61d12bf6abd5d4"
          },
          "AyaContent": "والذين اتخذوا من دونه أولياء الله حفيظ عليهم وما أنت عليهم بوكيل",
          "AyaNumber": 4278,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e42f61d12bf6abd5d5"
          },
          "AyaContent": "وكذٰلك أوحينا إليك قرآنا عربيا لتنذر أم القرىٰ ومن حولها وتنذر يوم الجمع لا ريب فيه ۚ فريق في الجنة وفريق في السعير",
          "AyaNumber": 4279,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e42f61d12bf6abd5d6"
          },
          "AyaContent": "ولو شاء الله لجعلهم أمة واحدة ولٰكن يدخل من يشاء في رحمته ۚ والظالمون ما لهم من ولي ولا نصير",
          "AyaNumber": 4280,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e42f61d12bf6abd5d7"
          },
          "AyaContent": "أم اتخذوا من دونه أولياء ۖ فالله هو الولي وهو يحيي الموتىٰ وهو علىٰ كل شيء قدير",
          "AyaNumber": 4281,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e52f61d12bf6abd5d8"
          },
          "AyaContent": "وما اختلفتم فيه من شيء فحكمه إلى الله ۚ ذٰلكم الله ربي عليه توكلت وإليه أنيب",
          "AyaNumber": 4282,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e52f61d12bf6abd5d9"
          },
          "AyaContent": "فاطر السماوات والأرض ۚ جعل لكم من أنفسكم أزواجا ومن الأنعام أزواجا ۖ يذرؤكم فيه ۚ ليس كمثله شيء ۖ وهو السميع البصير",
          "AyaNumber": 4283,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e52f61d12bf6abd5da"
          },
          "AyaContent": "له مقاليد السماوات والأرض ۖ يبسط الرزق لمن يشاء ويقدر ۚ إنه بكل شيء عليم",
          "AyaNumber": 4284,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e52f61d12bf6abd5db"
          },
          "AyaContent": "۞ شرع لكم من الدين ما وصىٰ به نوحا والذي أوحينا إليك وما وصينا به إبراهيم وموسىٰ وعيسىٰ ۖ أن أقيموا الدين ولا تتفرقوا فيه ۚ كبر على المشركين ما تدعوهم إليه ۚ الله يجتبي إليه من يشاء ويهدي إليه من ينيب",
          "AyaNumber": 4285,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e52f61d12bf6abd5dc"
          },
          "AyaContent": "وما تفرقوا إلا من بعد ما جاءهم العلم بغيا بينهم ۚ ولولا كلمة سبقت من ربك إلىٰ أجل مسمى لقضي بينهم ۚ وإن الذين أورثوا الكتاب من بعدهم لفي شك منه مريب",
          "AyaNumber": 4286,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e62f61d12bf6abd5dd"
          },
          "AyaContent": "فلذٰلك فادع ۖ واستقم كما أمرت ۖ ولا تتبع أهواءهم ۖ وقل آمنت بما أنزل الله من كتاب ۖ وأمرت لأعدل بينكم ۖ الله ربنا وربكم ۖ لنا أعمالنا ولكم أعمالكم ۖ لا حجة بيننا وبينكم ۖ الله يجمع بيننا ۖ وإليه المصير",
          "AyaNumber": 4287,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e62f61d12bf6abd5de"
          },
          "AyaContent": "والذين يحاجون في الله من بعد ما استجيب له حجتهم داحضة عند ربهم وعليهم غضب ولهم عذاب شديد",
          "AyaNumber": 4288,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e62f61d12bf6abd5df"
          },
          "AyaContent": "الله الذي أنزل الكتاب بالحق والميزان ۗ وما يدريك لعل الساعة قريب",
          "AyaNumber": 4289,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e62f61d12bf6abd5e0"
          },
          "AyaContent": "يستعجل بها الذين لا يؤمنون بها ۖ والذين آمنوا مشفقون منها ويعلمون أنها الحق ۗ ألا إن الذين يمارون في الساعة لفي ضلال بعيد",
          "AyaNumber": 4290,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e72f61d12bf6abd5e1"
          },
          "AyaContent": "الله لطيف بعباده يرزق من يشاء ۖ وهو القوي العزيز",
          "AyaNumber": 4291,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e72f61d12bf6abd5e2"
          },
          "AyaContent": "من كان يريد حرث الآخرة نزد له في حرثه ۖ ومن كان يريد حرث الدنيا نؤته منها وما له في الآخرة من نصيب",
          "AyaNumber": 4292,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e72f61d12bf6abd5e3"
          },
          "AyaContent": "أم لهم شركاء شرعوا لهم من الدين ما لم يأذن به الله ۚ ولولا كلمة الفصل لقضي بينهم ۗ وإن الظالمين لهم عذاب أليم",
          "AyaNumber": 4293,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e72f61d12bf6abd5e4"
          },
          "AyaContent": "ترى الظالمين مشفقين مما كسبوا وهو واقع بهم ۗ والذين آمنوا وعملوا الصالحات في روضات الجنات ۖ لهم ما يشاءون عند ربهم ۚ ذٰلك هو الفضل الكبير",
          "AyaNumber": 4294,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e72f61d12bf6abd5e5"
          },
          "AyaContent": "ذٰلك الذي يبشر الله عباده الذين آمنوا وعملوا الصالحات ۗ قل لا أسألكم عليه أجرا إلا المودة في القربىٰ ۗ ومن يقترف حسنة نزد له فيها حسنا ۚ إن الله غفور شكور",
          "AyaNumber": 4295,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e72f61d12bf6abd5e6"
          },
          "AyaContent": "أم يقولون افترىٰ على الله كذبا ۖ فإن يشإ الله يختم علىٰ قلبك ۗ ويمح الله الباطل ويحق الحق بكلماته ۚ إنه عليم بذات الصدور",
          "AyaNumber": 4296,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e82f61d12bf6abd5e7"
          },
          "AyaContent": "وهو الذي يقبل التوبة عن عباده ويعفو عن السيئات ويعلم ما تفعلون",
          "AyaNumber": 4297,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e82f61d12bf6abd5e8"
          },
          "AyaContent": "ويستجيب الذين آمنوا وعملوا الصالحات ويزيدهم من فضله ۚ والكافرون لهم عذاب شديد",
          "AyaNumber": 4298,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e82f61d12bf6abd5e9"
          },
          "AyaContent": "۞ ولو بسط الله الرزق لعباده لبغوا في الأرض ولٰكن ينزل بقدر ما يشاء ۚ إنه بعباده خبير بصير",
          "AyaNumber": 4299,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e82f61d12bf6abd5ea"
          },
          "AyaContent": "وهو الذي ينزل الغيث من بعد ما قنطوا وينشر رحمته ۚ وهو الولي الحميد",
          "AyaNumber": 4300,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e82f61d12bf6abd5eb"
          },
          "AyaContent": "ومن آياته خلق السماوات والأرض وما بث فيهما من دابة ۚ وهو علىٰ جمعهم إذا يشاء قدير",
          "AyaNumber": 4301,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e92f61d12bf6abd5ec"
          },
          "AyaContent": "وما أصابكم من مصيبة فبما كسبت أيديكم ويعفو عن كثير",
          "AyaNumber": 4302,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e92f61d12bf6abd5ed"
          },
          "AyaContent": "وما أنتم بمعجزين في الأرض ۖ وما لكم من دون الله من ولي ولا نصير",
          "AyaNumber": 4303,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e92f61d12bf6abd5ee"
          },
          "AyaContent": "ومن آياته الجوار في البحر كالأعلام",
          "AyaNumber": 4304,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e92f61d12bf6abd5ef"
          },
          "AyaContent": "إن يشأ يسكن الريح فيظللن رواكد علىٰ ظهره ۚ إن في ذٰلك لآيات لكل صبار شكور",
          "AyaNumber": 4305,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e92f61d12bf6abd5f0"
          },
          "AyaContent": "أو يوبقهن بما كسبوا ويعف عن كثير",
          "AyaNumber": 4306,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88e92f61d12bf6abd5f1"
          },
          "AyaContent": "ويعلم الذين يجادلون في آياتنا ما لهم من محيص",
          "AyaNumber": 4307,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ea2f61d12bf6abd5f2"
          },
          "AyaContent": "فما أوتيتم من شيء فمتاع الحياة الدنيا ۖ وما عند الله خير وأبقىٰ للذين آمنوا وعلىٰ ربهم يتوكلون",
          "AyaNumber": 4308,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ea2f61d12bf6abd5f3"
          },
          "AyaContent": "والذين يجتنبون كبائر الإثم والفواحش وإذا ما غضبوا هم يغفرون",
          "AyaNumber": 4309,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ea2f61d12bf6abd5f4"
          },
          "AyaContent": "والذين استجابوا لربهم وأقاموا الصلاة وأمرهم شورىٰ بينهم ومما رزقناهم ينفقون",
          "AyaNumber": 4310,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ea2f61d12bf6abd5f5"
          },
          "AyaContent": "والذين إذا أصابهم البغي هم ينتصرون",
          "AyaNumber": 4311,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ea2f61d12bf6abd5f6"
          },
          "AyaContent": "وجزاء سيئة سيئة مثلها ۖ فمن عفا وأصلح فأجره على الله ۚ إنه لا يحب الظالمين",
          "AyaNumber": 4312,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ea2f61d12bf6abd5f7"
          },
          "AyaContent": "ولمن انتصر بعد ظلمه فأولٰئك ما عليهم من سبيل",
          "AyaNumber": 4313,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ea2f61d12bf6abd5f8"
          },
          "AyaContent": "إنما السبيل على الذين يظلمون الناس ويبغون في الأرض بغير الحق ۚ أولٰئك لهم عذاب أليم",
          "AyaNumber": 4314,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88eb2f61d12bf6abd5f9"
          },
          "AyaContent": "ولمن صبر وغفر إن ذٰلك لمن عزم الأمور",
          "AyaNumber": 4315,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88eb2f61d12bf6abd5fa"
          },
          "AyaContent": "ومن يضلل الله فما له من ولي من بعده ۗ وترى الظالمين لما رأوا العذاب يقولون هل إلىٰ مرد من سبيل",
          "AyaNumber": 4316,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88eb2f61d12bf6abd5fb"
          },
          "AyaContent": "وتراهم يعرضون عليها خاشعين من الذل ينظرون من طرف خفي ۗ وقال الذين آمنوا إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ۗ ألا إن الظالمين في عذاب مقيم",
          "AyaNumber": 4317,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88eb2f61d12bf6abd5fc"
          },
          "AyaContent": "وما كان لهم من أولياء ينصرونهم من دون الله ۗ ومن يضلل الله فما له من سبيل",
          "AyaNumber": 4318,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88eb2f61d12bf6abd5fd"
          },
          "AyaContent": "استجيبوا لربكم من قبل أن يأتي يوم لا مرد له من الله ۚ ما لكم من ملجإ يومئذ وما لكم من نكير",
          "AyaNumber": 4319,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88eb2f61d12bf6abd5fe"
          },
          "AyaContent": "فإن أعرضوا فما أرسلناك عليهم حفيظا ۖ إن عليك إلا البلاغ ۗ وإنا إذا أذقنا الإنسان منا رحمة فرح بها ۖ وإن تصبهم سيئة بما قدمت أيديهم فإن الإنسان كفور",
          "AyaNumber": 4320,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88eb2f61d12bf6abd5ff"
          },
          "AyaContent": "لله ملك السماوات والأرض ۚ يخلق ما يشاء ۚ يهب لمن يشاء إناثا ويهب لمن يشاء الذكور",
          "AyaNumber": 4321,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ec2f61d12bf6abd600"
          },
          "AyaContent": "أو يزوجهم ذكرانا وإناثا ۖ ويجعل من يشاء عقيما ۚ إنه عليم قدير",
          "AyaNumber": 4322,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ec2f61d12bf6abd601"
          },
          "AyaContent": "۞ وما كان لبشر أن يكلمه الله إلا وحيا أو من وراء حجاب أو يرسل رسولا فيوحي بإذنه ما يشاء ۚ إنه علي حكيم",
          "AyaNumber": 4323,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ec2f61d12bf6abd602"
          },
          "AyaContent": "وكذٰلك أوحينا إليك روحا من أمرنا ۚ ما كنت تدري ما الكتاب ولا الإيمان ولٰكن جعلناه نورا نهدي به من نشاء من عبادنا ۚ وإنك لتهدي إلىٰ صراط مستقيم",
          "AyaNumber": 4324,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ec2f61d12bf6abd603"
          },
          "AyaContent": "صراط الله الذي له ما في السماوات وما في الأرض ۗ ألا إلى الله تصير الأمور",
          "AyaNumber": 4325,
          "SurahName": "سورة الشورى",
          "SurahNumber": 42
        },{
          "_id": {
            "$oid": "5ecd88ec2f61d12bf6abd604"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم حم",
          "AyaNumber": 4326,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ec2f61d12bf6abd605"
          },
          "AyaContent": "والكتاب المبين",
          "AyaNumber": 4327,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ec2f61d12bf6abd606"
          },
          "AyaContent": "إنا جعلناه قرآنا عربيا لعلكم تعقلون",
          "AyaNumber": 4328,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ed2f61d12bf6abd607"
          },
          "AyaContent": "وإنه في أم الكتاب لدينا لعلي حكيم",
          "AyaNumber": 4329,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ed2f61d12bf6abd608"
          },
          "AyaContent": "أفنضرب عنكم الذكر صفحا أن كنتم قوما مسرفين",
          "AyaNumber": 4330,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ed2f61d12bf6abd609"
          },
          "AyaContent": "وكم أرسلنا من نبي في الأولين",
          "AyaNumber": 4331,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ed2f61d12bf6abd60a"
          },
          "AyaContent": "وما يأتيهم من نبي إلا كانوا به يستهزئون",
          "AyaNumber": 4332,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ed2f61d12bf6abd60b"
          },
          "AyaContent": "فأهلكنا أشد منهم بطشا ومضىٰ مثل الأولين",
          "AyaNumber": 4333,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ed2f61d12bf6abd60c"
          },
          "AyaContent": "ولئن سألتهم من خلق السماوات والأرض ليقولن خلقهن العزيز العليم",
          "AyaNumber": 4334,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ed2f61d12bf6abd60d"
          },
          "AyaContent": "الذي جعل لكم الأرض مهدا وجعل لكم فيها سبلا لعلكم تهتدون",
          "AyaNumber": 4335,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ee2f61d12bf6abd60e"
          },
          "AyaContent": "والذي نزل من السماء ماء بقدر فأنشرنا به بلدة ميتا ۚ كذٰلك تخرجون",
          "AyaNumber": 4336,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ee2f61d12bf6abd60f"
          },
          "AyaContent": "والذي خلق الأزواج كلها وجعل لكم من الفلك والأنعام ما تركبون",
          "AyaNumber": 4337,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ee2f61d12bf6abd610"
          },
          "AyaContent": "لتستووا علىٰ ظهوره ثم تذكروا نعمة ربكم إذا استويتم عليه وتقولوا سبحان الذي سخر لنا هٰذا وما كنا له مقرنين",
          "AyaNumber": 4338,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ee2f61d12bf6abd611"
          },
          "AyaContent": "وإنا إلىٰ ربنا لمنقلبون",
          "AyaNumber": 4339,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ee2f61d12bf6abd612"
          },
          "AyaContent": "وجعلوا له من عباده جزءا ۚ إن الإنسان لكفور مبين",
          "AyaNumber": 4340,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ee2f61d12bf6abd613"
          },
          "AyaContent": "أم اتخذ مما يخلق بنات وأصفاكم بالبنين",
          "AyaNumber": 4341,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ef2f61d12bf6abd614"
          },
          "AyaContent": "وإذا بشر أحدهم بما ضرب للرحمٰن مثلا ظل وجهه مسودا وهو كظيم",
          "AyaNumber": 4342,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ef2f61d12bf6abd615"
          },
          "AyaContent": "أومن ينشأ في الحلية وهو في الخصام غير مبين",
          "AyaNumber": 4343,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ef2f61d12bf6abd616"
          },
          "AyaContent": "وجعلوا الملائكة الذين هم عباد الرحمٰن إناثا ۚ أشهدوا خلقهم ۚ ستكتب شهادتهم ويسألون",
          "AyaNumber": 4344,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ef2f61d12bf6abd617"
          },
          "AyaContent": "وقالوا لو شاء الرحمٰن ما عبدناهم ۗ ما لهم بذٰلك من علم ۖ إن هم إلا يخرصون",
          "AyaNumber": 4345,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ef2f61d12bf6abd618"
          },
          "AyaContent": "أم آتيناهم كتابا من قبله فهم به مستمسكون",
          "AyaNumber": 4346,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88ef2f61d12bf6abd619"
          },
          "AyaContent": "بل قالوا إنا وجدنا آباءنا علىٰ أمة وإنا علىٰ آثارهم مهتدون",
          "AyaNumber": 4347,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f02f61d12bf6abd61a"
          },
          "AyaContent": "وكذٰلك ما أرسلنا من قبلك في قرية من نذير إلا قال مترفوها إنا وجدنا آباءنا علىٰ أمة وإنا علىٰ آثارهم مقتدون",
          "AyaNumber": 4348,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f02f61d12bf6abd61b"
          },
          "AyaContent": "۞ قال أولو جئتكم بأهدىٰ مما وجدتم عليه آباءكم ۖ قالوا إنا بما أرسلتم به كافرون",
          "AyaNumber": 4349,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f02f61d12bf6abd61c"
          },
          "AyaContent": "فانتقمنا منهم ۖ فانظر كيف كان عاقبة المكذبين",
          "AyaNumber": 4350,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f02f61d12bf6abd61d"
          },
          "AyaContent": "وإذ قال إبراهيم لأبيه وقومه إنني براء مما تعبدون",
          "AyaNumber": 4351,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f02f61d12bf6abd61e"
          },
          "AyaContent": "إلا الذي فطرني فإنه سيهدين",
          "AyaNumber": 4352,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f12f61d12bf6abd61f"
          },
          "AyaContent": "وجعلها كلمة باقية في عقبه لعلهم يرجعون",
          "AyaNumber": 4353,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f12f61d12bf6abd620"
          },
          "AyaContent": "بل متعت هٰؤلاء وآباءهم حتىٰ جاءهم الحق ورسول مبين",
          "AyaNumber": 4354,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f12f61d12bf6abd621"
          },
          "AyaContent": "ولما جاءهم الحق قالوا هٰذا سحر وإنا به كافرون",
          "AyaNumber": 4355,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f12f61d12bf6abd622"
          },
          "AyaContent": "وقالوا لولا نزل هٰذا القرآن علىٰ رجل من القريتين عظيم",
          "AyaNumber": 4356,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f12f61d12bf6abd623"
          },
          "AyaContent": "أهم يقسمون رحمت ربك ۚ نحن قسمنا بينهم معيشتهم في الحياة الدنيا ۚ ورفعنا بعضهم فوق بعض درجات ليتخذ بعضهم بعضا سخريا ۗ ورحمت ربك خير مما يجمعون",
          "AyaNumber": 4357,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f12f61d12bf6abd624"
          },
          "AyaContent": "ولولا أن يكون الناس أمة واحدة لجعلنا لمن يكفر بالرحمٰن لبيوتهم سقفا من فضة ومعارج عليها يظهرون",
          "AyaNumber": 4358,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f22f61d12bf6abd625"
          },
          "AyaContent": "ولبيوتهم أبوابا وسررا عليها يتكئون",
          "AyaNumber": 4359,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f22f61d12bf6abd626"
          },
          "AyaContent": "وزخرفا ۚ وإن كل ذٰلك لما متاع الحياة الدنيا ۚ والآخرة عند ربك للمتقين",
          "AyaNumber": 4360,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f22f61d12bf6abd627"
          },
          "AyaContent": "ومن يعش عن ذكر الرحمٰن نقيض له شيطانا فهو له قرين",
          "AyaNumber": 4361,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f22f61d12bf6abd628"
          },
          "AyaContent": "وإنهم ليصدونهم عن السبيل ويحسبون أنهم مهتدون",
          "AyaNumber": 4362,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f22f61d12bf6abd629"
          },
          "AyaContent": "حتىٰ إذا جاءنا قال يا ليت بيني وبينك بعد المشرقين فبئس القرين",
          "AyaNumber": 4363,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f22f61d12bf6abd62a"
          },
          "AyaContent": "ولن ينفعكم اليوم إذ ظلمتم أنكم في العذاب مشتركون",
          "AyaNumber": 4364,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f22f61d12bf6abd62b"
          },
          "AyaContent": "أفأنت تسمع الصم أو تهدي العمي ومن كان في ضلال مبين",
          "AyaNumber": 4365,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f32f61d12bf6abd62c"
          },
          "AyaContent": "فإما نذهبن بك فإنا منهم منتقمون",
          "AyaNumber": 4366,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f32f61d12bf6abd62d"
          },
          "AyaContent": "أو نرينك الذي وعدناهم فإنا عليهم مقتدرون",
          "AyaNumber": 4367,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f32f61d12bf6abd62e"
          },
          "AyaContent": "فاستمسك بالذي أوحي إليك ۖ إنك علىٰ صراط مستقيم",
          "AyaNumber": 4368,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f32f61d12bf6abd62f"
          },
          "AyaContent": "وإنه لذكر لك ولقومك ۖ وسوف تسألون",
          "AyaNumber": 4369,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f32f61d12bf6abd630"
          },
          "AyaContent": "واسأل من أرسلنا من قبلك من رسلنا أجعلنا من دون الرحمٰن آلهة يعبدون",
          "AyaNumber": 4370,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f32f61d12bf6abd631"
          },
          "AyaContent": "ولقد أرسلنا موسىٰ بآياتنا إلىٰ فرعون وملئه فقال إني رسول رب العالمين",
          "AyaNumber": 4371,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f42f61d12bf6abd632"
          },
          "AyaContent": "فلما جاءهم بآياتنا إذا هم منها يضحكون",
          "AyaNumber": 4372,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f42f61d12bf6abd633"
          },
          "AyaContent": "وما نريهم من آية إلا هي أكبر من أختها ۖ وأخذناهم بالعذاب لعلهم يرجعون",
          "AyaNumber": 4373,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f42f61d12bf6abd634"
          },
          "AyaContent": "وقالوا يا أيه الساحر ادع لنا ربك بما عهد عندك إننا لمهتدون",
          "AyaNumber": 4374,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f42f61d12bf6abd635"
          },
          "AyaContent": "فلما كشفنا عنهم العذاب إذا هم ينكثون",
          "AyaNumber": 4375,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f42f61d12bf6abd636"
          },
          "AyaContent": "ونادىٰ فرعون في قومه قال يا قوم أليس لي ملك مصر وهٰذه الأنهار تجري من تحتي ۖ أفلا تبصرون",
          "AyaNumber": 4376,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f42f61d12bf6abd637"
          },
          "AyaContent": "أم أنا خير من هٰذا الذي هو مهين ولا يكاد يبين",
          "AyaNumber": 4377,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f52f61d12bf6abd638"
          },
          "AyaContent": "فلولا ألقي عليه أسورة من ذهب أو جاء معه الملائكة مقترنين",
          "AyaNumber": 4378,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f52f61d12bf6abd639"
          },
          "AyaContent": "فاستخف قومه فأطاعوه ۚ إنهم كانوا قوما فاسقين",
          "AyaNumber": 4379,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f52f61d12bf6abd63a"
          },
          "AyaContent": "فلما آسفونا انتقمنا منهم فأغرقناهم أجمعين",
          "AyaNumber": 4380,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f52f61d12bf6abd63b"
          },
          "AyaContent": "فجعلناهم سلفا ومثلا للآخرين",
          "AyaNumber": 4381,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f52f61d12bf6abd63c"
          },
          "AyaContent": "۞ ولما ضرب ابن مريم مثلا إذا قومك منه يصدون",
          "AyaNumber": 4382,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f52f61d12bf6abd63d"
          },
          "AyaContent": "وقالوا أآلهتنا خير أم هو ۚ ما ضربوه لك إلا جدلا ۚ بل هم قوم خصمون",
          "AyaNumber": 4383,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f62f61d12bf6abd63e"
          },
          "AyaContent": "إن هو إلا عبد أنعمنا عليه وجعلناه مثلا لبني إسرائيل",
          "AyaNumber": 4384,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f62f61d12bf6abd63f"
          },
          "AyaContent": "ولو نشاء لجعلنا منكم ملائكة في الأرض يخلفون",
          "AyaNumber": 4385,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f62f61d12bf6abd640"
          },
          "AyaContent": "وإنه لعلم للساعة فلا تمترن بها واتبعون ۚ هٰذا صراط مستقيم",
          "AyaNumber": 4386,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f62f61d12bf6abd641"
          },
          "AyaContent": "ولا يصدنكم الشيطان ۖ إنه لكم عدو مبين",
          "AyaNumber": 4387,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f62f61d12bf6abd642"
          },
          "AyaContent": "ولما جاء عيسىٰ بالبينات قال قد جئتكم بالحكمة ولأبين لكم بعض الذي تختلفون فيه ۖ فاتقوا الله وأطيعون",
          "AyaNumber": 4388,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f72f61d12bf6abd643"
          },
          "AyaContent": "إن الله هو ربي وربكم فاعبدوه ۚ هٰذا صراط مستقيم",
          "AyaNumber": 4389,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f72f61d12bf6abd644"
          },
          "AyaContent": "فاختلف الأحزاب من بينهم ۖ فويل للذين ظلموا من عذاب يوم أليم",
          "AyaNumber": 4390,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f72f61d12bf6abd645"
          },
          "AyaContent": "هل ينظرون إلا الساعة أن تأتيهم بغتة وهم لا يشعرون",
          "AyaNumber": 4391,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f72f61d12bf6abd646"
          },
          "AyaContent": "الأخلاء يومئذ بعضهم لبعض عدو إلا المتقين",
          "AyaNumber": 4392,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f72f61d12bf6abd647"
          },
          "AyaContent": "يا عباد لا خوف عليكم اليوم ولا أنتم تحزنون",
          "AyaNumber": 4393,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f82f61d12bf6abd648"
          },
          "AyaContent": "الذين آمنوا بآياتنا وكانوا مسلمين",
          "AyaNumber": 4394,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f82f61d12bf6abd649"
          },
          "AyaContent": "ادخلوا الجنة أنتم وأزواجكم تحبرون",
          "AyaNumber": 4395,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f82f61d12bf6abd64a"
          },
          "AyaContent": "يطاف عليهم بصحاف من ذهب وأكواب ۖ وفيها ما تشتهيه الأنفس وتلذ الأعين ۖ وأنتم فيها خالدون",
          "AyaNumber": 4396,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f82f61d12bf6abd64b"
          },
          "AyaContent": "وتلك الجنة التي أورثتموها بما كنتم تعملون",
          "AyaNumber": 4397,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f82f61d12bf6abd64c"
          },
          "AyaContent": "لكم فيها فاكهة كثيرة منها تأكلون",
          "AyaNumber": 4398,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f92f61d12bf6abd64d"
          },
          "AyaContent": "إن المجرمين في عذاب جهنم خالدون",
          "AyaNumber": 4399,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f92f61d12bf6abd64e"
          },
          "AyaContent": "لا يفتر عنهم وهم فيه مبلسون",
          "AyaNumber": 4400,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f92f61d12bf6abd64f"
          },
          "AyaContent": "وما ظلمناهم ولٰكن كانوا هم الظالمين",
          "AyaNumber": 4401,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f92f61d12bf6abd650"
          },
          "AyaContent": "ونادوا يا مالك ليقض علينا ربك ۖ قال إنكم ماكثون",
          "AyaNumber": 4402,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88f92f61d12bf6abd651"
          },
          "AyaContent": "لقد جئناكم بالحق ولٰكن أكثركم للحق كارهون",
          "AyaNumber": 4403,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fa2f61d12bf6abd652"
          },
          "AyaContent": "أم أبرموا أمرا فإنا مبرمون",
          "AyaNumber": 4404,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fa2f61d12bf6abd653"
          },
          "AyaContent": "أم يحسبون أنا لا نسمع سرهم ونجواهم ۚ بلىٰ ورسلنا لديهم يكتبون",
          "AyaNumber": 4405,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fa2f61d12bf6abd654"
          },
          "AyaContent": "قل إن كان للرحمٰن ولد فأنا أول العابدين",
          "AyaNumber": 4406,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fa2f61d12bf6abd655"
          },
          "AyaContent": "سبحان رب السماوات والأرض رب العرش عما يصفون",
          "AyaNumber": 4407,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fa2f61d12bf6abd656"
          },
          "AyaContent": "فذرهم يخوضوا ويلعبوا حتىٰ يلاقوا يومهم الذي يوعدون",
          "AyaNumber": 4408,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fa2f61d12bf6abd657"
          },
          "AyaContent": "وهو الذي في السماء إلٰه وفي الأرض إلٰه ۚ وهو الحكيم العليم",
          "AyaNumber": 4409,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fa2f61d12bf6abd658"
          },
          "AyaContent": "وتبارك الذي له ملك السماوات والأرض وما بينهما وعنده علم الساعة وإليه ترجعون",
          "AyaNumber": 4410,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fb2f61d12bf6abd659"
          },
          "AyaContent": "ولا يملك الذين يدعون من دونه الشفاعة إلا من شهد بالحق وهم يعلمون",
          "AyaNumber": 4411,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fb2f61d12bf6abd65a"
          },
          "AyaContent": "ولئن سألتهم من خلقهم ليقولن الله ۖ فأنىٰ يؤفكون",
          "AyaNumber": 4412,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fb2f61d12bf6abd65b"
          },
          "AyaContent": "وقيله يا رب إن هٰؤلاء قوم لا يؤمنون",
          "AyaNumber": 4413,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fb2f61d12bf6abd65c"
          },
          "AyaContent": "فاصفح عنهم وقل سلام ۚ فسوف يعلمون",
          "AyaNumber": 4414,
          "SurahName": "سورة الزخرف",
          "SurahNumber": 43
        },{
          "_id": {
            "$oid": "5ecd88fb2f61d12bf6abd65d"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم حم",
          "AyaNumber": 4415,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fb2f61d12bf6abd65e"
          },
          "AyaContent": "والكتاب المبين",
          "AyaNumber": 4416,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fc2f61d12bf6abd65f"
          },
          "AyaContent": "إنا أنزلناه في ليلة مباركة ۚ إنا كنا منذرين",
          "AyaNumber": 4417,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fc2f61d12bf6abd660"
          },
          "AyaContent": "فيها يفرق كل أمر حكيم",
          "AyaNumber": 4418,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fc2f61d12bf6abd661"
          },
          "AyaContent": "أمرا من عندنا ۚ إنا كنا مرسلين",
          "AyaNumber": 4419,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fc2f61d12bf6abd662"
          },
          "AyaContent": "رحمة من ربك ۚ إنه هو السميع العليم",
          "AyaNumber": 4420,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fc2f61d12bf6abd663"
          },
          "AyaContent": "رب السماوات والأرض وما بينهما ۖ إن كنتم موقنين",
          "AyaNumber": 4421,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fd2f61d12bf6abd664"
          },
          "AyaContent": "لا إلٰه إلا هو يحيي ويميت ۖ ربكم ورب آبائكم الأولين",
          "AyaNumber": 4422,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fd2f61d12bf6abd665"
          },
          "AyaContent": "بل هم في شك يلعبون",
          "AyaNumber": 4423,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fd2f61d12bf6abd666"
          },
          "AyaContent": "فارتقب يوم تأتي السماء بدخان مبين",
          "AyaNumber": 4424,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fd2f61d12bf6abd667"
          },
          "AyaContent": "يغشى الناس ۖ هٰذا عذاب أليم",
          "AyaNumber": 4425,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fd2f61d12bf6abd668"
          },
          "AyaContent": "ربنا اكشف عنا العذاب إنا مؤمنون",
          "AyaNumber": 4426,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fd2f61d12bf6abd669"
          },
          "AyaContent": "أنىٰ لهم الذكرىٰ وقد جاءهم رسول مبين",
          "AyaNumber": 4427,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fd2f61d12bf6abd66a"
          },
          "AyaContent": "ثم تولوا عنه وقالوا معلم مجنون",
          "AyaNumber": 4428,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fe2f61d12bf6abd66b"
          },
          "AyaContent": "إنا كاشفو العذاب قليلا ۚ إنكم عائدون",
          "AyaNumber": 4429,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fe2f61d12bf6abd66c"
          },
          "AyaContent": "يوم نبطش البطشة الكبرىٰ إنا منتقمون",
          "AyaNumber": 4430,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fe2f61d12bf6abd66d"
          },
          "AyaContent": "۞ ولقد فتنا قبلهم قوم فرعون وجاءهم رسول كريم",
          "AyaNumber": 4431,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fe2f61d12bf6abd66e"
          },
          "AyaContent": "أن أدوا إلي عباد الله ۖ إني لكم رسول أمين",
          "AyaNumber": 4432,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fe2f61d12bf6abd66f"
          },
          "AyaContent": "وأن لا تعلوا على الله ۖ إني آتيكم بسلطان مبين",
          "AyaNumber": 4433,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88fe2f61d12bf6abd670"
          },
          "AyaContent": "وإني عذت بربي وربكم أن ترجمون",
          "AyaNumber": 4434,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88ff2f61d12bf6abd671"
          },
          "AyaContent": "وإن لم تؤمنوا لي فاعتزلون",
          "AyaNumber": 4435,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88ff2f61d12bf6abd672"
          },
          "AyaContent": "فدعا ربه أن هٰؤلاء قوم مجرمون",
          "AyaNumber": 4436,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88ff2f61d12bf6abd673"
          },
          "AyaContent": "فأسر بعبادي ليلا إنكم متبعون",
          "AyaNumber": 4437,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88ff2f61d12bf6abd674"
          },
          "AyaContent": "واترك البحر رهوا ۖ إنهم جند مغرقون",
          "AyaNumber": 4438,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88ff2f61d12bf6abd675"
          },
          "AyaContent": "كم تركوا من جنات وعيون",
          "AyaNumber": 4439,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd88ff2f61d12bf6abd676"
          },
          "AyaContent": "وزروع ومقام كريم",
          "AyaNumber": 4440,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89002f61d12bf6abd677"
          },
          "AyaContent": "ونعمة كانوا فيها فاكهين",
          "AyaNumber": 4441,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89002f61d12bf6abd678"
          },
          "AyaContent": "كذٰلك ۖ وأورثناها قوما آخرين",
          "AyaNumber": 4442,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89002f61d12bf6abd679"
          },
          "AyaContent": "فما بكت عليهم السماء والأرض وما كانوا منظرين",
          "AyaNumber": 4443,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89002f61d12bf6abd67a"
          },
          "AyaContent": "ولقد نجينا بني إسرائيل من العذاب المهين",
          "AyaNumber": 4444,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89002f61d12bf6abd67b"
          },
          "AyaContent": "من فرعون ۚ إنه كان عاليا من المسرفين",
          "AyaNumber": 4445,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89002f61d12bf6abd67c"
          },
          "AyaContent": "ولقد اخترناهم علىٰ علم على العالمين",
          "AyaNumber": 4446,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89012f61d12bf6abd67d"
          },
          "AyaContent": "وآتيناهم من الآيات ما فيه بلاء مبين",
          "AyaNumber": 4447,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89012f61d12bf6abd67e"
          },
          "AyaContent": "إن هٰؤلاء ليقولون",
          "AyaNumber": 4448,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89012f61d12bf6abd67f"
          },
          "AyaContent": "إن هي إلا موتتنا الأولىٰ وما نحن بمنشرين",
          "AyaNumber": 4449,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89012f61d12bf6abd680"
          },
          "AyaContent": "فأتوا بآبائنا إن كنتم صادقين",
          "AyaNumber": 4450,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89012f61d12bf6abd681"
          },
          "AyaContent": "أهم خير أم قوم تبع والذين من قبلهم ۚ أهلكناهم ۖ إنهم كانوا مجرمين",
          "AyaNumber": 4451,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89012f61d12bf6abd682"
          },
          "AyaContent": "وما خلقنا السماوات والأرض وما بينهما لاعبين",
          "AyaNumber": 4452,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89022f61d12bf6abd683"
          },
          "AyaContent": "ما خلقناهما إلا بالحق ولٰكن أكثرهم لا يعلمون",
          "AyaNumber": 4453,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89022f61d12bf6abd684"
          },
          "AyaContent": "إن يوم الفصل ميقاتهم أجمعين",
          "AyaNumber": 4454,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89022f61d12bf6abd685"
          },
          "AyaContent": "يوم لا يغني مولى عن مولى شيئا ولا هم ينصرون",
          "AyaNumber": 4455,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89022f61d12bf6abd686"
          },
          "AyaContent": "إلا من رحم الله ۚ إنه هو العزيز الرحيم",
          "AyaNumber": 4456,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89022f61d12bf6abd687"
          },
          "AyaContent": "إن شجرت الزقوم",
          "AyaNumber": 4457,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89032f61d12bf6abd688"
          },
          "AyaContent": "طعام الأثيم",
          "AyaNumber": 4458,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89032f61d12bf6abd689"
          },
          "AyaContent": "كالمهل يغلي في البطون",
          "AyaNumber": 4459,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89032f61d12bf6abd68a"
          },
          "AyaContent": "كغلي الحميم",
          "AyaNumber": 4460,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89032f61d12bf6abd68b"
          },
          "AyaContent": "خذوه فاعتلوه إلىٰ سواء الجحيم",
          "AyaNumber": 4461,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89032f61d12bf6abd68c"
          },
          "AyaContent": "ثم صبوا فوق رأسه من عذاب الحميم",
          "AyaNumber": 4462,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89042f61d12bf6abd68d"
          },
          "AyaContent": "ذق إنك أنت العزيز الكريم",
          "AyaNumber": 4463,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89042f61d12bf6abd68e"
          },
          "AyaContent": "إن هٰذا ما كنتم به تمترون",
          "AyaNumber": 4464,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89042f61d12bf6abd68f"
          },
          "AyaContent": "إن المتقين في مقام أمين",
          "AyaNumber": 4465,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89042f61d12bf6abd690"
          },
          "AyaContent": "في جنات وعيون",
          "AyaNumber": 4466,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89052f61d12bf6abd691"
          },
          "AyaContent": "يلبسون من سندس وإستبرق متقابلين",
          "AyaNumber": 4467,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89052f61d12bf6abd692"
          },
          "AyaContent": "كذٰلك وزوجناهم بحور عين",
          "AyaNumber": 4468,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89052f61d12bf6abd693"
          },
          "AyaContent": "يدعون فيها بكل فاكهة آمنين",
          "AyaNumber": 4469,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89052f61d12bf6abd694"
          },
          "AyaContent": "لا يذوقون فيها الموت إلا الموتة الأولىٰ ۖ ووقاهم عذاب الجحيم",
          "AyaNumber": 4470,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89062f61d12bf6abd695"
          },
          "AyaContent": "فضلا من ربك ۚ ذٰلك هو الفوز العظيم",
          "AyaNumber": 4471,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89062f61d12bf6abd696"
          },
          "AyaContent": "فإنما يسرناه بلسانك لعلهم يتذكرون",
          "AyaNumber": 4472,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89062f61d12bf6abd697"
          },
          "AyaContent": "فارتقب إنهم مرتقبون",
          "AyaNumber": 4473,
          "SurahName": "سورة الدخان",
          "SurahNumber": 44
        },{
          "_id": {
            "$oid": "5ecd89062f61d12bf6abd698"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم حم",
          "AyaNumber": 4474,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89062f61d12bf6abd699"
          },
          "AyaContent": "تنزيل الكتاب من الله العزيز الحكيم",
          "AyaNumber": 4475,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89062f61d12bf6abd69a"
          },
          "AyaContent": "إن في السماوات والأرض لآيات للمؤمنين",
          "AyaNumber": 4476,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89062f61d12bf6abd69b"
          },
          "AyaContent": "وفي خلقكم وما يبث من دابة آيات لقوم يوقنون",
          "AyaNumber": 4477,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89072f61d12bf6abd69c"
          },
          "AyaContent": "واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون",
          "AyaNumber": 4478,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89072f61d12bf6abd69d"
          },
          "AyaContent": "تلك آيات الله نتلوها عليك بالحق ۖ فبأي حديث بعد الله وآياته يؤمنون",
          "AyaNumber": 4479,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89072f61d12bf6abd69e"
          },
          "AyaContent": "ويل لكل أفاك أثيم",
          "AyaNumber": 4480,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89072f61d12bf6abd69f"
          },
          "AyaContent": "يسمع آيات الله تتلىٰ عليه ثم يصر مستكبرا كأن لم يسمعها ۖ فبشره بعذاب أليم",
          "AyaNumber": 4481,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89072f61d12bf6abd6a0"
          },
          "AyaContent": "وإذا علم من آياتنا شيئا اتخذها هزوا ۚ أولٰئك لهم عذاب مهين",
          "AyaNumber": 4482,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89082f61d12bf6abd6a1"
          },
          "AyaContent": "من ورائهم جهنم ۖ ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله أولياء ۖ ولهم عذاب عظيم",
          "AyaNumber": 4483,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89082f61d12bf6abd6a2"
          },
          "AyaContent": "هٰذا هدى ۖ والذين كفروا بآيات ربهم لهم عذاب من رجز أليم",
          "AyaNumber": 4484,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89082f61d12bf6abd6a3"
          },
          "AyaContent": "۞ الله الذي سخر لكم البحر لتجري الفلك فيه بأمره ولتبتغوا من فضله ولعلكم تشكرون",
          "AyaNumber": 4485,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89082f61d12bf6abd6a4"
          },
          "AyaContent": "وسخر لكم ما في السماوات وما في الأرض جميعا منه ۚ إن في ذٰلك لآيات لقوم يتفكرون",
          "AyaNumber": 4486,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89092f61d12bf6abd6a5"
          },
          "AyaContent": "قل للذين آمنوا يغفروا للذين لا يرجون أيام الله ليجزي قوما بما كانوا يكسبون",
          "AyaNumber": 4487,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89092f61d12bf6abd6a6"
          },
          "AyaContent": "من عمل صالحا فلنفسه ۖ ومن أساء فعليها ۖ ثم إلىٰ ربكم ترجعون",
          "AyaNumber": 4488,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89092f61d12bf6abd6a7"
          },
          "AyaContent": "ولقد آتينا بني إسرائيل الكتاب والحكم والنبوة ورزقناهم من الطيبات وفضلناهم على العالمين",
          "AyaNumber": 4489,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89092f61d12bf6abd6a8"
          },
          "AyaContent": "وآتيناهم بينات من الأمر ۖ فما اختلفوا إلا من بعد ما جاءهم العلم بغيا بينهم ۚ إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون",
          "AyaNumber": 4490,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd89092f61d12bf6abd6a9"
          },
          "AyaContent": "ثم جعلناك علىٰ شريعة من الأمر فاتبعها ولا تتبع أهواء الذين لا يعلمون",
          "AyaNumber": 4491,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890a2f61d12bf6abd6aa"
          },
          "AyaContent": "إنهم لن يغنوا عنك من الله شيئا ۚ وإن الظالمين بعضهم أولياء بعض ۖ والله ولي المتقين",
          "AyaNumber": 4492,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890a2f61d12bf6abd6ab"
          },
          "AyaContent": "هٰذا بصائر للناس وهدى ورحمة لقوم يوقنون",
          "AyaNumber": 4493,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890a2f61d12bf6abd6ac"
          },
          "AyaContent": "أم حسب الذين اجترحوا السيئات أن نجعلهم كالذين آمنوا وعملوا الصالحات سواء محياهم ومماتهم ۚ ساء ما يحكمون",
          "AyaNumber": 4494,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890a2f61d12bf6abd6ad"
          },
          "AyaContent": "وخلق الله السماوات والأرض بالحق ولتجزىٰ كل نفس بما كسبت وهم لا يظلمون",
          "AyaNumber": 4495,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890b2f61d12bf6abd6ae"
          },
          "AyaContent": "أفرأيت من اتخذ إلٰهه هواه وأضله الله علىٰ علم وختم علىٰ سمعه وقلبه وجعل علىٰ بصره غشاوة فمن يهديه من بعد الله ۚ أفلا تذكرون",
          "AyaNumber": 4496,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890b2f61d12bf6abd6af"
          },
          "AyaContent": "وقالوا ما هي إلا حياتنا الدنيا نموت ونحيا وما يهلكنا إلا الدهر ۚ وما لهم بذٰلك من علم ۖ إن هم إلا يظنون",
          "AyaNumber": 4497,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890b2f61d12bf6abd6b0"
          },
          "AyaContent": "وإذا تتلىٰ عليهم آياتنا بينات ما كان حجتهم إلا أن قالوا ائتوا بآبائنا إن كنتم صادقين",
          "AyaNumber": 4498,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890b2f61d12bf6abd6b1"
          },
          "AyaContent": "قل الله يحييكم ثم يميتكم ثم يجمعكم إلىٰ يوم القيامة لا ريب فيه ولٰكن أكثر الناس لا يعلمون",
          "AyaNumber": 4499,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890b2f61d12bf6abd6b2"
          },
          "AyaContent": "ولله ملك السماوات والأرض ۚ ويوم تقوم الساعة يومئذ يخسر المبطلون",
          "AyaNumber": 4500,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890c2f61d12bf6abd6b3"
          },
          "AyaContent": "وترىٰ كل أمة جاثية ۚ كل أمة تدعىٰ إلىٰ كتابها اليوم تجزون ما كنتم تعملون",
          "AyaNumber": 4501,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890c2f61d12bf6abd6b4"
          },
          "AyaContent": "هٰذا كتابنا ينطق عليكم بالحق ۚ إنا كنا نستنسخ ما كنتم تعملون",
          "AyaNumber": 4502,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890c2f61d12bf6abd6b5"
          },
          "AyaContent": "فأما الذين آمنوا وعملوا الصالحات فيدخلهم ربهم في رحمته ۚ ذٰلك هو الفوز المبين",
          "AyaNumber": 4503,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890c2f61d12bf6abd6b6"
          },
          "AyaContent": "وأما الذين كفروا أفلم تكن آياتي تتلىٰ عليكم فاستكبرتم وكنتم قوما مجرمين",
          "AyaNumber": 4504,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890c2f61d12bf6abd6b7"
          },
          "AyaContent": "وإذا قيل إن وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة إن نظن إلا ظنا وما نحن بمستيقنين",
          "AyaNumber": 4505,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890c2f61d12bf6abd6b8"
          },
          "AyaContent": "وبدا لهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون",
          "AyaNumber": 4506,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890c2f61d12bf6abd6b9"
          },
          "AyaContent": "وقيل اليوم ننساكم كما نسيتم لقاء يومكم هٰذا ومأواكم النار وما لكم من ناصرين",
          "AyaNumber": 4507,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890d2f61d12bf6abd6ba"
          },
          "AyaContent": "ذٰلكم بأنكم اتخذتم آيات الله هزوا وغرتكم الحياة الدنيا ۚ فاليوم لا يخرجون منها ولا هم يستعتبون",
          "AyaNumber": 4508,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890d2f61d12bf6abd6bb"
          },
          "AyaContent": "فلله الحمد رب السماوات ورب الأرض رب العالمين",
          "AyaNumber": 4509,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890d2f61d12bf6abd6bc"
          },
          "AyaContent": "وله الكبرياء في السماوات والأرض ۖ وهو العزيز الحكيم",
          "AyaNumber": 4510,
          "SurahName": "سورة الجاثية",
          "SurahNumber": 45
        },{
          "_id": {
            "$oid": "5ecd890d2f61d12bf6abd6bd"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم حم",
          "AyaNumber": 4511,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890d2f61d12bf6abd6be"
          },
          "AyaContent": "تنزيل الكتاب من الله العزيز الحكيم",
          "AyaNumber": 4512,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890d2f61d12bf6abd6bf"
          },
          "AyaContent": "ما خلقنا السماوات والأرض وما بينهما إلا بالحق وأجل مسمى ۚ والذين كفروا عما أنذروا معرضون",
          "AyaNumber": 4513,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890e2f61d12bf6abd6c0"
          },
          "AyaContent": "قل أرأيتم ما تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات ۖ ائتوني بكتاب من قبل هٰذا أو أثارة من علم إن كنتم صادقين",
          "AyaNumber": 4514,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890e2f61d12bf6abd6c1"
          },
          "AyaContent": "ومن أضل ممن يدعو من دون الله من لا يستجيب له إلىٰ يوم القيامة وهم عن دعائهم غافلون",
          "AyaNumber": 4515,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890e2f61d12bf6abd6c2"
          },
          "AyaContent": "وإذا حشر الناس كانوا لهم أعداء وكانوا بعبادتهم كافرين",
          "AyaNumber": 4516,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890e2f61d12bf6abd6c3"
          },
          "AyaContent": "وإذا تتلىٰ عليهم آياتنا بينات قال الذين كفروا للحق لما جاءهم هٰذا سحر مبين",
          "AyaNumber": 4517,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890e2f61d12bf6abd6c4"
          },
          "AyaContent": "أم يقولون افتراه ۖ قل إن افتريته فلا تملكون لي من الله شيئا ۖ هو أعلم بما تفيضون فيه ۖ كفىٰ به شهيدا بيني وبينكم ۖ وهو الغفور الرحيم",
          "AyaNumber": 4518,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890e2f61d12bf6abd6c5"
          },
          "AyaContent": "قل ما كنت بدعا من الرسل وما أدري ما يفعل بي ولا بكم ۖ إن أتبع إلا ما يوحىٰ إلي وما أنا إلا نذير مبين",
          "AyaNumber": 4519,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890f2f61d12bf6abd6c6"
          },
          "AyaContent": "قل أرأيتم إن كان من عند الله وكفرتم به وشهد شاهد من بني إسرائيل علىٰ مثله فآمن واستكبرتم ۖ إن الله لا يهدي القوم الظالمين",
          "AyaNumber": 4520,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890f2f61d12bf6abd6c7"
          },
          "AyaContent": "وقال الذين كفروا للذين آمنوا لو كان خيرا ما سبقونا إليه ۚ وإذ لم يهتدوا به فسيقولون هٰذا إفك قديم",
          "AyaNumber": 4521,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890f2f61d12bf6abd6c8"
          },
          "AyaContent": "ومن قبله كتاب موسىٰ إماما ورحمة ۚ وهٰذا كتاب مصدق لسانا عربيا لينذر الذين ظلموا وبشرىٰ للمحسنين",
          "AyaNumber": 4522,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890f2f61d12bf6abd6c9"
          },
          "AyaContent": "إن الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون",
          "AyaNumber": 4523,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd890f2f61d12bf6abd6ca"
          },
          "AyaContent": "أولٰئك أصحاب الجنة خالدين فيها جزاء بما كانوا يعملون",
          "AyaNumber": 4524,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89102f61d12bf6abd6cb"
          },
          "AyaContent": "ووصينا الإنسان بوالديه إحسانا ۖ حملته أمه كرها ووضعته كرها ۖ وحمله وفصاله ثلاثون شهرا ۚ حتىٰ إذا بلغ أشده وبلغ أربعين سنة قال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلىٰ والدي وأن أعمل صالحا ترضاه وأصلح لي في ذريتي ۖ إني تبت إليك وإني من المسلمين",
          "AyaNumber": 4525,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89102f61d12bf6abd6cc"
          },
          "AyaContent": "أولٰئك الذين نتقبل عنهم أحسن ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة ۖ وعد الصدق الذي كانوا يوعدون",
          "AyaNumber": 4526,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89102f61d12bf6abd6cd"
          },
          "AyaContent": "والذي قال لوالديه أف لكما أتعدانني أن أخرج وقد خلت القرون من قبلي وهما يستغيثان الله ويلك آمن إن وعد الله حق فيقول ما هٰذا إلا أساطير الأولين",
          "AyaNumber": 4527,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89102f61d12bf6abd6ce"
          },
          "AyaContent": "أولٰئك الذين حق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس ۖ إنهم كانوا خاسرين",
          "AyaNumber": 4528,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89102f61d12bf6abd6cf"
          },
          "AyaContent": "ولكل درجات مما عملوا ۖ وليوفيهم أعمالهم وهم لا يظلمون",
          "AyaNumber": 4529,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89112f61d12bf6abd6d0"
          },
          "AyaContent": "ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الأرض بغير الحق وبما كنتم تفسقون",
          "AyaNumber": 4530,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89112f61d12bf6abd6d1"
          },
          "AyaContent": "۞ واذكر أخا عاد إذ أنذر قومه بالأحقاف وقد خلت النذر من بين يديه ومن خلفه ألا تعبدوا إلا الله إني أخاف عليكم عذاب يوم عظيم",
          "AyaNumber": 4531,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89112f61d12bf6abd6d2"
          },
          "AyaContent": "قالوا أجئتنا لتأفكنا عن آلهتنا فأتنا بما تعدنا إن كنت من الصادقين",
          "AyaNumber": 4532,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89112f61d12bf6abd6d3"
          },
          "AyaContent": "قال إنما العلم عند الله وأبلغكم ما أرسلت به ولٰكني أراكم قوما تجهلون",
          "AyaNumber": 4533,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89112f61d12bf6abd6d4"
          },
          "AyaContent": "فلما رأوه عارضا مستقبل أوديتهم قالوا هٰذا عارض ممطرنا ۚ بل هو ما استعجلتم به ۖ ريح فيها عذاب أليم",
          "AyaNumber": 4534,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89112f61d12bf6abd6d5"
          },
          "AyaContent": "تدمر كل شيء بأمر ربها فأصبحوا لا يرىٰ إلا مساكنهم ۚ كذٰلك نجزي القوم المجرمين",
          "AyaNumber": 4535,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89122f61d12bf6abd6d6"
          },
          "AyaContent": "ولقد مكناهم فيما إن مكناكم فيه وجعلنا لهم سمعا وأبصارا وأفئدة فما أغنىٰ عنهم سمعهم ولا أبصارهم ولا أفئدتهم من شيء إذ كانوا يجحدون بآيات الله وحاق بهم ما كانوا به يستهزئون",
          "AyaNumber": 4536,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89122f61d12bf6abd6d7"
          },
          "AyaContent": "ولقد أهلكنا ما حولكم من القرىٰ وصرفنا الآيات لعلهم يرجعون",
          "AyaNumber": 4537,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89122f61d12bf6abd6d8"
          },
          "AyaContent": "فلولا نصرهم الذين اتخذوا من دون الله قربانا آلهة ۖ بل ضلوا عنهم ۚ وذٰلك إفكهم وما كانوا يفترون",
          "AyaNumber": 4538,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89122f61d12bf6abd6d9"
          },
          "AyaContent": "وإذ صرفنا إليك نفرا من الجن يستمعون القرآن فلما حضروه قالوا أنصتوا ۖ فلما قضي ولوا إلىٰ قومهم منذرين",
          "AyaNumber": 4539,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89122f61d12bf6abd6da"
          },
          "AyaContent": "قالوا يا قومنا إنا سمعنا كتابا أنزل من بعد موسىٰ مصدقا لما بين يديه يهدي إلى الحق وإلىٰ طريق مستقيم",
          "AyaNumber": 4540,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89122f61d12bf6abd6db"
          },
          "AyaContent": "يا قومنا أجيبوا داعي الله وآمنوا به يغفر لكم من ذنوبكم ويجركم من عذاب أليم",
          "AyaNumber": 4541,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89132f61d12bf6abd6dc"
          },
          "AyaContent": "ومن لا يجب داعي الله فليس بمعجز في الأرض وليس له من دونه أولياء ۚ أولٰئك في ضلال مبين",
          "AyaNumber": 4542,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89132f61d12bf6abd6dd"
          },
          "AyaContent": "أولم يروا أن الله الذي خلق السماوات والأرض ولم يعي بخلقهن بقادر علىٰ أن يحيي الموتىٰ ۚ بلىٰ إنه علىٰ كل شيء قدير",
          "AyaNumber": 4543,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89132f61d12bf6abd6de"
          },
          "AyaContent": "ويوم يعرض الذين كفروا على النار أليس هٰذا بالحق ۖ قالوا بلىٰ وربنا ۚ قال فذوقوا العذاب بما كنتم تكفرون",
          "AyaNumber": 4544,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89132f61d12bf6abd6df"
          },
          "AyaContent": "فاصبر كما صبر أولو العزم من الرسل ولا تستعجل لهم ۚ كأنهم يوم يرون ما يوعدون لم يلبثوا إلا ساعة من نهار ۚ بلاغ ۚ فهل يهلك إلا القوم الفاسقون",
          "AyaNumber": 4545,
          "SurahName": "سورة الأحقاف",
          "SurahNumber": 46
        },{
          "_id": {
            "$oid": "5ecd89132f61d12bf6abd6e0"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الذين كفروا وصدوا عن سبيل الله أضل أعمالهم",
          "AyaNumber": 4546,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89142f61d12bf6abd6e1"
          },
          "AyaContent": "والذين آمنوا وعملوا الصالحات وآمنوا بما نزل علىٰ محمد وهو الحق من ربهم ۙ كفر عنهم سيئاتهم وأصلح بالهم",
          "AyaNumber": 4547,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89142f61d12bf6abd6e2"
          },
          "AyaContent": "ذٰلك بأن الذين كفروا اتبعوا الباطل وأن الذين آمنوا اتبعوا الحق من ربهم ۚ كذٰلك يضرب الله للناس أمثالهم",
          "AyaNumber": 4548,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89142f61d12bf6abd6e3"
          },
          "AyaContent": "فإذا لقيتم الذين كفروا فضرب الرقاب حتىٰ إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتىٰ تضع الحرب أوزارها ۚ ذٰلك ولو يشاء الله لانتصر منهم ولٰكن ليبلو بعضكم ببعض ۗ والذين قتلوا في سبيل الله فلن يضل أعمالهم",
          "AyaNumber": 4549,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89142f61d12bf6abd6e4"
          },
          "AyaContent": "سيهديهم ويصلح بالهم",
          "AyaNumber": 4550,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89142f61d12bf6abd6e5"
          },
          "AyaContent": "ويدخلهم الجنة عرفها لهم",
          "AyaNumber": 4551,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89142f61d12bf6abd6e6"
          },
          "AyaContent": "يا أيها الذين آمنوا إن تنصروا الله ينصركم ويثبت أقدامكم",
          "AyaNumber": 4552,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89142f61d12bf6abd6e7"
          },
          "AyaContent": "والذين كفروا فتعسا لهم وأضل أعمالهم",
          "AyaNumber": 4553,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89152f61d12bf6abd6e8"
          },
          "AyaContent": "ذٰلك بأنهم كرهوا ما أنزل الله فأحبط أعمالهم",
          "AyaNumber": 4554,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89152f61d12bf6abd6e9"
          },
          "AyaContent": "۞ أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم ۚ دمر الله عليهم ۖ وللكافرين أمثالها",
          "AyaNumber": 4555,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89152f61d12bf6abd6ea"
          },
          "AyaContent": "ذٰلك بأن الله مولى الذين آمنوا وأن الكافرين لا مولىٰ لهم",
          "AyaNumber": 4556,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89162f61d12bf6abd6eb"
          },
          "AyaContent": "إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار ۖ والذين كفروا يتمتعون ويأكلون كما تأكل الأنعام والنار مثوى لهم",
          "AyaNumber": 4557,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89162f61d12bf6abd6ec"
          },
          "AyaContent": "وكأين من قرية هي أشد قوة من قريتك التي أخرجتك أهلكناهم فلا ناصر لهم",
          "AyaNumber": 4558,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89162f61d12bf6abd6ed"
          },
          "AyaContent": "أفمن كان علىٰ بينة من ربه كمن زين له سوء عمله واتبعوا أهواءهم",
          "AyaNumber": 4559,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89172f61d12bf6abd6ee"
          },
          "AyaContent": "مثل الجنة التي وعد المتقون ۖ فيها أنهار من ماء غير آسن وأنهار من لبن لم يتغير طعمه وأنهار من خمر لذة للشاربين وأنهار من عسل مصفى ۖ ولهم فيها من كل الثمرات ومغفرة من ربهم ۖ كمن هو خالد في النار وسقوا ماء حميما فقطع أمعاءهم",
          "AyaNumber": 4560,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89172f61d12bf6abd6ef"
          },
          "AyaContent": "ومنهم من يستمع إليك حتىٰ إذا خرجوا من عندك قالوا للذين أوتوا العلم ماذا قال آنفا ۚ أولٰئك الذين طبع الله علىٰ قلوبهم واتبعوا أهواءهم",
          "AyaNumber": 4561,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89172f61d12bf6abd6f0"
          },
          "AyaContent": "والذين اهتدوا زادهم هدى وآتاهم تقواهم",
          "AyaNumber": 4562,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89172f61d12bf6abd6f1"
          },
          "AyaContent": "فهل ينظرون إلا الساعة أن تأتيهم بغتة ۖ فقد جاء أشراطها ۚ فأنىٰ لهم إذا جاءتهم ذكراهم",
          "AyaNumber": 4563,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89172f61d12bf6abd6f2"
          },
          "AyaContent": "فاعلم أنه لا إلٰه إلا الله واستغفر لذنبك وللمؤمنين والمؤمنات ۗ والله يعلم متقلبكم ومثواكم",
          "AyaNumber": 4564,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89172f61d12bf6abd6f3"
          },
          "AyaContent": "ويقول الذين آمنوا لولا نزلت سورة ۖ فإذا أنزلت سورة محكمة وذكر فيها القتال ۙ رأيت الذين في قلوبهم مرض ينظرون إليك نظر المغشي عليه من الموت ۖ فأولىٰ لهم",
          "AyaNumber": 4565,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89182f61d12bf6abd6f4"
          },
          "AyaContent": "طاعة وقول معروف ۚ فإذا عزم الأمر فلو صدقوا الله لكان خيرا لهم",
          "AyaNumber": 4566,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89182f61d12bf6abd6f5"
          },
          "AyaContent": "فهل عسيتم إن توليتم أن تفسدوا في الأرض وتقطعوا أرحامكم",
          "AyaNumber": 4567,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89182f61d12bf6abd6f6"
          },
          "AyaContent": "أولٰئك الذين لعنهم الله فأصمهم وأعمىٰ أبصارهم",
          "AyaNumber": 4568,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89182f61d12bf6abd6f7"
          },
          "AyaContent": "أفلا يتدبرون القرآن أم علىٰ قلوب أقفالها",
          "AyaNumber": 4569,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89182f61d12bf6abd6f8"
          },
          "AyaContent": "إن الذين ارتدوا علىٰ أدبارهم من بعد ما تبين لهم الهدى ۙ الشيطان سول لهم وأملىٰ لهم",
          "AyaNumber": 4570,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89192f61d12bf6abd6f9"
          },
          "AyaContent": "ذٰلك بأنهم قالوا للذين كرهوا ما نزل الله سنطيعكم في بعض الأمر ۖ والله يعلم إسرارهم",
          "AyaNumber": 4571,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89192f61d12bf6abd6fa"
          },
          "AyaContent": "فكيف إذا توفتهم الملائكة يضربون وجوههم وأدبارهم",
          "AyaNumber": 4572,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89192f61d12bf6abd6fb"
          },
          "AyaContent": "ذٰلك بأنهم اتبعوا ما أسخط الله وكرهوا رضوانه فأحبط أعمالهم",
          "AyaNumber": 4573,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd89192f61d12bf6abd6fc"
          },
          "AyaContent": "أم حسب الذين في قلوبهم مرض أن لن يخرج الله أضغانهم",
          "AyaNumber": 4574,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891a2f61d12bf6abd6fd"
          },
          "AyaContent": "ولو نشاء لأريناكهم فلعرفتهم بسيماهم ۚ ولتعرفنهم في لحن القول ۚ والله يعلم أعمالكم",
          "AyaNumber": 4575,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891a2f61d12bf6abd6fe"
          },
          "AyaContent": "ولنبلونكم حتىٰ نعلم المجاهدين منكم والصابرين ونبلو أخباركم",
          "AyaNumber": 4576,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891a2f61d12bf6abd6ff"
          },
          "AyaContent": "إن الذين كفروا وصدوا عن سبيل الله وشاقوا الرسول من بعد ما تبين لهم الهدىٰ لن يضروا الله شيئا وسيحبط أعمالهم",
          "AyaNumber": 4577,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891a2f61d12bf6abd700"
          },
          "AyaContent": "۞ يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول ولا تبطلوا أعمالكم",
          "AyaNumber": 4578,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891a2f61d12bf6abd701"
          },
          "AyaContent": "إن الذين كفروا وصدوا عن سبيل الله ثم ماتوا وهم كفار فلن يغفر الله لهم",
          "AyaNumber": 4579,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891a2f61d12bf6abd702"
          },
          "AyaContent": "فلا تهنوا وتدعوا إلى السلم وأنتم الأعلون والله معكم ولن يتركم أعمالكم",
          "AyaNumber": 4580,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891b2f61d12bf6abd703"
          },
          "AyaContent": "إنما الحياة الدنيا لعب ولهو ۚ وإن تؤمنوا وتتقوا يؤتكم أجوركم ولا يسألكم أموالكم",
          "AyaNumber": 4581,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891b2f61d12bf6abd704"
          },
          "AyaContent": "إن يسألكموها فيحفكم تبخلوا ويخرج أضغانكم",
          "AyaNumber": 4582,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891b2f61d12bf6abd705"
          },
          "AyaContent": "ها أنتم هٰؤلاء تدعون لتنفقوا في سبيل الله فمنكم من يبخل ۖ ومن يبخل فإنما يبخل عن نفسه ۚ والله الغني وأنتم الفقراء ۚ وإن تتولوا يستبدل قوما غيركم ثم لا يكونوا أمثالكم",
          "AyaNumber": 4583,
          "SurahName": "سورة محمد",
          "SurahNumber": 47
        },{
          "_id": {
            "$oid": "5ecd891b2f61d12bf6abd706"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم إنا فتحنا لك فتحا مبينا",
          "AyaNumber": 4584,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891c2f61d12bf6abd707"
          },
          "AyaContent": "ليغفر لك الله ما تقدم من ذنبك وما تأخر ويتم نعمته عليك ويهديك صراطا مستقيما",
          "AyaNumber": 4585,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891c2f61d12bf6abd708"
          },
          "AyaContent": "وينصرك الله نصرا عزيزا",
          "AyaNumber": 4586,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891c2f61d12bf6abd709"
          },
          "AyaContent": "هو الذي أنزل السكينة في قلوب المؤمنين ليزدادوا إيمانا مع إيمانهم ۗ ولله جنود السماوات والأرض ۚ وكان الله عليما حكيما",
          "AyaNumber": 4587,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891c2f61d12bf6abd70a"
          },
          "AyaContent": "ليدخل المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ويكفر عنهم سيئاتهم ۚ وكان ذٰلك عند الله فوزا عظيما",
          "AyaNumber": 4588,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891d2f61d12bf6abd70b"
          },
          "AyaContent": "ويعذب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السوء ۚ عليهم دائرة السوء ۖ وغضب الله عليهم ولعنهم وأعد لهم جهنم ۖ وساءت مصيرا",
          "AyaNumber": 4589,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891d2f61d12bf6abd70c"
          },
          "AyaContent": "ولله جنود السماوات والأرض ۚ وكان الله عزيزا حكيما",
          "AyaNumber": 4590,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891d2f61d12bf6abd70d"
          },
          "AyaContent": "إنا أرسلناك شاهدا ومبشرا ونذيرا",
          "AyaNumber": 4591,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891d2f61d12bf6abd70e"
          },
          "AyaContent": "لتؤمنوا بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة وأصيلا",
          "AyaNumber": 4592,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891d2f61d12bf6abd70f"
          },
          "AyaContent": "إن الذين يبايعونك إنما يبايعون الله يد الله فوق أيديهم ۚ فمن نكث فإنما ينكث علىٰ نفسه ۖ ومن أوفىٰ بما عاهد عليه الله فسيؤتيه أجرا عظيما",
          "AyaNumber": 4593,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891e2f61d12bf6abd710"
          },
          "AyaContent": "سيقول لك المخلفون من الأعراب شغلتنا أموالنا وأهلونا فاستغفر لنا ۚ يقولون بألسنتهم ما ليس في قلوبهم ۚ قل فمن يملك لكم من الله شيئا إن أراد بكم ضرا أو أراد بكم نفعا ۚ بل كان الله بما تعملون خبيرا",
          "AyaNumber": 4594,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891e2f61d12bf6abd711"
          },
          "AyaContent": "بل ظننتم أن لن ينقلب الرسول والمؤمنون إلىٰ أهليهم أبدا وزين ذٰلك في قلوبكم وظننتم ظن السوء وكنتم قوما بورا",
          "AyaNumber": 4595,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891e2f61d12bf6abd712"
          },
          "AyaContent": "ومن لم يؤمن بالله ورسوله فإنا أعتدنا للكافرين سعيرا",
          "AyaNumber": 4596,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891e2f61d12bf6abd713"
          },
          "AyaContent": "ولله ملك السماوات والأرض ۚ يغفر لمن يشاء ويعذب من يشاء ۚ وكان الله غفورا رحيما",
          "AyaNumber": 4597,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891e2f61d12bf6abd714"
          },
          "AyaContent": "سيقول المخلفون إذا انطلقتم إلىٰ مغانم لتأخذوها ذرونا نتبعكم ۖ يريدون أن يبدلوا كلام الله ۚ قل لن تتبعونا كذٰلكم قال الله من قبل ۖ فسيقولون بل تحسدوننا ۚ بل كانوا لا يفقهون إلا قليلا",
          "AyaNumber": 4598,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891f2f61d12bf6abd715"
          },
          "AyaContent": "قل للمخلفين من الأعراب ستدعون إلىٰ قوم أولي بأس شديد تقاتلونهم أو يسلمون ۖ فإن تطيعوا يؤتكم الله أجرا حسنا ۖ وإن تتولوا كما توليتم من قبل يعذبكم عذابا أليما",
          "AyaNumber": 4599,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891f2f61d12bf6abd716"
          },
          "AyaContent": "ليس على الأعمىٰ حرج ولا على الأعرج حرج ولا على المريض حرج ۗ ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار ۖ ومن يتول يعذبه عذابا أليما",
          "AyaNumber": 4600,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891f2f61d12bf6abd717"
          },
          "AyaContent": "۞ لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة فعلم ما في قلوبهم فأنزل السكينة عليهم وأثابهم فتحا قريبا",
          "AyaNumber": 4601,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891f2f61d12bf6abd718"
          },
          "AyaContent": "ومغانم كثيرة يأخذونها ۗ وكان الله عزيزا حكيما",
          "AyaNumber": 4602,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891f2f61d12bf6abd719"
          },
          "AyaContent": "وعدكم الله مغانم كثيرة تأخذونها فعجل لكم هٰذه وكف أيدي الناس عنكم ولتكون آية للمؤمنين ويهديكم صراطا مستقيما",
          "AyaNumber": 4603,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd891f2f61d12bf6abd71a"
          },
          "AyaContent": "وأخرىٰ لم تقدروا عليها قد أحاط الله بها ۚ وكان الله علىٰ كل شيء قديرا",
          "AyaNumber": 4604,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89202f61d12bf6abd71b"
          },
          "AyaContent": "ولو قاتلكم الذين كفروا لولوا الأدبار ثم لا يجدون وليا ولا نصيرا",
          "AyaNumber": 4605,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89202f61d12bf6abd71c"
          },
          "AyaContent": "سنة الله التي قد خلت من قبل ۖ ولن تجد لسنة الله تبديلا",
          "AyaNumber": 4606,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89202f61d12bf6abd71d"
          },
          "AyaContent": "وهو الذي كف أيديهم عنكم وأيديكم عنهم ببطن مكة من بعد أن أظفركم عليهم ۚ وكان الله بما تعملون بصيرا",
          "AyaNumber": 4607,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89212f61d12bf6abd71e"
          },
          "AyaContent": "هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا أن يبلغ محله ۚ ولولا رجال مؤمنون ونساء مؤمنات لم تعلموهم أن تطئوهم فتصيبكم منهم معرة بغير علم ۖ ليدخل الله في رحمته من يشاء ۚ لو تزيلوا لعذبنا الذين كفروا منهم عذابا أليما",
          "AyaNumber": 4608,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89212f61d12bf6abd71f"
          },
          "AyaContent": "إذ جعل الذين كفروا في قلوبهم الحمية حمية الجاهلية فأنزل الله سكينته علىٰ رسوله وعلى المؤمنين وألزمهم كلمة التقوىٰ وكانوا أحق بها وأهلها ۚ وكان الله بكل شيء عليما",
          "AyaNumber": 4609,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89212f61d12bf6abd720"
          },
          "AyaContent": "لقد صدق الله رسوله الرؤيا بالحق ۖ لتدخلن المسجد الحرام إن شاء الله آمنين محلقين رءوسكم ومقصرين لا تخافون ۖ فعلم ما لم تعلموا فجعل من دون ذٰلك فتحا قريبا",
          "AyaNumber": 4610,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89212f61d12bf6abd721"
          },
          "AyaContent": "هو الذي أرسل رسوله بالهدىٰ ودين الحق ليظهره على الدين كله ۚ وكفىٰ بالله شهيدا",
          "AyaNumber": 4611,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89222f61d12bf6abd722"
          },
          "AyaContent": "محمد رسول الله ۚ والذين معه أشداء على الكفار رحماء بينهم ۖ تراهم ركعا سجدا يبتغون فضلا من الله ورضوانا ۖ سيماهم في وجوههم من أثر السجود ۚ ذٰلك مثلهم في التوراة ۚ ومثلهم في الإنجيل كزرع أخرج شطأه فآزره فاستغلظ فاستوىٰ علىٰ سوقه يعجب الزراع ليغيظ بهم الكفار ۗ وعد الله الذين آمنوا وعملوا الصالحات منهم مغفرة وأجرا عظيما",
          "AyaNumber": 4612,
          "SurahName": "سورة الفتح",
          "SurahNumber": 48
        },{
          "_id": {
            "$oid": "5ecd89222f61d12bf6abd723"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله ۖ واتقوا الله ۚ إن الله سميع عليم",
          "AyaNumber": 4613,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89222f61d12bf6abd724"
          },
          "AyaContent": "يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض أن تحبط أعمالكم وأنتم لا تشعرون",
          "AyaNumber": 4614,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89222f61d12bf6abd725"
          },
          "AyaContent": "إن الذين يغضون أصواتهم عند رسول الله أولٰئك الذين امتحن الله قلوبهم للتقوىٰ ۚ لهم مغفرة وأجر عظيم",
          "AyaNumber": 4615,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89232f61d12bf6abd726"
          },
          "AyaContent": "إن الذين ينادونك من وراء الحجرات أكثرهم لا يعقلون",
          "AyaNumber": 4616,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89232f61d12bf6abd727"
          },
          "AyaContent": "ولو أنهم صبروا حتىٰ تخرج إليهم لكان خيرا لهم ۚ والله غفور رحيم",
          "AyaNumber": 4617,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89232f61d12bf6abd728"
          },
          "AyaContent": "يا أيها الذين آمنوا إن جاءكم فاسق بنبإ فتبينوا أن تصيبوا قوما بجهالة فتصبحوا علىٰ ما فعلتم نادمين",
          "AyaNumber": 4618,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89232f61d12bf6abd729"
          },
          "AyaContent": "واعلموا أن فيكم رسول الله ۚ لو يطيعكم في كثير من الأمر لعنتم ولٰكن الله حبب إليكم الإيمان وزينه في قلوبكم وكره إليكم الكفر والفسوق والعصيان ۚ أولٰئك هم الراشدون",
          "AyaNumber": 4619,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89242f61d12bf6abd72a"
          },
          "AyaContent": "فضلا من الله ونعمة ۚ والله عليم حكيم",
          "AyaNumber": 4620,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89242f61d12bf6abd72b"
          },
          "AyaContent": "وإن طائفتان من المؤمنين اقتتلوا فأصلحوا بينهما ۖ فإن بغت إحداهما على الأخرىٰ فقاتلوا التي تبغي حتىٰ تفيء إلىٰ أمر الله ۚ فإن فاءت فأصلحوا بينهما بالعدل وأقسطوا ۖ إن الله يحب المقسطين",
          "AyaNumber": 4621,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89242f61d12bf6abd72c"
          },
          "AyaContent": "إنما المؤمنون إخوة فأصلحوا بين أخويكم ۚ واتقوا الله لعلكم ترحمون",
          "AyaNumber": 4622,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89242f61d12bf6abd72d"
          },
          "AyaContent": "يا أيها الذين آمنوا لا يسخر قوم من قوم عسىٰ أن يكونوا خيرا منهم ولا نساء من نساء عسىٰ أن يكن خيرا منهن ۖ ولا تلمزوا أنفسكم ولا تنابزوا بالألقاب ۖ بئس الاسم الفسوق بعد الإيمان ۚ ومن لم يتب فأولٰئك هم الظالمون",
          "AyaNumber": 4623,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89242f61d12bf6abd72e"
          },
          "AyaContent": "يا أيها الذين آمنوا اجتنبوا كثيرا من الظن إن بعض الظن إثم ۖ ولا تجسسوا ولا يغتب بعضكم بعضا ۚ أيحب أحدكم أن يأكل لحم أخيه ميتا فكرهتموه ۚ واتقوا الله ۚ إن الله تواب رحيم",
          "AyaNumber": 4624,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89252f61d12bf6abd72f"
          },
          "AyaContent": "يا أيها الناس إنا خلقناكم من ذكر وأنثىٰ وجعلناكم شعوبا وقبائل لتعارفوا ۚ إن أكرمكم عند الله أتقاكم ۚ إن الله عليم خبير",
          "AyaNumber": 4625,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89252f61d12bf6abd730"
          },
          "AyaContent": "۞ قالت الأعراب آمنا ۖ قل لم تؤمنوا ولٰكن قولوا أسلمنا ولما يدخل الإيمان في قلوبكم ۖ وإن تطيعوا الله ورسوله لا يلتكم من أعمالكم شيئا ۚ إن الله غفور رحيم",
          "AyaNumber": 4626,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89282f61d12bf6abd731"
          },
          "AyaContent": "إنما المؤمنون الذين آمنوا بالله ورسوله ثم لم يرتابوا وجاهدوا بأموالهم وأنفسهم في سبيل الله ۚ أولٰئك هم الصادقون",
          "AyaNumber": 4627,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89282f61d12bf6abd732"
          },
          "AyaContent": "قل أتعلمون الله بدينكم والله يعلم ما في السماوات وما في الأرض ۚ والله بكل شيء عليم",
          "AyaNumber": 4628,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89282f61d12bf6abd733"
          },
          "AyaContent": "يمنون عليك أن أسلموا ۖ قل لا تمنوا علي إسلامكم ۖ بل الله يمن عليكم أن هداكم للإيمان إن كنتم صادقين",
          "AyaNumber": 4629,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89282f61d12bf6abd734"
          },
          "AyaContent": "إن الله يعلم غيب السماوات والأرض ۚ والله بصير بما تعملون",
          "AyaNumber": 4630,
          "SurahName": "سورة الحجرات",
          "SurahNumber": 49
        },{
          "_id": {
            "$oid": "5ecd89282f61d12bf6abd735"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم ق ۚ والقرآن المجيد",
          "AyaNumber": 4631,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89282f61d12bf6abd736"
          },
          "AyaContent": "بل عجبوا أن جاءهم منذر منهم فقال الكافرون هٰذا شيء عجيب",
          "AyaNumber": 4632,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89292f61d12bf6abd737"
          },
          "AyaContent": "أإذا متنا وكنا ترابا ۖ ذٰلك رجع بعيد",
          "AyaNumber": 4633,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89292f61d12bf6abd738"
          },
          "AyaContent": "قد علمنا ما تنقص الأرض منهم ۖ وعندنا كتاب حفيظ",
          "AyaNumber": 4634,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89292f61d12bf6abd739"
          },
          "AyaContent": "بل كذبوا بالحق لما جاءهم فهم في أمر مريج",
          "AyaNumber": 4635,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89292f61d12bf6abd73a"
          },
          "AyaContent": "أفلم ينظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج",
          "AyaNumber": 4636,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892a2f61d12bf6abd73b"
          },
          "AyaContent": "والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل زوج بهيج",
          "AyaNumber": 4637,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892a2f61d12bf6abd73c"
          },
          "AyaContent": "تبصرة وذكرىٰ لكل عبد منيب",
          "AyaNumber": 4638,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892b2f61d12bf6abd73d"
          },
          "AyaContent": "ونزلنا من السماء ماء مباركا فأنبتنا به جنات وحب الحصيد",
          "AyaNumber": 4639,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892b2f61d12bf6abd73e"
          },
          "AyaContent": "والنخل باسقات لها طلع نضيد",
          "AyaNumber": 4640,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892d2f61d12bf6abd73f"
          },
          "AyaContent": "رزقا للعباد ۖ وأحيينا به بلدة ميتا ۚ كذٰلك الخروج",
          "AyaNumber": 4641,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892e2f61d12bf6abd740"
          },
          "AyaContent": "كذبت قبلهم قوم نوح وأصحاب الرس وثمود",
          "AyaNumber": 4642,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892f2f61d12bf6abd741"
          },
          "AyaContent": "وعاد وفرعون وإخوان لوط",
          "AyaNumber": 4643,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd892f2f61d12bf6abd742"
          },
          "AyaContent": "وأصحاب الأيكة وقوم تبع ۚ كل كذب الرسل فحق وعيد",
          "AyaNumber": 4644,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89302f61d12bf6abd743"
          },
          "AyaContent": "أفعيينا بالخلق الأول ۚ بل هم في لبس من خلق جديد",
          "AyaNumber": 4645,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89302f61d12bf6abd744"
          },
          "AyaContent": "ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه ۖ ونحن أقرب إليه من حبل الوريد",
          "AyaNumber": 4646,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89312f61d12bf6abd745"
          },
          "AyaContent": "إذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد",
          "AyaNumber": 4647,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89312f61d12bf6abd746"
          },
          "AyaContent": "ما يلفظ من قول إلا لديه رقيب عتيد",
          "AyaNumber": 4648,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89312f61d12bf6abd747"
          },
          "AyaContent": "وجاءت سكرة الموت بالحق ۖ ذٰلك ما كنت منه تحيد",
          "AyaNumber": 4649,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89312f61d12bf6abd748"
          },
          "AyaContent": "ونفخ في الصور ۚ ذٰلك يوم الوعيد",
          "AyaNumber": 4650,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89322f61d12bf6abd749"
          },
          "AyaContent": "وجاءت كل نفس معها سائق وشهيد",
          "AyaNumber": 4651,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89322f61d12bf6abd74a"
          },
          "AyaContent": "لقد كنت في غفلة من هٰذا فكشفنا عنك غطاءك فبصرك اليوم حديد",
          "AyaNumber": 4652,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89332f61d12bf6abd74b"
          },
          "AyaContent": "وقال قرينه هٰذا ما لدي عتيد",
          "AyaNumber": 4653,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89332f61d12bf6abd74c"
          },
          "AyaContent": "ألقيا في جهنم كل كفار عنيد",
          "AyaNumber": 4654,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89342f61d12bf6abd74d"
          },
          "AyaContent": "مناع للخير معتد مريب",
          "AyaNumber": 4655,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89342f61d12bf6abd74e"
          },
          "AyaContent": "الذي جعل مع الله إلٰها آخر فألقياه في العذاب الشديد",
          "AyaNumber": 4656,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89342f61d12bf6abd74f"
          },
          "AyaContent": "۞ قال قرينه ربنا ما أطغيته ولٰكن كان في ضلال بعيد",
          "AyaNumber": 4657,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89352f61d12bf6abd750"
          },
          "AyaContent": "قال لا تختصموا لدي وقد قدمت إليكم بالوعيد",
          "AyaNumber": 4658,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89362f61d12bf6abd751"
          },
          "AyaContent": "ما يبدل القول لدي وما أنا بظلام للعبيد",
          "AyaNumber": 4659,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89362f61d12bf6abd752"
          },
          "AyaContent": "يوم نقول لجهنم هل امتلأت وتقول هل من مزيد",
          "AyaNumber": 4660,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89372f61d12bf6abd753"
          },
          "AyaContent": "وأزلفت الجنة للمتقين غير بعيد",
          "AyaNumber": 4661,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89372f61d12bf6abd754"
          },
          "AyaContent": "هٰذا ما توعدون لكل أواب حفيظ",
          "AyaNumber": 4662,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd89392f61d12bf6abd755"
          },
          "AyaContent": "من خشي الرحمٰن بالغيب وجاء بقلب منيب",
          "AyaNumber": 4663,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893b2f61d12bf6abd756"
          },
          "AyaContent": "ادخلوها بسلام ۖ ذٰلك يوم الخلود",
          "AyaNumber": 4664,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893b2f61d12bf6abd757"
          },
          "AyaContent": "لهم ما يشاءون فيها ولدينا مزيد",
          "AyaNumber": 4665,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893c2f61d12bf6abd758"
          },
          "AyaContent": "وكم أهلكنا قبلهم من قرن هم أشد منهم بطشا فنقبوا في البلاد هل من محيص",
          "AyaNumber": 4666,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893c2f61d12bf6abd759"
          },
          "AyaContent": "إن في ذٰلك لذكرىٰ لمن كان له قلب أو ألقى السمع وهو شهيد",
          "AyaNumber": 4667,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893c2f61d12bf6abd75a"
          },
          "AyaContent": "ولقد خلقنا السماوات والأرض وما بينهما في ستة أيام وما مسنا من لغوب",
          "AyaNumber": 4668,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893d2f61d12bf6abd75b"
          },
          "AyaContent": "فاصبر علىٰ ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل الغروب",
          "AyaNumber": 4669,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893d2f61d12bf6abd75c"
          },
          "AyaContent": "ومن الليل فسبحه وأدبار السجود",
          "AyaNumber": 4670,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893d2f61d12bf6abd75d"
          },
          "AyaContent": "واستمع يوم يناد المناد من مكان قريب",
          "AyaNumber": 4671,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893e2f61d12bf6abd75e"
          },
          "AyaContent": "يوم يسمعون الصيحة بالحق ۚ ذٰلك يوم الخروج",
          "AyaNumber": 4672,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893e2f61d12bf6abd75f"
          },
          "AyaContent": "إنا نحن نحيي ونميت وإلينا المصير",
          "AyaNumber": 4673,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893e2f61d12bf6abd760"
          },
          "AyaContent": "يوم تشقق الأرض عنهم سراعا ۚ ذٰلك حشر علينا يسير",
          "AyaNumber": 4674,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893e2f61d12bf6abd761"
          },
          "AyaContent": "نحن أعلم بما يقولون ۖ وما أنت عليهم بجبار ۖ فذكر بالقرآن من يخاف وعيد",
          "AyaNumber": 4675,
          "SurahName": "سورة ق",
          "SurahNumber": 50
        },{
          "_id": {
            "$oid": "5ecd893f2f61d12bf6abd762"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والذاريات ذروا",
          "AyaNumber": 4676,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd893f2f61d12bf6abd763"
          },
          "AyaContent": "فالحاملات وقرا",
          "AyaNumber": 4677,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd893f2f61d12bf6abd764"
          },
          "AyaContent": "فالجاريات يسرا",
          "AyaNumber": 4678,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89402f61d12bf6abd765"
          },
          "AyaContent": "فالمقسمات أمرا",
          "AyaNumber": 4679,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89402f61d12bf6abd766"
          },
          "AyaContent": "إنما توعدون لصادق",
          "AyaNumber": 4680,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89412f61d12bf6abd767"
          },
          "AyaContent": "وإن الدين لواقع",
          "AyaNumber": 4681,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89412f61d12bf6abd768"
          },
          "AyaContent": "والسماء ذات الحبك",
          "AyaNumber": 4682,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89412f61d12bf6abd769"
          },
          "AyaContent": "إنكم لفي قول مختلف",
          "AyaNumber": 4683,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89432f61d12bf6abd76a"
          },
          "AyaContent": "يؤفك عنه من أفك",
          "AyaNumber": 4684,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89432f61d12bf6abd76b"
          },
          "AyaContent": "قتل الخراصون",
          "AyaNumber": 4685,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89432f61d12bf6abd76c"
          },
          "AyaContent": "الذين هم في غمرة ساهون",
          "AyaNumber": 4686,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89432f61d12bf6abd76d"
          },
          "AyaContent": "يسألون أيان يوم الدين",
          "AyaNumber": 4687,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89432f61d12bf6abd76e"
          },
          "AyaContent": "يوم هم على النار يفتنون",
          "AyaNumber": 4688,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89432f61d12bf6abd76f"
          },
          "AyaContent": "ذوقوا فتنتكم هٰذا الذي كنتم به تستعجلون",
          "AyaNumber": 4689,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89442f61d12bf6abd770"
          },
          "AyaContent": "إن المتقين في جنات وعيون",
          "AyaNumber": 4690,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89442f61d12bf6abd771"
          },
          "AyaContent": "آخذين ما آتاهم ربهم ۚ إنهم كانوا قبل ذٰلك محسنين",
          "AyaNumber": 4691,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89442f61d12bf6abd772"
          },
          "AyaContent": "كانوا قليلا من الليل ما يهجعون",
          "AyaNumber": 4692,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89452f61d12bf6abd773"
          },
          "AyaContent": "وبالأسحار هم يستغفرون",
          "AyaNumber": 4693,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89462f61d12bf6abd774"
          },
          "AyaContent": "وفي أموالهم حق للسائل والمحروم",
          "AyaNumber": 4694,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89462f61d12bf6abd775"
          },
          "AyaContent": "وفي الأرض آيات للموقنين",
          "AyaNumber": 4695,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89462f61d12bf6abd776"
          },
          "AyaContent": "وفي أنفسكم ۚ أفلا تبصرون",
          "AyaNumber": 4696,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89472f61d12bf6abd777"
          },
          "AyaContent": "وفي السماء رزقكم وما توعدون",
          "AyaNumber": 4697,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89472f61d12bf6abd778"
          },
          "AyaContent": "فورب السماء والأرض إنه لحق مثل ما أنكم تنطقون",
          "AyaNumber": 4698,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89472f61d12bf6abd779"
          },
          "AyaContent": "هل أتاك حديث ضيف إبراهيم المكرمين",
          "AyaNumber": 4699,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89482f61d12bf6abd77a"
          },
          "AyaContent": "إذ دخلوا عليه فقالوا سلاما ۖ قال سلام قوم منكرون",
          "AyaNumber": 4700,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894a2f61d12bf6abd77b"
          },
          "AyaContent": "فراغ إلىٰ أهله فجاء بعجل سمين",
          "AyaNumber": 4701,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894b2f61d12bf6abd77c"
          },
          "AyaContent": "فقربه إليهم قال ألا تأكلون",
          "AyaNumber": 4702,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894c2f61d12bf6abd77d"
          },
          "AyaContent": "فأوجس منهم خيفة ۖ قالوا لا تخف ۖ وبشروه بغلام عليم",
          "AyaNumber": 4703,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894c2f61d12bf6abd77e"
          },
          "AyaContent": "فأقبلت امرأته في صرة فصكت وجهها وقالت عجوز عقيم",
          "AyaNumber": 4704,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894c2f61d12bf6abd77f"
          },
          "AyaContent": "قالوا كذٰلك قال ربك ۖ إنه هو الحكيم العليم",
          "AyaNumber": 4705,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894d2f61d12bf6abd780"
          },
          "AyaContent": "۞ قال فما خطبكم أيها المرسلون",
          "AyaNumber": 4706,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894e2f61d12bf6abd781"
          },
          "AyaContent": "قالوا إنا أرسلنا إلىٰ قوم مجرمين",
          "AyaNumber": 4707,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894e2f61d12bf6abd782"
          },
          "AyaContent": "لنرسل عليهم حجارة من طين",
          "AyaNumber": 4708,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894e2f61d12bf6abd783"
          },
          "AyaContent": "مسومة عند ربك للمسرفين",
          "AyaNumber": 4709,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894e2f61d12bf6abd784"
          },
          "AyaContent": "فأخرجنا من كان فيها من المؤمنين",
          "AyaNumber": 4710,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894e2f61d12bf6abd785"
          },
          "AyaContent": "فما وجدنا فيها غير بيت من المسلمين",
          "AyaNumber": 4711,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894f2f61d12bf6abd786"
          },
          "AyaContent": "وتركنا فيها آية للذين يخافون العذاب الأليم",
          "AyaNumber": 4712,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894f2f61d12bf6abd787"
          },
          "AyaContent": "وفي موسىٰ إذ أرسلناه إلىٰ فرعون بسلطان مبين",
          "AyaNumber": 4713,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd894f2f61d12bf6abd788"
          },
          "AyaContent": "فتولىٰ بركنه وقال ساحر أو مجنون",
          "AyaNumber": 4714,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89502f61d12bf6abd789"
          },
          "AyaContent": "فأخذناه وجنوده فنبذناهم في اليم وهو مليم",
          "AyaNumber": 4715,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89502f61d12bf6abd78a"
          },
          "AyaContent": "وفي عاد إذ أرسلنا عليهم الريح العقيم",
          "AyaNumber": 4716,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89512f61d12bf6abd78b"
          },
          "AyaContent": "ما تذر من شيء أتت عليه إلا جعلته كالرميم",
          "AyaNumber": 4717,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89522f61d12bf6abd78c"
          },
          "AyaContent": "وفي ثمود إذ قيل لهم تمتعوا حتىٰ حين",
          "AyaNumber": 4718,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89522f61d12bf6abd78d"
          },
          "AyaContent": "فعتوا عن أمر ربهم فأخذتهم الصاعقة وهم ينظرون",
          "AyaNumber": 4719,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89522f61d12bf6abd78e"
          },
          "AyaContent": "فما استطاعوا من قيام وما كانوا منتصرين",
          "AyaNumber": 4720,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89522f61d12bf6abd78f"
          },
          "AyaContent": "وقوم نوح من قبل ۖ إنهم كانوا قوما فاسقين",
          "AyaNumber": 4721,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89532f61d12bf6abd790"
          },
          "AyaContent": "والسماء بنيناها بأيد وإنا لموسعون",
          "AyaNumber": 4722,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89532f61d12bf6abd791"
          },
          "AyaContent": "والأرض فرشناها فنعم الماهدون",
          "AyaNumber": 4723,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89542f61d12bf6abd792"
          },
          "AyaContent": "ومن كل شيء خلقنا زوجين لعلكم تذكرون",
          "AyaNumber": 4724,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89542f61d12bf6abd793"
          },
          "AyaContent": "ففروا إلى الله ۖ إني لكم منه نذير مبين",
          "AyaNumber": 4725,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89552f61d12bf6abd794"
          },
          "AyaContent": "ولا تجعلوا مع الله إلٰها آخر ۖ إني لكم منه نذير مبين",
          "AyaNumber": 4726,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89562f61d12bf6abd795"
          },
          "AyaContent": "كذٰلك ما أتى الذين من قبلهم من رسول إلا قالوا ساحر أو مجنون",
          "AyaNumber": 4727,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89572f61d12bf6abd796"
          },
          "AyaContent": "أتواصوا به ۚ بل هم قوم طاغون",
          "AyaNumber": 4728,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89572f61d12bf6abd797"
          },
          "AyaContent": "فتول عنهم فما أنت بملوم",
          "AyaNumber": 4729,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89572f61d12bf6abd798"
          },
          "AyaContent": "وذكر فإن الذكرىٰ تنفع المؤمنين",
          "AyaNumber": 4730,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89572f61d12bf6abd799"
          },
          "AyaContent": "وما خلقت الجن والإنس إلا ليعبدون",
          "AyaNumber": 4731,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89582f61d12bf6abd79a"
          },
          "AyaContent": "ما أريد منهم من رزق وما أريد أن يطعمون",
          "AyaNumber": 4732,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89582f61d12bf6abd79b"
          },
          "AyaContent": "إن الله هو الرزاق ذو القوة المتين",
          "AyaNumber": 4733,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89582f61d12bf6abd79c"
          },
          "AyaContent": "فإن للذين ظلموا ذنوبا مثل ذنوب أصحابهم فلا يستعجلون",
          "AyaNumber": 4734,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89592f61d12bf6abd79d"
          },
          "AyaContent": "فويل للذين كفروا من يومهم الذي يوعدون",
          "AyaNumber": 4735,
          "SurahName": "سورة الذاريات",
          "SurahNumber": 51
        },{
          "_id": {
            "$oid": "5ecd89592f61d12bf6abd79e"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والطور",
          "AyaNumber": 4736,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89592f61d12bf6abd79f"
          },
          "AyaContent": "وكتاب مسطور",
          "AyaNumber": 4737,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89592f61d12bf6abd7a0"
          },
          "AyaContent": "في رق منشور",
          "AyaNumber": 4738,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89592f61d12bf6abd7a1"
          },
          "AyaContent": "والبيت المعمور",
          "AyaNumber": 4739,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89592f61d12bf6abd7a2"
          },
          "AyaContent": "والسقف المرفوع",
          "AyaNumber": 4740,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895b2f61d12bf6abd7a3"
          },
          "AyaContent": "والبحر المسجور",
          "AyaNumber": 4741,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895b2f61d12bf6abd7a4"
          },
          "AyaContent": "إن عذاب ربك لواقع",
          "AyaNumber": 4742,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895b2f61d12bf6abd7a5"
          },
          "AyaContent": "ما له من دافع",
          "AyaNumber": 4743,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895b2f61d12bf6abd7a6"
          },
          "AyaContent": "يوم تمور السماء مورا",
          "AyaNumber": 4744,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895c2f61d12bf6abd7a7"
          },
          "AyaContent": "وتسير الجبال سيرا",
          "AyaNumber": 4745,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895d2f61d12bf6abd7a8"
          },
          "AyaContent": "فويل يومئذ للمكذبين",
          "AyaNumber": 4746,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895d2f61d12bf6abd7a9"
          },
          "AyaContent": "الذين هم في خوض يلعبون",
          "AyaNumber": 4747,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895d2f61d12bf6abd7aa"
          },
          "AyaContent": "يوم يدعون إلىٰ نار جهنم دعا",
          "AyaNumber": 4748,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895e2f61d12bf6abd7ab"
          },
          "AyaContent": "هٰذه النار التي كنتم بها تكذبون",
          "AyaNumber": 4749,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895e2f61d12bf6abd7ac"
          },
          "AyaContent": "أفسحر هٰذا أم أنتم لا تبصرون",
          "AyaNumber": 4750,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895f2f61d12bf6abd7ad"
          },
          "AyaContent": "اصلوها فاصبروا أو لا تصبروا سواء عليكم ۖ إنما تجزون ما كنتم تعملون",
          "AyaNumber": 4751,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd895f2f61d12bf6abd7ae"
          },
          "AyaContent": "إن المتقين في جنات ونعيم",
          "AyaNumber": 4752,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89602f61d12bf6abd7af"
          },
          "AyaContent": "فاكهين بما آتاهم ربهم ووقاهم ربهم عذاب الجحيم",
          "AyaNumber": 4753,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89602f61d12bf6abd7b0"
          },
          "AyaContent": "كلوا واشربوا هنيئا بما كنتم تعملون",
          "AyaNumber": 4754,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89602f61d12bf6abd7b1"
          },
          "AyaContent": "متكئين علىٰ سرر مصفوفة ۖ وزوجناهم بحور عين",
          "AyaNumber": 4755,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89602f61d12bf6abd7b2"
          },
          "AyaContent": "والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم وما ألتناهم من عملهم من شيء ۚ كل امرئ بما كسب رهين",
          "AyaNumber": 4756,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89612f61d12bf6abd7b3"
          },
          "AyaContent": "وأمددناهم بفاكهة ولحم مما يشتهون",
          "AyaNumber": 4757,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89612f61d12bf6abd7b4"
          },
          "AyaContent": "يتنازعون فيها كأسا لا لغو فيها ولا تأثيم",
          "AyaNumber": 4758,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89612f61d12bf6abd7b5"
          },
          "AyaContent": "۞ ويطوف عليهم غلمان لهم كأنهم لؤلؤ مكنون",
          "AyaNumber": 4759,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89622f61d12bf6abd7b6"
          },
          "AyaContent": "وأقبل بعضهم علىٰ بعض يتساءلون",
          "AyaNumber": 4760,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89622f61d12bf6abd7b7"
          },
          "AyaContent": "قالوا إنا كنا قبل في أهلنا مشفقين",
          "AyaNumber": 4761,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89622f61d12bf6abd7b8"
          },
          "AyaContent": "فمن الله علينا ووقانا عذاب السموم",
          "AyaNumber": 4762,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89622f61d12bf6abd7b9"
          },
          "AyaContent": "إنا كنا من قبل ندعوه ۖ إنه هو البر الرحيم",
          "AyaNumber": 4763,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89632f61d12bf6abd7ba"
          },
          "AyaContent": "فذكر فما أنت بنعمت ربك بكاهن ولا مجنون",
          "AyaNumber": 4764,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89632f61d12bf6abd7bb"
          },
          "AyaContent": "أم يقولون شاعر نتربص به ريب المنون",
          "AyaNumber": 4765,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89642f61d12bf6abd7bc"
          },
          "AyaContent": "قل تربصوا فإني معكم من المتربصين",
          "AyaNumber": 4766,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89642f61d12bf6abd7bd"
          },
          "AyaContent": "أم تأمرهم أحلامهم بهٰذا ۚ أم هم قوم طاغون",
          "AyaNumber": 4767,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89652f61d12bf6abd7be"
          },
          "AyaContent": "أم يقولون تقوله ۚ بل لا يؤمنون",
          "AyaNumber": 4768,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89652f61d12bf6abd7bf"
          },
          "AyaContent": "فليأتوا بحديث مثله إن كانوا صادقين",
          "AyaNumber": 4769,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89652f61d12bf6abd7c0"
          },
          "AyaContent": "أم خلقوا من غير شيء أم هم الخالقون",
          "AyaNumber": 4770,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89652f61d12bf6abd7c1"
          },
          "AyaContent": "أم خلقوا السماوات والأرض ۚ بل لا يوقنون",
          "AyaNumber": 4771,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89662f61d12bf6abd7c2"
          },
          "AyaContent": "أم عندهم خزائن ربك أم هم المصيطرون",
          "AyaNumber": 4772,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89662f61d12bf6abd7c3"
          },
          "AyaContent": "أم لهم سلم يستمعون فيه ۖ فليأت مستمعهم بسلطان مبين",
          "AyaNumber": 4773,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89662f61d12bf6abd7c4"
          },
          "AyaContent": "أم له البنات ولكم البنون",
          "AyaNumber": 4774,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89672f61d12bf6abd7c5"
          },
          "AyaContent": "أم تسألهم أجرا فهم من مغرم مثقلون",
          "AyaNumber": 4775,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89672f61d12bf6abd7c6"
          },
          "AyaContent": "أم عندهم الغيب فهم يكتبون",
          "AyaNumber": 4776,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89672f61d12bf6abd7c7"
          },
          "AyaContent": "أم يريدون كيدا ۖ فالذين كفروا هم المكيدون",
          "AyaNumber": 4777,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89682f61d12bf6abd7c8"
          },
          "AyaContent": "أم لهم إلٰه غير الله ۚ سبحان الله عما يشركون",
          "AyaNumber": 4778,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89682f61d12bf6abd7c9"
          },
          "AyaContent": "وإن يروا كسفا من السماء ساقطا يقولوا سحاب مركوم",
          "AyaNumber": 4779,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd89682f61d12bf6abd7ca"
          },
          "AyaContent": "فذرهم حتىٰ يلاقوا يومهم الذي فيه يصعقون",
          "AyaNumber": 4780,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd896a2f61d12bf6abd7cb"
          },
          "AyaContent": "يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون",
          "AyaNumber": 4781,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd896a2f61d12bf6abd7cc"
          },
          "AyaContent": "وإن للذين ظلموا عذابا دون ذٰلك ولٰكن أكثرهم لا يعلمون",
          "AyaNumber": 4782,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd896a2f61d12bf6abd7cd"
          },
          "AyaContent": "واصبر لحكم ربك فإنك بأعيننا ۖ وسبح بحمد ربك حين تقوم",
          "AyaNumber": 4783,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd896a2f61d12bf6abd7ce"
          },
          "AyaContent": "ومن الليل فسبحه وإدبار النجوم",
          "AyaNumber": 4784,
          "SurahName": "سورة الطور",
          "SurahNumber": 52
        },{
          "_id": {
            "$oid": "5ecd896a2f61d12bf6abd7cf"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والنجم إذا هوىٰ",
          "AyaNumber": 4785,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896a2f61d12bf6abd7d0"
          },
          "AyaContent": "ما ضل صاحبكم وما غوىٰ",
          "AyaNumber": 4786,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896b2f61d12bf6abd7d1"
          },
          "AyaContent": "وما ينطق عن الهوىٰ",
          "AyaNumber": 4787,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896b2f61d12bf6abd7d2"
          },
          "AyaContent": "إن هو إلا وحي يوحىٰ",
          "AyaNumber": 4788,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896b2f61d12bf6abd7d3"
          },
          "AyaContent": "علمه شديد القوىٰ",
          "AyaNumber": 4789,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896b2f61d12bf6abd7d4"
          },
          "AyaContent": "ذو مرة فاستوىٰ",
          "AyaNumber": 4790,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896b2f61d12bf6abd7d5"
          },
          "AyaContent": "وهو بالأفق الأعلىٰ",
          "AyaNumber": 4791,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896c2f61d12bf6abd7d6"
          },
          "AyaContent": "ثم دنا فتدلىٰ",
          "AyaNumber": 4792,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896c2f61d12bf6abd7d7"
          },
          "AyaContent": "فكان قاب قوسين أو أدنىٰ",
          "AyaNumber": 4793,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896c2f61d12bf6abd7d8"
          },
          "AyaContent": "فأوحىٰ إلىٰ عبده ما أوحىٰ",
          "AyaNumber": 4794,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896c2f61d12bf6abd7d9"
          },
          "AyaContent": "ما كذب الفؤاد ما رأىٰ",
          "AyaNumber": 4795,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896d2f61d12bf6abd7da"
          },
          "AyaContent": "أفتمارونه علىٰ ما يرىٰ",
          "AyaNumber": 4796,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896d2f61d12bf6abd7db"
          },
          "AyaContent": "ولقد رآه نزلة أخرىٰ",
          "AyaNumber": 4797,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896d2f61d12bf6abd7dc"
          },
          "AyaContent": "عند سدرة المنتهىٰ",
          "AyaNumber": 4798,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896e2f61d12bf6abd7dd"
          },
          "AyaContent": "عندها جنة المأوىٰ",
          "AyaNumber": 4799,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896e2f61d12bf6abd7de"
          },
          "AyaContent": "إذ يغشى السدرة ما يغشىٰ",
          "AyaNumber": 4800,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896e2f61d12bf6abd7df"
          },
          "AyaContent": "ما زاغ البصر وما طغىٰ",
          "AyaNumber": 4801,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896f2f61d12bf6abd7e0"
          },
          "AyaContent": "لقد رأىٰ من آيات ربه الكبرىٰ",
          "AyaNumber": 4802,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896f2f61d12bf6abd7e1"
          },
          "AyaContent": "أفرأيتم اللات والعزىٰ",
          "AyaNumber": 4803,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd896f2f61d12bf6abd7e2"
          },
          "AyaContent": "ومناة الثالثة الأخرىٰ",
          "AyaNumber": 4804,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89702f61d12bf6abd7e3"
          },
          "AyaContent": "ألكم الذكر وله الأنثىٰ",
          "AyaNumber": 4805,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89702f61d12bf6abd7e4"
          },
          "AyaContent": "تلك إذا قسمة ضيزىٰ",
          "AyaNumber": 4806,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89702f61d12bf6abd7e5"
          },
          "AyaContent": "إن هي إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان ۚ إن يتبعون إلا الظن وما تهوى الأنفس ۖ ولقد جاءهم من ربهم الهدىٰ",
          "AyaNumber": 4807,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89702f61d12bf6abd7e6"
          },
          "AyaContent": "أم للإنسان ما تمنىٰ",
          "AyaNumber": 4808,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89712f61d12bf6abd7e7"
          },
          "AyaContent": "فلله الآخرة والأولىٰ",
          "AyaNumber": 4809,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89712f61d12bf6abd7e8"
          },
          "AyaContent": "۞ وكم من ملك في السماوات لا تغني شفاعتهم شيئا إلا من بعد أن يأذن الله لمن يشاء ويرضىٰ",
          "AyaNumber": 4810,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89712f61d12bf6abd7e9"
          },
          "AyaContent": "إن الذين لا يؤمنون بالآخرة ليسمون الملائكة تسمية الأنثىٰ",
          "AyaNumber": 4811,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89712f61d12bf6abd7ea"
          },
          "AyaContent": "وما لهم به من علم ۖ إن يتبعون إلا الظن ۖ وإن الظن لا يغني من الحق شيئا",
          "AyaNumber": 4812,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89722f61d12bf6abd7eb"
          },
          "AyaContent": "فأعرض عن من تولىٰ عن ذكرنا ولم يرد إلا الحياة الدنيا",
          "AyaNumber": 4813,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89722f61d12bf6abd7ec"
          },
          "AyaContent": "ذٰلك مبلغهم من العلم ۚ إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بمن اهتدىٰ",
          "AyaNumber": 4814,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89722f61d12bf6abd7ed"
          },
          "AyaContent": "ولله ما في السماوات وما في الأرض ليجزي الذين أساءوا بما عملوا ويجزي الذين أحسنوا بالحسنى",
          "AyaNumber": 4815,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89722f61d12bf6abd7ee"
          },
          "AyaContent": "الذين يجتنبون كبائر الإثم والفواحش إلا اللمم ۚ إن ربك واسع المغفرة ۚ هو أعلم بكم إذ أنشأكم من الأرض وإذ أنتم أجنة في بطون أمهاتكم ۖ فلا تزكوا أنفسكم ۖ هو أعلم بمن اتقىٰ",
          "AyaNumber": 4816,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89722f61d12bf6abd7ef"
          },
          "AyaContent": "أفرأيت الذي تولىٰ",
          "AyaNumber": 4817,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89732f61d12bf6abd7f0"
          },
          "AyaContent": "وأعطىٰ قليلا وأكدىٰ",
          "AyaNumber": 4818,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89732f61d12bf6abd7f1"
          },
          "AyaContent": "أعنده علم الغيب فهو يرىٰ",
          "AyaNumber": 4819,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89742f61d12bf6abd7f2"
          },
          "AyaContent": "أم لم ينبأ بما في صحف موسىٰ",
          "AyaNumber": 4820,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89742f61d12bf6abd7f3"
          },
          "AyaContent": "وإبراهيم الذي وفىٰ",
          "AyaNumber": 4821,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89742f61d12bf6abd7f4"
          },
          "AyaContent": "ألا تزر وازرة وزر أخرىٰ",
          "AyaNumber": 4822,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89742f61d12bf6abd7f5"
          },
          "AyaContent": "وأن ليس للإنسان إلا ما سعىٰ",
          "AyaNumber": 4823,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89752f61d12bf6abd7f6"
          },
          "AyaContent": "وأن سعيه سوف يرىٰ",
          "AyaNumber": 4824,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89752f61d12bf6abd7f7"
          },
          "AyaContent": "ثم يجزاه الجزاء الأوفىٰ",
          "AyaNumber": 4825,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89752f61d12bf6abd7f8"
          },
          "AyaContent": "وأن إلىٰ ربك المنتهىٰ",
          "AyaNumber": 4826,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89762f61d12bf6abd7f9"
          },
          "AyaContent": "وأنه هو أضحك وأبكىٰ",
          "AyaNumber": 4827,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89772f61d12bf6abd7fa"
          },
          "AyaContent": "وأنه هو أمات وأحيا",
          "AyaNumber": 4828,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89772f61d12bf6abd7fb"
          },
          "AyaContent": "وأنه خلق الزوجين الذكر والأنثىٰ",
          "AyaNumber": 4829,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89782f61d12bf6abd7fc"
          },
          "AyaContent": "من نطفة إذا تمنىٰ",
          "AyaNumber": 4830,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89782f61d12bf6abd7fd"
          },
          "AyaContent": "وأن عليه النشأة الأخرىٰ",
          "AyaNumber": 4831,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89782f61d12bf6abd7fe"
          },
          "AyaContent": "وأنه هو أغنىٰ وأقنىٰ",
          "AyaNumber": 4832,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89792f61d12bf6abd7ff"
          },
          "AyaContent": "وأنه هو رب الشعرىٰ",
          "AyaNumber": 4833,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89792f61d12bf6abd800"
          },
          "AyaContent": "وأنه أهلك عادا الأولىٰ",
          "AyaNumber": 4834,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89792f61d12bf6abd801"
          },
          "AyaContent": "وثمود فما أبقىٰ",
          "AyaNumber": 4835,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd89792f61d12bf6abd802"
          },
          "AyaContent": "وقوم نوح من قبل ۖ إنهم كانوا هم أظلم وأطغىٰ",
          "AyaNumber": 4836,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897a2f61d12bf6abd803"
          },
          "AyaContent": "والمؤتفكة أهوىٰ",
          "AyaNumber": 4837,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897a2f61d12bf6abd804"
          },
          "AyaContent": "فغشاها ما غشىٰ",
          "AyaNumber": 4838,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897a2f61d12bf6abd805"
          },
          "AyaContent": "فبأي آلاء ربك تتمارىٰ",
          "AyaNumber": 4839,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897b2f61d12bf6abd806"
          },
          "AyaContent": "هٰذا نذير من النذر الأولىٰ",
          "AyaNumber": 4840,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897b2f61d12bf6abd807"
          },
          "AyaContent": "أزفت الآزفة",
          "AyaNumber": 4841,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897b2f61d12bf6abd808"
          },
          "AyaContent": "ليس لها من دون الله كاشفة",
          "AyaNumber": 4842,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897b2f61d12bf6abd809"
          },
          "AyaContent": "أفمن هٰذا الحديث تعجبون",
          "AyaNumber": 4843,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897c2f61d12bf6abd80a"
          },
          "AyaContent": "وتضحكون ولا تبكون",
          "AyaNumber": 4844,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897c2f61d12bf6abd80b"
          },
          "AyaContent": "وأنتم سامدون",
          "AyaNumber": 4845,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897c2f61d12bf6abd80c"
          },
          "AyaContent": "فاسجدوا لله واعبدوا ۩",
          "AyaNumber": 4846,
          "SurahName": "سورة النجم",
          "SurahNumber": 53
        },{
          "_id": {
            "$oid": "5ecd897d2f61d12bf6abd80d"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم اقتربت الساعة وانشق القمر",
          "AyaNumber": 4847,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897d2f61d12bf6abd80e"
          },
          "AyaContent": "وإن يروا آية يعرضوا ويقولوا سحر مستمر",
          "AyaNumber": 4848,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897d2f61d12bf6abd80f"
          },
          "AyaContent": "وكذبوا واتبعوا أهواءهم ۚ وكل أمر مستقر",
          "AyaNumber": 4849,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897d2f61d12bf6abd810"
          },
          "AyaContent": "ولقد جاءهم من الأنباء ما فيه مزدجر",
          "AyaNumber": 4850,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897e2f61d12bf6abd811"
          },
          "AyaContent": "حكمة بالغة ۖ فما تغن النذر",
          "AyaNumber": 4851,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897e2f61d12bf6abd812"
          },
          "AyaContent": "فتول عنهم ۘ يوم يدع الداع إلىٰ شيء نكر",
          "AyaNumber": 4852,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897e2f61d12bf6abd813"
          },
          "AyaContent": "خشعا أبصارهم يخرجون من الأجداث كأنهم جراد منتشر",
          "AyaNumber": 4853,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897e2f61d12bf6abd814"
          },
          "AyaContent": "مهطعين إلى الداع ۖ يقول الكافرون هٰذا يوم عسر",
          "AyaNumber": 4854,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897f2f61d12bf6abd815"
          },
          "AyaContent": "۞ كذبت قبلهم قوم نوح فكذبوا عبدنا وقالوا مجنون وازدجر",
          "AyaNumber": 4855,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897f2f61d12bf6abd816"
          },
          "AyaContent": "فدعا ربه أني مغلوب فانتصر",
          "AyaNumber": 4856,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd897f2f61d12bf6abd817"
          },
          "AyaContent": "ففتحنا أبواب السماء بماء منهمر",
          "AyaNumber": 4857,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89802f61d12bf6abd818"
          },
          "AyaContent": "وفجرنا الأرض عيونا فالتقى الماء علىٰ أمر قد قدر",
          "AyaNumber": 4858,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89802f61d12bf6abd819"
          },
          "AyaContent": "وحملناه علىٰ ذات ألواح ودسر",
          "AyaNumber": 4859,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89802f61d12bf6abd81a"
          },
          "AyaContent": "تجري بأعيننا جزاء لمن كان كفر",
          "AyaNumber": 4860,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89812f61d12bf6abd81b"
          },
          "AyaContent": "ولقد تركناها آية فهل من مدكر",
          "AyaNumber": 4861,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89812f61d12bf6abd81c"
          },
          "AyaContent": "فكيف كان عذابي ونذر",
          "AyaNumber": 4862,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89812f61d12bf6abd81d"
          },
          "AyaContent": "ولقد يسرنا القرآن للذكر فهل من مدكر",
          "AyaNumber": 4863,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89812f61d12bf6abd81e"
          },
          "AyaContent": "كذبت عاد فكيف كان عذابي ونذر",
          "AyaNumber": 4864,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89822f61d12bf6abd81f"
          },
          "AyaContent": "إنا أرسلنا عليهم ريحا صرصرا في يوم نحس مستمر",
          "AyaNumber": 4865,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89822f61d12bf6abd820"
          },
          "AyaContent": "تنزع الناس كأنهم أعجاز نخل منقعر",
          "AyaNumber": 4866,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89822f61d12bf6abd821"
          },
          "AyaContent": "فكيف كان عذابي ونذر",
          "AyaNumber": 4867,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89832f61d12bf6abd822"
          },
          "AyaContent": "ولقد يسرنا القرآن للذكر فهل من مدكر",
          "AyaNumber": 4868,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89832f61d12bf6abd823"
          },
          "AyaContent": "كذبت ثمود بالنذر",
          "AyaNumber": 4869,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89832f61d12bf6abd824"
          },
          "AyaContent": "فقالوا أبشرا منا واحدا نتبعه إنا إذا لفي ضلال وسعر",
          "AyaNumber": 4870,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89842f61d12bf6abd825"
          },
          "AyaContent": "أألقي الذكر عليه من بيننا بل هو كذاب أشر",
          "AyaNumber": 4871,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89842f61d12bf6abd826"
          },
          "AyaContent": "سيعلمون غدا من الكذاب الأشر",
          "AyaNumber": 4872,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89842f61d12bf6abd827"
          },
          "AyaContent": "إنا مرسلو الناقة فتنة لهم فارتقبهم واصطبر",
          "AyaNumber": 4873,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89852f61d12bf6abd828"
          },
          "AyaContent": "ونبئهم أن الماء قسمة بينهم ۖ كل شرب محتضر",
          "AyaNumber": 4874,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89852f61d12bf6abd829"
          },
          "AyaContent": "فنادوا صاحبهم فتعاطىٰ فعقر",
          "AyaNumber": 4875,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89852f61d12bf6abd82a"
          },
          "AyaContent": "فكيف كان عذابي ونذر",
          "AyaNumber": 4876,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89852f61d12bf6abd82b"
          },
          "AyaContent": "إنا أرسلنا عليهم صيحة واحدة فكانوا كهشيم المحتظر",
          "AyaNumber": 4877,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89862f61d12bf6abd82c"
          },
          "AyaContent": "ولقد يسرنا القرآن للذكر فهل من مدكر",
          "AyaNumber": 4878,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89862f61d12bf6abd82d"
          },
          "AyaContent": "كذبت قوم لوط بالنذر",
          "AyaNumber": 4879,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89862f61d12bf6abd82e"
          },
          "AyaContent": "إنا أرسلنا عليهم حاصبا إلا آل لوط ۖ نجيناهم بسحر",
          "AyaNumber": 4880,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89872f61d12bf6abd82f"
          },
          "AyaContent": "نعمة من عندنا ۚ كذٰلك نجزي من شكر",
          "AyaNumber": 4881,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89872f61d12bf6abd830"
          },
          "AyaContent": "ولقد أنذرهم بطشتنا فتماروا بالنذر",
          "AyaNumber": 4882,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89872f61d12bf6abd831"
          },
          "AyaContent": "ولقد راودوه عن ضيفه فطمسنا أعينهم فذوقوا عذابي ونذر",
          "AyaNumber": 4883,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89872f61d12bf6abd832"
          },
          "AyaContent": "ولقد صبحهم بكرة عذاب مستقر",
          "AyaNumber": 4884,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89882f61d12bf6abd833"
          },
          "AyaContent": "فذوقوا عذابي ونذر",
          "AyaNumber": 4885,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89882f61d12bf6abd834"
          },
          "AyaContent": "ولقد يسرنا القرآن للذكر فهل من مدكر",
          "AyaNumber": 4886,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
        },{
          "_id": {
            "$oid": "5ecd89892f61d12bf6abd835"
          },
          "AyaContent": "ولقد جاء آل فرعون النذر",
          "AyaNumber": 4887,
          "SurahName": "سورة القمر",
          "SurahNumber": 54
