            "$oid": "5ecd89ac2f61d12bf6abd8cf"
          },
          "AyaContent": "ولقد علمتم النشأة الأولىٰ فلولا تذكرون",
          "AyaNumber": 5041,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ac2f61d12bf6abd8d0"
          },
          "AyaContent": "أفرأيتم ما تحرثون",
          "AyaNumber": 5042,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ac2f61d12bf6abd8d1"
          },
          "AyaContent": "أأنتم تزرعونه أم نحن الزارعون",
          "AyaNumber": 5043,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ac2f61d12bf6abd8d2"
          },
          "AyaContent": "لو نشاء لجعلناه حطاما فظلتم تفكهون",
          "AyaNumber": 5044,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ad2f61d12bf6abd8d3"
          },
          "AyaContent": "إنا لمغرمون",
          "AyaNumber": 5045,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ad2f61d12bf6abd8d4"
          },
          "AyaContent": "بل نحن محرومون",
          "AyaNumber": 5046,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ad2f61d12bf6abd8d5"
          },
          "AyaContent": "أفرأيتم الماء الذي تشربون",
          "AyaNumber": 5047,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ad2f61d12bf6abd8d6"
          },
          "AyaContent": "أأنتم أنزلتموه من المزن أم نحن المنزلون",
          "AyaNumber": 5048,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ad2f61d12bf6abd8d7"
          },
          "AyaContent": "لو نشاء جعلناه أجاجا فلولا تشكرون",
          "AyaNumber": 5049,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ae2f61d12bf6abd8d8"
          },
          "AyaContent": "أفرأيتم النار التي تورون",
          "AyaNumber": 5050,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ae2f61d12bf6abd8d9"
          },
          "AyaContent": "أأنتم أنشأتم شجرتها أم نحن المنشئون",
          "AyaNumber": 5051,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ae2f61d12bf6abd8da"
          },
          "AyaContent": "نحن جعلناها تذكرة ومتاعا للمقوين",
          "AyaNumber": 5052,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89ae2f61d12bf6abd8db"
          },
          "AyaContent": "فسبح باسم ربك العظيم",
          "AyaNumber": 5053,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89af2f61d12bf6abd8dc"
          },
          "AyaContent": "۞ فلا أقسم بمواقع النجوم",
          "AyaNumber": 5054,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89af2f61d12bf6abd8dd"
          },
          "AyaContent": "وإنه لقسم لو تعلمون عظيم",
          "AyaNumber": 5055,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89af2f61d12bf6abd8de"
          },
          "AyaContent": "إنه لقرآن كريم",
          "AyaNumber": 5056,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b02f61d12bf6abd8df"
          },
          "AyaContent": "في كتاب مكنون",
          "AyaNumber": 5057,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b02f61d12bf6abd8e0"
          },
          "AyaContent": "لا يمسه إلا المطهرون",
          "AyaNumber": 5058,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b02f61d12bf6abd8e1"
          },
          "AyaContent": "تنزيل من رب العالمين",
          "AyaNumber": 5059,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b02f61d12bf6abd8e2"
          },
          "AyaContent": "أفبهٰذا الحديث أنتم مدهنون",
          "AyaNumber": 5060,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b02f61d12bf6abd8e3"
          },
          "AyaContent": "وتجعلون رزقكم أنكم تكذبون",
          "AyaNumber": 5061,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b12f61d12bf6abd8e4"
          },
          "AyaContent": "فلولا إذا بلغت الحلقوم",
          "AyaNumber": 5062,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b12f61d12bf6abd8e5"
          },
          "AyaContent": "وأنتم حينئذ تنظرون",
          "AyaNumber": 5063,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b12f61d12bf6abd8e6"
          },
          "AyaContent": "ونحن أقرب إليه منكم ولٰكن لا تبصرون",
          "AyaNumber": 5064,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b22f61d12bf6abd8e7"
          },
          "AyaContent": "فلولا إن كنتم غير مدينين",
          "AyaNumber": 5065,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b22f61d12bf6abd8e8"
          },
          "AyaContent": "ترجعونها إن كنتم صادقين",
          "AyaNumber": 5066,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b22f61d12bf6abd8e9"
          },
          "AyaContent": "فأما إن كان من المقربين",
          "AyaNumber": 5067,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b22f61d12bf6abd8ea"
          },
          "AyaContent": "فروح وريحان وجنت نعيم",
          "AyaNumber": 5068,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b22f61d12bf6abd8eb"
          },
          "AyaContent": "وأما إن كان من أصحاب اليمين",
          "AyaNumber": 5069,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b32f61d12bf6abd8ec"
          },
          "AyaContent": "فسلام لك من أصحاب اليمين",
          "AyaNumber": 5070,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b32f61d12bf6abd8ed"
          },
          "AyaContent": "وأما إن كان من المكذبين الضالين",
          "AyaNumber": 5071,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b32f61d12bf6abd8ee"
          },
          "AyaContent": "فنزل من حميم",
          "AyaNumber": 5072,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b42f61d12bf6abd8ef"
          },
          "AyaContent": "وتصلية جحيم",
          "AyaNumber": 5073,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b42f61d12bf6abd8f0"
          },
          "AyaContent": "إن هٰذا لهو حق اليقين",
          "AyaNumber": 5074,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b42f61d12bf6abd8f1"
          },
          "AyaContent": "فسبح باسم ربك العظيم",
          "AyaNumber": 5075,
          "SurahName": "سورة الواقعة",
          "SurahNumber": 56
        },{
          "_id": {
            "$oid": "5ecd89b42f61d12bf6abd8f2"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم سبح لله ما في السماوات والأرض ۖ وهو العزيز الحكيم",
          "AyaNumber": 5076,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b42f61d12bf6abd8f3"
          },
          "AyaContent": "له ملك السماوات والأرض ۖ يحيي ويميت ۖ وهو علىٰ كل شيء قدير",
          "AyaNumber": 5077,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b52f61d12bf6abd8f4"
          },
          "AyaContent": "هو الأول والآخر والظاهر والباطن ۖ وهو بكل شيء عليم",
          "AyaNumber": 5078,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b52f61d12bf6abd8f5"
          },
          "AyaContent": "هو الذي خلق السماوات والأرض في ستة أيام ثم استوىٰ على العرش ۚ يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها ۖ وهو معكم أين ما كنتم ۚ والله بما تعملون بصير",
          "AyaNumber": 5079,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b52f61d12bf6abd8f6"
          },
          "AyaContent": "له ملك السماوات والأرض ۚ وإلى الله ترجع الأمور",
          "AyaNumber": 5080,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b52f61d12bf6abd8f7"
          },
          "AyaContent": "يولج الليل في النهار ويولج النهار في الليل ۚ وهو عليم بذات الصدور",
          "AyaNumber": 5081,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b62f61d12bf6abd8f8"
          },
          "AyaContent": "آمنوا بالله ورسوله وأنفقوا مما جعلكم مستخلفين فيه ۖ فالذين آمنوا منكم وأنفقوا لهم أجر كبير",
          "AyaNumber": 5082,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b62f61d12bf6abd8f9"
          },
          "AyaContent": "وما لكم لا تؤمنون بالله ۙ والرسول يدعوكم لتؤمنوا بربكم وقد أخذ ميثاقكم إن كنتم مؤمنين",
          "AyaNumber": 5083,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b62f61d12bf6abd8fa"
          },
          "AyaContent": "هو الذي ينزل علىٰ عبده آيات بينات ليخرجكم من الظلمات إلى النور ۚ وإن الله بكم لرءوف رحيم",
          "AyaNumber": 5084,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b62f61d12bf6abd8fb"
          },
          "AyaContent": "وما لكم ألا تنفقوا في سبيل الله ولله ميراث السماوات والأرض ۚ لا يستوي منكم من أنفق من قبل الفتح وقاتل ۚ أولٰئك أعظم درجة من الذين أنفقوا من بعد وقاتلوا ۚ وكلا وعد الله الحسنىٰ ۚ والله بما تعملون خبير",
          "AyaNumber": 5085,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b72f61d12bf6abd8fc"
          },
          "AyaContent": "من ذا الذي يقرض الله قرضا حسنا فيضاعفه له وله أجر كريم",
          "AyaNumber": 5086,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b72f61d12bf6abd8fd"
          },
          "AyaContent": "يوم ترى المؤمنين والمؤمنات يسعىٰ نورهم بين أيديهم وبأيمانهم بشراكم اليوم جنات تجري من تحتها الأنهار خالدين فيها ۚ ذٰلك هو الفوز العظيم",
          "AyaNumber": 5087,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b72f61d12bf6abd8fe"
          },
          "AyaContent": "يوم يقول المنافقون والمنافقات للذين آمنوا انظرونا نقتبس من نوركم قيل ارجعوا وراءكم فالتمسوا نورا فضرب بينهم بسور له باب باطنه فيه الرحمة وظاهره من قبله العذاب",
          "AyaNumber": 5088,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b82f61d12bf6abd8ff"
          },
          "AyaContent": "ينادونهم ألم نكن معكم ۖ قالوا بلىٰ ولٰكنكم فتنتم أنفسكم وتربصتم وارتبتم وغرتكم الأماني حتىٰ جاء أمر الله وغركم بالله الغرور",
          "AyaNumber": 5089,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b82f61d12bf6abd900"
          },
          "AyaContent": "فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا ۚ مأواكم النار ۖ هي مولاكم ۖ وبئس المصير",
          "AyaNumber": 5090,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b82f61d12bf6abd901"
          },
          "AyaContent": "۞ ألم يأن للذين آمنوا أن تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين أوتوا الكتاب من قبل فطال عليهم الأمد فقست قلوبهم ۖ وكثير منهم فاسقون",
          "AyaNumber": 5091,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b82f61d12bf6abd902"
          },
          "AyaContent": "اعلموا أن الله يحيي الأرض بعد موتها ۚ قد بينا لكم الآيات لعلكم تعقلون",
          "AyaNumber": 5092,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b92f61d12bf6abd903"
          },
          "AyaContent": "إن المصدقين والمصدقات وأقرضوا الله قرضا حسنا يضاعف لهم ولهم أجر كريم",
          "AyaNumber": 5093,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b92f61d12bf6abd904"
          },
          "AyaContent": "والذين آمنوا بالله ورسله أولٰئك هم الصديقون ۖ والشهداء عند ربهم لهم أجرهم ونورهم ۖ والذين كفروا وكذبوا بآياتنا أولٰئك أصحاب الجحيم",
          "AyaNumber": 5094,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89b92f61d12bf6abd905"
          },
          "AyaContent": "اعلموا أنما الحياة الدنيا لعب ولهو وزينة وتفاخر بينكم وتكاثر في الأموال والأولاد ۖ كمثل غيث أعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما ۖ وفي الآخرة عذاب شديد ومغفرة من الله ورضوان ۚ وما الحياة الدنيا إلا متاع الغرور",
          "AyaNumber": 5095,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89ba2f61d12bf6abd906"
          },
          "AyaContent": "سابقوا إلىٰ مغفرة من ربكم وجنة عرضها كعرض السماء والأرض أعدت للذين آمنوا بالله ورسله ۚ ذٰلك فضل الله يؤتيه من يشاء ۚ والله ذو الفضل العظيم",
          "AyaNumber": 5096,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89ba2f61d12bf6abd907"
          },
          "AyaContent": "ما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها ۚ إن ذٰلك على الله يسير",
          "AyaNumber": 5097,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89ba2f61d12bf6abd908"
          },
          "AyaContent": "لكيلا تأسوا علىٰ ما فاتكم ولا تفرحوا بما آتاكم ۗ والله لا يحب كل مختال فخور",
          "AyaNumber": 5098,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89ba2f61d12bf6abd909"
          },
          "AyaContent": "الذين يبخلون ويأمرون الناس بالبخل ۗ ومن يتول فإن الله هو الغني الحميد",
          "AyaNumber": 5099,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89ba2f61d12bf6abd90a"
          },
          "AyaContent": "لقد أرسلنا رسلنا بالبينات وأنزلنا معهم الكتاب والميزان ليقوم الناس بالقسط ۖ وأنزلنا الحديد فيه بأس شديد ومنافع للناس وليعلم الله من ينصره ورسله بالغيب ۚ إن الله قوي عزيز",
          "AyaNumber": 5100,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89bb2f61d12bf6abd90b"
          },
          "AyaContent": "ولقد أرسلنا نوحا وإبراهيم وجعلنا في ذريتهما النبوة والكتاب ۖ فمنهم مهتد ۖ وكثير منهم فاسقون",
          "AyaNumber": 5101,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89bb2f61d12bf6abd90c"
          },
          "AyaContent": "ثم قفينا علىٰ آثارهم برسلنا وقفينا بعيسى ابن مريم وآتيناه الإنجيل وجعلنا في قلوب الذين اتبعوه رأفة ورحمة ورهبانية ابتدعوها ما كتبناها عليهم إلا ابتغاء رضوان الله فما رعوها حق رعايتها ۖ فآتينا الذين آمنوا منهم أجرهم ۖ وكثير منهم فاسقون",
          "AyaNumber": 5102,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89bb2f61d12bf6abd90d"
          },
          "AyaContent": "يا أيها الذين آمنوا اتقوا الله وآمنوا برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم ۚ والله غفور رحيم",
          "AyaNumber": 5103,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89bb2f61d12bf6abd90e"
          },
          "AyaContent": "لئلا يعلم أهل الكتاب ألا يقدرون علىٰ شيء من فضل الله ۙ وأن الفضل بيد الله يؤتيه من يشاء ۚ والله ذو الفضل العظيم",
          "AyaNumber": 5104,
          "SurahName": "سورة الحديد",
          "SurahNumber": 57
        },{
          "_id": {
            "$oid": "5ecd89bb2f61d12bf6abd90f"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم قد سمع الله قول التي تجادلك في زوجها وتشتكي إلى الله والله يسمع تحاوركما ۚ إن الله سميع بصير",
          "AyaNumber": 5105,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bc2f61d12bf6abd910"
          },
          "AyaContent": "الذين يظاهرون منكم من نسائهم ما هن أمهاتهم ۖ إن أمهاتهم إلا اللائي ولدنهم ۚ وإنهم ليقولون منكرا من القول وزورا ۚ وإن الله لعفو غفور",
          "AyaNumber": 5106,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bc2f61d12bf6abd911"
          },
          "AyaContent": "والذين يظاهرون من نسائهم ثم يعودون لما قالوا فتحرير رقبة من قبل أن يتماسا ۚ ذٰلكم توعظون به ۚ والله بما تعملون خبير",
          "AyaNumber": 5107,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bc2f61d12bf6abd912"
          },
          "AyaContent": "فمن لم يجد فصيام شهرين متتابعين من قبل أن يتماسا ۖ فمن لم يستطع فإطعام ستين مسكينا ۚ ذٰلك لتؤمنوا بالله ورسوله ۚ وتلك حدود الله ۗ وللكافرين عذاب أليم",
          "AyaNumber": 5108,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bc2f61d12bf6abd913"
          },
          "AyaContent": "إن الذين يحادون الله ورسوله كبتوا كما كبت الذين من قبلهم ۚ وقد أنزلنا آيات بينات ۚ وللكافرين عذاب مهين",
          "AyaNumber": 5109,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bc2f61d12bf6abd914"
          },
          "AyaContent": "يوم يبعثهم الله جميعا فينبئهم بما عملوا ۚ أحصاه الله ونسوه ۚ والله علىٰ كل شيء شهيد",
          "AyaNumber": 5110,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bc2f61d12bf6abd915"
          },
          "AyaContent": "ألم تر أن الله يعلم ما في السماوات وما في الأرض ۖ ما يكون من نجوىٰ ثلاثة إلا هو رابعهم ولا خمسة إلا هو سادسهم ولا أدنىٰ من ذٰلك ولا أكثر إلا هو معهم أين ما كانوا ۖ ثم ينبئهم بما عملوا يوم القيامة ۚ إن الله بكل شيء عليم",
          "AyaNumber": 5111,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bd2f61d12bf6abd916"
          },
          "AyaContent": "ألم تر إلى الذين نهوا عن النجوىٰ ثم يعودون لما نهوا عنه ويتناجون بالإثم والعدوان ومعصيت الرسول وإذا جاءوك حيوك بما لم يحيك به الله ويقولون في أنفسهم لولا يعذبنا الله بما نقول ۚ حسبهم جهنم يصلونها ۖ فبئس المصير",
          "AyaNumber": 5112,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bd2f61d12bf6abd917"
          },
          "AyaContent": "يا أيها الذين آمنوا إذا تناجيتم فلا تتناجوا بالإثم والعدوان ومعصيت الرسول وتناجوا بالبر والتقوىٰ ۖ واتقوا الله الذي إليه تحشرون",
          "AyaNumber": 5113,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bd2f61d12bf6abd918"
          },
          "AyaContent": "إنما النجوىٰ من الشيطان ليحزن الذين آمنوا وليس بضارهم شيئا إلا بإذن الله ۚ وعلى الله فليتوكل المؤمنون",
          "AyaNumber": 5114,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bd2f61d12bf6abd919"
          },
          "AyaContent": "يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم ۖ وإذا قيل انشزوا فانشزوا يرفع الله الذين آمنوا منكم والذين أوتوا العلم درجات ۚ والله بما تعملون خبير",
          "AyaNumber": 5115,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bd2f61d12bf6abd91a"
          },
          "AyaContent": "يا أيها الذين آمنوا إذا ناجيتم الرسول فقدموا بين يدي نجواكم صدقة ۚ ذٰلك خير لكم وأطهر ۚ فإن لم تجدوا فإن الله غفور رحيم",
          "AyaNumber": 5116,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89be2f61d12bf6abd91b"
          },
          "AyaContent": "أأشفقتم أن تقدموا بين يدي نجواكم صدقات ۚ فإذ لم تفعلوا وتاب الله عليكم فأقيموا الصلاة وآتوا الزكاة وأطيعوا الله ورسوله ۚ والله خبير بما تعملون",
          "AyaNumber": 5117,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89be2f61d12bf6abd91c"
          },
          "AyaContent": "۞ ألم تر إلى الذين تولوا قوما غضب الله عليهم ما هم منكم ولا منهم ويحلفون على الكذب وهم يعلمون",
          "AyaNumber": 5118,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89be2f61d12bf6abd91d"
          },
          "AyaContent": "أعد الله لهم عذابا شديدا ۖ إنهم ساء ما كانوا يعملون",
          "AyaNumber": 5119,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89be2f61d12bf6abd91e"
          },
          "AyaContent": "اتخذوا أيمانهم جنة فصدوا عن سبيل الله فلهم عذاب مهين",
          "AyaNumber": 5120,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89be2f61d12bf6abd91f"
          },
          "AyaContent": "لن تغني عنهم أموالهم ولا أولادهم من الله شيئا ۚ أولٰئك أصحاب النار ۖ هم فيها خالدون",
          "AyaNumber": 5121,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bf2f61d12bf6abd920"
          },
          "AyaContent": "يوم يبعثهم الله جميعا فيحلفون له كما يحلفون لكم ۖ ويحسبون أنهم علىٰ شيء ۚ ألا إنهم هم الكاذبون",
          "AyaNumber": 5122,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bf2f61d12bf6abd921"
          },
          "AyaContent": "استحوذ عليهم الشيطان فأنساهم ذكر الله ۚ أولٰئك حزب الشيطان ۚ ألا إن حزب الشيطان هم الخاسرون",
          "AyaNumber": 5123,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bf2f61d12bf6abd922"
          },
          "AyaContent": "إن الذين يحادون الله ورسوله أولٰئك في الأذلين",
          "AyaNumber": 5124,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89bf2f61d12bf6abd923"
          },
          "AyaContent": "كتب الله لأغلبن أنا ورسلي ۚ إن الله قوي عزيز",
          "AyaNumber": 5125,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89c02f61d12bf6abd924"
          },
          "AyaContent": "لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو عشيرتهم ۚ أولٰئك كتب في قلوبهم الإيمان وأيدهم بروح منه ۖ ويدخلهم جنات تجري من تحتها الأنهار خالدين فيها ۚ رضي الله عنهم ورضوا عنه ۚ أولٰئك حزب الله ۚ ألا إن حزب الله هم المفلحون",
          "AyaNumber": 5126,
          "SurahName": "سورة المجادلة",
          "SurahNumber": 58
        },{
          "_id": {
            "$oid": "5ecd89c02f61d12bf6abd925"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم سبح لله ما في السماوات وما في الأرض ۖ وهو العزيز الحكيم",
          "AyaNumber": 5127,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c02f61d12bf6abd926"
          },
          "AyaContent": "هو الذي أخرج الذين كفروا من أهل الكتاب من ديارهم لأول الحشر ۚ ما ظننتم أن يخرجوا ۖ وظنوا أنهم مانعتهم حصونهم من الله فأتاهم الله من حيث لم يحتسبوا ۖ وقذف في قلوبهم الرعب ۚ يخربون بيوتهم بأيديهم وأيدي المؤمنين فاعتبروا يا أولي الأبصار",
          "AyaNumber": 5128,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c02f61d12bf6abd927"
          },
          "AyaContent": "ولولا أن كتب الله عليهم الجلاء لعذبهم في الدنيا ۖ ولهم في الآخرة عذاب النار",
          "AyaNumber": 5129,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c02f61d12bf6abd928"
          },
          "AyaContent": "ذٰلك بأنهم شاقوا الله ورسوله ۖ ومن يشاق الله فإن الله شديد العقاب",
          "AyaNumber": 5130,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c12f61d12bf6abd929"
          },
          "AyaContent": "ما قطعتم من لينة أو تركتموها قائمة علىٰ أصولها فبإذن الله وليخزي الفاسقين",
          "AyaNumber": 5131,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c12f61d12bf6abd92a"
          },
          "AyaContent": "وما أفاء الله علىٰ رسوله منهم فما أوجفتم عليه من خيل ولا ركاب ولٰكن الله يسلط رسله علىٰ من يشاء ۚ والله علىٰ كل شيء قدير",
          "AyaNumber": 5132,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c12f61d12bf6abd92b"
          },
          "AyaContent": "ما أفاء الله علىٰ رسوله من أهل القرىٰ فلله وللرسول ولذي القربىٰ واليتامىٰ والمساكين وابن السبيل كي لا يكون دولة بين الأغنياء منكم ۚ وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا ۚ واتقوا الله ۖ إن الله شديد العقاب",
          "AyaNumber": 5133,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c12f61d12bf6abd92c"
          },
          "AyaContent": "للفقراء المهاجرين الذين أخرجوا من ديارهم وأموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله ۚ أولٰئك هم الصادقون",
          "AyaNumber": 5134,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c22f61d12bf6abd92d"
          },
          "AyaContent": "والذين تبوءوا الدار والإيمان من قبلهم يحبون من هاجر إليهم ولا يجدون في صدورهم حاجة مما أوتوا ويؤثرون علىٰ أنفسهم ولو كان بهم خصاصة ۚ ومن يوق شح نفسه فأولٰئك هم المفلحون",
          "AyaNumber": 5135,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c22f61d12bf6abd92e"
          },
          "AyaContent": "والذين جاءوا من بعدهم يقولون ربنا اغفر لنا ولإخواننا الذين سبقونا بالإيمان ولا تجعل في قلوبنا غلا للذين آمنوا ربنا إنك رءوف رحيم",
          "AyaNumber": 5136,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c22f61d12bf6abd92f"
          },
          "AyaContent": "۞ ألم تر إلى الذين نافقوا يقولون لإخوانهم الذين كفروا من أهل الكتاب لئن أخرجتم لنخرجن معكم ولا نطيع فيكم أحدا أبدا وإن قوتلتم لننصرنكم والله يشهد إنهم لكاذبون",
          "AyaNumber": 5137,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c22f61d12bf6abd930"
          },
          "AyaContent": "لئن أخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم ليولن الأدبار ثم لا ينصرون",
          "AyaNumber": 5138,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c32f61d12bf6abd931"
          },
          "AyaContent": "لأنتم أشد رهبة في صدورهم من الله ۚ ذٰلك بأنهم قوم لا يفقهون",
          "AyaNumber": 5139,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c32f61d12bf6abd932"
          },
          "AyaContent": "لا يقاتلونكم جميعا إلا في قرى محصنة أو من وراء جدر ۚ بأسهم بينهم شديد ۚ تحسبهم جميعا وقلوبهم شتىٰ ۚ ذٰلك بأنهم قوم لا يعقلون",
          "AyaNumber": 5140,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c32f61d12bf6abd933"
          },
          "AyaContent": "كمثل الذين من قبلهم قريبا ۖ ذاقوا وبال أمرهم ولهم عذاب أليم",
          "AyaNumber": 5141,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c42f61d12bf6abd934"
          },
          "AyaContent": "كمثل الشيطان إذ قال للإنسان اكفر فلما كفر قال إني بريء منك إني أخاف الله رب العالمين",
          "AyaNumber": 5142,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c42f61d12bf6abd935"
          },
          "AyaContent": "فكان عاقبتهما أنهما في النار خالدين فيها ۚ وذٰلك جزاء الظالمين",
          "AyaNumber": 5143,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c42f61d12bf6abd936"
          },
          "AyaContent": "يا أيها الذين آمنوا اتقوا الله ولتنظر نفس ما قدمت لغد ۖ واتقوا الله ۚ إن الله خبير بما تعملون",
          "AyaNumber": 5144,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c42f61d12bf6abd937"
          },
          "AyaContent": "ولا تكونوا كالذين نسوا الله فأنساهم أنفسهم ۚ أولٰئك هم الفاسقون",
          "AyaNumber": 5145,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c42f61d12bf6abd938"
          },
          "AyaContent": "لا يستوي أصحاب النار وأصحاب الجنة ۚ أصحاب الجنة هم الفائزون",
          "AyaNumber": 5146,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c52f61d12bf6abd939"
          },
          "AyaContent": "لو أنزلنا هٰذا القرآن علىٰ جبل لرأيته خاشعا متصدعا من خشية الله ۚ وتلك الأمثال نضربها للناس لعلهم يتفكرون",
          "AyaNumber": 5147,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c52f61d12bf6abd93a"
          },
          "AyaContent": "هو الله الذي لا إلٰه إلا هو ۖ عالم الغيب والشهادة ۖ هو الرحمٰن الرحيم",
          "AyaNumber": 5148,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c52f61d12bf6abd93b"
          },
          "AyaContent": "هو الله الذي لا إلٰه إلا هو الملك القدوس السلام المؤمن المهيمن العزيز الجبار المتكبر ۚ سبحان الله عما يشركون",
          "AyaNumber": 5149,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c52f61d12bf6abd93c"
          },
          "AyaContent": "هو الله الخالق البارئ المصور ۖ له الأسماء الحسنىٰ ۚ يسبح له ما في السماوات والأرض ۖ وهو العزيز الحكيم",
          "AyaNumber": 5150,
          "SurahName": "سورة الحشر",
          "SurahNumber": 59
        },{
          "_id": {
            "$oid": "5ecd89c62f61d12bf6abd93d"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يا أيها الذين آمنوا لا تتخذوا عدوي وعدوكم أولياء تلقون إليهم بالمودة وقد كفروا بما جاءكم من الحق يخرجون الرسول وإياكم ۙ أن تؤمنوا بالله ربكم إن كنتم خرجتم جهادا في سبيلي وابتغاء مرضاتي ۚ تسرون إليهم بالمودة وأنا أعلم بما أخفيتم وما أعلنتم ۚ ومن يفعله منكم فقد ضل سواء السبيل",
          "AyaNumber": 5151,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c62f61d12bf6abd93e"
          },
          "AyaContent": "إن يثقفوكم يكونوا لكم أعداء ويبسطوا إليكم أيديهم وألسنتهم بالسوء وودوا لو تكفرون",
          "AyaNumber": 5152,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c62f61d12bf6abd93f"
          },
          "AyaContent": "لن تنفعكم أرحامكم ولا أولادكم ۚ يوم القيامة يفصل بينكم ۚ والله بما تعملون بصير",
          "AyaNumber": 5153,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c72f61d12bf6abd940"
          },
          "AyaContent": "قد كانت لكم أسوة حسنة في إبراهيم والذين معه إذ قالوا لقومهم إنا برآء منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء أبدا حتىٰ تؤمنوا بالله وحده إلا قول إبراهيم لأبيه لأستغفرن لك وما أملك لك من الله من شيء ۖ ربنا عليك توكلنا وإليك أنبنا وإليك المصير",
          "AyaNumber": 5154,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c72f61d12bf6abd941"
          },
          "AyaContent": "ربنا لا تجعلنا فتنة للذين كفروا واغفر لنا ربنا ۖ إنك أنت العزيز الحكيم",
          "AyaNumber": 5155,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c72f61d12bf6abd942"
          },
          "AyaContent": "لقد كان لكم فيهم أسوة حسنة لمن كان يرجو الله واليوم الآخر ۚ ومن يتول فإن الله هو الغني الحميد",
          "AyaNumber": 5156,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c72f61d12bf6abd943"
          },
          "AyaContent": "۞ عسى الله أن يجعل بينكم وبين الذين عاديتم منهم مودة ۚ والله قدير ۚ والله غفور رحيم",
          "AyaNumber": 5157,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c82f61d12bf6abd944"
          },
          "AyaContent": "لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم أن تبروهم وتقسطوا إليهم ۚ إن الله يحب المقسطين",
          "AyaNumber": 5158,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c82f61d12bf6abd945"
          },
          "AyaContent": "إنما ينهاكم الله عن الذين قاتلوكم في الدين وأخرجوكم من دياركم وظاهروا علىٰ إخراجكم أن تولوهم ۚ ومن يتولهم فأولٰئك هم الظالمون",
          "AyaNumber": 5159,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c82f61d12bf6abd946"
          },
          "AyaContent": "يا أيها الذين آمنوا إذا جاءكم المؤمنات مهاجرات فامتحنوهن ۖ الله أعلم بإيمانهن ۖ فإن علمتموهن مؤمنات فلا ترجعوهن إلى الكفار ۖ لا هن حل لهم ولا هم يحلون لهن ۖ وآتوهم ما أنفقوا ۚ ولا جناح عليكم أن تنكحوهن إذا آتيتموهن أجورهن ۚ ولا تمسكوا بعصم الكوافر واسألوا ما أنفقتم وليسألوا ما أنفقوا ۚ ذٰلكم حكم الله ۖ يحكم بينكم ۚ والله عليم حكيم",
          "AyaNumber": 5160,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c82f61d12bf6abd947"
          },
          "AyaContent": "وإن فاتكم شيء من أزواجكم إلى الكفار فعاقبتم فآتوا الذين ذهبت أزواجهم مثل ما أنفقوا ۚ واتقوا الله الذي أنتم به مؤمنون",
          "AyaNumber": 5161,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c92f61d12bf6abd948"
          },
          "AyaContent": "يا أيها النبي إذا جاءك المؤمنات يبايعنك علىٰ أن لا يشركن بالله شيئا ولا يسرقن ولا يزنين ولا يقتلن أولادهن ولا يأتين ببهتان يفترينه بين أيديهن وأرجلهن ولا يعصينك في معروف ۙ فبايعهن واستغفر لهن الله ۖ إن الله غفور رحيم",
          "AyaNumber": 5162,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c92f61d12bf6abd949"
          },
          "AyaContent": "يا أيها الذين آمنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الآخرة كما يئس الكفار من أصحاب القبور",
          "AyaNumber": 5163,
          "SurahName": "سورة الممتحنة",
          "SurahNumber": 60
        },{
          "_id": {
            "$oid": "5ecd89c92f61d12bf6abd94a"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم سبح لله ما في السماوات وما في الأرض ۖ وهو العزيز الحكيم",
          "AyaNumber": 5164,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89ca2f61d12bf6abd94b"
          },
          "AyaContent": "يا أيها الذين آمنوا لم تقولون ما لا تفعلون",
          "AyaNumber": 5165,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89ca2f61d12bf6abd94c"
          },
          "AyaContent": "كبر مقتا عند الله أن تقولوا ما لا تفعلون",
          "AyaNumber": 5166,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89ca2f61d12bf6abd94d"
          },
          "AyaContent": "إن الله يحب الذين يقاتلون في سبيله صفا كأنهم بنيان مرصوص",
          "AyaNumber": 5167,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cb2f61d12bf6abd94e"
          },
          "AyaContent": "وإذ قال موسىٰ لقومه يا قوم لم تؤذونني وقد تعلمون أني رسول الله إليكم ۖ فلما زاغوا أزاغ الله قلوبهم ۚ والله لا يهدي القوم الفاسقين",
          "AyaNumber": 5168,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cb2f61d12bf6abd94f"
          },
          "AyaContent": "وإذ قال عيسى ابن مريم يا بني إسرائيل إني رسول الله إليكم مصدقا لما بين يدي من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد ۖ فلما جاءهم بالبينات قالوا هٰذا سحر مبين",
          "AyaNumber": 5169,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cb2f61d12bf6abd950"
          },
          "AyaContent": "ومن أظلم ممن افترىٰ على الله الكذب وهو يدعىٰ إلى الإسلام ۚ والله لا يهدي القوم الظالمين",
          "AyaNumber": 5170,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cb2f61d12bf6abd951"
          },
          "AyaContent": "يريدون ليطفئوا نور الله بأفواههم والله متم نوره ولو كره الكافرون",
          "AyaNumber": 5171,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cc2f61d12bf6abd952"
          },
          "AyaContent": "هو الذي أرسل رسوله بالهدىٰ ودين الحق ليظهره على الدين كله ولو كره المشركون",
          "AyaNumber": 5172,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cc2f61d12bf6abd953"
          },
          "AyaContent": "يا أيها الذين آمنوا هل أدلكم علىٰ تجارة تنجيكم من عذاب أليم",
          "AyaNumber": 5173,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cc2f61d12bf6abd954"
          },
          "AyaContent": "تؤمنون بالله ورسوله وتجاهدون في سبيل الله بأموالكم وأنفسكم ۚ ذٰلكم خير لكم إن كنتم تعلمون",
          "AyaNumber": 5174,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cd2f61d12bf6abd955"
          },
          "AyaContent": "يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الأنهار ومساكن طيبة في جنات عدن ۚ ذٰلك الفوز العظيم",
          "AyaNumber": 5175,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cd2f61d12bf6abd956"
          },
          "AyaContent": "وأخرىٰ تحبونها ۖ نصر من الله وفتح قريب ۗ وبشر المؤمنين",
          "AyaNumber": 5176,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cd2f61d12bf6abd957"
          },
          "AyaContent": "يا أيها الذين آمنوا كونوا أنصار الله كما قال عيسى ابن مريم للحواريين من أنصاري إلى الله ۖ قال الحواريون نحن أنصار الله ۖ فآمنت طائفة من بني إسرائيل وكفرت طائفة ۖ فأيدنا الذين آمنوا علىٰ عدوهم فأصبحوا ظاهرين",
          "AyaNumber": 5177,
          "SurahName": "سورة الصف",
          "SurahNumber": 61
        },{
          "_id": {
            "$oid": "5ecd89cd2f61d12bf6abd958"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يسبح لله ما في السماوات وما في الأرض الملك القدوس العزيز الحكيم",
          "AyaNumber": 5178,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89ce2f61d12bf6abd959"
          },
          "AyaContent": "هو الذي بعث في الأميين رسولا منهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين",
          "AyaNumber": 5179,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89ce2f61d12bf6abd95a"
          },
          "AyaContent": "وآخرين منهم لما يلحقوا بهم ۚ وهو العزيز الحكيم",
          "AyaNumber": 5180,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89ce2f61d12bf6abd95b"
          },
          "AyaContent": "ذٰلك فضل الله يؤتيه من يشاء ۚ والله ذو الفضل العظيم",
          "AyaNumber": 5181,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89cf2f61d12bf6abd95c"
          },
          "AyaContent": "مثل الذين حملوا التوراة ثم لم يحملوها كمثل الحمار يحمل أسفارا ۚ بئس مثل القوم الذين كذبوا بآيات الله ۚ والله لا يهدي القوم الظالمين",
          "AyaNumber": 5182,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89cf2f61d12bf6abd95d"
          },
          "AyaContent": "قل يا أيها الذين هادوا إن زعمتم أنكم أولياء لله من دون الناس فتمنوا الموت إن كنتم صادقين",
          "AyaNumber": 5183,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89cf2f61d12bf6abd95e"
          },
          "AyaContent": "ولا يتمنونه أبدا بما قدمت أيديهم ۚ والله عليم بالظالمين",
          "AyaNumber": 5184,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89cf2f61d12bf6abd95f"
          },
          "AyaContent": "قل إن الموت الذي تفرون منه فإنه ملاقيكم ۖ ثم تردون إلىٰ عالم الغيب والشهادة فينبئكم بما كنتم تعملون",
          "AyaNumber": 5185,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89cf2f61d12bf6abd960"
          },
          "AyaContent": "يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلىٰ ذكر الله وذروا البيع ۚ ذٰلكم خير لكم إن كنتم تعلمون",
          "AyaNumber": 5186,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89cf2f61d12bf6abd961"
          },
          "AyaContent": "فإذا قضيت الصلاة فانتشروا في الأرض وابتغوا من فضل الله واذكروا الله كثيرا لعلكم تفلحون",
          "AyaNumber": 5187,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89d02f61d12bf6abd962"
          },
          "AyaContent": "وإذا رأوا تجارة أو لهوا انفضوا إليها وتركوك قائما ۚ قل ما عند الله خير من اللهو ومن التجارة ۚ والله خير الرازقين",
          "AyaNumber": 5188,
          "SurahName": "سورة الجمعة",
          "SurahNumber": 62
        },{
          "_id": {
            "$oid": "5ecd89d02f61d12bf6abd963"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم إذا جاءك المنافقون قالوا نشهد إنك لرسول الله ۗ والله يعلم إنك لرسوله والله يشهد إن المنافقين لكاذبون",
          "AyaNumber": 5189,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d02f61d12bf6abd964"
          },
          "AyaContent": "اتخذوا أيمانهم جنة فصدوا عن سبيل الله ۚ إنهم ساء ما كانوا يعملون",
          "AyaNumber": 5190,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d02f61d12bf6abd965"
          },
          "AyaContent": "ذٰلك بأنهم آمنوا ثم كفروا فطبع علىٰ قلوبهم فهم لا يفقهون",
          "AyaNumber": 5191,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d02f61d12bf6abd966"
          },
          "AyaContent": "۞ وإذا رأيتهم تعجبك أجسامهم ۖ وإن يقولوا تسمع لقولهم ۖ كأنهم خشب مسندة ۖ يحسبون كل صيحة عليهم ۚ هم العدو فاحذرهم ۚ قاتلهم الله ۖ أنىٰ يؤفكون",
          "AyaNumber": 5192,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d12f61d12bf6abd967"
          },
          "AyaContent": "وإذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رءوسهم ورأيتهم يصدون وهم مستكبرون",
          "AyaNumber": 5193,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d12f61d12bf6abd968"
          },
          "AyaContent": "سواء عليهم أستغفرت لهم أم لم تستغفر لهم لن يغفر الله لهم ۚ إن الله لا يهدي القوم الفاسقين",
          "AyaNumber": 5194,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d12f61d12bf6abd969"
          },
          "AyaContent": "هم الذين يقولون لا تنفقوا علىٰ من عند رسول الله حتىٰ ينفضوا ۗ ولله خزائن السماوات والأرض ولٰكن المنافقين لا يفقهون",
          "AyaNumber": 5195,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d12f61d12bf6abd96a"
          },
          "AyaContent": "يقولون لئن رجعنا إلى المدينة ليخرجن الأعز منها الأذل ۚ ولله العزة ولرسوله وللمؤمنين ولٰكن المنافقين لا يعلمون",
          "AyaNumber": 5196,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d22f61d12bf6abd96b"
          },
          "AyaContent": "يا أيها الذين آمنوا لا تلهكم أموالكم ولا أولادكم عن ذكر الله ۚ ومن يفعل ذٰلك فأولٰئك هم الخاسرون",
          "AyaNumber": 5197,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d22f61d12bf6abd96c"
          },
          "AyaContent": "وأنفقوا من ما رزقناكم من قبل أن يأتي أحدكم الموت فيقول رب لولا أخرتني إلىٰ أجل قريب فأصدق وأكن من الصالحين",
          "AyaNumber": 5198,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d22f61d12bf6abd96d"
          },
          "AyaContent": "ولن يؤخر الله نفسا إذا جاء أجلها ۚ والله خبير بما تعملون",
          "AyaNumber": 5199,
          "SurahName": "سورة المنافقون",
          "SurahNumber": 63
        },{
          "_id": {
            "$oid": "5ecd89d32f61d12bf6abd96e"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يسبح لله ما في السماوات وما في الأرض ۖ له الملك وله الحمد ۖ وهو علىٰ كل شيء قدير",
          "AyaNumber": 5200,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d32f61d12bf6abd96f"
          },
          "AyaContent": "هو الذي خلقكم فمنكم كافر ومنكم مؤمن ۚ والله بما تعملون بصير",
          "AyaNumber": 5201,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d32f61d12bf6abd970"
          },
          "AyaContent": "خلق السماوات والأرض بالحق وصوركم فأحسن صوركم ۖ وإليه المصير",
          "AyaNumber": 5202,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d32f61d12bf6abd971"
          },
          "AyaContent": "يعلم ما في السماوات والأرض ويعلم ما تسرون وما تعلنون ۚ والله عليم بذات الصدور",
          "AyaNumber": 5203,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d42f61d12bf6abd972"
          },
          "AyaContent": "ألم يأتكم نبأ الذين كفروا من قبل فذاقوا وبال أمرهم ولهم عذاب أليم",
          "AyaNumber": 5204,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d42f61d12bf6abd973"
          },
          "AyaContent": "ذٰلك بأنه كانت تأتيهم رسلهم بالبينات فقالوا أبشر يهدوننا فكفروا وتولوا ۚ واستغنى الله ۚ والله غني حميد",
          "AyaNumber": 5205,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d42f61d12bf6abd974"
          },
          "AyaContent": "زعم الذين كفروا أن لن يبعثوا ۚ قل بلىٰ وربي لتبعثن ثم لتنبؤن بما عملتم ۚ وذٰلك على الله يسير",
          "AyaNumber": 5206,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d42f61d12bf6abd975"
          },
          "AyaContent": "فآمنوا بالله ورسوله والنور الذي أنزلنا ۚ والله بما تعملون خبير",
          "AyaNumber": 5207,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d52f61d12bf6abd976"
          },
          "AyaContent": "يوم يجمعكم ليوم الجمع ۖ ذٰلك يوم التغابن ۗ ومن يؤمن بالله ويعمل صالحا يكفر عنه سيئاته ويدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا ۚ ذٰلك الفوز العظيم",
          "AyaNumber": 5208,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d52f61d12bf6abd977"
          },
          "AyaContent": "والذين كفروا وكذبوا بآياتنا أولٰئك أصحاب النار خالدين فيها ۖ وبئس المصير",
          "AyaNumber": 5209,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d52f61d12bf6abd978"
          },
          "AyaContent": "ما أصاب من مصيبة إلا بإذن الله ۗ ومن يؤمن بالله يهد قلبه ۚ والله بكل شيء عليم",
          "AyaNumber": 5210,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d52f61d12bf6abd979"
          },
          "AyaContent": "وأطيعوا الله وأطيعوا الرسول ۚ فإن توليتم فإنما علىٰ رسولنا البلاغ المبين",
          "AyaNumber": 5211,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d52f61d12bf6abd97a"
          },
          "AyaContent": "الله لا إلٰه إلا هو ۚ وعلى الله فليتوكل المؤمنون",
          "AyaNumber": 5212,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d52f61d12bf6abd97b"
          },
          "AyaContent": "يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدوا لكم فاحذروهم ۚ وإن تعفوا وتصفحوا وتغفروا فإن الله غفور رحيم",
          "AyaNumber": 5213,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d62f61d12bf6abd97c"
          },
          "AyaContent": "إنما أموالكم وأولادكم فتنة ۚ والله عنده أجر عظيم",
          "AyaNumber": 5214,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d62f61d12bf6abd97d"
          },
          "AyaContent": "فاتقوا الله ما استطعتم واسمعوا وأطيعوا وأنفقوا خيرا لأنفسكم ۗ ومن يوق شح نفسه فأولٰئك هم المفلحون",
          "AyaNumber": 5215,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d62f61d12bf6abd97e"
          },
          "AyaContent": "إن تقرضوا الله قرضا حسنا يضاعفه لكم ويغفر لكم ۚ والله شكور حليم",
          "AyaNumber": 5216,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d62f61d12bf6abd97f"
          },
          "AyaContent": "عالم الغيب والشهادة العزيز الحكيم",
          "AyaNumber": 5217,
          "SurahName": "سورة التغابن",
          "SurahNumber": 64
        },{
          "_id": {
            "$oid": "5ecd89d62f61d12bf6abd980"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يا أيها النبي إذا طلقتم النساء فطلقوهن لعدتهن وأحصوا العدة ۖ واتقوا الله ربكم ۖ لا تخرجوهن من بيوتهن ولا يخرجن إلا أن يأتين بفاحشة مبينة ۚ وتلك حدود الله ۚ ومن يتعد حدود الله فقد ظلم نفسه ۚ لا تدري لعل الله يحدث بعد ذٰلك أمرا",
          "AyaNumber": 5218,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d62f61d12bf6abd981"
          },
          "AyaContent": "فإذا بلغن أجلهن فأمسكوهن بمعروف أو فارقوهن بمعروف وأشهدوا ذوي عدل منكم وأقيموا الشهادة لله ۚ ذٰلكم يوعظ به من كان يؤمن بالله واليوم الآخر ۚ ومن يتق الله يجعل له مخرجا",
          "AyaNumber": 5219,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d72f61d12bf6abd982"
          },
          "AyaContent": "ويرزقه من حيث لا يحتسب ۚ ومن يتوكل على الله فهو حسبه ۚ إن الله بالغ أمره ۚ قد جعل الله لكل شيء قدرا",
          "AyaNumber": 5220,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d72f61d12bf6abd983"
          },
          "AyaContent": "واللائي يئسن من المحيض من نسائكم إن ارتبتم فعدتهن ثلاثة أشهر واللائي لم يحضن ۚ وأولات الأحمال أجلهن أن يضعن حملهن ۚ ومن يتق الله يجعل له من أمره يسرا",
          "AyaNumber": 5221,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d72f61d12bf6abd984"
          },
          "AyaContent": "ذٰلك أمر الله أنزله إليكم ۚ ومن يتق الله يكفر عنه سيئاته ويعظم له أجرا",
          "AyaNumber": 5222,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d72f61d12bf6abd985"
          },
          "AyaContent": "أسكنوهن من حيث سكنتم من وجدكم ولا تضاروهن لتضيقوا عليهن ۚ وإن كن أولات حمل فأنفقوا عليهن حتىٰ يضعن حملهن ۚ فإن أرضعن لكم فآتوهن أجورهن ۖ وأتمروا بينكم بمعروف ۖ وإن تعاسرتم فسترضع له أخرىٰ",
          "AyaNumber": 5223,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d72f61d12bf6abd986"
          },
          "AyaContent": "لينفق ذو سعة من سعته ۖ ومن قدر عليه رزقه فلينفق مما آتاه الله ۚ لا يكلف الله نفسا إلا ما آتاها ۚ سيجعل الله بعد عسر يسرا",
          "AyaNumber": 5224,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d82f61d12bf6abd987"
          },
          "AyaContent": "وكأين من قرية عتت عن أمر ربها ورسله فحاسبناها حسابا شديدا وعذبناها عذابا نكرا",
          "AyaNumber": 5225,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d82f61d12bf6abd988"
          },
          "AyaContent": "فذاقت وبال أمرها وكان عاقبة أمرها خسرا",
          "AyaNumber": 5226,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d82f61d12bf6abd989"
          },
          "AyaContent": "أعد الله لهم عذابا شديدا ۖ فاتقوا الله يا أولي الألباب الذين آمنوا ۚ قد أنزل الله إليكم ذكرا",
          "AyaNumber": 5227,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d82f61d12bf6abd98a"
          },
          "AyaContent": "رسولا يتلو عليكم آيات الله مبينات ليخرج الذين آمنوا وعملوا الصالحات من الظلمات إلى النور ۚ ومن يؤمن بالله ويعمل صالحا يدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا ۖ قد أحسن الله له رزقا",
          "AyaNumber": 5228,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d92f61d12bf6abd98b"
          },
          "AyaContent": "الله الذي خلق سبع سماوات ومن الأرض مثلهن يتنزل الأمر بينهن لتعلموا أن الله علىٰ كل شيء قدير وأن الله قد أحاط بكل شيء علما",
          "AyaNumber": 5229,
          "SurahName": "سورة الطلاق",
          "SurahNumber": 65
        },{
          "_id": {
            "$oid": "5ecd89d92f61d12bf6abd98c"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يا أيها النبي لم تحرم ما أحل الله لك ۖ تبتغي مرضات أزواجك ۚ والله غفور رحيم",
          "AyaNumber": 5230,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89d92f61d12bf6abd98d"
          },
          "AyaContent": "قد فرض الله لكم تحلة أيمانكم ۚ والله مولاكم ۖ وهو العليم الحكيم",
          "AyaNumber": 5231,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89d92f61d12bf6abd98e"
          },
          "AyaContent": "وإذ أسر النبي إلىٰ بعض أزواجه حديثا فلما نبأت به وأظهره الله عليه عرف بعضه وأعرض عن بعض ۖ فلما نبأها به قالت من أنبأك هٰذا ۖ قال نبأني العليم الخبير",
          "AyaNumber": 5232,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89d92f61d12bf6abd98f"
          },
          "AyaContent": "إن تتوبا إلى الله فقد صغت قلوبكما ۖ وإن تظاهرا عليه فإن الله هو مولاه وجبريل وصالح المؤمنين ۖ والملائكة بعد ذٰلك ظهير",
          "AyaNumber": 5233,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89da2f61d12bf6abd990"
          },
          "AyaContent": "عسىٰ ربه إن طلقكن أن يبدله أزواجا خيرا منكن مسلمات مؤمنات قانتات تائبات عابدات سائحات ثيبات وأبكارا",
          "AyaNumber": 5234,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89da2f61d12bf6abd991"
          },
          "AyaContent": "يا أيها الذين آمنوا قوا أنفسكم وأهليكم نارا وقودها الناس والحجارة عليها ملائكة غلاظ شداد لا يعصون الله ما أمرهم ويفعلون ما يؤمرون",
          "AyaNumber": 5235,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89da2f61d12bf6abd992"
          },
          "AyaContent": "يا أيها الذين كفروا لا تعتذروا اليوم ۖ إنما تجزون ما كنتم تعملون",
          "AyaNumber": 5236,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89da2f61d12bf6abd993"
          },
          "AyaContent": "يا أيها الذين آمنوا توبوا إلى الله توبة نصوحا عسىٰ ربكم أن يكفر عنكم سيئاتكم ويدخلكم جنات تجري من تحتها الأنهار يوم لا يخزي الله النبي والذين آمنوا معه ۖ نورهم يسعىٰ بين أيديهم وبأيمانهم يقولون ربنا أتمم لنا نورنا واغفر لنا ۖ إنك علىٰ كل شيء قدير",
          "AyaNumber": 5237,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89da2f61d12bf6abd994"
          },
          "AyaContent": "يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم ۚ ومأواهم جهنم ۖ وبئس المصير",
          "AyaNumber": 5238,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89db2f61d12bf6abd995"
          },
          "AyaContent": "ضرب الله مثلا للذين كفروا امرأت نوح وامرأت لوط ۖ كانتا تحت عبدين من عبادنا صالحين فخانتاهما فلم يغنيا عنهما من الله شيئا وقيل ادخلا النار مع الداخلين",
          "AyaNumber": 5239,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89db2f61d12bf6abd996"
          },
          "AyaContent": "وضرب الله مثلا للذين آمنوا امرأت فرعون إذ قالت رب ابن لي عندك بيتا في الجنة ونجني من فرعون وعمله ونجني من القوم الظالمين",
          "AyaNumber": 5240,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89db2f61d12bf6abd997"
          },
          "AyaContent": "ومريم ابنت عمران التي أحصنت فرجها فنفخنا فيه من روحنا وصدقت بكلمات ربها وكتبه وكانت من القانتين",
          "AyaNumber": 5241,
          "SurahName": "سورة التحريم",
          "SurahNumber": 66
        },{
          "_id": {
            "$oid": "5ecd89db2f61d12bf6abd998"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم تبارك الذي بيده الملك وهو علىٰ كل شيء قدير",
          "AyaNumber": 5242,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89db2f61d12bf6abd999"
          },
          "AyaContent": "الذي خلق الموت والحياة ليبلوكم أيكم أحسن عملا ۚ وهو العزيز الغفور",
          "AyaNumber": 5243,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89dc2f61d12bf6abd99a"
          },
          "AyaContent": "الذي خلق سبع سماوات طباقا ۖ ما ترىٰ في خلق الرحمٰن من تفاوت ۖ فارجع البصر هل ترىٰ من فطور",
          "AyaNumber": 5244,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89dc2f61d12bf6abd99b"
          },
          "AyaContent": "ثم ارجع البصر كرتين ينقلب إليك البصر خاسئا وهو حسير",
          "AyaNumber": 5245,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89dc2f61d12bf6abd99c"
          },
          "AyaContent": "ولقد زينا السماء الدنيا بمصابيح وجعلناها رجوما للشياطين ۖ وأعتدنا لهم عذاب السعير",
          "AyaNumber": 5246,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89dd2f61d12bf6abd99d"
          },
          "AyaContent": "وللذين كفروا بربهم عذاب جهنم ۖ وبئس المصير",
          "AyaNumber": 5247,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89dd2f61d12bf6abd99e"
          },
          "AyaContent": "إذا ألقوا فيها سمعوا لها شهيقا وهي تفور",
          "AyaNumber": 5248,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89dd2f61d12bf6abd99f"
          },
          "AyaContent": "تكاد تميز من الغيظ ۖ كلما ألقي فيها فوج سألهم خزنتها ألم يأتكم نذير",
          "AyaNumber": 5249,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89de2f61d12bf6abd9a0"
          },
          "AyaContent": "قالوا بلىٰ قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء إن أنتم إلا في ضلال كبير",
          "AyaNumber": 5250,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89de2f61d12bf6abd9a1"
          },
          "AyaContent": "وقالوا لو كنا نسمع أو نعقل ما كنا في أصحاب السعير",
          "AyaNumber": 5251,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89de2f61d12bf6abd9a2"
          },
          "AyaContent": "فاعترفوا بذنبهم فسحقا لأصحاب السعير",
          "AyaNumber": 5252,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89df2f61d12bf6abd9a3"
          },
          "AyaContent": "إن الذين يخشون ربهم بالغيب لهم مغفرة وأجر كبير",
          "AyaNumber": 5253,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89df2f61d12bf6abd9a4"
          },
          "AyaContent": "وأسروا قولكم أو اجهروا به ۖ إنه عليم بذات الصدور",
          "AyaNumber": 5254,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89df2f61d12bf6abd9a5"
          },
          "AyaContent": "ألا يعلم من خلق وهو اللطيف الخبير",
          "AyaNumber": 5255,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89df2f61d12bf6abd9a6"
          },
          "AyaContent": "هو الذي جعل لكم الأرض ذلولا فامشوا في مناكبها وكلوا من رزقه ۖ وإليه النشور",
          "AyaNumber": 5256,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89df2f61d12bf6abd9a7"
          },
          "AyaContent": "أأمنتم من في السماء أن يخسف بكم الأرض فإذا هي تمور",
          "AyaNumber": 5257,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e02f61d12bf6abd9a8"
          },
          "AyaContent": "أم أمنتم من في السماء أن يرسل عليكم حاصبا ۖ فستعلمون كيف نذير",
          "AyaNumber": 5258,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e02f61d12bf6abd9a9"
          },
          "AyaContent": "ولقد كذب الذين من قبلهم فكيف كان نكير",
          "AyaNumber": 5259,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e02f61d12bf6abd9aa"
          },
          "AyaContent": "أولم يروا إلى الطير فوقهم صافات ويقبضن ۚ ما يمسكهن إلا الرحمٰن ۚ إنه بكل شيء بصير",
          "AyaNumber": 5260,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e12f61d12bf6abd9ab"
          },
          "AyaContent": "أمن هٰذا الذي هو جند لكم ينصركم من دون الرحمٰن ۚ إن الكافرون إلا في غرور",
          "AyaNumber": 5261,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e12f61d12bf6abd9ac"
          },
          "AyaContent": "أمن هٰذا الذي يرزقكم إن أمسك رزقه ۚ بل لجوا في عتو ونفور",
          "AyaNumber": 5262,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e12f61d12bf6abd9ad"
          },
          "AyaContent": "أفمن يمشي مكبا علىٰ وجهه أهدىٰ أمن يمشي سويا علىٰ صراط مستقيم",
          "AyaNumber": 5263,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e12f61d12bf6abd9ae"
          },
          "AyaContent": "قل هو الذي أنشأكم وجعل لكم السمع والأبصار والأفئدة ۖ قليلا ما تشكرون",
          "AyaNumber": 5264,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e22f61d12bf6abd9af"
          },
          "AyaContent": "قل هو الذي ذرأكم في الأرض وإليه تحشرون",
          "AyaNumber": 5265,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e22f61d12bf6abd9b0"
          },
          "AyaContent": "ويقولون متىٰ هٰذا الوعد إن كنتم صادقين",
          "AyaNumber": 5266,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e22f61d12bf6abd9b1"
          },
          "AyaContent": "قل إنما العلم عند الله وإنما أنا نذير مبين",
          "AyaNumber": 5267,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e22f61d12bf6abd9b2"
          },
          "AyaContent": "فلما رأوه زلفة سيئت وجوه الذين كفروا وقيل هٰذا الذي كنتم به تدعون",
          "AyaNumber": 5268,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e32f61d12bf6abd9b3"
          },
          "AyaContent": "قل أرأيتم إن أهلكني الله ومن معي أو رحمنا فمن يجير الكافرين من عذاب أليم",
          "AyaNumber": 5269,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e32f61d12bf6abd9b4"
          },
          "AyaContent": "قل هو الرحمٰن آمنا به وعليه توكلنا ۖ فستعلمون من هو في ضلال مبين",
          "AyaNumber": 5270,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e32f61d12bf6abd9b5"
          },
          "AyaContent": "قل أرأيتم إن أصبح ماؤكم غورا فمن يأتيكم بماء معين",
          "AyaNumber": 5271,
          "SurahName": "سورة الملك",
          "SurahNumber": 67
        },{
          "_id": {
            "$oid": "5ecd89e32f61d12bf6abd9b6"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم ن ۚ والقلم وما يسطرون",
          "AyaNumber": 5272,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e32f61d12bf6abd9b7"
          },
          "AyaContent": "ما أنت بنعمة ربك بمجنون",
          "AyaNumber": 5273,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e42f61d12bf6abd9b8"
          },
          "AyaContent": "وإن لك لأجرا غير ممنون",
          "AyaNumber": 5274,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e42f61d12bf6abd9b9"
          },
          "AyaContent": "وإنك لعلىٰ خلق عظيم",
          "AyaNumber": 5275,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e42f61d12bf6abd9ba"
          },
          "AyaContent": "فستبصر ويبصرون",
          "AyaNumber": 5276,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e42f61d12bf6abd9bb"
          },
          "AyaContent": "بأييكم المفتون",
          "AyaNumber": 5277,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e42f61d12bf6abd9bc"
          },
          "AyaContent": "إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بالمهتدين",
          "AyaNumber": 5278,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e42f61d12bf6abd9bd"
          },
          "AyaContent": "فلا تطع المكذبين",
          "AyaNumber": 5279,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e52f61d12bf6abd9be"
          },
          "AyaContent": "ودوا لو تدهن فيدهنون",
          "AyaNumber": 5280,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e52f61d12bf6abd9bf"
          },
          "AyaContent": "ولا تطع كل حلاف مهين",
          "AyaNumber": 5281,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e52f61d12bf6abd9c0"
          },
          "AyaContent": "هماز مشاء بنميم",
          "AyaNumber": 5282,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e52f61d12bf6abd9c1"
          },
          "AyaContent": "مناع للخير معتد أثيم",
          "AyaNumber": 5283,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e52f61d12bf6abd9c2"
          },
          "AyaContent": "عتل بعد ذٰلك زنيم",
          "AyaNumber": 5284,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e62f61d12bf6abd9c3"
          },
          "AyaContent": "أن كان ذا مال وبنين",
          "AyaNumber": 5285,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e62f61d12bf6abd9c4"
          },
          "AyaContent": "إذا تتلىٰ عليه آياتنا قال أساطير الأولين",
          "AyaNumber": 5286,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e62f61d12bf6abd9c5"
          },
          "AyaContent": "سنسمه على الخرطوم",
          "AyaNumber": 5287,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e62f61d12bf6abd9c6"
          },
          "AyaContent": "إنا بلوناهم كما بلونا أصحاب الجنة إذ أقسموا ليصرمنها مصبحين",
          "AyaNumber": 5288,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e72f61d12bf6abd9c7"
          },
          "AyaContent": "ولا يستثنون",
          "AyaNumber": 5289,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e72f61d12bf6abd9c8"
          },
          "AyaContent": "فطاف عليها طائف من ربك وهم نائمون",
          "AyaNumber": 5290,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e72f61d12bf6abd9c9"
          },
          "AyaContent": "فأصبحت كالصريم",
          "AyaNumber": 5291,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e82f61d12bf6abd9ca"
          },
          "AyaContent": "فتنادوا مصبحين",
          "AyaNumber": 5292,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e82f61d12bf6abd9cb"
          },
          "AyaContent": "أن اغدوا علىٰ حرثكم إن كنتم صارمين",
          "AyaNumber": 5293,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e82f61d12bf6abd9cc"
          },
          "AyaContent": "فانطلقوا وهم يتخافتون",
          "AyaNumber": 5294,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e92f61d12bf6abd9cd"
          },
          "AyaContent": "أن لا يدخلنها اليوم عليكم مسكين",
          "AyaNumber": 5295,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e92f61d12bf6abd9ce"
          },
          "AyaContent": "وغدوا علىٰ حرد قادرين",
          "AyaNumber": 5296,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89e92f61d12bf6abd9cf"
          },
          "AyaContent": "فلما رأوها قالوا إنا لضالون",
          "AyaNumber": 5297,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ea2f61d12bf6abd9d0"
          },
          "AyaContent": "بل نحن محرومون",
          "AyaNumber": 5298,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ea2f61d12bf6abd9d1"
          },
          "AyaContent": "قال أوسطهم ألم أقل لكم لولا تسبحون",
          "AyaNumber": 5299,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ea2f61d12bf6abd9d2"
          },
          "AyaContent": "قالوا سبحان ربنا إنا كنا ظالمين",
          "AyaNumber": 5300,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89eb2f61d12bf6abd9d3"
          },
          "AyaContent": "فأقبل بعضهم علىٰ بعض يتلاومون",
          "AyaNumber": 5301,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89eb2f61d12bf6abd9d4"
          },
          "AyaContent": "قالوا يا ويلنا إنا كنا طاغين",
          "AyaNumber": 5302,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89eb2f61d12bf6abd9d5"
          },
          "AyaContent": "عسىٰ ربنا أن يبدلنا خيرا منها إنا إلىٰ ربنا راغبون",
          "AyaNumber": 5303,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89eb2f61d12bf6abd9d6"
          },
          "AyaContent": "كذٰلك العذاب ۖ ولعذاب الآخرة أكبر ۚ لو كانوا يعلمون",
          "AyaNumber": 5304,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89eb2f61d12bf6abd9d7"
          },
          "AyaContent": "إن للمتقين عند ربهم جنات النعيم",
          "AyaNumber": 5305,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89eb2f61d12bf6abd9d8"
          },
          "AyaContent": "أفنجعل المسلمين كالمجرمين",
          "AyaNumber": 5306,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ec2f61d12bf6abd9d9"
          },
          "AyaContent": "ما لكم كيف تحكمون",
          "AyaNumber": 5307,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ec2f61d12bf6abd9da"
          },
          "AyaContent": "أم لكم كتاب فيه تدرسون",
          "AyaNumber": 5308,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ed2f61d12bf6abd9db"
          },
          "AyaContent": "إن لكم فيه لما تخيرون",
          "AyaNumber": 5309,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ed2f61d12bf6abd9dc"
          },
          "AyaContent": "أم لكم أيمان علينا بالغة إلىٰ يوم القيامة ۙ إن لكم لما تحكمون",
          "AyaNumber": 5310,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ee2f61d12bf6abd9dd"
          },
          "AyaContent": "سلهم أيهم بذٰلك زعيم",
          "AyaNumber": 5311,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ee2f61d12bf6abd9de"
          },
          "AyaContent": "أم لهم شركاء فليأتوا بشركائهم إن كانوا صادقين",
          "AyaNumber": 5312,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ee2f61d12bf6abd9df"
          },
          "AyaContent": "يوم يكشف عن ساق ويدعون إلى السجود فلا يستطيعون",
          "AyaNumber": 5313,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ee2f61d12bf6abd9e0"
          },
          "AyaContent": "خاشعة أبصارهم ترهقهم ذلة ۖ وقد كانوا يدعون إلى السجود وهم سالمون",
          "AyaNumber": 5314,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ee2f61d12bf6abd9e1"
          },
          "AyaContent": "فذرني ومن يكذب بهٰذا الحديث ۖ سنستدرجهم من حيث لا يعلمون",
          "AyaNumber": 5315,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ef2f61d12bf6abd9e2"
          },
          "AyaContent": "وأملي لهم ۚ إن كيدي متين",
          "AyaNumber": 5316,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ef2f61d12bf6abd9e3"
          },
          "AyaContent": "أم تسألهم أجرا فهم من مغرم مثقلون",
          "AyaNumber": 5317,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89ef2f61d12bf6abd9e4"
          },
          "AyaContent": "أم عندهم الغيب فهم يكتبون",
          "AyaNumber": 5318,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89f02f61d12bf6abd9e5"
          },
          "AyaContent": "فاصبر لحكم ربك ولا تكن كصاحب الحوت إذ نادىٰ وهو مكظوم",
          "AyaNumber": 5319,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89f02f61d12bf6abd9e6"
          },
          "AyaContent": "لولا أن تداركه نعمة من ربه لنبذ بالعراء وهو مذموم",
          "AyaNumber": 5320,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89f12f61d12bf6abd9e7"
          },
          "AyaContent": "فاجتباه ربه فجعله من الصالحين",
          "AyaNumber": 5321,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89f12f61d12bf6abd9e8"
          },
          "AyaContent": "وإن يكاد الذين كفروا ليزلقونك بأبصارهم لما سمعوا الذكر ويقولون إنه لمجنون",
          "AyaNumber": 5322,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89f12f61d12bf6abd9e9"
          },
          "AyaContent": "وما هو إلا ذكر للعالمين",
          "AyaNumber": 5323,
          "SurahName": "سورة القلم",
          "SurahNumber": 68
        },{
          "_id": {
            "$oid": "5ecd89f12f61d12bf6abd9ea"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم الحاقة",
          "AyaNumber": 5324,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f22f61d12bf6abd9eb"
          },
          "AyaContent": "ما الحاقة",
          "AyaNumber": 5325,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f22f61d12bf6abd9ec"
          },
          "AyaContent": "وما أدراك ما الحاقة",
          "AyaNumber": 5326,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f22f61d12bf6abd9ed"
          },
          "AyaContent": "كذبت ثمود وعاد بالقارعة",
          "AyaNumber": 5327,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f32f61d12bf6abd9ee"
          },
          "AyaContent": "فأما ثمود فأهلكوا بالطاغية",
          "AyaNumber": 5328,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f32f61d12bf6abd9ef"
          },
          "AyaContent": "وأما عاد فأهلكوا بريح صرصر عاتية",
          "AyaNumber": 5329,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f32f61d12bf6abd9f0"
          },
          "AyaContent": "سخرها عليهم سبع ليال وثمانية أيام حسوما فترى القوم فيها صرعىٰ كأنهم أعجاز نخل خاوية",
          "AyaNumber": 5330,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f42f61d12bf6abd9f1"
          },
          "AyaContent": "فهل ترىٰ لهم من باقية",
          "AyaNumber": 5331,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f42f61d12bf6abd9f2"
          },
          "AyaContent": "وجاء فرعون ومن قبله والمؤتفكات بالخاطئة",
          "AyaNumber": 5332,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f42f61d12bf6abd9f3"
          },
          "AyaContent": "فعصوا رسول ربهم فأخذهم أخذة رابية",
          "AyaNumber": 5333,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f42f61d12bf6abd9f4"
          },
          "AyaContent": "إنا لما طغى الماء حملناكم في الجارية",
          "AyaNumber": 5334,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f42f61d12bf6abd9f5"
          },
          "AyaContent": "لنجعلها لكم تذكرة وتعيها أذن واعية",
          "AyaNumber": 5335,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f52f61d12bf6abd9f6"
          },
          "AyaContent": "فإذا نفخ في الصور نفخة واحدة",
          "AyaNumber": 5336,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f52f61d12bf6abd9f7"
          },
          "AyaContent": "وحملت الأرض والجبال فدكتا دكة واحدة",
          "AyaNumber": 5337,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f52f61d12bf6abd9f8"
          },
          "AyaContent": "فيومئذ وقعت الواقعة",
          "AyaNumber": 5338,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f62f61d12bf6abd9f9"
          },
          "AyaContent": "وانشقت السماء فهي يومئذ واهية",
          "AyaNumber": 5339,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f62f61d12bf6abd9fa"
          },
          "AyaContent": "والملك علىٰ أرجائها ۚ ويحمل عرش ربك فوقهم يومئذ ثمانية",
          "AyaNumber": 5340,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f62f61d12bf6abd9fb"
          },
          "AyaContent": "يومئذ تعرضون لا تخفىٰ منكم خافية",
          "AyaNumber": 5341,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f62f61d12bf6abd9fc"
          },
          "AyaContent": "فأما من أوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه",
          "AyaNumber": 5342,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f72f61d12bf6abd9fd"
          },
          "AyaContent": "إني ظننت أني ملاق حسابيه",
          "AyaNumber": 5343,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f72f61d12bf6abd9fe"
          },
          "AyaContent": "فهو في عيشة راضية",
          "AyaNumber": 5344,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f72f61d12bf6abd9ff"
          },
          "AyaContent": "في جنة عالية",
          "AyaNumber": 5345,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f82f61d12bf6abda00"
          },
          "AyaContent": "قطوفها دانية",
          "AyaNumber": 5346,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f82f61d12bf6abda01"
          },
          "AyaContent": "كلوا واشربوا هنيئا بما أسلفتم في الأيام الخالية",
          "AyaNumber": 5347,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f82f61d12bf6abda02"
          },
          "AyaContent": "وأما من أوتي كتابه بشماله فيقول يا ليتني لم أوت كتابيه",
          "AyaNumber": 5348,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f82f61d12bf6abda03"
          },
          "AyaContent": "ولم أدر ما حسابيه",
          "AyaNumber": 5349,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f82f61d12bf6abda04"
          },
          "AyaContent": "يا ليتها كانت القاضية",
          "AyaNumber": 5350,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f92f61d12bf6abda05"
          },
          "AyaContent": "ما أغنىٰ عني ماليه ۜ",
          "AyaNumber": 5351,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f92f61d12bf6abda06"
          },
          "AyaContent": "هلك عني سلطانيه",
          "AyaNumber": 5352,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89f92f61d12bf6abda07"
          },
          "AyaContent": "خذوه فغلوه",
          "AyaNumber": 5353,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fa2f61d12bf6abda08"
          },
          "AyaContent": "ثم الجحيم صلوه",
          "AyaNumber": 5354,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fa2f61d12bf6abda09"
          },
          "AyaContent": "ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه",
          "AyaNumber": 5355,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fa2f61d12bf6abda0a"
          },
          "AyaContent": "إنه كان لا يؤمن بالله العظيم",
          "AyaNumber": 5356,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fa2f61d12bf6abda0b"
          },
          "AyaContent": "ولا يحض علىٰ طعام المسكين",
          "AyaNumber": 5357,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fa2f61d12bf6abda0c"
          },
          "AyaContent": "فليس له اليوم هاهنا حميم",
          "AyaNumber": 5358,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fb2f61d12bf6abda0d"
          },
          "AyaContent": "ولا طعام إلا من غسلين",
          "AyaNumber": 5359,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fb2f61d12bf6abda0e"
          },
          "AyaContent": "لا يأكله إلا الخاطئون",
          "AyaNumber": 5360,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fb2f61d12bf6abda0f"
          },
          "AyaContent": "فلا أقسم بما تبصرون",
          "AyaNumber": 5361,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fb2f61d12bf6abda10"
          },
          "AyaContent": "وما لا تبصرون",
          "AyaNumber": 5362,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fc2f61d12bf6abda11"
          },
          "AyaContent": "إنه لقول رسول كريم",
          "AyaNumber": 5363,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fc2f61d12bf6abda12"
          },
          "AyaContent": "وما هو بقول شاعر ۚ قليلا ما تؤمنون",
          "AyaNumber": 5364,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fc2f61d12bf6abda13"
          },
          "AyaContent": "ولا بقول كاهن ۚ قليلا ما تذكرون",
          "AyaNumber": 5365,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fd2f61d12bf6abda14"
          },
          "AyaContent": "تنزيل من رب العالمين",
          "AyaNumber": 5366,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fd2f61d12bf6abda15"
          },
          "AyaContent": "ولو تقول علينا بعض الأقاويل",
          "AyaNumber": 5367,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fd2f61d12bf6abda16"
          },
          "AyaContent": "لأخذنا منه باليمين",
          "AyaNumber": 5368,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fe2f61d12bf6abda17"
          },
          "AyaContent": "ثم لقطعنا منه الوتين",
          "AyaNumber": 5369,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fe2f61d12bf6abda18"
          },
          "AyaContent": "فما منكم من أحد عنه حاجزين",
          "AyaNumber": 5370,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89fe2f61d12bf6abda19"
          },
          "AyaContent": "وإنه لتذكرة للمتقين",
          "AyaNumber": 5371,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89ff2f61d12bf6abda1a"
          },
          "AyaContent": "وإنا لنعلم أن منكم مكذبين",
          "AyaNumber": 5372,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd89ff2f61d12bf6abda1b"
          },
          "AyaContent": "وإنه لحسرة على الكافرين",
          "AyaNumber": 5373,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd8a002f61d12bf6abda1c"
          },
          "AyaContent": "وإنه لحق اليقين",
          "AyaNumber": 5374,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd8a012f61d12bf6abda1d"
          },
          "AyaContent": "فسبح باسم ربك العظيم",
          "AyaNumber": 5375,
          "SurahName": "سورة الحاقة",
          "SurahNumber": 69
        },{
          "_id": {
            "$oid": "5ecd8a012f61d12bf6abda1e"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم سأل سائل بعذاب واقع",
          "AyaNumber": 5376,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a012f61d12bf6abda1f"
          },
          "AyaContent": "للكافرين ليس له دافع",
          "AyaNumber": 5377,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a022f61d12bf6abda20"
          },
          "AyaContent": "من الله ذي المعارج",
          "AyaNumber": 5378,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a022f61d12bf6abda21"
          },
          "AyaContent": "تعرج الملائكة والروح إليه في يوم كان مقداره خمسين ألف سنة",
          "AyaNumber": 5379,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a022f61d12bf6abda22"
          },
          "AyaContent": "فاصبر صبرا جميلا",
          "AyaNumber": 5380,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a032f61d12bf6abda23"
          },
          "AyaContent": "إنهم يرونه بعيدا",
          "AyaNumber": 5381,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a032f61d12bf6abda24"
          },
          "AyaContent": "ونراه قريبا",
          "AyaNumber": 5382,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a032f61d12bf6abda25"
          },
          "AyaContent": "يوم تكون السماء كالمهل",
          "AyaNumber": 5383,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a032f61d12bf6abda26"
          },
          "AyaContent": "وتكون الجبال كالعهن",
          "AyaNumber": 5384,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a032f61d12bf6abda27"
          },
          "AyaContent": "ولا يسأل حميم حميما",
          "AyaNumber": 5385,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a042f61d12bf6abda28"
          },
          "AyaContent": "يبصرونهم ۚ يود المجرم لو يفتدي من عذاب يومئذ ببنيه",
          "AyaNumber": 5386,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a042f61d12bf6abda29"
          },
          "AyaContent": "وصاحبته وأخيه",
          "AyaNumber": 5387,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a042f61d12bf6abda2a"
          },
          "AyaContent": "وفصيلته التي تؤويه",
          "AyaNumber": 5388,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a042f61d12bf6abda2b"
          },
          "AyaContent": "ومن في الأرض جميعا ثم ينجيه",
          "AyaNumber": 5389,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a042f61d12bf6abda2c"
          },
          "AyaContent": "كلا ۖ إنها لظىٰ",
          "AyaNumber": 5390,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a052f61d12bf6abda2d"
          },
          "AyaContent": "نزاعة للشوىٰ",
          "AyaNumber": 5391,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a052f61d12bf6abda2e"
          },
          "AyaContent": "تدعو من أدبر وتولىٰ",
          "AyaNumber": 5392,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a052f61d12bf6abda2f"
          },
          "AyaContent": "وجمع فأوعىٰ",
          "AyaNumber": 5393,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a052f61d12bf6abda30"
          },
          "AyaContent": "۞ إن الإنسان خلق هلوعا",
          "AyaNumber": 5394,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a052f61d12bf6abda31"
          },
          "AyaContent": "إذا مسه الشر جزوعا",
          "AyaNumber": 5395,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a062f61d12bf6abda32"
          },
          "AyaContent": "وإذا مسه الخير منوعا",
          "AyaNumber": 5396,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a062f61d12bf6abda33"
          },
          "AyaContent": "إلا المصلين",
          "AyaNumber": 5397,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a062f61d12bf6abda34"
          },
          "AyaContent": "الذين هم علىٰ صلاتهم دائمون",
          "AyaNumber": 5398,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a062f61d12bf6abda35"
          },
          "AyaContent": "والذين في أموالهم حق معلوم",
          "AyaNumber": 5399,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a062f61d12bf6abda36"
          },
          "AyaContent": "للسائل والمحروم",
          "AyaNumber": 5400,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a062f61d12bf6abda37"
          },
          "AyaContent": "والذين يصدقون بيوم الدين",
          "AyaNumber": 5401,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a072f61d12bf6abda38"
          },
          "AyaContent": "والذين هم من عذاب ربهم مشفقون",
          "AyaNumber": 5402,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a072f61d12bf6abda39"
          },
          "AyaContent": "إن عذاب ربهم غير مأمون",
          "AyaNumber": 5403,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a072f61d12bf6abda3a"
          },
          "AyaContent": "والذين هم لفروجهم حافظون",
          "AyaNumber": 5404,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a072f61d12bf6abda3b"
          },
          "AyaContent": "إلا علىٰ أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين",
          "AyaNumber": 5405,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a072f61d12bf6abda3c"
          },
          "AyaContent": "فمن ابتغىٰ وراء ذٰلك فأولٰئك هم العادون",
          "AyaNumber": 5406,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a082f61d12bf6abda3d"
          },
          "AyaContent": "والذين هم لأماناتهم وعهدهم راعون",
          "AyaNumber": 5407,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a082f61d12bf6abda3e"
          },
          "AyaContent": "والذين هم بشهاداتهم قائمون",
          "AyaNumber": 5408,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a082f61d12bf6abda3f"
          },
          "AyaContent": "والذين هم علىٰ صلاتهم يحافظون",
          "AyaNumber": 5409,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a082f61d12bf6abda40"
          },
          "AyaContent": "أولٰئك في جنات مكرمون",
          "AyaNumber": 5410,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a082f61d12bf6abda41"
          },
          "AyaContent": "فمال الذين كفروا قبلك مهطعين",
          "AyaNumber": 5411,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a092f61d12bf6abda42"
          },
          "AyaContent": "عن اليمين وعن الشمال عزين",
          "AyaNumber": 5412,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a092f61d12bf6abda43"
          },
          "AyaContent": "أيطمع كل امرئ منهم أن يدخل جنة نعيم",
          "AyaNumber": 5413,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a092f61d12bf6abda44"
          },
          "AyaContent": "كلا ۖ إنا خلقناهم مما يعلمون",
          "AyaNumber": 5414,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a092f61d12bf6abda45"
          },
          "AyaContent": "فلا أقسم برب المشارق والمغارب إنا لقادرون",
          "AyaNumber": 5415,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a092f61d12bf6abda46"
          },
          "AyaContent": "علىٰ أن نبدل خيرا منهم وما نحن بمسبوقين",
          "AyaNumber": 5416,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a092f61d12bf6abda47"
          },
          "AyaContent": "فذرهم يخوضوا ويلعبوا حتىٰ يلاقوا يومهم الذي يوعدون",
          "AyaNumber": 5417,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a0a2f61d12bf6abda48"
          },
          "AyaContent": "يوم يخرجون من الأجداث سراعا كأنهم إلىٰ نصب يوفضون",
          "AyaNumber": 5418,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a0a2f61d12bf6abda49"
          },
          "AyaContent": "خاشعة أبصارهم ترهقهم ذلة ۚ ذٰلك اليوم الذي كانوا يوعدون",
          "AyaNumber": 5419,
          "SurahName": "سورة المعارج",
          "SurahNumber": 70
        },{
          "_id": {
            "$oid": "5ecd8a0a2f61d12bf6abda4a"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم إنا أرسلنا نوحا إلىٰ قومه أن أنذر قومك من قبل أن يأتيهم عذاب أليم",
          "AyaNumber": 5420,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0a2f61d12bf6abda4b"
          },
          "AyaContent": "قال يا قوم إني لكم نذير مبين",
          "AyaNumber": 5421,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0a2f61d12bf6abda4c"
          },
          "AyaContent": "أن اعبدوا الله واتقوه وأطيعون",
          "AyaNumber": 5422,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0a2f61d12bf6abda4d"
          },
          "AyaContent": "يغفر لكم من ذنوبكم ويؤخركم إلىٰ أجل مسمى ۚ إن أجل الله إذا جاء لا يؤخر ۖ لو كنتم تعلمون",
          "AyaNumber": 5423,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0b2f61d12bf6abda4e"
          },
          "AyaContent": "قال رب إني دعوت قومي ليلا ونهارا",
          "AyaNumber": 5424,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0b2f61d12bf6abda4f"
          },
          "AyaContent": "فلم يزدهم دعائي إلا فرارا",
          "AyaNumber": 5425,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0b2f61d12bf6abda50"
          },
          "AyaContent": "وإني كلما دعوتهم لتغفر لهم جعلوا أصابعهم في آذانهم واستغشوا ثيابهم وأصروا واستكبروا استكبارا",
          "AyaNumber": 5426,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0b2f61d12bf6abda51"
          },
          "AyaContent": "ثم إني دعوتهم جهارا",
          "AyaNumber": 5427,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0c2f61d12bf6abda52"
          },
          "AyaContent": "ثم إني أعلنت لهم وأسررت لهم إسرارا",
          "AyaNumber": 5428,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0c2f61d12bf6abda53"
          },
          "AyaContent": "فقلت استغفروا ربكم إنه كان غفارا",
          "AyaNumber": 5429,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0c2f61d12bf6abda54"
          },
          "AyaContent": "يرسل السماء عليكم مدرارا",
          "AyaNumber": 5430,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0c2f61d12bf6abda55"
          },
          "AyaContent": "ويمددكم بأموال وبنين ويجعل لكم جنات ويجعل لكم أنهارا",
          "AyaNumber": 5431,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0d2f61d12bf6abda56"
          },
          "AyaContent": "ما لكم لا ترجون لله وقارا",
          "AyaNumber": 5432,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0d2f61d12bf6abda57"
          },
          "AyaContent": "وقد خلقكم أطوارا",
          "AyaNumber": 5433,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0d2f61d12bf6abda58"
          },
          "AyaContent": "ألم تروا كيف خلق الله سبع سماوات طباقا",
          "AyaNumber": 5434,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0d2f61d12bf6abda59"
          },
          "AyaContent": "وجعل القمر فيهن نورا وجعل الشمس سراجا",
          "AyaNumber": 5435,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0d2f61d12bf6abda5a"
          },
          "AyaContent": "والله أنبتكم من الأرض نباتا",
          "AyaNumber": 5436,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0e2f61d12bf6abda5b"
          },
          "AyaContent": "ثم يعيدكم فيها ويخرجكم إخراجا",
          "AyaNumber": 5437,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0e2f61d12bf6abda5c"
          },
          "AyaContent": "والله جعل لكم الأرض بساطا",
          "AyaNumber": 5438,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0e2f61d12bf6abda5d"
          },
          "AyaContent": "لتسلكوا منها سبلا فجاجا",
          "AyaNumber": 5439,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0e2f61d12bf6abda5e"
          },
          "AyaContent": "قال نوح رب إنهم عصوني واتبعوا من لم يزده ماله وولده إلا خسارا",
          "AyaNumber": 5440,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0e2f61d12bf6abda5f"
          },
          "AyaContent": "ومكروا مكرا كبارا",
          "AyaNumber": 5441,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0e2f61d12bf6abda60"
          },
          "AyaContent": "وقالوا لا تذرن آلهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا",
          "AyaNumber": 5442,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0f2f61d12bf6abda61"
          },
          "AyaContent": "وقد أضلوا كثيرا ۖ ولا تزد الظالمين إلا ضلالا",
          "AyaNumber": 5443,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0f2f61d12bf6abda62"
          },
          "AyaContent": "مما خطيئاتهم أغرقوا فأدخلوا نارا فلم يجدوا لهم من دون الله أنصارا",
          "AyaNumber": 5444,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0f2f61d12bf6abda63"
          },
          "AyaContent": "وقال نوح رب لا تذر على الأرض من الكافرين ديارا",
          "AyaNumber": 5445,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a0f2f61d12bf6abda64"
          },
          "AyaContent": "إنك إن تذرهم يضلوا عبادك ولا يلدوا إلا فاجرا كفارا",
          "AyaNumber": 5446,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a102f61d12bf6abda65"
          },
          "AyaContent": "رب اغفر لي ولوالدي ولمن دخل بيتي مؤمنا وللمؤمنين والمؤمنات ولا تزد الظالمين إلا تبارا",
          "AyaNumber": 5447,
          "SurahName": "سورة نوح",
          "SurahNumber": 71
        },{
          "_id": {
            "$oid": "5ecd8a102f61d12bf6abda66"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم قل أوحي إلي أنه استمع نفر من الجن فقالوا إنا سمعنا قرآنا عجبا",
          "AyaNumber": 5448,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a102f61d12bf6abda67"
          },
          "AyaContent": "يهدي إلى الرشد فآمنا به ۖ ولن نشرك بربنا أحدا",
          "AyaNumber": 5449,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a102f61d12bf6abda68"
          },
          "AyaContent": "وأنه تعالىٰ جد ربنا ما اتخذ صاحبة ولا ولدا",
          "AyaNumber": 5450,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a102f61d12bf6abda69"
          },
          "AyaContent": "وأنه كان يقول سفيهنا على الله شططا",
          "AyaNumber": 5451,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a112f61d12bf6abda6a"
          },
          "AyaContent": "وأنا ظننا أن لن تقول الإنس والجن على الله كذبا",
          "AyaNumber": 5452,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a112f61d12bf6abda6b"
          },
          "AyaContent": "وأنه كان رجال من الإنس يعوذون برجال من الجن فزادوهم رهقا",
          "AyaNumber": 5453,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a132f61d12bf6abda6c"
          },
          "AyaContent": "وأنهم ظنوا كما ظننتم أن لن يبعث الله أحدا",
          "AyaNumber": 5454,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a132f61d12bf6abda6d"
          },
          "AyaContent": "وأنا لمسنا السماء فوجدناها ملئت حرسا شديدا وشهبا",
          "AyaNumber": 5455,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a132f61d12bf6abda6e"
          },
          "AyaContent": "وأنا كنا نقعد منها مقاعد للسمع ۖ فمن يستمع الآن يجد له شهابا رصدا",
          "AyaNumber": 5456,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a132f61d12bf6abda6f"
          },
          "AyaContent": "وأنا لا ندري أشر أريد بمن في الأرض أم أراد بهم ربهم رشدا",
          "AyaNumber": 5457,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a142f61d12bf6abda70"
          },
          "AyaContent": "وأنا منا الصالحون ومنا دون ذٰلك ۖ كنا طرائق قددا",
          "AyaNumber": 5458,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a152f61d12bf6abda71"
          },
          "AyaContent": "وأنا ظننا أن لن نعجز الله في الأرض ولن نعجزه هربا",
          "AyaNumber": 5459,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a152f61d12bf6abda72"
          },
          "AyaContent": "وأنا لما سمعنا الهدىٰ آمنا به ۖ فمن يؤمن بربه فلا يخاف بخسا ولا رهقا",
          "AyaNumber": 5460,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a152f61d12bf6abda73"
          },
          "AyaContent": "وأنا منا المسلمون ومنا القاسطون ۖ فمن أسلم فأولٰئك تحروا رشدا",
          "AyaNumber": 5461,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a162f61d12bf6abda74"
          },
          "AyaContent": "وأما القاسطون فكانوا لجهنم حطبا",
          "AyaNumber": 5462,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a162f61d12bf6abda75"
          },
          "AyaContent": "وأن لو استقاموا على الطريقة لأسقيناهم ماء غدقا",
          "AyaNumber": 5463,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a162f61d12bf6abda76"
          },
          "AyaContent": "لنفتنهم فيه ۚ ومن يعرض عن ذكر ربه يسلكه عذابا صعدا",
          "AyaNumber": 5464,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a162f61d12bf6abda77"
          },
          "AyaContent": "وأن المساجد لله فلا تدعوا مع الله أحدا",
          "AyaNumber": 5465,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a162f61d12bf6abda78"
          },
          "AyaContent": "وأنه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا",
          "AyaNumber": 5466,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a172f61d12bf6abda79"
          },
          "AyaContent": "قل إنما أدعو ربي ولا أشرك به أحدا",
          "AyaNumber": 5467,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a172f61d12bf6abda7a"
          },
          "AyaContent": "قل إني لا أملك لكم ضرا ولا رشدا",
          "AyaNumber": 5468,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a172f61d12bf6abda7b"
          },
          "AyaContent": "قل إني لن يجيرني من الله أحد ولن أجد من دونه ملتحدا",
          "AyaNumber": 5469,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a172f61d12bf6abda7c"
          },
          "AyaContent": "إلا بلاغا من الله ورسالاته ۚ ومن يعص الله ورسوله فإن له نار جهنم خالدين فيها أبدا",
          "AyaNumber": 5470,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a182f61d12bf6abda7d"
          },
          "AyaContent": "حتىٰ إذا رأوا ما يوعدون فسيعلمون من أضعف ناصرا وأقل عددا",
          "AyaNumber": 5471,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a182f61d12bf6abda7e"
          },
          "AyaContent": "قل إن أدري أقريب ما توعدون أم يجعل له ربي أمدا",
          "AyaNumber": 5472,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a182f61d12bf6abda7f"
          },
          "AyaContent": "عالم الغيب فلا يظهر علىٰ غيبه أحدا",
          "AyaNumber": 5473,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a182f61d12bf6abda80"
          },
          "AyaContent": "إلا من ارتضىٰ من رسول فإنه يسلك من بين يديه ومن خلفه رصدا",
          "AyaNumber": 5474,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a192f61d12bf6abda81"
          },
          "AyaContent": "ليعلم أن قد أبلغوا رسالات ربهم وأحاط بما لديهم وأحصىٰ كل شيء عددا",
          "AyaNumber": 5475,
          "SurahName": "سورة الجن",
          "SurahNumber": 72
        },{
          "_id": {
            "$oid": "5ecd8a192f61d12bf6abda82"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يا أيها المزمل",
          "AyaNumber": 5476,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1a2f61d12bf6abda83"
          },
          "AyaContent": "قم الليل إلا قليلا",
          "AyaNumber": 5477,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1b2f61d12bf6abda84"
          },
          "AyaContent": "نصفه أو انقص منه قليلا",
          "AyaNumber": 5478,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1b2f61d12bf6abda85"
          },
          "AyaContent": "أو زد عليه ورتل القرآن ترتيلا",
          "AyaNumber": 5479,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1b2f61d12bf6abda86"
          },
          "AyaContent": "إنا سنلقي عليك قولا ثقيلا",
          "AyaNumber": 5480,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1c2f61d12bf6abda87"
          },
          "AyaContent": "إن ناشئة الليل هي أشد وطئا وأقوم قيلا",
          "AyaNumber": 5481,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1c2f61d12bf6abda88"
          },
          "AyaContent": "إن لك في النهار سبحا طويلا",
          "AyaNumber": 5482,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1c2f61d12bf6abda89"
          },
          "AyaContent": "واذكر اسم ربك وتبتل إليه تبتيلا",
          "AyaNumber": 5483,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1c2f61d12bf6abda8a"
          },
          "AyaContent": "رب المشرق والمغرب لا إلٰه إلا هو فاتخذه وكيلا",
          "AyaNumber": 5484,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1d2f61d12bf6abda8b"
          },
          "AyaContent": "واصبر علىٰ ما يقولون واهجرهم هجرا جميلا",
          "AyaNumber": 5485,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1d2f61d12bf6abda8c"
          },
          "AyaContent": "وذرني والمكذبين أولي النعمة ومهلهم قليلا",
          "AyaNumber": 5486,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1d2f61d12bf6abda8d"
          },
          "AyaContent": "إن لدينا أنكالا وجحيما",
          "AyaNumber": 5487,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1d2f61d12bf6abda8e"
          },
          "AyaContent": "وطعاما ذا غصة وعذابا أليما",
          "AyaNumber": 5488,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1e2f61d12bf6abda8f"
          },
          "AyaContent": "يوم ترجف الأرض والجبال وكانت الجبال كثيبا مهيلا",
          "AyaNumber": 5489,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1e2f61d12bf6abda90"
          },
          "AyaContent": "إنا أرسلنا إليكم رسولا شاهدا عليكم كما أرسلنا إلىٰ فرعون رسولا",
          "AyaNumber": 5490,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1e2f61d12bf6abda91"
          },
          "AyaContent": "فعصىٰ فرعون الرسول فأخذناه أخذا وبيلا",
          "AyaNumber": 5491,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1e2f61d12bf6abda92"
          },
          "AyaContent": "فكيف تتقون إن كفرتم يوما يجعل الولدان شيبا",
          "AyaNumber": 5492,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1f2f61d12bf6abda93"
          },
          "AyaContent": "السماء منفطر به ۚ كان وعده مفعولا",
          "AyaNumber": 5493,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1f2f61d12bf6abda94"
          },
          "AyaContent": "إن هٰذه تذكرة ۖ فمن شاء اتخذ إلىٰ ربه سبيلا",
          "AyaNumber": 5494,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a1f2f61d12bf6abda95"
          },
          "AyaContent": "۞ إن ربك يعلم أنك تقوم أدنىٰ من ثلثي الليل ونصفه وثلثه وطائفة من الذين معك ۚ والله يقدر الليل والنهار ۚ علم أن لن تحصوه فتاب عليكم ۖ فاقرءوا ما تيسر من القرآن ۚ علم أن سيكون منكم مرضىٰ ۙ وآخرون يضربون في الأرض يبتغون من فضل الله ۙ وآخرون يقاتلون في سبيل الله ۖ فاقرءوا ما تيسر منه ۚ وأقيموا الصلاة وآتوا الزكاة وأقرضوا الله قرضا حسنا ۚ وما تقدموا لأنفسكم من خير تجدوه عند الله هو خيرا وأعظم أجرا ۚ واستغفروا الله ۖ إن الله غفور رحيم",
          "AyaNumber": 5495,
          "SurahName": "سورة المزمل",
          "SurahNumber": 73
        },{
          "_id": {
            "$oid": "5ecd8a202f61d12bf6abda96"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم يا أيها المدثر",
          "AyaNumber": 5496,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a202f61d12bf6abda97"
          },
          "AyaContent": "قم فأنذر",
          "AyaNumber": 5497,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a202f61d12bf6abda98"
          },
          "AyaContent": "وربك فكبر",
          "AyaNumber": 5498,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a212f61d12bf6abda99"
          },
          "AyaContent": "وثيابك فطهر",
          "AyaNumber": 5499,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a212f61d12bf6abda9a"
          },
          "AyaContent": "والرجز فاهجر",
          "AyaNumber": 5500,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a212f61d12bf6abda9b"
          },
          "AyaContent": "ولا تمنن تستكثر",
          "AyaNumber": 5501,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a222f61d12bf6abda9c"
          },
          "AyaContent": "ولربك فاصبر",
          "AyaNumber": 5502,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a222f61d12bf6abda9d"
          },
          "AyaContent": "فإذا نقر في الناقور",
          "AyaNumber": 5503,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a222f61d12bf6abda9e"
          },
          "AyaContent": "فذٰلك يومئذ يوم عسير",
          "AyaNumber": 5504,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a222f61d12bf6abda9f"
          },
          "AyaContent": "على الكافرين غير يسير",
          "AyaNumber": 5505,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a222f61d12bf6abdaa0"
          },
          "AyaContent": "ذرني ومن خلقت وحيدا",
          "AyaNumber": 5506,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a232f61d12bf6abdaa1"
          },
          "AyaContent": "وجعلت له مالا ممدودا",
          "AyaNumber": 5507,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a232f61d12bf6abdaa2"
          },
          "AyaContent": "وبنين شهودا",
          "AyaNumber": 5508,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a232f61d12bf6abdaa3"
          },
          "AyaContent": "ومهدت له تمهيدا",
          "AyaNumber": 5509,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a242f61d12bf6abdaa4"
          },
          "AyaContent": "ثم يطمع أن أزيد",
          "AyaNumber": 5510,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a242f61d12bf6abdaa5"
          },
          "AyaContent": "كلا ۖ إنه كان لآياتنا عنيدا",
          "AyaNumber": 5511,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a242f61d12bf6abdaa6"
          },
          "AyaContent": "سأرهقه صعودا",
          "AyaNumber": 5512,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a252f61d12bf6abdaa7"
          },
          "AyaContent": "إنه فكر وقدر",
          "AyaNumber": 5513,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a252f61d12bf6abdaa8"
          },
          "AyaContent": "فقتل كيف قدر",
          "AyaNumber": 5514,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a252f61d12bf6abdaa9"
          },
          "AyaContent": "ثم قتل كيف قدر",
          "AyaNumber": 5515,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a262f61d12bf6abdaaa"
          },
          "AyaContent": "ثم نظر",
          "AyaNumber": 5516,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a262f61d12bf6abdaab"
          },
          "AyaContent": "ثم عبس وبسر",
          "AyaNumber": 5517,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a262f61d12bf6abdaac"
          },
          "AyaContent": "ثم أدبر واستكبر",
          "AyaNumber": 5518,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a272f61d12bf6abdaad"
          },
          "AyaContent": "فقال إن هٰذا إلا سحر يؤثر",
          "AyaNumber": 5519,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a272f61d12bf6abdaae"
          },
          "AyaContent": "إن هٰذا إلا قول البشر",
          "AyaNumber": 5520,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a272f61d12bf6abdaaf"
          },
          "AyaContent": "سأصليه سقر",
          "AyaNumber": 5521,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a272f61d12bf6abdab0"
          },
          "AyaContent": "وما أدراك ما سقر",
          "AyaNumber": 5522,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a272f61d12bf6abdab1"
          },
          "AyaContent": "لا تبقي ولا تذر",
          "AyaNumber": 5523,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a282f61d12bf6abdab2"
          },
          "AyaContent": "لواحة للبشر",
          "AyaNumber": 5524,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a282f61d12bf6abdab3"
          },
          "AyaContent": "عليها تسعة عشر",
          "AyaNumber": 5525,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a282f61d12bf6abdab4"
          },
          "AyaContent": "وما جعلنا أصحاب النار إلا ملائكة ۙ وما جعلنا عدتهم إلا فتنة للذين كفروا ليستيقن الذين أوتوا الكتاب ويزداد الذين آمنوا إيمانا ۙ ولا يرتاب الذين أوتوا الكتاب والمؤمنون ۙ وليقول الذين في قلوبهم مرض والكافرون ماذا أراد الله بهٰذا مثلا ۚ كذٰلك يضل الله من يشاء ويهدي من يشاء ۚ وما يعلم جنود ربك إلا هو ۚ وما هي إلا ذكرىٰ للبشر",
          "AyaNumber": 5526,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a292f61d12bf6abdab5"
          },
          "AyaContent": "كلا والقمر",
          "AyaNumber": 5527,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a292f61d12bf6abdab6"
          },
          "AyaContent": "والليل إذ أدبر",
          "AyaNumber": 5528,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a292f61d12bf6abdab7"
          },
          "AyaContent": "والصبح إذا أسفر",
          "AyaNumber": 5529,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a292f61d12bf6abdab8"
          },
          "AyaContent": "إنها لإحدى الكبر",
          "AyaNumber": 5530,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2a2f61d12bf6abdab9"
          },
          "AyaContent": "نذيرا للبشر",
          "AyaNumber": 5531,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2a2f61d12bf6abdaba"
          },
          "AyaContent": "لمن شاء منكم أن يتقدم أو يتأخر",
          "AyaNumber": 5532,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2a2f61d12bf6abdabb"
          },
          "AyaContent": "كل نفس بما كسبت رهينة",
          "AyaNumber": 5533,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2b2f61d12bf6abdabc"
          },
          "AyaContent": "إلا أصحاب اليمين",
          "AyaNumber": 5534,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2b2f61d12bf6abdabd"
          },
          "AyaContent": "في جنات يتساءلون",
          "AyaNumber": 5535,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2b2f61d12bf6abdabe"
          },
          "AyaContent": "عن المجرمين",
          "AyaNumber": 5536,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2b2f61d12bf6abdabf"
          },
          "AyaContent": "ما سلككم في سقر",
          "AyaNumber": 5537,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2b2f61d12bf6abdac0"
          },
          "AyaContent": "قالوا لم نك من المصلين",
          "AyaNumber": 5538,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2c2f61d12bf6abdac1"
          },
          "AyaContent": "ولم نك نطعم المسكين",
          "AyaNumber": 5539,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2c2f61d12bf6abdac2"
          },
          "AyaContent": "وكنا نخوض مع الخائضين",
          "AyaNumber": 5540,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2c2f61d12bf6abdac3"
          },
          "AyaContent": "وكنا نكذب بيوم الدين",
          "AyaNumber": 5541,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2d2f61d12bf6abdac4"
          },
          "AyaContent": "حتىٰ أتانا اليقين",
          "AyaNumber": 5542,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2d2f61d12bf6abdac5"
          },
          "AyaContent": "فما تنفعهم شفاعة الشافعين",
          "AyaNumber": 5543,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2d2f61d12bf6abdac6"
          },
          "AyaContent": "فما لهم عن التذكرة معرضين",
          "AyaNumber": 5544,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2e2f61d12bf6abdac7"
          },
          "AyaContent": "كأنهم حمر مستنفرة",
          "AyaNumber": 5545,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2e2f61d12bf6abdac8"
          },
          "AyaContent": "فرت من قسورة",
          "AyaNumber": 5546,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2e2f61d12bf6abdac9"
          },
          "AyaContent": "بل يريد كل امرئ منهم أن يؤتىٰ صحفا منشرة",
          "AyaNumber": 5547,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2e2f61d12bf6abdaca"
          },
          "AyaContent": "كلا ۖ بل لا يخافون الآخرة",
          "AyaNumber": 5548,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2f2f61d12bf6abdacb"
          },
          "AyaContent": "كلا إنه تذكرة",
          "AyaNumber": 5549,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2f2f61d12bf6abdacc"
          },
          "AyaContent": "فمن شاء ذكره",
          "AyaNumber": 5550,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2f2f61d12bf6abdacd"
          },
          "AyaContent": "وما يذكرون إلا أن يشاء الله ۚ هو أهل التقوىٰ وأهل المغفرة",
          "AyaNumber": 5551,
          "SurahName": "سورة المدثر",
          "SurahNumber": 74
        },{
          "_id": {
            "$oid": "5ecd8a2f2f61d12bf6abdace"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم لا أقسم بيوم القيامة",
          "AyaNumber": 5552,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a302f61d12bf6abdacf"
          },
          "AyaContent": "ولا أقسم بالنفس اللوامة",
          "AyaNumber": 5553,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a302f61d12bf6abdad0"
          },
          "AyaContent": "أيحسب الإنسان ألن نجمع عظامه",
          "AyaNumber": 5554,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a302f61d12bf6abdad1"
          },
          "AyaContent": "بلىٰ قادرين علىٰ أن نسوي بنانه",
          "AyaNumber": 5555,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a312f61d12bf6abdad2"
          },
          "AyaContent": "بل يريد الإنسان ليفجر أمامه",
          "AyaNumber": 5556,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a312f61d12bf6abdad3"
          },
          "AyaContent": "يسأل أيان يوم القيامة",
          "AyaNumber": 5557,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a312f61d12bf6abdad4"
          },
          "AyaContent": "فإذا برق البصر",
          "AyaNumber": 5558,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a322f61d12bf6abdad5"
          },
          "AyaContent": "وخسف القمر",
          "AyaNumber": 5559,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a322f61d12bf6abdad6"
          },
          "AyaContent": "وجمع الشمس والقمر",
          "AyaNumber": 5560,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a322f61d12bf6abdad7"
          },
          "AyaContent": "يقول الإنسان يومئذ أين المفر",
          "AyaNumber": 5561,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a332f61d12bf6abdad8"
          },
          "AyaContent": "كلا لا وزر",
          "AyaNumber": 5562,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a332f61d12bf6abdad9"
          },
          "AyaContent": "إلىٰ ربك يومئذ المستقر",
          "AyaNumber": 5563,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a332f61d12bf6abdada"
          },
          "AyaContent": "ينبأ الإنسان يومئذ بما قدم وأخر",
          "AyaNumber": 5564,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a332f61d12bf6abdadb"
          },
          "AyaContent": "بل الإنسان علىٰ نفسه بصيرة",
          "AyaNumber": 5565,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a332f61d12bf6abdadc"
          },
          "AyaContent": "ولو ألقىٰ معاذيره",
          "AyaNumber": 5566,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a342f61d12bf6abdadd"
          },
          "AyaContent": "لا تحرك به لسانك لتعجل به",
          "AyaNumber": 5567,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a342f61d12bf6abdade"
          },
          "AyaContent": "إن علينا جمعه وقرآنه",
          "AyaNumber": 5568,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a342f61d12bf6abdadf"
          },
          "AyaContent": "فإذا قرأناه فاتبع قرآنه",
          "AyaNumber": 5569,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a342f61d12bf6abdae0"
          },
          "AyaContent": "ثم إن علينا بيانه",
          "AyaNumber": 5570,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a352f61d12bf6abdae1"
          },
          "AyaContent": "كلا بل تحبون العاجلة",
          "AyaNumber": 5571,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a352f61d12bf6abdae2"
          },
          "AyaContent": "وتذرون الآخرة",
          "AyaNumber": 5572,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a352f61d12bf6abdae3"
          },
          "AyaContent": "وجوه يومئذ ناضرة",
          "AyaNumber": 5573,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a352f61d12bf6abdae4"
          },
          "AyaContent": "إلىٰ ربها ناظرة",
          "AyaNumber": 5574,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a352f61d12bf6abdae5"
          },
          "AyaContent": "ووجوه يومئذ باسرة",
          "AyaNumber": 5575,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a362f61d12bf6abdae6"
          },
          "AyaContent": "تظن أن يفعل بها فاقرة",
          "AyaNumber": 5576,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a362f61d12bf6abdae7"
          },
          "AyaContent": "كلا إذا بلغت التراقي",
          "AyaNumber": 5577,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a362f61d12bf6abdae8"
          },
          "AyaContent": "وقيل من ۜ راق",
          "AyaNumber": 5578,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a362f61d12bf6abdae9"
          },
          "AyaContent": "وظن أنه الفراق",
          "AyaNumber": 5579,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a362f61d12bf6abdaea"
          },
          "AyaContent": "والتفت الساق بالساق",
          "AyaNumber": 5580,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a372f61d12bf6abdaeb"
          },
          "AyaContent": "إلىٰ ربك يومئذ المساق",
          "AyaNumber": 5581,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a372f61d12bf6abdaec"
          },
          "AyaContent": "فلا صدق ولا صلىٰ",
          "AyaNumber": 5582,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a372f61d12bf6abdaed"
          },
          "AyaContent": "ولٰكن كذب وتولىٰ",
          "AyaNumber": 5583,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a372f61d12bf6abdaee"
          },
          "AyaContent": "ثم ذهب إلىٰ أهله يتمطىٰ",
          "AyaNumber": 5584,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a372f61d12bf6abdaef"
          },
          "AyaContent": "أولىٰ لك فأولىٰ",
          "AyaNumber": 5585,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a382f61d12bf6abdaf0"
          },
          "AyaContent": "ثم أولىٰ لك فأولىٰ",
          "AyaNumber": 5586,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a382f61d12bf6abdaf1"
          },
          "AyaContent": "أيحسب الإنسان أن يترك سدى",
          "AyaNumber": 5587,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a382f61d12bf6abdaf2"
          },
          "AyaContent": "ألم يك نطفة من مني يمنىٰ",
          "AyaNumber": 5588,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a392f61d12bf6abdaf3"
          },
          "AyaContent": "ثم كان علقة فخلق فسوىٰ",
          "AyaNumber": 5589,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a392f61d12bf6abdaf4"
          },
          "AyaContent": "فجعل منه الزوجين الذكر والأنثىٰ",
          "AyaNumber": 5590,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a392f61d12bf6abdaf5"
          },
          "AyaContent": "أليس ذٰلك بقادر علىٰ أن يحيي الموتىٰ",
          "AyaNumber": 5591,
          "SurahName": "سورة القيامة",
          "SurahNumber": 75
        },{
          "_id": {
            "$oid": "5ecd8a3a2f61d12bf6abdaf6"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم هل أتىٰ على الإنسان حين من الدهر لم يكن شيئا مذكورا",
          "AyaNumber": 5592,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3a2f61d12bf6abdaf7"
          },
          "AyaContent": "إنا خلقنا الإنسان من نطفة أمشاج نبتليه فجعلناه سميعا بصيرا",
          "AyaNumber": 5593,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3a2f61d12bf6abdaf8"
          },
          "AyaContent": "إنا هديناه السبيل إما شاكرا وإما كفورا",
          "AyaNumber": 5594,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3a2f61d12bf6abdaf9"
          },
          "AyaContent": "إنا أعتدنا للكافرين سلاسل وأغلالا وسعيرا",
          "AyaNumber": 5595,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3b2f61d12bf6abdafa"
          },
          "AyaContent": "إن الأبرار يشربون من كأس كان مزاجها كافورا",
          "AyaNumber": 5596,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3b2f61d12bf6abdafb"
          },
          "AyaContent": "عينا يشرب بها عباد الله يفجرونها تفجيرا",
          "AyaNumber": 5597,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3c2f61d12bf6abdafc"
          },
          "AyaContent": "يوفون بالنذر ويخافون يوما كان شره مستطيرا",
          "AyaNumber": 5598,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3c2f61d12bf6abdafd"
          },
          "AyaContent": "ويطعمون الطعام علىٰ حبه مسكينا ويتيما وأسيرا",
          "AyaNumber": 5599,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3d2f61d12bf6abdafe"
          },
          "AyaContent": "إنما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا",
          "AyaNumber": 5600,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3d2f61d12bf6abdaff"
          },
          "AyaContent": "إنا نخاف من ربنا يوما عبوسا قمطريرا",
          "AyaNumber": 5601,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3d2f61d12bf6abdb00"
          },
          "AyaContent": "فوقاهم الله شر ذٰلك اليوم ولقاهم نضرة وسرورا",
          "AyaNumber": 5602,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3d2f61d12bf6abdb01"
          },
          "AyaContent": "وجزاهم بما صبروا جنة وحريرا",
          "AyaNumber": 5603,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3e2f61d12bf6abdb02"
          },
          "AyaContent": "متكئين فيها على الأرائك ۖ لا يرون فيها شمسا ولا زمهريرا",
          "AyaNumber": 5604,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3e2f61d12bf6abdb03"
          },
          "AyaContent": "ودانية عليهم ظلالها وذللت قطوفها تذليلا",
          "AyaNumber": 5605,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3e2f61d12bf6abdb04"
          },
          "AyaContent": "ويطاف عليهم بآنية من فضة وأكواب كانت قواريرا",
          "AyaNumber": 5606,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3e2f61d12bf6abdb05"
          },
          "AyaContent": "قوارير من فضة قدروها تقديرا",
          "AyaNumber": 5607,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3f2f61d12bf6abdb06"
          },
          "AyaContent": "ويسقون فيها كأسا كان مزاجها زنجبيلا",
          "AyaNumber": 5608,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3f2f61d12bf6abdb07"
          },
          "AyaContent": "عينا فيها تسمىٰ سلسبيلا",
          "AyaNumber": 5609,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a3f2f61d12bf6abdb08"
          },
          "AyaContent": "۞ ويطوف عليهم ولدان مخلدون إذا رأيتهم حسبتهم لؤلؤا منثورا",
          "AyaNumber": 5610,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a402f61d12bf6abdb09"
          },
          "AyaContent": "وإذا رأيت ثم رأيت نعيما وملكا كبيرا",
          "AyaNumber": 5611,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a402f61d12bf6abdb0a"
          },
          "AyaContent": "عاليهم ثياب سندس خضر وإستبرق ۖ وحلوا أساور من فضة وسقاهم ربهم شرابا طهورا",
          "AyaNumber": 5612,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a402f61d12bf6abdb0b"
          },
          "AyaContent": "إن هٰذا كان لكم جزاء وكان سعيكم مشكورا",
          "AyaNumber": 5613,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a412f61d12bf6abdb0c"
          },
          "AyaContent": "إنا نحن نزلنا عليك القرآن تنزيلا",
          "AyaNumber": 5614,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a412f61d12bf6abdb0d"
          },
          "AyaContent": "فاصبر لحكم ربك ولا تطع منهم آثما أو كفورا",
          "AyaNumber": 5615,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a412f61d12bf6abdb0e"
          },
          "AyaContent": "واذكر اسم ربك بكرة وأصيلا",
          "AyaNumber": 5616,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a412f61d12bf6abdb0f"
          },
          "AyaContent": "ومن الليل فاسجد له وسبحه ليلا طويلا",
          "AyaNumber": 5617,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a412f61d12bf6abdb10"
          },
          "AyaContent": "إن هٰؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا",
          "AyaNumber": 5618,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a422f61d12bf6abdb11"
          },
          "AyaContent": "نحن خلقناهم وشددنا أسرهم ۖ وإذا شئنا بدلنا أمثالهم تبديلا",
          "AyaNumber": 5619,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a422f61d12bf6abdb12"
          },
          "AyaContent": "إن هٰذه تذكرة ۖ فمن شاء اتخذ إلىٰ ربه سبيلا",
          "AyaNumber": 5620,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a432f61d12bf6abdb13"
          },
          "AyaContent": "وما تشاءون إلا أن يشاء الله ۚ إن الله كان عليما حكيما",
          "AyaNumber": 5621,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a432f61d12bf6abdb14"
          },
          "AyaContent": "يدخل من يشاء في رحمته ۚ والظالمين أعد لهم عذابا أليما",
          "AyaNumber": 5622,
          "SurahName": "سورة الانسان",
          "SurahNumber": 76
        },{
          "_id": {
            "$oid": "5ecd8a432f61d12bf6abdb15"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والمرسلات عرفا",
          "AyaNumber": 5623,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a432f61d12bf6abdb16"
          },
          "AyaContent": "فالعاصفات عصفا",
          "AyaNumber": 5624,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a442f61d12bf6abdb17"
          },
          "AyaContent": "والناشرات نشرا",
          "AyaNumber": 5625,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a452f61d12bf6abdb18"
          },
          "AyaContent": "فالفارقات فرقا",
          "AyaNumber": 5626,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a452f61d12bf6abdb19"
          },
          "AyaContent": "فالملقيات ذكرا",
          "AyaNumber": 5627,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a452f61d12bf6abdb1a"
          },
          "AyaContent": "عذرا أو نذرا",
          "AyaNumber": 5628,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a452f61d12bf6abdb1b"
          },
          "AyaContent": "إنما توعدون لواقع",
          "AyaNumber": 5629,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a462f61d12bf6abdb1c"
          },
          "AyaContent": "فإذا النجوم طمست",
          "AyaNumber": 5630,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a462f61d12bf6abdb1d"
          },
          "AyaContent": "وإذا السماء فرجت",
          "AyaNumber": 5631,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a462f61d12bf6abdb1e"
          },
          "AyaContent": "وإذا الجبال نسفت",
          "AyaNumber": 5632,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a472f61d12bf6abdb1f"
          },
          "AyaContent": "وإذا الرسل أقتت",
          "AyaNumber": 5633,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a472f61d12bf6abdb20"
          },
          "AyaContent": "لأي يوم أجلت",
          "AyaNumber": 5634,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a472f61d12bf6abdb21"
          },
          "AyaContent": "ليوم الفصل",
          "AyaNumber": 5635,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a472f61d12bf6abdb22"
          },
          "AyaContent": "وما أدراك ما يوم الفصل",
          "AyaNumber": 5636,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a472f61d12bf6abdb23"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5637,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a482f61d12bf6abdb24"
          },
          "AyaContent": "ألم نهلك الأولين",
          "AyaNumber": 5638,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a482f61d12bf6abdb25"
          },
          "AyaContent": "ثم نتبعهم الآخرين",
          "AyaNumber": 5639,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a482f61d12bf6abdb26"
          },
          "AyaContent": "كذٰلك نفعل بالمجرمين",
          "AyaNumber": 5640,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a482f61d12bf6abdb27"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5641,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a492f61d12bf6abdb28"
          },
          "AyaContent": "ألم نخلقكم من ماء مهين",
          "AyaNumber": 5642,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a492f61d12bf6abdb29"
          },
          "AyaContent": "فجعلناه في قرار مكين",
          "AyaNumber": 5643,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a492f61d12bf6abdb2a"
          },
          "AyaContent": "إلىٰ قدر معلوم",
          "AyaNumber": 5644,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a492f61d12bf6abdb2b"
          },
          "AyaContent": "فقدرنا فنعم القادرون",
          "AyaNumber": 5645,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4a2f61d12bf6abdb2c"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5646,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4a2f61d12bf6abdb2d"
          },
          "AyaContent": "ألم نجعل الأرض كفاتا",
          "AyaNumber": 5647,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4a2f61d12bf6abdb2e"
          },
          "AyaContent": "أحياء وأمواتا",
          "AyaNumber": 5648,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4b2f61d12bf6abdb2f"
          },
          "AyaContent": "وجعلنا فيها رواسي شامخات وأسقيناكم ماء فراتا",
          "AyaNumber": 5649,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4b2f61d12bf6abdb30"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5650,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4b2f61d12bf6abdb31"
          },
          "AyaContent": "انطلقوا إلىٰ ما كنتم به تكذبون",
          "AyaNumber": 5651,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4b2f61d12bf6abdb32"
          },
          "AyaContent": "انطلقوا إلىٰ ظل ذي ثلاث شعب",
          "AyaNumber": 5652,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4b2f61d12bf6abdb33"
          },
          "AyaContent": "لا ظليل ولا يغني من اللهب",
          "AyaNumber": 5653,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4c2f61d12bf6abdb34"
          },
          "AyaContent": "إنها ترمي بشرر كالقصر",
          "AyaNumber": 5654,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4c2f61d12bf6abdb35"
          },
          "AyaContent": "كأنه جمالت صفر",
          "AyaNumber": 5655,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4c2f61d12bf6abdb36"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5656,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4d2f61d12bf6abdb37"
          },
          "AyaContent": "هٰذا يوم لا ينطقون",
          "AyaNumber": 5657,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4d2f61d12bf6abdb38"
          },
          "AyaContent": "ولا يؤذن لهم فيعتذرون",
          "AyaNumber": 5658,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4d2f61d12bf6abdb39"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5659,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4d2f61d12bf6abdb3a"
          },
          "AyaContent": "هٰذا يوم الفصل ۖ جمعناكم والأولين",
          "AyaNumber": 5660,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4e2f61d12bf6abdb3b"
          },
          "AyaContent": "فإن كان لكم كيد فكيدون",
          "AyaNumber": 5661,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4e2f61d12bf6abdb3c"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5662,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4e2f61d12bf6abdb3d"
          },
          "AyaContent": "إن المتقين في ظلال وعيون",
          "AyaNumber": 5663,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4e2f61d12bf6abdb3e"
          },
          "AyaContent": "وفواكه مما يشتهون",
          "AyaNumber": 5664,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4e2f61d12bf6abdb3f"
          },
          "AyaContent": "كلوا واشربوا هنيئا بما كنتم تعملون",
          "AyaNumber": 5665,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4e2f61d12bf6abdb40"
          },
          "AyaContent": "إنا كذٰلك نجزي المحسنين",
