          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4f2f61d12bf6abdb43"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5669,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4f2f61d12bf6abdb44"
          },
          "AyaContent": "وإذا قيل لهم اركعوا لا يركعون",
          "AyaNumber": 5670,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4f2f61d12bf6abdb45"
          },
          "AyaContent": "ويل يومئذ للمكذبين",
          "AyaNumber": 5671,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4f2f61d12bf6abdb46"
          },
          "AyaContent": "فبأي حديث بعده يؤمنون",
          "AyaNumber": 5672,
          "SurahName": "سورة المرسلات",
          "SurahNumber": 77
        },{
          "_id": {
            "$oid": "5ecd8a4f2f61d12bf6abdb47"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم عم يتساءلون",
