          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a502f61d12bf6abdb4a"
          },
          "AyaContent": "كلا سيعلمون",
          "AyaNumber": 5676,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a502f61d12bf6abdb4b"
          },
          "AyaContent": "ثم كلا سيعلمون",
