          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a512f61d12bf6abdb4e"
          },
          "AyaContent": "وخلقناكم أزواجا",
          "AyaNumber": 5680,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a512f61d12bf6abdb4f"
          },
