          },
          "AyaContent": "وجعلنا سراجا وهاجا",
          "AyaNumber": 5685,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a522f61d12bf6abdb54"
          },
          "AyaContent": "وأنزلنا من المعصرات ماء ثجاجا",
          "AyaNumber": 5686,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a532f61d12bf6abdb55"
          },
          "AyaContent": "لنخرج به حبا ونباتا",
          "AyaNumber": 5687,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a532f61d12bf6abdb56"
          },
          "AyaContent": "وجنات ألفافا",
          "AyaNumber": 5688,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a532f61d12bf6abdb57"
          },
          "AyaContent": "إن يوم الفصل كان ميقاتا",
          "AyaNumber": 5689,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a532f61d12bf6abdb58"
          },
          "AyaContent": "يوم ينفخ في الصور فتأتون أفواجا",
          "AyaNumber": 5690,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a542f61d12bf6abdb59"
          },
          "AyaContent": "وفتحت السماء فكانت أبوابا",
          "AyaNumber": 5691,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a542f61d12bf6abdb5a"
          },
          "AyaContent": "وسيرت الجبال فكانت سرابا",
          "AyaNumber": 5692,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a542f61d12bf6abdb5b"
          },
          "AyaContent": "إن جهنم كانت مرصادا",
          "AyaNumber": 5693,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a552f61d12bf6abdb5c"
          },
          "AyaContent": "للطاغين مآبا",
          "AyaNumber": 5694,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a552f61d12bf6abdb5d"
          },
          "AyaContent": "لابثين فيها أحقابا",
          "AyaNumber": 5695,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a562f61d12bf6abdb5e"
          },
          "AyaContent": "لا يذوقون فيها بردا ولا شرابا",
          "AyaNumber": 5696,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a562f61d12bf6abdb5f"
          },
          "AyaContent": "إلا حميما وغساقا",
          "AyaNumber": 5697,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a562f61d12bf6abdb60"
          },
          "AyaContent": "جزاء وفاقا",
          "AyaNumber": 5698,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a572f61d12bf6abdb61"
          },
          "AyaContent": "إنهم كانوا لا يرجون حسابا",
          "AyaNumber": 5699,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a572f61d12bf6abdb62"
          },
          "AyaContent": "وكذبوا بآياتنا كذابا",
          "AyaNumber": 5700,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a572f61d12bf6abdb63"
          },
          "AyaContent": "وكل شيء أحصيناه كتابا",
          "AyaNumber": 5701,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a572f61d12bf6abdb64"
          },
          "AyaContent": "فذوقوا فلن نزيدكم إلا عذابا",
          "AyaNumber": 5702,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a582f61d12bf6abdb65"
          },
          "AyaContent": "إن للمتقين مفازا",
          "AyaNumber": 5703,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a582f61d12bf6abdb66"
          },
          "AyaContent": "حدائق وأعنابا",
          "AyaNumber": 5704,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a582f61d12bf6abdb67"
          },
          "AyaContent": "وكواعب أترابا",
          "AyaNumber": 5705,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a592f61d12bf6abdb68"
          },
          "AyaContent": "وكأسا دهاقا",
          "AyaNumber": 5706,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a592f61d12bf6abdb69"
          },
          "AyaContent": "لا يسمعون فيها لغوا ولا كذابا",
          "AyaNumber": 5707,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a592f61d12bf6abdb6a"
          },
          "AyaContent": "جزاء من ربك عطاء حسابا",
          "AyaNumber": 5708,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a592f61d12bf6abdb6b"
          },
          "AyaContent": "رب السماوات والأرض وما بينهما الرحمٰن ۖ لا يملكون منه خطابا",
          "AyaNumber": 5709,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a5a2f61d12bf6abdb6c"
          },
          "AyaContent": "يوم يقوم الروح والملائكة صفا ۖ لا يتكلمون إلا من أذن له الرحمٰن وقال صوابا",
          "AyaNumber": 5710,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a5a2f61d12bf6abdb6d"
          },
          "AyaContent": "ذٰلك اليوم الحق ۖ فمن شاء اتخذ إلىٰ ربه مآبا",
          "AyaNumber": 5711,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a5a2f61d12bf6abdb6e"
          },
          "AyaContent": "إنا أنذرناكم عذابا قريبا يوم ينظر المرء ما قدمت يداه ويقول الكافر يا ليتني كنت ترابا",
          "AyaNumber": 5712,
          "SurahName": "سورة النبأ",
          "SurahNumber": 78
        },{
          "_id": {
            "$oid": "5ecd8a5a2f61d12bf6abdb6f"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والنازعات غرقا",
          "AyaNumber": 5713,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5b2f61d12bf6abdb70"
          },
          "AyaContent": "والناشطات نشطا",
          "AyaNumber": 5714,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5b2f61d12bf6abdb71"
          },
          "AyaContent": "والسابحات سبحا",
          "AyaNumber": 5715,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5b2f61d12bf6abdb72"
          },
          "AyaContent": "فالسابقات سبقا",
          "AyaNumber": 5716,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5c2f61d12bf6abdb73"
          },
          "AyaContent": "فالمدبرات أمرا",
          "AyaNumber": 5717,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5c2f61d12bf6abdb74"
          },
          "AyaContent": "يوم ترجف الراجفة",
          "AyaNumber": 5718,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5c2f61d12bf6abdb75"
          },
          "AyaContent": "تتبعها الرادفة",
          "AyaNumber": 5719,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5c2f61d12bf6abdb76"
          },
          "AyaContent": "قلوب يومئذ واجفة",
          "AyaNumber": 5720,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5c2f61d12bf6abdb77"
          },
          "AyaContent": "أبصارها خاشعة",
          "AyaNumber": 5721,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5d2f61d12bf6abdb78"
          },
          "AyaContent": "يقولون أإنا لمردودون في الحافرة",
          "AyaNumber": 5722,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5d2f61d12bf6abdb79"
          },
          "AyaContent": "أإذا كنا عظاما نخرة",
          "AyaNumber": 5723,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5d2f61d12bf6abdb7a"
          },
          "AyaContent": "قالوا تلك إذا كرة خاسرة",
          "AyaNumber": 5724,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5e2f61d12bf6abdb7b"
          },
          "AyaContent": "فإنما هي زجرة واحدة",
          "AyaNumber": 5725,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5e2f61d12bf6abdb7c"
          },
          "AyaContent": "فإذا هم بالساهرة",
          "AyaNumber": 5726,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5e2f61d12bf6abdb7d"
          },
          "AyaContent": "هل أتاك حديث موسىٰ",
          "AyaNumber": 5727,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5f2f61d12bf6abdb7e"
          },
          "AyaContent": "إذ ناداه ربه بالواد المقدس طوى",
          "AyaNumber": 5728,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a5f2f61d12bf6abdb7f"
          },
          "AyaContent": "اذهب إلىٰ فرعون إنه طغىٰ",
          "AyaNumber": 5729,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a602f61d12bf6abdb80"
          },
          "AyaContent": "فقل هل لك إلىٰ أن تزكىٰ",
          "AyaNumber": 5730,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a602f61d12bf6abdb81"
          },
          "AyaContent": "وأهديك إلىٰ ربك فتخشىٰ",
          "AyaNumber": 5731,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a612f61d12bf6abdb82"
          },
          "AyaContent": "فأراه الآية الكبرىٰ",
          "AyaNumber": 5732,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a612f61d12bf6abdb83"
          },
          "AyaContent": "فكذب وعصىٰ",
          "AyaNumber": 5733,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a612f61d12bf6abdb84"
          },
          "AyaContent": "ثم أدبر يسعىٰ",
          "AyaNumber": 5734,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a622f61d12bf6abdb85"
          },
          "AyaContent": "فحشر فنادىٰ",
          "AyaNumber": 5735,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a622f61d12bf6abdb86"
          },
          "AyaContent": "فقال أنا ربكم الأعلىٰ",
          "AyaNumber": 5736,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a632f61d12bf6abdb87"
          },
          "AyaContent": "فأخذه الله نكال الآخرة والأولىٰ",
          "AyaNumber": 5737,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a632f61d12bf6abdb88"
          },
          "AyaContent": "إن في ذٰلك لعبرة لمن يخشىٰ",
          "AyaNumber": 5738,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a632f61d12bf6abdb89"
          },
          "AyaContent": "أأنتم أشد خلقا أم السماء ۚ بناها",
          "AyaNumber": 5739,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a632f61d12bf6abdb8a"
          },
          "AyaContent": "رفع سمكها فسواها",
          "AyaNumber": 5740,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a642f61d12bf6abdb8b"
          },
          "AyaContent": "وأغطش ليلها وأخرج ضحاها",
          "AyaNumber": 5741,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a642f61d12bf6abdb8c"
          },
          "AyaContent": "والأرض بعد ذٰلك دحاها",
          "AyaNumber": 5742,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a642f61d12bf6abdb8d"
          },
          "AyaContent": "أخرج منها ماءها ومرعاها",
          "AyaNumber": 5743,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a652f61d12bf6abdb8e"
          },
          "AyaContent": "والجبال أرساها",
          "AyaNumber": 5744,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a652f61d12bf6abdb8f"
          },
          "AyaContent": "متاعا لكم ولأنعامكم",
          "AyaNumber": 5745,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a652f61d12bf6abdb90"
          },
          "AyaContent": "فإذا جاءت الطامة الكبرىٰ",
          "AyaNumber": 5746,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a652f61d12bf6abdb91"
          },
          "AyaContent": "يوم يتذكر الإنسان ما سعىٰ",
          "AyaNumber": 5747,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a662f61d12bf6abdb92"
          },
          "AyaContent": "وبرزت الجحيم لمن يرىٰ",
          "AyaNumber": 5748,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a662f61d12bf6abdb93"
          },
          "AyaContent": "فأما من طغىٰ",
          "AyaNumber": 5749,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a662f61d12bf6abdb94"
          },
          "AyaContent": "وآثر الحياة الدنيا",
          "AyaNumber": 5750,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a672f61d12bf6abdb95"
          },
          "AyaContent": "فإن الجحيم هي المأوىٰ",
          "AyaNumber": 5751,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a672f61d12bf6abdb96"
          },
          "AyaContent": "وأما من خاف مقام ربه ونهى النفس عن الهوىٰ",
          "AyaNumber": 5752,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a672f61d12bf6abdb97"
          },
          "AyaContent": "فإن الجنة هي المأوىٰ",
          "AyaNumber": 5753,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a672f61d12bf6abdb98"
          },
          "AyaContent": "يسألونك عن الساعة أيان مرساها",
          "AyaNumber": 5754,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a682f61d12bf6abdb99"
          },
          "AyaContent": "فيم أنت من ذكراها",
          "AyaNumber": 5755,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a682f61d12bf6abdb9a"
          },
          "AyaContent": "إلىٰ ربك منتهاها",
          "AyaNumber": 5756,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a682f61d12bf6abdb9b"
          },
          "AyaContent": "إنما أنت منذر من يخشاها",
          "AyaNumber": 5757,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a692f61d12bf6abdb9c"
          },
          "AyaContent": "كأنهم يوم يرونها لم يلبثوا إلا عشية أو ضحاها",
          "AyaNumber": 5758,
          "SurahName": "سورة النازعات",
          "SurahNumber": 79
        },{
          "_id": {
            "$oid": "5ecd8a692f61d12bf6abdb9d"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم عبس وتولىٰ",
          "AyaNumber": 5759,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a692f61d12bf6abdb9e"
          },
          "AyaContent": "أن جاءه الأعمىٰ",
          "AyaNumber": 5760,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6a2f61d12bf6abdb9f"
          },
          "AyaContent": "وما يدريك لعله يزكىٰ",
          "AyaNumber": 5761,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6a2f61d12bf6abdba0"
          },
          "AyaContent": "أو يذكر فتنفعه الذكرىٰ",
          "AyaNumber": 5762,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6a2f61d12bf6abdba1"
          },
          "AyaContent": "أما من استغنىٰ",
          "AyaNumber": 5763,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6a2f61d12bf6abdba2"
          },
          "AyaContent": "فأنت له تصدىٰ",
          "AyaNumber": 5764,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6a2f61d12bf6abdba3"
          },
          "AyaContent": "وما عليك ألا يزكىٰ",
          "AyaNumber": 5765,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6b2f61d12bf6abdba4"
          },
          "AyaContent": "وأما من جاءك يسعىٰ",
          "AyaNumber": 5766,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6b2f61d12bf6abdba5"
          },
          "AyaContent": "وهو يخشىٰ",
          "AyaNumber": 5767,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6b2f61d12bf6abdba6"
          },
          "AyaContent": "فأنت عنه تلهىٰ",
          "AyaNumber": 5768,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6b2f61d12bf6abdba7"
          },
          "AyaContent": "كلا إنها تذكرة",
          "AyaNumber": 5769,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6c2f61d12bf6abdba8"
          },
          "AyaContent": "فمن شاء ذكره",
          "AyaNumber": 5770,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6c2f61d12bf6abdba9"
          },
          "AyaContent": "في صحف مكرمة",
          "AyaNumber": 5771,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6c2f61d12bf6abdbaa"
          },
          "AyaContent": "مرفوعة مطهرة",
          "AyaNumber": 5772,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6c2f61d12bf6abdbab"
          },
          "AyaContent": "بأيدي سفرة",
          "AyaNumber": 5773,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6d2f61d12bf6abdbac"
          },
          "AyaContent": "كرام بررة",
          "AyaNumber": 5774,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6d2f61d12bf6abdbad"
          },
          "AyaContent": "قتل الإنسان ما أكفره",
          "AyaNumber": 5775,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6d2f61d12bf6abdbae"
          },
          "AyaContent": "من أي شيء خلقه",
          "AyaNumber": 5776,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6d2f61d12bf6abdbaf"
          },
          "AyaContent": "من نطفة خلقه فقدره",
          "AyaNumber": 5777,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6e2f61d12bf6abdbb0"
          },
          "AyaContent": "ثم السبيل يسره",
          "AyaNumber": 5778,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6e2f61d12bf6abdbb1"
          },
          "AyaContent": "ثم أماته فأقبره",
          "AyaNumber": 5779,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6e2f61d12bf6abdbb2"
          },
          "AyaContent": "ثم إذا شاء أنشره",
          "AyaNumber": 5780,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6e2f61d12bf6abdbb3"
          },
          "AyaContent": "كلا لما يقض ما أمره",
          "AyaNumber": 5781,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6f2f61d12bf6abdbb4"
          },
          "AyaContent": "فلينظر الإنسان إلىٰ طعامه",
          "AyaNumber": 5782,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6f2f61d12bf6abdbb5"
          },
          "AyaContent": "أنا صببنا الماء صبا",
          "AyaNumber": 5783,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6f2f61d12bf6abdbb6"
          },
          "AyaContent": "ثم شققنا الأرض شقا",
          "AyaNumber": 5784,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a6f2f61d12bf6abdbb7"
          },
          "AyaContent": "فأنبتنا فيها حبا",
          "AyaNumber": 5785,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a702f61d12bf6abdbb8"
          },
          "AyaContent": "وعنبا وقضبا",
          "AyaNumber": 5786,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a702f61d12bf6abdbb9"
          },
          "AyaContent": "وزيتونا ونخلا",
          "AyaNumber": 5787,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a702f61d12bf6abdbba"
          },
          "AyaContent": "وحدائق غلبا",
          "AyaNumber": 5788,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a702f61d12bf6abdbbb"
          },
          "AyaContent": "وفاكهة وأبا",
          "AyaNumber": 5789,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a702f61d12bf6abdbbc"
          },
          "AyaContent": "متاعا لكم ولأنعامكم",
          "AyaNumber": 5790,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a712f61d12bf6abdbbd"
          },
          "AyaContent": "فإذا جاءت الصاخة",
          "AyaNumber": 5791,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a712f61d12bf6abdbbe"
          },
          "AyaContent": "يوم يفر المرء من أخيه",
          "AyaNumber": 5792,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a712f61d12bf6abdbbf"
          },
          "AyaContent": "وأمه وأبيه",
          "AyaNumber": 5793,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a712f61d12bf6abdbc0"
          },
          "AyaContent": "وصاحبته وبنيه",
          "AyaNumber": 5794,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a722f61d12bf6abdbc1"
          },
          "AyaContent": "لكل امرئ منهم يومئذ شأن يغنيه",
          "AyaNumber": 5795,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a722f61d12bf6abdbc2"
          },
          "AyaContent": "وجوه يومئذ مسفرة",
          "AyaNumber": 5796,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a722f61d12bf6abdbc3"
          },
          "AyaContent": "ضاحكة مستبشرة",
          "AyaNumber": 5797,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a722f61d12bf6abdbc4"
          },
          "AyaContent": "ووجوه يومئذ عليها غبرة",
          "AyaNumber": 5798,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a722f61d12bf6abdbc5"
          },
          "AyaContent": "ترهقها قترة",
          "AyaNumber": 5799,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a732f61d12bf6abdbc6"
          },
          "AyaContent": "أولٰئك هم الكفرة الفجرة",
          "AyaNumber": 5800,
          "SurahName": "سورة عبس",
          "SurahNumber": 80
        },{
          "_id": {
            "$oid": "5ecd8a732f61d12bf6abdbc7"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم إذا الشمس كورت",
          "AyaNumber": 5801,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a732f61d12bf6abdbc8"
          },
          "AyaContent": "وإذا النجوم انكدرت",
          "AyaNumber": 5802,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a732f61d12bf6abdbc9"
          },
          "AyaContent": "وإذا الجبال سيرت",
          "AyaNumber": 5803,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a742f61d12bf6abdbca"
          },
          "AyaContent": "وإذا العشار عطلت",
          "AyaNumber": 5804,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a742f61d12bf6abdbcb"
          },
          "AyaContent": "وإذا الوحوش حشرت",
          "AyaNumber": 5805,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a742f61d12bf6abdbcc"
          },
          "AyaContent": "وإذا البحار سجرت",
          "AyaNumber": 5806,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a752f61d12bf6abdbcd"
          },
          "AyaContent": "وإذا النفوس زوجت",
          "AyaNumber": 5807,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a752f61d12bf6abdbce"
          },
          "AyaContent": "وإذا الموءودة سئلت",
          "AyaNumber": 5808,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a752f61d12bf6abdbcf"
          },
          "AyaContent": "بأي ذنب قتلت",
          "AyaNumber": 5809,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a762f61d12bf6abdbd0"
          },
          "AyaContent": "وإذا الصحف نشرت",
          "AyaNumber": 5810,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a762f61d12bf6abdbd1"
          },
          "AyaContent": "وإذا السماء كشطت",
          "AyaNumber": 5811,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a762f61d12bf6abdbd2"
          },
          "AyaContent": "وإذا الجحيم سعرت",
          "AyaNumber": 5812,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a762f61d12bf6abdbd3"
          },
          "AyaContent": "وإذا الجنة أزلفت",
          "AyaNumber": 5813,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a772f61d12bf6abdbd4"
          },
          "AyaContent": "علمت نفس ما أحضرت",
          "AyaNumber": 5814,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a772f61d12bf6abdbd5"
          },
          "AyaContent": "فلا أقسم بالخنس",
          "AyaNumber": 5815,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a772f61d12bf6abdbd6"
          },
          "AyaContent": "الجوار الكنس",
          "AyaNumber": 5816,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a772f61d12bf6abdbd7"
          },
          "AyaContent": "والليل إذا عسعس",
          "AyaNumber": 5817,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a782f61d12bf6abdbd8"
          },
          "AyaContent": "والصبح إذا تنفس",
          "AyaNumber": 5818,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a782f61d12bf6abdbd9"
          },
          "AyaContent": "إنه لقول رسول كريم",
          "AyaNumber": 5819,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a782f61d12bf6abdbda"
          },
          "AyaContent": "ذي قوة عند ذي العرش مكين",
          "AyaNumber": 5820,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a792f61d12bf6abdbdb"
          },
          "AyaContent": "مطاع ثم أمين",
          "AyaNumber": 5821,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a792f61d12bf6abdbdc"
          },
          "AyaContent": "وما صاحبكم بمجنون",
          "AyaNumber": 5822,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a792f61d12bf6abdbdd"
          },
          "AyaContent": "ولقد رآه بالأفق المبين",
          "AyaNumber": 5823,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a792f61d12bf6abdbde"
          },
          "AyaContent": "وما هو على الغيب بضنين",
          "AyaNumber": 5824,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a7a2f61d12bf6abdbdf"
          },
          "AyaContent": "وما هو بقول شيطان رجيم",
          "AyaNumber": 5825,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a7a2f61d12bf6abdbe0"
          },
          "AyaContent": "فأين تذهبون",
          "AyaNumber": 5826,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a7a2f61d12bf6abdbe1"
          },
          "AyaContent": "إن هو إلا ذكر للعالمين",
          "AyaNumber": 5827,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a7a2f61d12bf6abdbe2"
          },
          "AyaContent": "لمن شاء منكم أن يستقيم",
          "AyaNumber": 5828,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a7b2f61d12bf6abdbe3"
          },
          "AyaContent": "وما تشاءون إلا أن يشاء الله رب العالمين",
          "AyaNumber": 5829,
          "SurahName": "سورة التكوير",
          "SurahNumber": 81
        },{
          "_id": {
            "$oid": "5ecd8a7b2f61d12bf6abdbe4"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم إذا السماء انفطرت",
          "AyaNumber": 5830,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7b2f61d12bf6abdbe5"
          },
          "AyaContent": "وإذا الكواكب انتثرت",
          "AyaNumber": 5831,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7b2f61d12bf6abdbe6"
          },
          "AyaContent": "وإذا البحار فجرت",
          "AyaNumber": 5832,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7b2f61d12bf6abdbe7"
          },
          "AyaContent": "وإذا القبور بعثرت",
          "AyaNumber": 5833,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7c2f61d12bf6abdbe8"
          },
          "AyaContent": "علمت نفس ما قدمت وأخرت",
          "AyaNumber": 5834,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7c2f61d12bf6abdbe9"
          },
          "AyaContent": "يا أيها الإنسان ما غرك بربك الكريم",
          "AyaNumber": 5835,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7d2f61d12bf6abdbea"
          },
          "AyaContent": "الذي خلقك فسواك فعدلك",
          "AyaNumber": 5836,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7d2f61d12bf6abdbeb"
          },
          "AyaContent": "في أي صورة ما شاء ركبك",
          "AyaNumber": 5837,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7d2f61d12bf6abdbec"
          },
          "AyaContent": "كلا بل تكذبون بالدين",
          "AyaNumber": 5838,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7e2f61d12bf6abdbed"
          },
          "AyaContent": "وإن عليكم لحافظين",
          "AyaNumber": 5839,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7e2f61d12bf6abdbee"
          },
          "AyaContent": "كراما كاتبين",
          "AyaNumber": 5840,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7e2f61d12bf6abdbef"
          },
          "AyaContent": "يعلمون ما تفعلون",
          "AyaNumber": 5841,
          "SurahName": "سورة الإنفطار",
          "SurahNumber": 82
        },{
          "_id": {
            "$oid": "5ecd8a7e2f61d12bf6abdbf0"
          },
          "AyaContent": "إن الأبرار لفي نعيم",
