        },{
          "_id": {
            "$oid": "5ecd8aaf2f61d12bf6abdc8b"
          },
          "AyaContent": "والليل إذا يسر",
          "AyaNumber": 5997,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8aaf2f61d12bf6abdc8c"
          },
          "AyaContent": "هل في ذٰلك قسم لذي حجر",
          "AyaNumber": 5998,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab02f61d12bf6abdc8d"
          },
          "AyaContent": "ألم تر كيف فعل ربك بعاد",
          "AyaNumber": 5999,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab02f61d12bf6abdc8e"
          },
          "AyaContent": "إرم ذات العماد",
          "AyaNumber": 6000,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab02f61d12bf6abdc8f"
          },
          "AyaContent": "التي لم يخلق مثلها في البلاد",
          "AyaNumber": 6001,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab12f61d12bf6abdc90"
          },
          "AyaContent": "وثمود الذين جابوا الصخر بالواد",
          "AyaNumber": 6002,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab12f61d12bf6abdc91"
          },
          "AyaContent": "وفرعون ذي الأوتاد",
          "AyaNumber": 6003,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab12f61d12bf6abdc92"
          },
          "AyaContent": "الذين طغوا في البلاد",
          "AyaNumber": 6004,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab12f61d12bf6abdc93"
          },
          "AyaContent": "فأكثروا فيها الفساد",
          "AyaNumber": 6005,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab22f61d12bf6abdc94"
          },
          "AyaContent": "فصب عليهم ربك سوط عذاب",
          "AyaNumber": 6006,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab22f61d12bf6abdc95"
          },
          "AyaContent": "إن ربك لبالمرصاد",
          "AyaNumber": 6007,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab22f61d12bf6abdc96"
          },
          "AyaContent": "فأما الإنسان إذا ما ابتلاه ربه فأكرمه ونعمه فيقول ربي أكرمن",
          "AyaNumber": 6008,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab22f61d12bf6abdc97"
          },
          "AyaContent": "وأما إذا ما ابتلاه فقدر عليه رزقه فيقول ربي أهانن",
          "AyaNumber": 6009,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab32f61d12bf6abdc98"
          },
          "AyaContent": "كلا ۖ بل لا تكرمون اليتيم",
          "AyaNumber": 6010,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab32f61d12bf6abdc99"
          },
          "AyaContent": "ولا تحاضون علىٰ طعام المسكين",
          "AyaNumber": 6011,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab32f61d12bf6abdc9a"
          },
          "AyaContent": "وتأكلون التراث أكلا لما",
          "AyaNumber": 6012,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab32f61d12bf6abdc9b"
          },
          "AyaContent": "وتحبون المال حبا جما",
          "AyaNumber": 6013,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab42f61d12bf6abdc9c"
          },
          "AyaContent": "كلا إذا دكت الأرض دكا دكا",
          "AyaNumber": 6014,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab42f61d12bf6abdc9d"
          },
          "AyaContent": "وجاء ربك والملك صفا صفا",
          "AyaNumber": 6015,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab42f61d12bf6abdc9e"
          },
          "AyaContent": "وجيء يومئذ بجهنم ۚ يومئذ يتذكر الإنسان وأنىٰ له الذكرىٰ",
          "AyaNumber": 6016,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab42f61d12bf6abdc9f"
          },
          "AyaContent": "يقول يا ليتني قدمت لحياتي",
          "AyaNumber": 6017,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab52f61d12bf6abdca0"
          },
          "AyaContent": "فيومئذ لا يعذب عذابه أحد",
          "AyaNumber": 6018,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab52f61d12bf6abdca1"
          },
          "AyaContent": "ولا يوثق وثاقه أحد",
          "AyaNumber": 6019,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab52f61d12bf6abdca2"
          },
          "AyaContent": "يا أيتها النفس المطمئنة",
          "AyaNumber": 6020,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab52f61d12bf6abdca3"
          },
          "AyaContent": "ارجعي إلىٰ ربك راضية مرضية",
          "AyaNumber": 6021,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab52f61d12bf6abdca4"
          },
          "AyaContent": "فادخلي في عبادي",
          "AyaNumber": 6022,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab62f61d12bf6abdca5"
          },
          "AyaContent": "وادخلي جنتي",
          "AyaNumber": 6023,
          "SurahName": "سورة الفجر",
          "SurahNumber": 89
        },{
          "_id": {
            "$oid": "5ecd8ab62f61d12bf6abdca6"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم لا أقسم بهٰذا البلد",
          "AyaNumber": 6024,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab62f61d12bf6abdca7"
          },
          "AyaContent": "وأنت حل بهٰذا البلد",
          "AyaNumber": 6025,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab62f61d12bf6abdca8"
          },
          "AyaContent": "ووالد وما ولد",
          "AyaNumber": 6026,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab62f61d12bf6abdca9"
          },
          "AyaContent": "لقد خلقنا الإنسان في كبد",
          "AyaNumber": 6027,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab62f61d12bf6abdcaa"
          },
          "AyaContent": "أيحسب أن لن يقدر عليه أحد",
          "AyaNumber": 6028,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab72f61d12bf6abdcab"
          },
          "AyaContent": "يقول أهلكت مالا لبدا",
          "AyaNumber": 6029,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab72f61d12bf6abdcac"
          },
          "AyaContent": "أيحسب أن لم يره أحد",
          "AyaNumber": 6030,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab72f61d12bf6abdcad"
          },
          "AyaContent": "ألم نجعل له عينين",
          "AyaNumber": 6031,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab72f61d12bf6abdcae"
          },
          "AyaContent": "ولسانا وشفتين",
          "AyaNumber": 6032,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab72f61d12bf6abdcaf"
          },
          "AyaContent": "وهديناه النجدين",
          "AyaNumber": 6033,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab72f61d12bf6abdcb0"
          },
          "AyaContent": "فلا اقتحم العقبة",
          "AyaNumber": 6034,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab82f61d12bf6abdcb1"
          },
          "AyaContent": "وما أدراك ما العقبة",
          "AyaNumber": 6035,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab82f61d12bf6abdcb2"
          },
          "AyaContent": "فك رقبة",
          "AyaNumber": 6036,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab82f61d12bf6abdcb3"
          },
          "AyaContent": "أو إطعام في يوم ذي مسغبة",
          "AyaNumber": 6037,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab92f61d12bf6abdcb4"
          },
          "AyaContent": "يتيما ذا مقربة",
          "AyaNumber": 6038,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab92f61d12bf6abdcb5"
          },
          "AyaContent": "أو مسكينا ذا متربة",
          "AyaNumber": 6039,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab92f61d12bf6abdcb6"
          },
          "AyaContent": "ثم كان من الذين آمنوا وتواصوا بالصبر وتواصوا بالمرحمة",
          "AyaNumber": 6040,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab92f61d12bf6abdcb7"
          },
          "AyaContent": "أولٰئك أصحاب الميمنة",
          "AyaNumber": 6041,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8ab92f61d12bf6abdcb8"
          },
          "AyaContent": "والذين كفروا بآياتنا هم أصحاب المشأمة",
          "AyaNumber": 6042,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8aba2f61d12bf6abdcb9"
          },
          "AyaContent": "عليهم نار مؤصدة",
          "AyaNumber": 6043,
          "SurahName": "سورة البلد",
          "SurahNumber": 90
        },{
          "_id": {
            "$oid": "5ecd8aba2f61d12bf6abdcba"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والشمس وضحاها",
          "AyaNumber": 6044,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abb2f61d12bf6abdcbb"
          },
          "AyaContent": "والقمر إذا تلاها",
          "AyaNumber": 6045,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abb2f61d12bf6abdcbc"
          },
          "AyaContent": "والنهار إذا جلاها",
          "AyaNumber": 6046,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abb2f61d12bf6abdcbd"
          },
          "AyaContent": "والليل إذا يغشاها",
          "AyaNumber": 6047,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abb2f61d12bf6abdcbe"
          },
          "AyaContent": "والسماء وما بناها",
          "AyaNumber": 6048,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abb2f61d12bf6abdcbf"
          },
          "AyaContent": "والأرض وما طحاها",
          "AyaNumber": 6049,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abc2f61d12bf6abdcc0"
          },
          "AyaContent": "ونفس وما سواها",
          "AyaNumber": 6050,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abc2f61d12bf6abdcc1"
          },
          "AyaContent": "فألهمها فجورها وتقواها",
          "AyaNumber": 6051,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abc2f61d12bf6abdcc2"
          },
          "AyaContent": "قد أفلح من زكاها",
          "AyaNumber": 6052,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abd2f61d12bf6abdcc3"
          },
          "AyaContent": "وقد خاب من دساها",
          "AyaNumber": 6053,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abd2f61d12bf6abdcc4"
          },
          "AyaContent": "كذبت ثمود بطغواها",
          "AyaNumber": 6054,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abd2f61d12bf6abdcc5"
          },
          "AyaContent": "إذ انبعث أشقاها",
          "AyaNumber": 6055,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abd2f61d12bf6abdcc6"
          },
          "AyaContent": "فقال لهم رسول الله ناقة الله وسقياها",
          "AyaNumber": 6056,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abe2f61d12bf6abdcc7"
          },
          "AyaContent": "فكذبوه فعقروها فدمدم عليهم ربهم بذنبهم فسواها",
          "AyaNumber": 6057,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abe2f61d12bf6abdcc8"
          },
          "AyaContent": "ولا يخاف عقباها",
          "AyaNumber": 6058,
          "SurahName": "سورة الشمس",
          "SurahNumber": 91
        },{
          "_id": {
            "$oid": "5ecd8abe2f61d12bf6abdcc9"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والليل إذا يغشىٰ",
          "AyaNumber": 6059,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8abf2f61d12bf6abdcca"
          },
          "AyaContent": "والنهار إذا تجلىٰ",
          "AyaNumber": 6060,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8abf2f61d12bf6abdccb"
          },
          "AyaContent": "وما خلق الذكر والأنثىٰ",
          "AyaNumber": 6061,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac02f61d12bf6abdccc"
          },
          "AyaContent": "إن سعيكم لشتىٰ",
          "AyaNumber": 6062,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac02f61d12bf6abdccd"
          },
          "AyaContent": "فأما من أعطىٰ واتقىٰ",
          "AyaNumber": 6063,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac02f61d12bf6abdcce"
          },
          "AyaContent": "وصدق بالحسنىٰ",
          "AyaNumber": 6064,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac02f61d12bf6abdccf"
          },
          "AyaContent": "فسنيسره لليسرىٰ",
          "AyaNumber": 6065,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac12f61d12bf6abdcd0"
          },
          "AyaContent": "وأما من بخل واستغنىٰ",
          "AyaNumber": 6066,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac12f61d12bf6abdcd1"
          },
          "AyaContent": "وكذب بالحسنىٰ",
          "AyaNumber": 6067,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac12f61d12bf6abdcd2"
          },
          "AyaContent": "فسنيسره للعسرىٰ",
          "AyaNumber": 6068,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac12f61d12bf6abdcd3"
          },
          "AyaContent": "وما يغني عنه ماله إذا تردىٰ",
          "AyaNumber": 6069,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac22f61d12bf6abdcd4"
          },
          "AyaContent": "إن علينا للهدىٰ",
          "AyaNumber": 6070,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac22f61d12bf6abdcd5"
          },
          "AyaContent": "وإن لنا للآخرة والأولىٰ",
          "AyaNumber": 6071,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac22f61d12bf6abdcd6"
          },
          "AyaContent": "فأنذرتكم نارا تلظىٰ",
          "AyaNumber": 6072,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac32f61d12bf6abdcd7"
          },
          "AyaContent": "لا يصلاها إلا الأشقى",
          "AyaNumber": 6073,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac32f61d12bf6abdcd8"
          },
          "AyaContent": "الذي كذب وتولىٰ",
          "AyaNumber": 6074,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac32f61d12bf6abdcd9"
          },
          "AyaContent": "وسيجنبها الأتقى",
          "AyaNumber": 6075,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac42f61d12bf6abdcda"
          },
          "AyaContent": "الذي يؤتي ماله يتزكىٰ",
          "AyaNumber": 6076,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac42f61d12bf6abdcdb"
          },
          "AyaContent": "وما لأحد عنده من نعمة تجزىٰ",
          "AyaNumber": 6077,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac42f61d12bf6abdcdc"
          },
          "AyaContent": "إلا ابتغاء وجه ربه الأعلىٰ",
          "AyaNumber": 6078,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac52f61d12bf6abdcdd"
          },
          "AyaContent": "ولسوف يرضىٰ",
          "AyaNumber": 6079,
          "SurahName": "سورة الليل",
          "SurahNumber": 92
        },{
          "_id": {
            "$oid": "5ecd8ac52f61d12bf6abdcde"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والضحىٰ",
          "AyaNumber": 6080,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac52f61d12bf6abdcdf"
          },
          "AyaContent": "والليل إذا سجىٰ",
          "AyaNumber": 6081,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac52f61d12bf6abdce0"
          },
          "AyaContent": "ما ودعك ربك وما قلىٰ",
          "AyaNumber": 6082,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac62f61d12bf6abdce1"
          },
          "AyaContent": "وللآخرة خير لك من الأولىٰ",
          "AyaNumber": 6083,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac62f61d12bf6abdce2"
          },
          "AyaContent": "ولسوف يعطيك ربك فترضىٰ",
          "AyaNumber": 6084,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac62f61d12bf6abdce3"
          },
          "AyaContent": "ألم يجدك يتيما فآوىٰ",
          "AyaNumber": 6085,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac72f61d12bf6abdce4"
          },
          "AyaContent": "ووجدك ضالا فهدىٰ",
          "AyaNumber": 6086,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac72f61d12bf6abdce5"
          },
          "AyaContent": "ووجدك عائلا فأغنىٰ",
          "AyaNumber": 6087,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac72f61d12bf6abdce6"
          },
          "AyaContent": "فأما اليتيم فلا تقهر",
          "AyaNumber": 6088,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac82f61d12bf6abdce7"
          },
          "AyaContent": "وأما السائل فلا تنهر",
          "AyaNumber": 6089,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac82f61d12bf6abdce8"
          },
          "AyaContent": "وأما بنعمة ربك فحدث",
          "AyaNumber": 6090,
          "SurahName": "سورة الضحى",
          "SurahNumber": 93
        },{
          "_id": {
            "$oid": "5ecd8ac82f61d12bf6abdce9"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم ألم نشرح لك صدرك",
          "AyaNumber": 6091,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8ac92f61d12bf6abdcea"
          },
          "AyaContent": "ووضعنا عنك وزرك",
          "AyaNumber": 6092,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8ac92f61d12bf6abdceb"
          },
          "AyaContent": "الذي أنقض ظهرك",
          "AyaNumber": 6093,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8ac92f61d12bf6abdcec"
          },
          "AyaContent": "ورفعنا لك ذكرك",
          "AyaNumber": 6094,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8aca2f61d12bf6abdced"
          },
          "AyaContent": "فإن مع العسر يسرا",
          "AyaNumber": 6095,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8aca2f61d12bf6abdcee"
          },
          "AyaContent": "إن مع العسر يسرا",
          "AyaNumber": 6096,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8acb2f61d12bf6abdcef"
          },
          "AyaContent": "فإذا فرغت فانصب",
          "AyaNumber": 6097,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8acb2f61d12bf6abdcf0"
          },
          "AyaContent": "وإلىٰ ربك فارغب",
          "AyaNumber": 6098,
          "SurahName": "سورة الشرح",
          "SurahNumber": 94
        },{
          "_id": {
            "$oid": "5ecd8acd2f61d12bf6abdcf1"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والتين والزيتون",
          "AyaNumber": 6099,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8acd2f61d12bf6abdcf2"
          },
          "AyaContent": "وطور سينين",
          "AyaNumber": 6100,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8acd2f61d12bf6abdcf3"
          },
          "AyaContent": "وهٰذا البلد الأمين",
          "AyaNumber": 6101,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8acd2f61d12bf6abdcf4"
          },
          "AyaContent": "لقد خلقنا الإنسان في أحسن تقويم",
          "AyaNumber": 6102,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8ace2f61d12bf6abdcf5"
          },
          "AyaContent": "ثم رددناه أسفل سافلين",
          "AyaNumber": 6103,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8ace2f61d12bf6abdcf6"
          },
          "AyaContent": "إلا الذين آمنوا وعملوا الصالحات فلهم أجر غير ممنون",
          "AyaNumber": 6104,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8ace2f61d12bf6abdcf7"
          },
          "AyaContent": "فما يكذبك بعد بالدين",
          "AyaNumber": 6105,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8acf2f61d12bf6abdcf8"
          },
          "AyaContent": "أليس الله بأحكم الحاكمين",
          "AyaNumber": 6106,
          "SurahName": "سورة التين",
          "SurahNumber": 95
        },{
          "_id": {
            "$oid": "5ecd8acf2f61d12bf6abdcf9"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم اقرأ باسم ربك الذي خلق",
          "AyaNumber": 6107,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8acf2f61d12bf6abdcfa"
          },
          "AyaContent": "خلق الإنسان من علق",
          "AyaNumber": 6108,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad02f61d12bf6abdcfb"
          },
          "AyaContent": "اقرأ وربك الأكرم",
          "AyaNumber": 6109,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad02f61d12bf6abdcfc"
          },
          "AyaContent": "الذي علم بالقلم",
          "AyaNumber": 6110,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad02f61d12bf6abdcfd"
          },
          "AyaContent": "علم الإنسان ما لم يعلم",
          "AyaNumber": 6111,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad02f61d12bf6abdcfe"
          },
          "AyaContent": "كلا إن الإنسان ليطغىٰ",
          "AyaNumber": 6112,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad02f61d12bf6abdcff"
          },
          "AyaContent": "أن رآه استغنىٰ",
          "AyaNumber": 6113,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad12f61d12bf6abdd00"
          },
          "AyaContent": "إن إلىٰ ربك الرجعىٰ",
          "AyaNumber": 6114,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad12f61d12bf6abdd01"
          },
          "AyaContent": "أرأيت الذي ينهىٰ",
          "AyaNumber": 6115,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad12f61d12bf6abdd02"
          },
          "AyaContent": "عبدا إذا صلىٰ",
          "AyaNumber": 6116,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad12f61d12bf6abdd03"
          },
          "AyaContent": "أرأيت إن كان على الهدىٰ",
          "AyaNumber": 6117,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad12f61d12bf6abdd04"
          },
          "AyaContent": "أو أمر بالتقوىٰ",
          "AyaNumber": 6118,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad22f61d12bf6abdd05"
          },
          "AyaContent": "أرأيت إن كذب وتولىٰ",
          "AyaNumber": 6119,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad22f61d12bf6abdd06"
          },
          "AyaContent": "ألم يعلم بأن الله يرىٰ",
          "AyaNumber": 6120,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad22f61d12bf6abdd07"
          },
          "AyaContent": "كلا لئن لم ينته لنسفعا بالناصية",
          "AyaNumber": 6121,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad22f61d12bf6abdd08"
          },
          "AyaContent": "ناصية كاذبة خاطئة",
          "AyaNumber": 6122,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad22f61d12bf6abdd09"
          },
          "AyaContent": "فليدع ناديه",
          "AyaNumber": 6123,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad22f61d12bf6abdd0a"
          },
          "AyaContent": "سندع الزبانية",
          "AyaNumber": 6124,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad32f61d12bf6abdd0b"
          },
          "AyaContent": "كلا لا تطعه واسجد واقترب ۩",
          "AyaNumber": 6125,
          "SurahName": "سورة العلق",
          "SurahNumber": 96
        },{
          "_id": {
            "$oid": "5ecd8ad32f61d12bf6abdd0c"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم إنا أنزلناه في ليلة القدر",
          "AyaNumber": 6126,
          "SurahName": "سورة القدر",
          "SurahNumber": 97
        },{
          "_id": {
            "$oid": "5ecd8ad32f61d12bf6abdd0d"
          },
          "AyaContent": "وما أدراك ما ليلة القدر",
          "AyaNumber": 6127,
          "SurahName": "سورة القدر",
          "SurahNumber": 97
        },{
          "_id": {
            "$oid": "5ecd8ad32f61d12bf6abdd0e"
          },
          "AyaContent": "ليلة القدر خير من ألف شهر",
          "AyaNumber": 6128,
          "SurahName": "سورة القدر",
          "SurahNumber": 97
        },{
          "_id": {
            "$oid": "5ecd8ad32f61d12bf6abdd0f"
          },
          "AyaContent": "تنزل الملائكة والروح فيها بإذن ربهم من كل أمر",
          "AyaNumber": 6129,
          "SurahName": "سورة القدر",
          "SurahNumber": 97
        },{
          "_id": {
            "$oid": "5ecd8ad42f61d12bf6abdd10"
          },
          "AyaContent": "سلام هي حتىٰ مطلع الفجر",
          "AyaNumber": 6130,
          "SurahName": "سورة القدر",
          "SurahNumber": 97
        },{
          "_id": {
            "$oid": "5ecd8ad42f61d12bf6abdd11"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم لم يكن الذين كفروا من أهل الكتاب والمشركين منفكين حتىٰ تأتيهم البينة",
          "AyaNumber": 6131,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad42f61d12bf6abdd12"
          },
          "AyaContent": "رسول من الله يتلو صحفا مطهرة",
          "AyaNumber": 6132,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad42f61d12bf6abdd13"
          },
          "AyaContent": "فيها كتب قيمة",
          "AyaNumber": 6133,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad42f61d12bf6abdd14"
          },
          "AyaContent": "وما تفرق الذين أوتوا الكتاب إلا من بعد ما جاءتهم البينة",
          "AyaNumber": 6134,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad42f61d12bf6abdd15"
          },
          "AyaContent": "وما أمروا إلا ليعبدوا الله مخلصين له الدين حنفاء ويقيموا الصلاة ويؤتوا الزكاة ۚ وذٰلك دين القيمة",
          "AyaNumber": 6135,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad42f61d12bf6abdd16"
          },
          "AyaContent": "إن الذين كفروا من أهل الكتاب والمشركين في نار جهنم خالدين فيها ۚ أولٰئك هم شر البرية",
          "AyaNumber": 6136,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad52f61d12bf6abdd17"
          },
          "AyaContent": "إن الذين آمنوا وعملوا الصالحات أولٰئك هم خير البرية",
          "AyaNumber": 6137,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad52f61d12bf6abdd18"
          },
          "AyaContent": "جزاؤهم عند ربهم جنات عدن تجري من تحتها الأنهار خالدين فيها أبدا ۖ رضي الله عنهم ورضوا عنه ۚ ذٰلك لمن خشي ربه",
          "AyaNumber": 6138,
          "SurahName": "سورة البينة",
          "SurahNumber": 98
        },{
          "_id": {
            "$oid": "5ecd8ad52f61d12bf6abdd19"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم إذا زلزلت الأرض زلزالها",
          "AyaNumber": 6139,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad52f61d12bf6abdd1a"
          },
          "AyaContent": "وأخرجت الأرض أثقالها",
          "AyaNumber": 6140,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad62f61d12bf6abdd1b"
          },
          "AyaContent": "وقال الإنسان ما لها",
          "AyaNumber": 6141,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad62f61d12bf6abdd1c"
          },
          "AyaContent": "يومئذ تحدث أخبارها",
          "AyaNumber": 6142,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad62f61d12bf6abdd1d"
          },
          "AyaContent": "بأن ربك أوحىٰ لها",
          "AyaNumber": 6143,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad62f61d12bf6abdd1e"
          },
          "AyaContent": "يومئذ يصدر الناس أشتاتا ليروا أعمالهم",
          "AyaNumber": 6144,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad62f61d12bf6abdd1f"
          },
          "AyaContent": "فمن يعمل مثقال ذرة خيرا يره",
          "AyaNumber": 6145,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad72f61d12bf6abdd20"
          },
          "AyaContent": "ومن يعمل مثقال ذرة شرا يره",
          "AyaNumber": 6146,
          "SurahName": "سورة الزلزلة",
          "SurahNumber": 99
        },{
          "_id": {
            "$oid": "5ecd8ad72f61d12bf6abdd21"
          },
          "AyaContent": "بسم الله الرحمٰن الرحيم والعاديات ضبحا",
          "AyaNumber": 6147,
          "SurahName": "سورة العاديات",
          "SurahNumber": 100
        },{
          "_id": {
            "$oid": "5ecd8ad72f61d12bf6abdd22"
          },
          "AyaContent": "فالموريات قدحا",
          "AyaNumber": 6148,
          "SurahName": "سورة العاديات",
          "SurahNumber": 100
        },{
          "_id": {
            "$oid": "5ecd8ad72f61d12bf6abdd23"
          },
          "AyaContent": "فالمغيرات صبحا",
          "AyaNumber": 6149,
          "SurahName": "سورة العاديات",
          "SurahNumber": 100
        },{
          "_id": {
            "$oid": "5ecd8ad82f61d12bf6abdd24"
          },
          "AyaContent": "فأثرن به نقعا",
          "AyaNumber": 6150,
          "SurahName": "سورة العاديات",
          "SurahNumber": 100
        },{
          "_id": {
            "$oid": "5ecd8ad82f61d12bf6abdd25"
          },
          "AyaContent": "فوسطن به جمعا",
